(57, '', 1, 'Lá bưởi tuy có công dụng mạnh trong 1 số liệu pháp trị bệnh nhưng đối với nám thì không.', 30, 28, 0, '09/11/2019', 1, NULL, NULL, NULL),
(58, '', 1, 'Cần cách li trẻ nếu có hiện tượng mắc bệnh hoặc trong vùng dịch, bệnh lây lan nhanh và ảnh hưởng nghiêm trọng, hãy chú ý', 30, 3, 0, '09/11/2019', 1, NULL, NULL, NULL),
