INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(1, 19, 'Thực đơn cho bệnh nhân viêm da', '“Chế độ dinh dưỡng thế nào tốt cho người bị viêm da” là thắc mắc của rất nhiều người đặc biệt là vào mùa thu &#8211; mùa của bệnh viêm da. Để điều trị bệnh hiệu quả và nhanh chóng thì ngoài việc uống thuốc bạn nên có chế độ ăn uống và sinh hoạt hợp lý hơn.\n Theo các bác sĩ, có rất nhiều nguyên nhân khiến nhiều người mắc bệnh viêm da dị ứng và phổ biến nhất là do gen tương tác với yếu tố môi trường gây ảnh hưởng đến da và dễ phát bệnh. Bên cạnh đó, chế độ dinh dưỡng cũng là yếu tố dẫn đến viêm da. Nhằm đưa đến những thông tin bổ ích giúp người bệnh có chế độ ăn uống hợp lý, đồng thời tránh được những món ăn gây hại cho cơ thể, các bác sĩ của Phòng khám Da liễu Hà Nội sẽ đưa ra thực đơn gồm những món nên và không nên cho bệnh nhân viêm da.\n Thực phẩm bệnh nhân viêm da nên ăn\n Với những triệu chứng người bệnh mắc phải như bong tróc da, da sần sùi, nguy cơ để lại sẹo là rất cao. Chính vì vậy, người bị bệnh viêm da cần có chế độ ăn uống với những thực phẩm riêng nên việc lên thực đơn cho bữa ăn hàng ngày là rất quan trọng. Thuốc điều trị viêm da là yếu tố giúp trị tận gốc bệnh, song đó chưa hẳn đã là tất cả nếu như cơ thể bạn không đủ sức đề kháng. Cải thiện sức đề kháng từ bên trong cơ thể, cùng với những thực phẩm bổ sung chất giúp tái tạo làn da, xóa bỏ tàn dư của bệnh để lại. Dưới đây là một số thực phẩm bác sĩ khuyên bệnh nhân viêm da nên ăn:\n &#8211; Chè đậu xanh: Đậu xanh là nguyên liệu dễ kiếm và rẻ tiền nhưng lại rất tốt cho người bị viêm da. Đậu xanh có tác dụng làm mát gan, điều hòa cơ thể. Chính vì vậy, đây là món ăn dễ làm, giúp bệnh nhanh khỏi.\n &#8211; Các loại ngũ cốc, lạc, lòng đỏ trứng gà, hoa quả và rau xanh, thịt gia súc gia cầm… Là những thực phẩm có chứa vitamin B5 giúp cải thiện làn da, hạn chế quá trình lão hóa da, ngăn ngừa da khô, da bị bong tróc. Cải thiện là da từ sâu bên trong.\n &#8211; Các thực phẩm có chứa vitamin E, vitamin C giúp đẩy mạnh hệ miễn dịch, kháng khuẩn, đồng thời cung cấp độ ẩm, giữ độ ẩm ổn định cho da. Là thực đơn phù hợp cho bệnh nhân viêm da, bao gồm các loại quả như cam, bưởi,.. hay thực phẩm như cà chua, đu đủ, cải bắp, rau họ đậu,… Từ đó bạn có thể cải thiện vùng da bị viêm theo từng ngày, làn da sẽ trở nên mịn màng, không để lại sẹo thâm đen.\n &#8211; Bên cạnh đó bạn nên cung cấp cho cơ thể ít nhất 2 lít nước mỗi ngày để giúp da căng, không bị khô.\n Thực phẩm không nên ăn\n Có nhiều thực phẩm tốt cho sức khỏe của người bình thường nhưng lại phản tác dụng đối với các bệnh nhân viêm da. Hạn chế ăn các thực phẩm này sẽ giúp cho bệnh không bị biến chứng nặng hơn và giúp quá trình điều trị nhanh hơn, hiệu quả hơn.\n &#8211; Thực phẩm hàng đầu mà bệnh nhân viêm da không nên sử dụng đó là các loại protein và đạm có trong các loại cá, các loại có vỏ như tôm, cua, sò biển, một số loại thịt như dê, bò&#8230; Tuy hải sản là một thực phẩm rất tốt cho mỗi người chúng ta, nhưng nếu bạn không biết chế biến hoặc chế biến không đúng cách sẽ khiến cho thực phẩm này sản sinh ra histamin gây ngứa và dị ứng cho người bệnh, khi ăn vào sẽ khiến cho tình trạng bệnh viêm da đặc biệt là viêm da tiếp xúc dị ứng sẽ trở nên tồi tệ hơn\n &#8211; Không nên ăn các thực phẩm chứa nhiều dầu mỡ hoặc chế biến sẵn, vì chứa nhiều chất bảo quản, phụ gia, phẩm màu\n &#8211; Hạn chế sử dụng các loại đồ uống có gas, các chất kích thích có cồn như rượu bia&#8230; Vì các loại chất này sẽ khiến cơ thể bạn dễ bị dị ứng và những cơn ngứa ngáy do bệnh viêm da gây ra sẽ tái phát ngay lập tức\n &#8211; Không sử dụng cải chua: Cải chua là một món ăn ưa chuộng và được sử dụng nhiều ở Việt Nam. Tuy nó là một món ăn ngon nhưng với những bị bệnh viêm da hoặc có tiền sử bị mắc bệnh thì không nên sử dụng món ăn này, vì sẽ khiến cho bệnh nặng hơn và dễ tái phát. Cải chua là một thực phẩm được lên men và rất mặn nên sẽ gây hại cho thận, dẫn đến việc đào thải chất độc bên trong cơ thể không còn hiệu quả. Không những thế, nếu chế biến không cẩn thận rất dễ khiến vùng da bị thương tổn nhiễm khuẩn, ảnh hưởng xấu đến sức khỏe của người bệnh.\n &#8211; Không nên ăn thịt gà: Các món ăn được chế từ thịt gà khá hấp dẫn và được nhiều người sử dụng. Tuy đây được xem là một món ăn khá lành tính nhưng thịt gà có tính nóng và hàm lượng đạm cao nên rất dễ gây nên hiện tượng bị dị ứng, nổi mẩn ngứa và mẩn đỏ.\n &#8211; Hạn chế sử dụng thịt bò: Đây cũng được xem là một món ăn khá phổ biến nhưng lại không tốt cho người bệnh viêm da, vì hàm lượng protein có trong thịt bò sẽ làm cho tình trạng bị dị ứng, mẩn ngứa trở nên tồi tệ hơn.\n &gt;&gt;Thực phẩm là nguồn cung cấp dinh dưỡng tốt nhất cho cơ thể, để có được sức khỏe tốt nhất thì bạn nên có thực đơn ăn phù hợp, vừa đảm bảo đủ chất dinh dưỡng đồng thời tránh được những loại thức ăn gây hại cho cơ thể bạn. Hy vọng bài viết này đã đem lại cho bạn nhiều thông tin bệnh viêm da nên ăn gì, giúp bạn có một cơ thể khỏe mạnh, đẩy lùi các tác nhân gây bệnh từ bên ngoài như phát ban, mụn rộp, mụn mủ, lở loét, sưng, ngứa, tổ đỉa, viêm nang lông&#8230;\n BẠN CẦN TƯ VẤN THỰC ĐƠN DÀNH CHO BỆNH NHÂN VIÊM DA, bác sĩ của chúng tôi luôn sẵn sàng chia sẻ\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 11, NULL, NULL, 1),
(2, 19, 'Cách phòng và chữa trị viêm da tiếp xúc triệt để', 'Bệnh viêm da dị ứng tiếp xúc rất dễ bị nhưng lại khó để chữa trị nếu không phát hiện và áp dụng phương pháp chữa trị kịp thời. Bên cạnh đó, việc phòng bệnh cũng rất cần thiết và quan trọng. Bạn có thể tham khảo một số gợi ý hướng dẫn cách chữa bệnh viêm da tiếp xúc hiệu quả để áp dụng chữa trị bệnh cho mình nhanh chóng đạt được kết quả tốt nhất.\n 1. Viêm da tiếp xúc là gì ?\n Viêm da tiếp xúc là bệnh về da thường gặp, biểu hiện lâm sàng là phát ban tại chỗ hoặc kích ứng trên da do các tác nhân từ bên ngoài gây ra. Bệnh thường tiến triển dai dẳng, tái phát nếu không phát hiện và loại bỏ được các dị nguyên gây bệnh.\n Có 3 loại viêm xúc là viêm da tiếp xúc dị ứng, viêm da tiếp xúc kích ứng và viêm da tiếp xúc ánh sáng.\n 2. Nguyên nhân gây bệnh\n Các y bác sĩ cho biết rằng bệnh viêm da dị ứng thường xuất phát từ rất nhiều yếu tố khác nhau vì vậy rất khó để xác định một cách rõ ràng đâu là nguyên nhân gây bệnh. Tuy nhiên, chúng ta vẫn có thể xác định được các yếu tố thường gặp gây nguy cơ cao hình thành bệnh viêm da dị ứng mà bạn nên biết như sau:\n – Do hóa chất độc hại: Tiếp xúc với các loại hóa chất độc hại từ môi trường làm việc, chất tẩy rửa gia đình, thuốc trừ sâu, thuốc nhuộm tóc….có thể dẫn đến bệnh viêm da dị ứng.\n – Dị ứng thời tiết: Môi trường thay đổi từ nóng sang lạnh hoặc ngược lại cũng khiến nhiều người không thích nghi kịp, bị dị ứng và gây nên tình trạng viêm da.\n – Do thức ăn: Nhiều người có cơ địa dị ứng với một số loại thức ăn như: hải sản, phô mai, sữa, thịt bò…nên cũng không loại trừ bị viêm da dị ứng là do nguyên nhân này.\n &#8211; Các dị nguyên: phấn hoa, lông ấu trùng, côn trùng…là một trong những nguyên nhân gây ra viêm da tiếp xúc, đặc biệt là vào mùa hè và mùa thu.\n Những yếu tố trên khi tiếp xúc với da sẽ gây nên tình trạng dị ứng và gây nên hiện tượng viêm da. Chính những nguyên nhân chưa rõ ràng này mà mọi người nên chú ý hơn, khi thấy xuất hiện các triệu chứng bệnh viêm da tiếp xúc để phát hiện nguyên nhân gây bệnh cụ thể từ đó việc điều trị bệnh mới cho hiệu quả cao được.\n 3. Điều trị viêm da tiếp xúc\n Nguyên tắc\n Để điều trị bệnh hiệu quả, bệnh nhân cần tuân thủ một số nguyên tắc như sau:\n &#8211; Phải tìm ra căn nguyên gây bệnh và loại bỏ càng sớm càng tốt.\n &#8211; Dùng thuốc phải phù hợp với từng giai đoạn, bệnh nhân phải để ý đến sự chuyển biến của bệnh để biết bệnh thuyển giảm hay biến chứng để kịp thời điều trị.\n &#8211; Kết hợp điều trị tại chỗ và toàn thân. Tùy thuộc vào mức độ nặng, nhẹ của bệnh mà người bệnh lựa chọn bôi thuốc tại vùng da bị thương tổn hay kết hợp với uống thuốc để loại bỏ từ bên trong.\n Điều trị bệnh\n &#8211; Tại chỗ\n Khi phát hiện bị viêm da dị ứng, bạn cần nhanh chóng rửa thương tổn bằng xà phòng hoặc nước ấm sạch nếu không phải viêm da tiếp xúc do chất kiềm. Thương tổn da đang phồng rộp thì đắp khăn lạnh. Nếu thương tổn có chảy dịch thì bôi bằng dung dịch màu như milian, xanh methylen; nếu thương tổn khô có thể bôi kem hoặc mỡ corticoid…\n &#8211; Toàn thân\n Bệnh nhân nên đến gặp bác sĩ để khám và chỉ định dùng thuốc uống, thuốc bôi phù hợp. Một số loại thuốc đặc trị viêm da tiếp xúc như: corticoid, khánh sinh phòng và chống bội nhiễm, thuốc giảm đau khi cần thiết.\n Bệnh nhân lưu ý không tự ý mua và dùng thuốc tránh làm cho bệnh nặng hơn và những tác dụng phụ ảnh hưởng đến sức khỏe.\n 4. Một số mẹo phòng tránh viêm da tiếp xúc\n Miền Bắc đang bước vào mua Thu, mặc dù thời tiết mát mẻ và dễ chịu nhưng lại là mùa rất dễ bị viêm da tiếp xúc vì vậy phòng tránh bệnh là điều hết sức cần thiết. Đối với các trường hợp bị viêm da tiếp xúc thông thường, một số mẹo nhỏ sau đây sẽ giúp bạn phòng tránh được căn bệnh này:\n &#8211; Ngay khi thấy có biểu hiện viêm da tiếp xúc đầu tiên, bạn có thể dùng bột khoai tây thoa lên vùng da bị tổn thương khoảng 20 phút, nên làm đều đặn mỗi ngày 2 lần cho đến khi những biểu hiện của bệnh biến mất.\n &#8211; Pha chanh với 1 cốc nước ấm cùng một chút mật ong, uống vào buổi sáng sớm khi thức dậy, uống đều đặn trong một vài tháng, cách làm này giúp cải thiện hệ thống miễn dịch trong cơ thể, làm giảm nguy cơ bị viêm da tiếp xúc.\n &#8211; Đơn giản hơn, bạn có thể sử dụng lá trà xanh, đun với nước rồi pha với mật ong. Cách này có tác dụng chữa trị khi bạn mắc viêm da tiếp xúc dị ứng.\n &#8211; Bạn cũng nên tránh hút thuốc, dùng đồ uống có cồn, tiếp xúc với khói bụi và phấn hoa, bởi đó là những yếu tố làm tăng nguy cơ mắc viêm da tiếp xúc.\n Nếu bạn đã thử nhiều cách nhưng triệu chứng viêm da tiếp xúc không có dấu hiệu thuyên giảm thì bạn nên nhanh chóng đến gặp bác sĩ chuyên khoa da liễu để được kịp thời điều trị. Các bác sĩ tại Phòng khám Da liễu Hà Nội với kinh nghiệm, sự tận tâm sẽ giúp bạn chữa bệnh viêm da tiếp xúc khỏi hoàn toàn và không tái phát.<br>Nếu còn bất cứ thắc mắc nào về bệnh viêm da tiếp xúc, hãy gọi cho các bác sĩ của chúng tôi để được giải đáp:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Số 447 Hoàng Quốc Việt, Hà Nội<br> Hotline: 098.123.1234 <br>', 14, NULL, NULL, 1),
(3, 19, 'Phân biệt các loại viêm da để điều trị an toàn, hiệu quả', 'Viêm da là một bệnh lý da liễu thường gặp. Viêm da thường xảy ra trên cơ địa dị ứng và gặp ở mọi lứa tuổi. Tuy không ảnh hưởng nhiều đến sinh hoạt hàng ngày và ít nguy hiểm đến tính mạng nhưng thường dai dẳng, rất khó trị liệu bởi bệnh dễ quay trở lại khi tiếp xúc với tác nhân gây bệnh như đồ ăn, hóa chất hay thời tiết thay đổi. Bệnh ảnh hưởng nhiều tới sinh hoạt, công việc của người bệnh. Phân biệt viêm da giúp điều trị dễ dàng và đem lại hiệu quả cao hơn.\n 1. Bệnh viêm da là gì ?\n Bệnh viêm da là một trong những biểu hiện bệnh lý ở da thường gặp nhất. Đây là một nhóm gồm nhiều loại bệnh với các nguyên nhân khác nhau, đặc trưng bởi tình trạng viêm nông trên bề mặt của da, gây ra các biểu hiện ngứa, nề đỏ, nổi mụn nước, khi vỡ gây tiết dịch và đóng vảy, hay gặp nhất là viêm da tiếp xúc, viêm da cơ địa, viêm da dầu, viêm da thần kinh, tổ đỉa và đỏ da toàn thân. Biến chứng thường gặp của tất cả các bệnh viêm da này là dày da do gãi nhiều và nhiễm trùng tại các vết trợt loét\n 2. Tác hại<br>Viêm da không nguy hiểm, tuy nhiên lại gây ra nhiều biến chứng nếu không điều trị kịp thời sẽ để lại hậu quả nghiêm trọng. Có thể kể đến một số biến chứng mà viêm da gây ra như:\n &#8211; Chốc lở: nếu bị viêm da mà không điều trị kịp thời và đúng cách có thể dẫn đến nhiễm trùng, gây nên các vết loét và vết nứt trên da.\n &#8211; Viêm tế bào: khi nhận thấy vệt đỏ trên da, có các tế bào chết, các mô dưới da bị nhiễm khuẩn, da sưng đỏ có thể bạn đã bị viêm tế bào. Người có hệ thống miễn dịch suy yếu khi bị bệnh rất dễ dẫn đên biến chứng viêm tế bào, biến chứng này có thể đe dọa tính mạng bệnh nhân.Vì thế, nếu có dấu hiệu viêm tế bào bạn cần đến gặp bác sĩ sớm nhất.\n &#8211; Dẫn đến sẹo và những thay đổi màu da gây mất thẩm mỹ và khiến người bệnh ngại giao tiếp, gián tiếp ảnh hưởng đến chất lượng cuộc sống.<br>3. Phân loại và biểu hiện\n Tùy thuộc vào biểu hiện và tác hại của viêm da để phân loại, thông thường viêm da bao gồm 5 loại:\n ==&gt; Viêm da cơ địa\n Viêm da cơ địa trước đây còn gọi là chàm thể tạng hay chàm cơ địa, là một bệnh về da rất hay gặp.\n &#8211; Bệnh xuất hiện ở mọi lứa tuổi, nhưng hay gặp nhất ở trẻ em dưới 2 tuổi.\n &#8211; Bệnh thường dai dẳng và có thể kéo dài tới tuổi trưởng thành\n &#8211; Ở độ tuổi khác nhau, biểu hiện của viêm da cơ địa cũng khác nhau. Đối với cơ địa trẻ nhỏ là các đám mụn trước trán, má; người lớn là các mảng da dày, sẩn nổi cao hơn da mặt và rất ngứa.\n ==&gt; Viêm da tiếp xúc\n Viêm da tiếp xúc là bệnh ngoài da thường gặp (chiếm 1,5 – 5,4% dân số thế giới)\n Thương tổn mà viêm da tiếp xúc gây ra là dát đỏ, mụn nước, ngứa, có khi loét hoại tử và ảnh hưởng đến các mô tại chỗ hoặc lân cận.\n Bệnh tiến triển dai dẳng, sẽ phát triển nhanh nếu không phát hiện và loại bỏ căn nguyên gây bệnh.\n ==&gt; Viêm da tiếp xúc côn trùng\n Bệnh thường do 2 loại côn trùng là bướm đen và kiến khoang gây ra do da tiếp xúc trực tiếp với lông của ấu trùng hoặc gián tiếp qua quần áo.\n Bệnh xuất hiện nhiều vào tháng 6 đến tháng 9 hàng năm\n Thương tổn ban đầu là những dát đỏ da, sau đó phù nề. Người bệnh thấy rát, đau, ngứa. Hình dạng có nhiều loại khác nhau tùy vào sự tiếp xúc với lông của ấu trùng như hình oval, hình tròn, loang lổ…có kích thước từ 2mm đến 10-20cm.\n ==&gt; Viêm da quanh miệng\n Viêm da có mụn mủ, đôi khi là các sẩn mụn nhỏ, rất ngứa, xung quanh miệng,phần thấp của má, diễn biến chậm\n Viêm da quanh miệng có thể là do biến chứng của sử dụng sản phẩm chứa nhiều corticoid\n ==&gt; Viêm da thần kinh\n Là bệnh thường gặp, mạn tính, phát triển chậm và tái phát.\n Thương tổn xuất hiện ở cổ, mặt duỗi của tay chân, thương tổn ngày càng lan rộng, ngứa từng cơn dữ dội nhất về đêm.<br>Dù là loại viêm da nào thì bệnh có sự tiến triển và biến chứng qua từng giai đoạn. Chính vì vậy, khi nhận thấy mình đang có những biểu hiện của bệnh viêm da, bạn cần đến gặp bác sĩ để khám và được tư vấn cách chữa trị phù hợp và kịp thời.\n LIÊN HỆ VỚI CHÚNG TÔI ĐỂ ĐƯỢC TƯ VẤN\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số: 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 15, NULL, NULL, 1),
(4, 19, 'Bệnh viêm da và những biểu hiện dễ nhận thấy.', 'Bệnh viêm da là tình trạng khá phổ biến nhưng không đe dọa tới tính mạng và cũng không phải căn bệnh truyền nhiễm. Tuy nhiên bệnh gây cảm giác khó chịu, ngứa, bệnh thường gặp ở những người có cơ địa dễ dị ứng ở mọi lứa tuổi. Có 3 nhóm nguyên nhân viêm da thường gặp nhất là Dị ứng – tiếp xúc – viêm da tiết bã.<br>Viêm da theo y học\n &#8211; Viêm da là một tình trạng viêm ở da. Có rất nhiều nguyên nhân gây viêm da và xảy ra dưới nhiều dạng. Bệnh thường biểu hiện là phát ban ngứa trên nền da bị sưng, đỏ.\n &#8211; Da bị viêm sẽ có bóng nước, rỉ dịch, bề mặt hình thành một lớp mài và tróc ra. Ví dụ như viêm da dị ứng (chàm): nổi vẩy gàu và mẩn ngứa do tiếp xúc với bất kỳ chất nào, chẳng hạn như chất độc hoa thường xuân, xà phòng và đồ trang sức có niken.\n &#8211; Viêm da là một bệnh phổ biến không lây nhiễm, nhưng có thể làm cho bạn cảm thấy khó chịu và mất tự tin. Việc kết hợp các bước tự chăm sóc và thuốc có thể giúp bạn điều trị viêm da.<br>Dấu hiệu của bệnh viêm da<br>Triệu chứng của bệnh\n Các dấu hiệu và triệu chứng của viêm da sẽ phụ thuộc vào loại bệnh da:\n Viêm da dị ứng (chàm): thường bắt đầu thời thơ ấu, vùng da đỏ, nổi mẩn ngứa này thường ở các nếp gấp bên trong khuỷu tay, phía sau đầu gối và mặt trước cổ, khi bị trầy xước, có thể rỉ dịch và đóng vảy. Những người bị viêm da dị ứng có những đợt bệnh thuyên giảm nhưng sau đó lại bị tái phát;\n Viêm da tiếp xúc: da bị phát ban trên những vùng cơ thể tiếp xúc với các chất gây kích ứng hoặc gây dị ứng, chẳng hạn như chất độc cây thường xuân, xà phòng và các loại tinh dầu. Phát ban đỏ có thể nóng, châm chích hay ngứa. Có thể hình thành mụn nước;\n Viêm da tiết bã: viêm da thành mảng có vảy, da đỏ và tróc vẩy như gàu, khó chữa. Bệnh thường ảnh hưởng đến khu vực tiết nhờn của cơ thể, như mặt, ngực trên và lưng. Bệnh có thể mạn tính với từng đợt thuyên giảm và bùng phát. Ở trẻ sơ sinh, bệnh này còn gọi là “cứt trâu” với từng mảng da đầu bị đóng vẩy cứng.\n Bạn có thể gặp các triệu chứng khác không được đề cập. Nếu bạn có bất kỳ thắc mắc nào về các dấu hiệu bệnh, hãy tham khảo ý kiến bác sĩ.<br>Viêm da ở trẻ nhỏ<br>Nguyên nhân gây viêm da\n Do điều kiện về sức khỏe, bị dị ứng, các yếu tố di truyền những căng thẳng về thể chất và tâm thần, và chất kích thích có thể gây viêm da.<br>Điều trị viêm da\n Bệnh viêm da có nhiều phương pháp điều trị khác nhau tùy vào tình trạng và nguyên nhân gây viêm da ở mỗi người. Ngoài ra, thay đổi lối sống và áp dụng các biện pháp khắc phục cũng được khuyến nghị. Hầu hết các chế độ điều trị viêm da đều bao gồm một hoặc nhiều yếu tố sau đây:<br> <br>', 13, NULL, NULL, 1),
(5, 23, 'Cách chữa trị một số loại nấm da thường gặp', 'Việt Nam là một nước có khí hậu nhiệt đới vừa nóng vừa ẩm nên rất thuận lợi cho các chủng nấm phát triển. Các bệnh nấm ở Việt Nam rất đa dạng và phong phú, việc phân biệt các loại nấm giúp cho bệnh nhân dễ dàng nhận ra triệu chứng và có cách điều trị phù hợp, đem lại hiệu quả cao.\n 1. Phân loại và biểu hiện\n Có nhiều loại nấm da nhưng hay gặp nhất là một số nấm da sau đây:\n Nấm thân\n Điển hình là nấm gây bệnh hắc lào. Dấu hiệu đầu tiên là ngứa vùng bị bệnh, sau đó thấy một vệt màu hơi đỏ, có viền, bờ rõ rệt, trên viền đó có các mụn nước lấm tấm. Vùng da này ngày càng lan rộng tạo thành nhiều hình vòng cung, nếu không được phát hiện và điều trị kịp thời. Nhiều khi do người bệnh ngứa, gãi và làm lây lan ra nhiều vùng hắc lào trên cơ thể của mình. Bệnh hắc lào là một trong những bệnh nấm da có khả năng lây từ người này sang người khác do sử dụng chung các đồ dùng sinh hoạt như quần, áo, khăn mặt, khăn tắm, ngủ chung giường, đắp chung chăn&#8230;<br>Nấm kẽ (thường xảy ra trong mùa mưa)\n Căn nguyên của bệnh là do vi nấm epidermophyton, nấm trichophyton hay còn do nấm candida albicans. Bệnh bắt đầu ở giữa kẽ ngón chân thứ 3 và thứ 4. Kẽ ngón có hiện tượng bong xước da, màu hơi vàng, chảy dịch, có thể xuất hiện mụn nước. Bệnh thường gặp ở những người do nghề nghiệp phải tiếp xúc nước nhiều giờ liên tục nhiều ngày như nông dân, người làm vệ sinh cống rãnh, vận động viên bơi lội&#8230;\n Nấm móng\n Nấm móng thường do trichophyton gây nên. Bệnh biểu hiện ở bờ tự do của móng hay ở hai bên cạnh của móng. Khi bị bệnh, móng mất màu bóng, bị đẩy nhô lên hoặc khuyết vào, trên mặt móng lỗ chỗ hoặc thành rãnh. Dưới rãnh có chất bột vụn. Như vậy móng càng ngày càng bị sù sì, màu vàng hoặc đục. Bệnh có thể lây từ móng này sang móng khác.\n Nấm tóc\n Nấm tóc do nấm Piedra Hortai gây nên. Biểu hiện là trên mỗi sợi tóc có nhiều hạt màu đen bám vào. Tuy vậy bệnh nhân không thấy có gì khác thường và tóc cũng không bị rụng.\n Trong khi đó, loại nấm tóc do trichophyton gây ra thì biểu hiện tổn thương trên da đầu. Da đầu có nhiều vết tròn nhỏ, kích thước bé khoảng từ 3-5mm,da đầu có vảy mỏng hay ngứa da vùng đầu.\n 2. Nguyên nhân\n Nấm da là bệnh rất dễ lây lan từ người này sang người khác, do vậy tìm hiểu nguyên nhân gây bệnh là điều hết sức cần thiết, giúp bạn chủ động và phòng tránh đúng cách.\n Nấm da được xác định do một số nguyên nhân:\n &#8211; Lây nhiễm từ người bệnh: mầm bệnh có thể được lây lan từ người bị nấm qua người không bị bệnh thông qua con đường tiếp xúc trực tiếp hoặc dùng chung đồ dùng với người bệnh như: Khăn tắm, mặc chung quần áo, chung chăn màn…\n &#8211; Vệ sinh kém: Vệ sinh không sạch sẽ hoặc lười vệ sinh thân thể cũng tạo môi trường cho nấm mốc phát triển, khi mồ hôi tiết ra kết hợp với tế bào da chết sẽ làm cho da trở thành nơi ở thuận lợi cho nấm gây bệnh.\n &#8211; Do nguồn nước: Nguồn nước bẩn có chứa vi nấm gây bệnh nếu như bạn thường xuyên tiếp xúc cũng sẽ làm tăng khả năng bị nấm da.\n &#8211; Do lây nhiễm từ động vật: Một số động vật nuôi như chó, mèo, gà, ngựa…có thể mắc nhiễm vi nấm rồi lây nhiễm sang con người qua những đồ vật mà chúng từng tiếp xúc. Tuy nhiên, các bác sĩ cho rằng sự lây lan này là hiếm.\n Ngoài những nguyên nhân nấm da cơ bản trên thì vẫn còn một số nguyên nhân gây bệnh khác như do ảnh hưởng từ các căn bệnh da liễu (vảy nến, á sừng, viêm da…) hoặc do da tiếp xúc nhiều với hóa chất, mỹ phẩm không phù hợp.\n Đây chính là những nguyên nhân gây bệnh nấm da thường gặp nhất, bạn có thể tránh xa những thói quen lối sống không tốt để phòng ngừa nấm da xuất hiện..\n 3. Cách phòng và chữa trị nấm da\n Bệnh nấm da thường để lại khá nhiều tác hại không tốt tới thẩm mỹ, sức khỏe do đó bạn cần chủ động phòng bệnh.\n &#8211; Để phòng tránh bệnh hiệu quả, trước hết bạn cần trang bị cho mình những kiến thức về bệnh nấm da, đồng thời tuyên truyền cho người thân cách phòng bệnh.\n &#8211; Có lối sống lành mạnh, sạch sẽ, không sử dụng chung các đồ dùng cá nhân với người khác\n &#8211; Nếu bạn nuôi động vật nên thường xuyên kiểm tra xem có nấm trên da động vật đó không (biểu hiện rõ nhất là trụi lông) để chủ động phòng bệnh.\n Chữa trị\n Mặc dù đã áp dụng nhiều cách để phòng chống nhưng bạn vẫn mắc phải căn bệnh này thì cũng đừng quá lo lắng, hãy đến gặp bác sĩ để xét nghiệm và tìm ra cách chữa trị nhanh chóng, an toàn.\n Đối với bệnh nấm ngoài da thì người ta thường dùng một số cách điều trị điển hình đó là việc dùng thuốc điều trị. Đối với các thương tổn kẽ tay, kẽ chân, nấm thân, nấm móng và lang ben với diện tích nhỏ, người bệnh có thể dùng các loại kem bôi có chứa ketoconazole, miconazole, terbinafine… để điều trị. Trong trường hợp lang ben đã lây lan trên diện tích lớn, ở những vùng da khó bôi thuốc như lưng, gáy cổ…, người bệnh có thể dùng dầu gội có chứa ketoconazole 2% để thoa và tắm trong 5 ngày liên tiếp để diệt tận gốc vi nấm, tránh để bệnh kéo dài, dễ tái phát.\n Một số người có thói quen dùng thuốc Corticoid trị bệnh nấm ngoài da nhưng đối với loại thuốc này người bệnh nên thận trọng và không nên lạm dụng vì thuốc có thể gây nên nhiều tác dụng phụ không tốt ảnh hưởng tới da như: teo da, rạn da, và tạo cơ hội nấm phát triển nhiều hơn….\n Nấm da không nguy hiểm đến sức khỏe nhưng lại là bệnh dai dẳng và khó chữa. Khi nhận thấy dấu hiệu bệnh cách tốt nhất, bạn nên đến các cơ sở y tế để khám và sử dụng thuốc theo chỉ định của bác sĩ chuyên khoa da liễu.\n Với đội ngũ bác sĩ chuyên khoa kinh nghiệm, tận tâm Phòng khám Da liễu chắc chắn là nơi bạn nên đặt niềm tin. Đừng để bệnh nấm da khiến bạn kém tự tin, ngại giao tiếp khi cách chữa trị ngày càng đơn giản. LIÊN HỆ VỚI CHÚNG TÔI ĐỂ ĐẶT LỊCH THĂM KHÁM\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 15, NULL, NULL, 1),
(6, 23, 'Bệnh nấm da và cách phòng ngừa trong mùa hè', 'Bệnh nấm da là bệnh thường gặp do vi nấm dermatophytes gây nên và phát triển ở vùng da ẩm ướt, có nhiều mồ hôi như ở bẹn, bìu, kẽ các ngón chân, tay, xung quanh thắt lưng, nếp dưới vú, nách, cổ và ngay cả da vùng đầu. Trong quá trình sống, sợi nấm phát triển và tiết ra độc tố kích thích da gây ngứa.<br>Dấu hiệu của bệnh nấm da<br>Dấu hiệu nấm da<br>Nguyên nhân gây bệnh\n Nguyên nhân của bệnh nấm da là do nấm ký sinh ở các tế bào thượng bì chết. Bệnh có thể lây truyền theo các đường sau:<br>Bệnh nấm da có lây không\n Bệnh nấm da rất dễ lây cho các vị trí khác trên cơ thể của bản thân và cho cả người khác. Phương thức lây truyền chủ yếu là lây trực tiếp. Gồm các hình thức sau đây:\n – Tiếp xúc với bào tử nấm có trong thiên nhiên và khi chúng bám vào da, quần áo, khăn mặt một cách tình cờ.\n – Tiếp xúc với một số động vật nuôi trong nhà mà các động vật đó bị nấm da.\n – Bệnh nấm lây từ người này qua người khác do nằm chung giường, mặc chung quần áo, dùng chung khăn mặt, khăn tắm…<br>Khi bị bệnh nấm da nên làm gì ?\n Khi có dấu hiệu về bệnh nên đến bác sĩ da liễu để được chẩn đoán lâm sàng và làm các xét nghiệm cần thiết. Từ các kết quả có được, bác sĩ sẽ tư vấn, chỉ định điều trị đúng bệnh, dùng đúng thuốc. Không nên tự mua thuốc về điều trị không những bệnh không khỏi mà còn làm bệnh nặng thêm.\n &gt;&gt; Bệnh nấm da hiếm khi lan xuống dưới bề mặt da gây nguy hiểm, tuy nhiên người bị suy giảm miễn dịch (như người nhiễm HIV/AIDS) rất khó chữa khỏi bệnh.<br>Tình trạng nấm da lan rộng<br>Cách phòng ngừa bệnh\n Bệnh nấm da rất khó phòng ngừa. Nấm gây bệnh rất phổ biến và dễ lây thậm chí trước khi triệu chứng xuất hiện\n Tuy nhiên, có thể hạn chế nguy cơ mắc bệnh bằng cách:<br> <br>', 14, 3, NULL, 1),
(7, 10, 'Tác hại do sai lầm trong chữa trị hắc lào', 'Hắc lào là căn bệnh khá “nhạy cảm” vì khiến cho người bệnh tự ti và ngại giao tiếp. Với sự phát triển mạnh mẽ của y học ngày càng có nhiều phương pháp chữa trị hiệu quả thế nhưng một số người do thiếu hiểu biết, chủ quan với bệnh hắc hào khiến cho bệnh biến chứng và gây ra nhiều tác hại. Trong bài viết này, các bác sĩ sẽ chỉ ra những sai lầm trong cách điều trị và tác hại qua đó hướng dẫn các bạn cách để phòng tránh bệnh hắc lào hiệu quả.\n 1. Sai lầm trong chữa trị\n Để có thể điều trị bệnh hiệu quả, an toàn trước hết bạn cần phải tìm ra căn nguyên gây bệnh thông qua các triệu chứng. Nếu không có đủ kiến thức về bệnh, bạn nên đến gặp bác sĩ để khám, tránh những sai lầm như một số bệnh nhân dưới đây:\n &#8211; Khi bị bệnh hắc lào, nhiều người cho rằng đây là bệnh ngoài da do đó không nguy hiểm, hơn thế chỉ cần giữ cho da khô thoáng là bệnh sẽ tự khỏi. Tuy nhiên đây là những suy nghĩ sai lầm, bệnh không thể tự khỏi nếu không có sự can thiệp đúng cách.\n &#8211; Khi bị hắc lào, nhiều người thường tự áp dụng các biện pháp bôi thuốc tại nhà, dùng các mẹo vặt dân gian như thuốc lào, lá trầu không để trị liệu….đây là những biện pháp sai. Bệnh do nấm nông gây ra do đó khi trị liệu cần tuân thủ quy trình trị liệu như mắc 1 bệnh nấm da tức là cần diệt hết khuẩn nấm gây bệnh.\n &#8211; Việc dùng các loại thuốc bôi khi không có chỉ định của bác sỹ (thuốc quá mạnh, bôi dây sang da lành hay da non, thuốc không đúng bệnh… ) có thể gây ra tình trạng bỏng, chảy nước nhiều, ngứa dữ dội… Trong một số ít trường hợp có thể gây nhiễm trùng, đi lại khó khăn.\n &#8211; Hắc lào là bệnh lây lan từ người này sang người khác, thế nhưng khi trong gia đình có người bị hắc lào nhưng các thành viên khác vẫn chủ quan, không chủ động phòng tránh.\n 2. Tác hại\n Hắc lào tuy không phải là căn bệnh nguy hiểm, nhưng gây ngứa ngáy, khó chịu, ảnh hưởng lớn đến sinh hoạt của người bệnh, đặc biệt là vấn đề thẩm mỹ. Thêm vào đó, sai lầm trong cách điều trị đã khiến cho bệnh nặng hơn và dai dẳng, khó điều trị. Có thể kể đến một số tác hại mà hắc lào gây ra do điều trị sai cách sau đây:\n &#8211; Nếu không xử lý kịp thời bệnh sẽ lan thêm ra những vị trí khác, tăng kích thước, chàm hóa hoặc lây sang người khác do tiếp xúc trực tiếp hay lây qua quần áo.\n &#8211; Nếu bôi thuốc không đúng (thuốc quá mạnh, bôi dây sang da lành hay da non, thuốc không đúng bệnh… ) có thể gây ra tình trạng bỏng, chảy nước nhiều, ngứa dữ dội… Trong một số ít trường hợp có thể gây nhiễm trùng, đi lại khó khăn.\n &#8211; Đặc biệt là tình trạng da bị chàm. Các vết chàm gây ra bởi hắc lào thường rất khó xóa bỏ, nếu những vết chàm này xuất hiện trên mặt, bàn tay hay các vị trí nhạy cảm khác thì sẽ đem lại cảm giác rất khó chịu về mặt thẩm mỹ cho người bệnh khi chúng ảnh hưởng trực tiếp tới công việc của họ.\n &#8211; Hắc lào gây ngứa dữ dội, do đó người bệnh thường gãi nhiều, gây tổn thương ngoài da, vi khuẩn có thể xâm nhập theo vết thương hở đó, gây nhiễm trùng máu cấp vô cùng nguy hiểm.\n &#8211; Hắc lào thường bùng phát theo mùa do đó dễ phải sống với nó lâu dài, gây áp lực lên công việc, cuộc sống. Việc trị liệu cũng phải thực hiện thường xuyên do đó tốn kém chi phí.\n 3. Nên làm gì để phòng tránh hắc lào ?\n Hắc lào là bệnh ngoài da thường xuất hiện nhiều vào mùa hè, bệnh sẽ dai dẳng và khó điều trị do đó phòng tránh bệnh là điều cần thiết.\n &#8211; Có lối sống lành mạnh, luôn vệ sinh thân thể sạch sẽ, quần áo được giặt sạch và phơi khô trước khi mặc\n &#8211; Không sử chung đồ dùng cá nhân như quần áo, chăn màn… với người khác đặc biệt là những người bị bệnh hắc lào\n &#8211; Nên đi khám bác sĩ nếu trong gia đình hoặc người xung quanh bị bệnh hắc lào vì bệnh rất dễ lây nhiễm\n &#8211; Có chế độ dinh dưỡng đảm bảo, không nên sử dụng các chất kích thích như rượu, bia…\n &#8211; Nếu bạn nuôi động vật, nên thường xuyên kiểm tra để tránh nấm ở động vật lây sang bạn. Trường hợp này khá hiếm, tuy nhiên bạn vẫn nên cẩn thận.\n &#8211; Mặc dù đều là bệnh hắc lào, nhưng đối với cơ thể của mỗi người bệnh sẽ tiến triển khác nhau do vậy việc nhìn vào người khác và tự điều trị bệnh là hoàn toàn sai lầm.\n Khi đã thực hiện nhiều cách để phòng chống nhưng bạn vẫn không may mắc phải căn bệnh “nhạy cảm” này. Hãy bình tĩnh để tìm ra nguyên nhân của bệnh để điều trị kịp thời trước khi bệnh có những biến chứng nguy hiểm.\n Bệnh hắc lào không phải là bệnh lý đáng lo ngại, không ảnh hưởng tới sức khỏe của người bệnh. Tuy nhiên, bệnh hắc lào kéo dài trên da sẽ làm mất thẩm mỹ và làm người bệnh cảm thấy khó chịu trong sinh hoạt hàng ngày. Để tránh những hậu quả nặng nề, bạn nên đến gặp bác sĩ để được tư vấn và hướng dẫn điều trị. Hi vọng những thông tin mà bác sĩ của chúng tôi chia sẻ sẽ giúp các bạn có thêm kiến thức bổ ích để phòng tránh bệnh hắc lào.\n BẠN VẪN CÒN THẮC MẮC CẦN GIẢI ĐÁP VỀ BỆNH HẮC LÀO, XIN VUI LÒNG LIÊN HỆ:\n  PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 12, NULL, NULL, 1),
(8, 10, 'Bệnh hắc lào và những điều ít người biết', 'Hắc lào (còn gọi là lác) là từ dân gian được sử dụng để chỉ bệnh ngoài da do vi nấm gây nên. Tác nhân gây bệnh thuộc nhóm Dermatophytes và thường gặp nhất là hai loại: trychophyton và epidermophyton. Đây là một bệnh da phổ biến. Bệnh có thể gặp ở mọi lứa tuổi, gặp nhiều ở tuổi thanh thiếu niên và trung niên, nam nhiều hơn nữ. Nếu không điều trị kịp thời, bệnh hắc lào sẽ lây lan nhanh sang những vùng da lành, tăng mức độ tổn thương trên da, chàm hóa hoặc dễ dàng lây sang người khác.\n Dấu hiệu nhận biết:\n &#8211; Triệu chứng dễ nhận biết nhất là nổi mẩn đỏ trên da, ngứa ngáy, xuất hiện mụn nước.\n &#8211; Tại những vùng da bị tổn thương, cơn ngứa sẽ xuất hiện cả ban ngày lẫn ban đêm, tăng lên khi trời nắng nóng, cơ thể ra nhiều mồ hôi hoặc ban đêm.\n &#8211; Nốt mẩn đỏ tại một vùng thường ở một giới hạn nhất định, trên bề mặt da có nổi nhiều mụn nước, tuy nhiên nó lại tập trung ở phần rìa của tổn thương.\n &#8211; Hắc lào là bệnh lây lan nhanh trên cơ thể của người bệnh như tay, chân, người và khi bị chàm hóa sẽ rất khó để chữa trị.<br>Hắc lào thường có hình tròn<br>Nguyên nhân bị bệnh hắc lào\n Chủ yếu do ý thức sinh hoạt có nhân, môi trường sống không phù hợp hoặc dùng chung đồ với người khác cũng dễ gây ra hắc lào. Chính vì vậy, để phòng tránh hắc lào, cần phải có lối sống lành mạnh, vệ sinh thân thể, quần áo sạch sẽ.\n Mặc dù khiến cho người bệnh khó chịu nhưng chữa trị hắc lào hoàn toàn không khó nếu biết cách và phát hiện sớm.\n Điều trị hắc lào\n &gt;&gt; Sử dụng phương pháp dân gian để chữa hắc lào được khá nhiều người lựa chọn vì cách làm này tiết kiệm chi phí mà hiệu quả đã được kiểm chứng trong thực tế. Có thể kể ra một vài biện pháp thường được sử dụng như chuối xanh, gáo dừa… Tuy nhiên, phương pháp này chỉ được áp dụng khi mới bị hắc lào và bệnh chưa lan rộng ra các vùng trên cơ thể.\n &gt;&gt; Thuốc Tây y sẽ đem lại hiệu quả nhanh chóng hơn rất nhiều, nhưng đây cũng là “con dao 2 lưỡi’’ nếu sử dụng không đúng cách vì sẽ làm cho vùng da xung quanh bị tổn thương và vùng da non bị bỏng. Do đó, nếu bệnh hắc nào đã lây lan, bạn cần đến gặp bác sĩ để được khám và tư vấn cũng như chỉ định và hướng dẫn sử dụng loại thuốc phù hợp với cơ địa, đem lại hiệu quả, an toàn.<br>Tình trạng hắc lào lan rộng<br>Lời khuyên dành cho người bệnh\n &#8211; Khi dùng thuốc điều trị hắc lào, bệnh nhân cần tuân thủ các nguyên tắc để tránh sự tái nhiễm và bảo đảm kết quả điều trị như: điều trị liên tục (ngày bôi thuốc 2-3 lần) cho đến khi da lành, sau đó cần tiếp tục thoa thuốc ít nhất hai tuần nữa để tránh tái phát.\n &#8211; Nếu điều trị sau 4 tuần không có dấu hiệu cải thiện thì nên tái khám bác sĩ. Khi bôi thuốc cũng phải theo hướng dẫn, vì nếu bôi thuốc không đúng (bôi thuốc quá mạnh, bôi dây sang da lành hay da non&#8230;) có thể làm bệnh càng lây lan hơn hoặc gây ra tình trạng phỏng, chảy nước nhiều, ngứa dữ dội&#8230; Trong một số ít trường hợp có thể gây nhiễm khuẩn, đi lại khó khăn.\n &#8211; Nguyên nhân nhiễm vi nấm không chỉ do ăn ở kém vệ sinh mà còn do lạm dụng thuốc kháng sinh, lạm dụng thuốc corticoid, dẫn đến nhiễm bệnh nấm “cơ hội” (do kháng sinh chỉ trị được vi khuẩn, còn thuốc corticoid làm giảm sự đề kháng nên các vi nấm có điều kiện tăng sinh). Vì vậy, mọi người nên từ bỏ thói quen dùng thuốc bừa bãi để phòng bệnh và bảo vệ sức khỏe.\n &#8211; Để hạn chế bệnh hắc lào tái phát, bên cạnh việc dùng thuốc đúng chỉ định cần phải diệt nấm ở những vật dụng cá nhân như áo quần, mùng mền, chiếu gối&#8230; bằng cách luộc nước sôi 100 độ C trong vòng 15 phút, rắc bột chống nấm. Không mặc chung quần áo với người khác, tránh làm việc ở những nơi ẩm ướt, ra mồ hôi nhiều, vệ sinh thân thể sạch sẽ.\n Phòng khám Da liễu Hà Nội với đội ngũ bác sĩ chuyên khoa, máy móc hiện đại sẽ giúp bạn xóa tan những lo lắng về căn bệnh “ nhạy cảm’’ này. Nhà ai có người bị bệnh hắc lào, hãy liên hệ với chúng tôi để khám và chữa trị ngay trước khi trở nên nguy hiểm.\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 11, NULL, NULL, 1),
(9, 3, 'Nguyên nhân và cách chữa trị bệnh á sừng', 'Bệnh á sừng ngày càng xuất hiện nhiều, đặc biệt là khi mùa đông đang đến. Bệnh xuất hiện ở mọi lứa tuổi, gây ra sự đau đớn, khó chịu cho bệnh nhân. Mặc dù nguyên nhân gây bệnh vẫn chưa xác định rõ ràng nhưng với sự phát triển mạnh mẽ của ngành y học, các phương pháp chữa bệnh á sừng ngày càng hiệu quả, giúp những người sống chung với căn bệnh này vẫn có thể vui sống và sinh hoạt bình thường. Vậy có những cách chữa trị nào ? cùng nghe các bác sĩ da liễu của chúng tôi chia sẻ trong bài viết dưới đây.\n 1. Triệu chứng bệnh Á sừng\n &#8211; Dày sừng ở da đầu ngón chân, tay, gót chân: Làn da khô, đỏ ở các đầu ngón tay, chân ranh giới không rõ ràng.\n &#8211; Các dát đỏ có thể lan rộng ra ở bàn tay, bàn chân, gót chân. Vào mùa hè, thương tổn có thể đỏ, ngứa nổi mụn nước như trong bệnh tổ đỉa, lâu ngày có thể làm các móng xù xì, lỗ chỗ.\n &#8211; Vào mùa đông khi độ ẩm trong không khí thấp, tình trạng nứt nẻ càng nặng thêm, phần da bệnh thường bị nứt toác ra, rớm máu, đau đớn, có thể ảnh hưởng đến sinh hoạt và công việc. Nếu tiếp xúc với bột giặt, các chất tẩy rửa, các loại nước bẩn, xăng, dầu, khói thuốc, hóa chất… thì bệnh càng nặng thêm.\n &#8211; Người bệnh cũng dễ có nguy cơ nhiễm nấm, vi khuẩn tại các vùng tổn thương. Với các trường hợp viêm da tiếp xúc chủ yếu là các trường hợp viêm da trong công nghiệp.\n &#8211; Bệnh thường gặp ở các bà nội trợ, người làm nông nghiệp, công nhân giặt, công nhân nhà máy xà phòng, thợ làm tóc hay kỹ thuật viên y tế. Các yếu tố thuận lợi là cọ xát, sang chấn, độ ẩm thấp\n 2. Nguyên nhân\n Nguyên nhân gây bệnh á sừng hiện nay vẫn chưa được xác định chính xác, tuy nhiên có 1 số nguyên nhân có thể kể đến như:\n + Yếu tố di truyền: hiện chưa có nghiên cứu nào công bố về tỉ lệ di truyền của bệnh, tuy nhiên theo các nghiên cứu lâm sàng thì tỷ lệ người mắc á sừng có bố mẹ mắc bệnh này khá cao.\n + Vào mùa hè, thương tổn có thể đỏ, ngứa nổi mụn nước như trong bệnh tổ đỉa, lâu ngày có thể làm các móng xù xì, lỗ chỗ.\n + Vào mùa đông khi độ ẩm trong không khí thấp, tình trạng nứt nẻ càng nặng thêm, phần da bệnh dễ bị nứt toác ra, rớm máu, đau đớn, có thể ảnh hưởng đến sinh hoạt và công việc.\n 3. Cách chữa trị\n Một số mẹo dân gian:\n &#8211; Lá sung + lá đu đủ + khoai tây\n Đây là cách chữa á sừng đơn giản, các nguyên liệu dễ dàng tìm thấy trong vườn nhà bạn. Cách làm: bạn lấy lá sung, lá đu đủ, khoai tây xay nhuyễn với nhau thành hỗn hợp . Trước khi đắp hỗn hợp lên vết thương, bạn nên rửa mặt sạch bằng nước trà xanh để sát khuẩn và đem lại hiệu quả cao hơn. Bạn nên sử dụng hỗn hợp từ 1-2 tiếng vào buổi tối trước khi đi ngủ, liên tục trong khoảng 1 tuần.\n &#8211; Sử dụng rau răm\n Sài đất đem rửa sạch, đun sôi để ấm rồi dùng để rửa tay; rau răm rửa sạch rồi giã nát đắp vào vết nứt ở tay. Mỗi ngày đắp 1-2 lần mỗi lần 1 tiếng.\n &#8211; Lá trà xanh\n Dùng trà xanh ngâm chân và lá trà xanh chà xát vào chỗ nứt nẻ rất hiệu quả. Bạn sử dụng một bó trà xanh, rửa sạch, đun sôi trong khoảng 15 phút sau đó cho một ít muối để nước ấm thì ngâm tay, chân. Thực hiện 1 tiếng/1đêm. Có thể dùng lá trà xanh xát vào vùng da bị mụn nước, nứt nẻ để giúp mụn nước khô miệng, không bị loét. Nếu phù hợp bạn sẽ cảm giác chân dịu đi ngay trong lúc ngâm, ngược lại nếu thực hiện trong khoảng 1 tuần điều trị mà vẫn không thấy dấu hiệu bệnh thuyên giảm thì bạn nên áp dụng cách chữa trị khác hiệu quả hơn.\n &#8211; Cây đinh lăng và cây huyết dụ\n Theo các bài thuốc dân gian, bệnh á sừng cũng có thể chữa trị bằng cây đinh lăng kết hợp cây huyết dụ. Cách làm vô cùng đơn giản: Bạn lấy 1 nắm cây đinh lăng và cây huyết dụ (cây huyết dụ bằng ½ đinh lăng) đem rửa sạch và cắt nhỏ như thuốc bắc, sau đó đem lên sắc nước uống sao cho vừa miệng. Nếu khó uống, bạn có thể kết hợp với đường hoặc cho 1 ít cam thảo. Hai loại lá này uống vào không sợ tăng cân, có tính mát nên tốt cho cơ thể bạn có thể uống thay nước.\n Điều trị bằng Tây y\n Phương pháp điều trị hiện nay các bác sĩ hay dùng đó là thuốc bôi bạt sừng, tạo sừng như axit salixilic, diprosalic, betnoval kết hợp với thuốc kháng sinh tác động ngay tại vùng da bệnh hoặc toàn thân nếu bị nhiễm khuẩn phụ, dùng các thuốc chống nấm nếu có nhiễm nấm như mỡ nizoral, dẫn xuất imidazol, griseofulvin. Trường hợp nặng có thể phải dùng corticoid, kháng histamin.\n Khi có biểu hiện của bệnh, bệnh nhân nên đi khám tại chuyên khoa da liễu để được chẩn đoán bệnh và hướng dẫn cụ thể hơn. Không nên tự ý sử dụng thuốc, chỉ được sử dụng thuốc theo chỉ định của bác sĩ vì thuốc điều trị bệnh da liễu có nhiều tác dụng phụ, dùng không đúng sẽ lợi bất cập hại.\n 4. Chế độ ăn uống, sinh hoạt của bệnh nhân á sừng\n &#8211; Hạn chế tiếp xúc với hóa chất: xà phòng, dầu gội, sữa tắm, nước rửa bát… Phải đeo găng tay khi tiếp xúc với những hóa chất đó.\n &#8211; Thận trọng khi sử dụng mỹ phẩm: các loại nước hoa, các loại son phấn, kem dưỡng da….\n &#8211; Thận trọng với thời tiết: khi thời tiết thay đổi những người có cơ địa dị ứng, mẫn cảm với môi trường, cần phải chú ý trang bị đầy đủ như: đeo kính, đeo khẩu trang, găng tay…\n &#8211; Thận trọng với các món ăn lạ\n + Đồ ăn nhiều protein và chất tanh: tôm, cua, ghẹ, măng, cà, lạp xưởng, xúc xích, gà, đồ hộp, trứng,…\n + Đồ uống có chất kích thích: rược, bia, cafe, trà, thuốc lá, tiêu, ớt…\n + Các đồ ăn có chứa chất béo: đường, sữa, mỡ, bơ, chocolate, đồ ngọt tổng hợp….Ngoài ra người bị viêm da cơ địa nên dùng các thực phẩm giàu vitamin A, B, C và các thức ăn dễ tiêu, chống táo bón: cà chua, khoai lang ruột vàng, đu đủ, chuối tiêu. Ăn những loại rau xoắn như cải xoắn, súp lơ, bắp cải, mướp đắng….\n + Có thể ăn những loại thực phẩm chứa nhiều omega3 và chất kẽm có trong cá hồi, cá thu, cá ba sa, canh nghêu, sò. Một tuần có thể ăn thịt bò 1-2 lần, ăn thịt lợn, đậu phụ, thịt ếch…Với bệnh này tăng cường ăn nhiều rau, uống nhiều nước mát hằng ngày rất tốt cho quá trình điều trị.\n + Cần có chế độ ăn uống hợp lý theo một giờ nhất định, chịu khó tập thể dục thể thao để bài tiết mồ hôi.\n + Đối với những trường hợp bị phù nề, rịn nước nên giảm thức ăn có nhiều nước như: chanh, súp, uống ít nước, không uống nước cam nước chanh…\n Chú ý: Trong thời gian điều trị bằng thuốc nam thì không ăn rau muống, đậu xanh, thịt gà, đồ nếp, rau má…. sẽ làm mất tác dụng của thuốc.\n &#8211; Tắm bằng các loại cây lá:\n • Sài đất + chó đẻ răng cưa\n • Kinh giới + lá trầu\n • Cây vòi vòi\n • Vỏ xoan hoặc vỏ cây xà cừ + lá trầu không\n • Xấu hổ, cây cứt lợn để đun nước tắm\n • Kim ngân hoa + bồ công anh\n Ngoài ra có thể sử dụng lá trầu không, kim ngân hoa, bồ công anh, chó đẻ răng cưa để rửa vết thương hoặc ngâm vết thương. Khi vết thương có hiện tượng bài tiết dịch thì có thể giã lá trầu không hoặc lá lược vàng cho ít muối, đặt lên bếp đun sôi để ấm ấm đắp vào vết thương. Hoặc lấy băng gạc lau khô đi và tăng cường bôi thuốc.\n &gt;&gt; Ngày càng có nhiều phương pháp chữa trị á sừng hiệu quả nhưng nhiều bệnh nhân vẫn đang loay hoay không biết cách điều trị nào là phù hợp. Hãy đến các Phòng khám uy tín để được bác sĩ tư vấn, chỉ định sử dụng thuốc nếu các mẹo dân gian bạn áp dụng không đem lại hiệu quả. Với kinh nghiệm, chuyên môn giỏi các bác sĩ tại Phòng khám Da liễu Hà Nội đã chữa trị thành công cho hàng trăm bệnh nhân á sừng, giúp họ tự tin trong cuộc sống và làm việc dễ dàng, hiệu quả. <br>', 15, NULL, NULL, 1),
(10, 3, 'Làm gì để phòng ngừa bệnh á sừng cho nam giới mùa nóng ?', 'Mùa đông đến bệnh á sừng trở thành nỗi sợ hãi của rất nhiều người, nó khiến người bệnh khó chịu, đau đớn vì các mảng da bị bong, nứt và thậm chí ứa máu. Trường hợp nặng hơn da tay còn bị biến dạng, sần sùi và không thể làm việc. Á sừng có thật sự là căn bệnh nguy hiểm và làm cách nào để phòng tránh căn bệnh này ? Hôm nay các bác sĩ da liễu sẽ chỉ cho bạn một số cách để phòng ngừa bệnh hiệu quả.\n 1. Bệnh á sừng là gì ?\n Á sừng là trạng thái lớp sừng chuyển hóa dở dang, tế bào còn nhân và nguyên sinh, chưa chuyển hóa hết thành sừng. Nếu không giữ gìn vệ sinh, người bệnh dễ bị nhiễm khuẩn thứ phát gây sưng tấy. Lớp sừng chuyển hóa dở dang gọi là lớp sừng non, sừng bở, sừng tạp, kém chất lượng.\n Bệnh á sừng là một bệnh viêm da cơ địa dị ứng, là một bệnh ngoài da khá phổ biến, có thể gặp ở nhiều vị trí da khác nhau, nhưng rõ rệt nhất là ở các đầu ngón tay, chân, gót chân. Lớp da ở những vị trí này thường khô ráp, róc da, nứt nẻ gây đau đớn.\n 2. Đối tượng dễ bị á sừng\n + Á sừng hay gặp ở các nhân viên nhà hàng, đầu bếp, người nội trợ, y tá, hộ lý. Tất cả những người này thường xuyên phải tiếp xúc với chất tẩy rửa. Do đó, khả năng mắc bệnh của những người này thường cao hơn những người bình thường.\n + Khí hậu khô hanh của mùa đông miền Bắc nước ta là yếu tố làm cho bệnh càng nặng lên. Trong mùa này nên sử dụng kem dưỡng ẩm để cân bằng độ PH của da.\n + Chị em nội trợ cần lưu ý có một số rau quả, hải sản có thể gây viêm da kích ứng làm khởi động cho viêm da cơ địa như: hành tỏi, củ cải, nước nho, cam, tôm, cá. Vì vậy, để phòng tránh bệnh cho bản thân và những người trong gia đình, các chị em cần cẩn thận trọng trong việc lựa chọn thực phẩm.\n + Một số chất như găng tay cao su, chất mạ nickel của một số đồ dùng và đồ trang sức, chất thuộc da là một trong những yếu tố tạo điều khiển để bệnh á sừng xuất hiện và phát triển. Những chất này gây viêm da kích ứng, tế bào da vùng đó bị mất nước khô nứt ra, tạo nên bệnh cảnh lâm sàng là những đám da đỏ dày khô, nứt nẻ bong vảy, chảy máu, đau đớn.\n 3. Nên làm gì để phòng tránh bệnh á sừng\n Bệnh tuy khó trị liệu và thường kéo dài dai dẳng nhưng để hạn chế tác hại của bệnh, chúng ta vẫn có thể phòng tránh bệnh nếu tuân thủ một số phương pháp sau:\n &#8211; Hạn chế rửa tay, chân nhiều lần với nước\n Việc này để tránh bong tróc da. Không nên ngâm chân, tay với nước muối vì nước muối sẽ hút nước trong tế bào làm cho da khô và nứt nẻ.\n &#8211; Hạn chế tiếp xúc với các hóa chất độc hại\n Các chất tẩy rửa, hóa chất là nguyên nhân gây ra á sừng, do đó bạn không nên tiếp xúc nhiều, nếu bắt buộc phải sử dụng bạn nên đeo găng tay, đồ bảo hộ nếu cần thiết.\n &#8211; Cung cấp đủ nước cho cơ thể\n Tình trạng da khô do thiếu nước rất dễ dẫn tới bệnh á sừng. Thường xuyên uống nước, cung cấp đầy đủ vitamin cho cơ thể qua việc sử dụng thực phẩm, hoa quả hàng ngày sẽ góp phần làm tăng lượng nước và chất dinh dưỡng, giúp bảo vệ da, giúp da bạn mạnh khỏe đầy sức sống\n &#8211; Sử dụng kem dưỡng ẩm\n Vào mùa Đông, khí hậu nước ta khá khô hanh khiến cho da dễ bị khô, bạn nên sử dụng kem dưỡng ẩm để đảm bảo da được bảo vệ khỏi các tác nhân gây bệnh từ bên ngoài.\n &#8211; Tránh làm việc quá lâu trong môi trường nhiều khí lạnh, gió mạnh. Nếu nhiệt độ trong phòng làm việc của bạn thấp, bạn nên sử dụng kem dưỡng ẩm, bên cạnh đó nên đi tất chân (vớ) để bảo vệ cơ thể không bị tác động xấu.\n &gt;&gt; Bệnh á sừng rất khó chữa và lâu khỏi, vì vậy bác sĩ khuyên nên chủ động phòng ngừa bệnh từ những hành động nhỏ hàng ngày. Đặc biệt, khi miền Bắc đang chịu không khí lạnh, căn bệnh này lại càng dễ xuất hiện. Nếu bạn cần tư vấn hoặc thăm khám để phòng ngừa bệnh á sừng thì Phòng khám Da liễu Hà Nội chính là địa điểm bạn đang tìm kiếm.<br>BẠN ĐANG LO LẮNG VÀ KHÔNG BIẾT CÁCH PHÒNG TRÁNH BỆNH Á SỪNG, liên lạc với chúng tôi để được các bác sĩ chuyên khoa da liễu tư vấn và giải đáp các thắc mắc của bạn hoàn toàn MIỄN PHÍ.<br>PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Số 447 Hoàng Quốc Việt, Hà Nội<br> Hotline: 098.123.1234 <br>', 14, 3, 9, 1),
(11, 3, 'Bệnh vảy nến có lây không ?', 'Bệnh vảy nến là một bệnh da phổ biến có ảnh hưởng đến chu kỳ sống của các tế bào da. Bệnh vảy nến gây ra các tế bào xây dựng nhanh chóng trên bề mặt của da, tạo thành vảy dày màu bạc và khô ngứa, các bản vá lỗi màu đỏ mà đôi khi đau đớn. Bệnh vảy nến với biểu hiện rất đặc trưng có thể nhận biết ngay bằng mắt thường đó là những mảng bám kì dị trên da. Chính vì bệnh dễ nhận biết nên khiến cho người e ngại và thắc mắc không biết bệnh vảy nến có lây không?\n 1. Bệnh vảy nến là gì ?\n Bệnh vảy nến là tình trạng rối loạn hệ thống miễn dịch, kết hợp với nhiều tác nhân như: yếu tố di truyền, các tác nhân hóa chất, nhiễm độc, nhiễm trùng, tâm lý căng thẳng… cũng ảnh hưởng trên sự khởi phát, tái phát hoặc làm bệnh nặng thêm…. Là căn bệnh ngoài da nhưng bệnh vảy nến lại gây ra khá nhiều phiền toái cho người bệnh bởi các triệu chứng như: xuất hiện các mảng đỏ, da khô, bong tróc vảy thành từng lớp, trường hợp nặng người bệnh có thể bị viêm nhiễm có mủ xuất hiện.\n 2. Bệnh vảy nến có lây không ?\n Theo các bác sĩ da liễu, bệnh vảy nến không phải là một bệnh lây nhiễm, tức là bệnh không có khả năng lây nhiễm từ người sang người. Mặc dù nguyên nhân gây bệnh chưa rõ ràng, tuy nhiên theo các kiến thức y khoa và kiến thức lâm sàng cho thấy bệnh này không có hiện tượng lây nhiễm.\n Nhiều người khi bước vào giai đoạn bệnh nặng thường thấy vùng cơ thể bị bệnh nhiều hơn, triệu chứng phát tác nhiều hơn thì lo sợ việc bệnh này lây nhiễm. Hơn thế nhiều người đã đi trị liệu xong 1 thời gian sau lại thấy bệnh quay lại thì cho rằng bị lây nhiễm lại từ người khác. Tuy nhiên đây là hiện tượng tái diễn của bệnh, có thể do nhiều nguyên nhân khác nhau.\n Như vậy, vảy nến không phải là một bệnh lây nhiễm. Người bệnh vảy nến có thể chung sống bình thường với những người khác mà không lo có bất cứ sự lây nhiễm nào. Để khắc phục và hạn chế tình trạng bệnh, các bạn cần lưu ý thực hiện phòng tránh tốt dựa trên các yếu tố nguyên nhân và nguy cơ gây bệnh.\n 3. Làm gì để phòng tránh bệnh vảy nến ?\n Mặc dù đã có nhiều tiến bộ trong điều trị vảy nến với các phương pháp hiện đại, tuy nhiên do nguyên nhân gây bệnh chưa rõ ràng nên chưa có cách nào chữa khỏi hoàn toàn căn bệnh này. Bệnh hay tái phát nên trong quá trình điều trị cần được sự tư vấn của bác sĩ về cách chăm sóc da, chế độ ăn uống.\n Việc phòng tránh bệnh vảy nến khá đơn giản,dưới đây là lời khuyên của bác sĩ tại Phòng khám Da liễu Hà Nội:\n &#8211; Giữ vệ sinh thân thể sạch sẽ, thường xuyên tắm giặt, thay quần áo.\n &#8211; Áp dụng chế độ dinh dưỡng hợp lý, bổ sung đầy đủ cá loại vitamin và khoáng chất cần thiết nhằm tăng cường sức đề kháng. Nên ăn nhiều rau xanh, củ quả tươi, chứa nhiều chất xơ như các loại rau họ cải; các loại quả, củ có màu sắc như đu đủ, cà rốt, cà chua… Thường xuyên uống nước để da không bị khô ráp\n &#8211; Làm việc và nghỉ ngơi hợp lý, hạn chế căng thẳng, mệt mỏi, giảm stress. Thường xuyên vận động hàng ngày để tăng cường thể lực.\n &#8211; Căng thẳng về mặt cảm xúc hoặc vật lý. Sự căng thẳng có thể khiến cho vảy nến xuất hiện đột ngột hoặc là các triệu chứng trở nên tệ hơn. Chính vì vậy, bạn phải luôn giữ tâm trạng thoải mái, vui vẻ tránh căng thẳng thần kinh quá mức.\n &#8211; Tránh tiếp xúc trực tiếp với hóa chất độc hại cũng là cách phòng tránh bệnh vảy nến hiệu quả. Nếu buộc phải làm việc với hóa chất cần mặc trang phục bảo hộ, đeo găng tay để tránh nhiễm độc.\n &#8211; Sử dụng kem dưỡng ẩm hàng ngày để da luôn mềm mại.\n &#8211; Nên thường xuyên đi khám và sử dụng thuốc theo chỉ định của bác sĩ, tránh dùng các thuốc ức chế miễn dịch hệ thống để điều trị bệnh vì có thể làm bệnh nặng hơn. Tránh các vết thương trầy xước ngoài da.\n &gt;&gt; Bệnh vảy nến xuất hiện trên da sẽ làm mất thẩm mỹ và làm người bệnh khó chịu, ảnh hưởng tới chất lượng cuộc sống và sinh hoạt. Do đó, áp dụng đúng phương pháp phòng tránh bệnh vảy nến sẽ giúp giảm nguy cơ mắc bệnh, luôn có một làn da khỏe mạnh\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234<br> <br>', 11, NULL, NULL, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(12, 3, 'Triệu chứng điển hình của bệnh vảy nến', 'Bệnh vảy nến là bệnh rất hay gặp, cơ chế bệnh sinh chưa rõ ràng. Mặc dù có nhiều tiến bộ trong điều trị với nhiều phương pháp hiện đại, song cho đến nay chưa có một phương pháp nào có thể điều trị hoàn toàn bệnh vảy nến. Để phòng tránh bệnh vảy nến cần có chế độ sinh hoạt điều độ và chữa trị hợp lý để bệnh không tiến triển xấu cũng như các biến chứng khi đã mắc bệnh.\n 1. Bệnh vảy nến là gì ?\n Vảy nến là một bệnh ngoài da khá phổ biến gặp ở mọi lứa tuổi, mọi chủng tộc và bất kỳ đâu. Bệnh vảy nến là bệnh tự miễn dịch kéo dài, do sự rối loạn nội tiết tạo nên một màng ngăn bám chặt trên da. Màng bám này sản sinh tế bào nhanh gấp 10 lần tế bào bình thường rồi chết. Thay vì chết đi như tế bào bình thường thì nó bám trên da và tạo thành vảy màu trắng như vỏ xà cừ.\n 2. Nguyên nhân\n &#8211; Yếu tố di truyền là một trong những nguyên nhân được các nghiên cứu đề cập đến. Bệnh vảy nến có tính chất gia đình, do vậy nếu trong nhà có người vảy nến thì nguy cơ bạn mắc bệnh sẽ cao hơn người bình thường.\n &#8211; Sự thay đổi cơ chế miễn dịch làm rối loạn quá trình biệt hóa tế bào sừng.\n Ngoài ra còn có một số các yếu tố khác tạo thuận lợi cho bệnh phát triển nhanh:\n &#8211; Người có bệnh nhiễm khuẩn hoặc bệnh mạn tính\n &#8211; Bị chấn thương thượng bì\n &#8211; Chấn thương tâm lý có thể làm bệnh thôi tái phát hoặc bệnh nặng hơn\n &#8211; Sự rối loạn nội tiết; rối loạn chuyển hóa do nghiện rượu, môi trường, khí hậu…\n 3. Triệu chứng\n &#8211; Đỏ da\n Kích thước to nhỏ nhỏ khác nhau khoảng vài cm, có khi tạo thành mảng có giới hạn rõ ràng, số lượng nhiều hay ít tùy thuộc vào từng bệnh nhân\n &#8211; Thương tổn da\n Thương tổn da điển hình là dát đỏ có vảy hình tròn hoặc bầu dục, có đặc điểm ấn kính mất màu, ranh giới rõ với da lành; vị trí chủ yếu ở vùng tì đè. Có vảy trắng khô, dễ bong, nhiều tầng xếp lên nhau, khi cạo hết các lớp vảy nền da dưới đỏ tươi.\n &#8211; Thương tổn móng\n Khoảng 30-40% bệnh nhân có thương tổn ở móng tay, móng chân: Móng ngả màu vàng, tách móng; có các chấm rõ trên bề mặt.\n &#8211; Thương tổn khớp\n Biểu hiện hay gặp của bệnh nhân là viêm khớp mạn tính, biến dạng nhiều khớp, cứng khớp\n &#8211; Thương tổn niêm mạc\n Thường gặp ở niêm mạc quy đầu là những vết màu hồng, không thâm nhiễm, giới hạn rõ, không hoặc có ít vảy, tiến triển mạn tính. Ở lưỡi, giống viêm lưỡi hình bản đồ hoặc viêm lưỡi phí đại tróc vảy. Ở mắt, hình ảnh viêm kết mạc, viêm giác, viêm mí mắt.\n 4. Biến chứng\n Bệnh vảy nến tồn tại suốt đời, tiến triển thất thường theo từng đợt, sau 1 đợt cấp phát bệnh có thể ổn định, tạm lắng một thời gian. Các vị trí như khuỷu tay, đầu gối thường dai dẳng và rất lâu khỏi. Trong quá trình tiến triển thương tổn có thể lây lan các vị trí xung quanh, khi khỏi sẽ để lại vết thâm hoặc vết màu trắng. Sau một thời gian, bệnh sẽ tái phát tại vị trí bị thương tổn cũ. Nhìn chung, thể trạng người bệnh không bị ảnh hưởng nhiều trừ các bệnh nhân bị thể mủ, đỏ rát.. Một số biến chứng thường gặp:\n &#8211; Chàm hóa, bội nhiễm, ung thư da\n &#8211; Đỏ da toàn thân\n 5. Điều trị\n Hiện nay chưa có thuốc nào chữa khỏi hoàn toàn bệnh vảy nến. Nhưng nếu điều trị một cách hợp lý cùng với việc tuân thủ điều trị của bệnh nhân và có 1 chế độ sinh hoạt điều độ thì duy trì được sự ổn định của bệnh. Hạn chế những đợt bùng phát và cải thiện được chất lượng cuộc sống của bệnh nhân.\n Để điều trị bệnh, bệnh nhân có thể sử dụng các thuốc bong vảy, khử oxy, chống viêm. Bệnh nhân tuyệt đối không sử dụng các dược phẩm chứa corticoid. Bệnh vảy nến có tiến triển rất thất thường nên không thể lơ là được, không được tự ý bỏ thuốc ki thấy bệnh có dấu hiệu thuyên giảm hay biến mất.\n Nguyên tắc điều trị tối ưu hiện nay là:\n &#8211; Làm sạch thương tổn nhanh chóng\n &#8211; Hạn chế tái phát\n &#8211; An toàn, ít độc hại , rẻ tiền, dễ áp dụng\n Để biết thêm thông tin về bệnh vảy nến, xin vui lòng liên hệ với chúng tôi để được bác sĩ tư vấn:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 13, NULL, NULL, 1),
(13, 3, 'Bệnh vảy nến và những biến chứng thường gặp', 'Bệnh vảy nến là 1 trong số những bệnh viêm da thường gặp, tại Việt Nam chiếm khoảng 2 đến 3% dân số mắc phải. Là bệnh mãn tính thường gặp do tăng sinh tế bào và viêm gây tổn thương da, tạo thành những mảng đỏ bề mặt bong tróc có giới hạn rất rõ.\n Trên thế giới khoảng 1 đến 2% dân số mắc bệnh này, mọi lứa tuổi đều có thể bị vảy nến, tuy nhiên bệnh thường khởi phát trong độ tuổi từ 15 đến 30 tuổi.<br>Nguyên nhân gây bệnh\n Hiện nay khoa học vẫn chưa xác minh được nguyên nhân gây bệnh vảy nến chỉ nhận thấy bệnh có tính chất di truyền và do hệ miễn dịch. Tuy nhiên có một số điều kiện để gây bệnh như sau:\n &#8211; Chấn thương: vẩy nến có thể xuất hiện ở những vùng da bị chấn thương thậm chí cả những vết trầy xước nhẹ.\n &#8211; Nhiễm trùng: nhiễm trùng đường hô hấp trên do liên cầu trùng như viêm họng, viêm amidan có thể gây khởi phát vẩy nến giọt (một dạng vẩy nến) hoặc làm nặng thêm tình trạng bệnh vẩy nến hiện mắc. Nhiễm HIV cũng làm nặng thêm bệnh vẩy nến.\n &#8211; Thuốc: một số thuốc có thể gây khởi phát hoặc làm nặng thêm bệnh vẩy nến như thuốc điều trị cao huyết áp (ức chế men chuyển, beta-blocker), kháng sốt rét tổng hợp (chloroquin), lithium, một số kháng viêm non-steroid (indomethacine), progesterone và corticosteroid.\n &#8211; Stress: buồn phiền, lo lắng, giận dữ, stress thường dễ làm bùng phát và nặng thêm bệnh vẩy nến.\n &#8211; Thời tiết: thời tiết lạnh và khô dễ gây bùng phát bệnh vẩy nến. Thời tiết nắng, nóng và ẩm thường làm giảm nhẹ bệnh. Tuy nhiên, một số người thường bùng phát bệnh nặng hơn khi tiếp xúc với nắng (vẩy nến nhạy cảm ánh sáng).\n &#8211; Rượu và thuốc lá: làm nặng thêm bệnh vẩy nến.<br>Vảy nến ở nam giới<br>Phân loại vảy nến<br>Vẩy nến ở bàn tay<br>Vẩy nến không lây từ người bệnh qua người khác cũng như không lây từ vị trí này sang vị trí khác trong cơ thể người bệnh. Tuy nhiên, vẩy nến ngày nay nhiều nghiên cứu cho thấy có tính di truyền (10% con mắc bệnh nếu bố hoặc mẹ bệnh, 40% con mắc bệnh nếu cả bố mẹ đều bệnh).\n Biến chứng vảy nến để lại cho người bệnh\n Vẩy nến là một bệnh không ổn định, nếu không được chăm sóc và điều trị đúng người bệnh có thể bị các biến chứng sau:\n &#8211; Đỏ da toàn thân.\n &#8211; Vẩy nến mủ.\n &#8211; Viêm khớp.\n &#8211; Nhiễm trùng da.\n &#8211; Nghiện rượu, thuốc lá.\n &#8211; Tổn thương gan do thuốc, rượu.\n &#8211; Một số nghiên cứu gần đây cho thấy vẩy nến là yếu tố nguy cơ đối với hội chứng chuyển hóa và bệnh tim mạch (nghĩa là dễ mắc hội chứng chuyển hóa và bệnh tim mạch hơn) nhất là đối với những người vẩy nến nặng.<br>Điều trị vảy nến\n Khi có dấu hiệu của bệnh vảy nên điều đầu tiên cần đến bác sĩ da liễu để được khám và điều trị, không nên tùy tiện mua thuốc tự sử dụng. Bác sĩ sẽ đưa ra phương pháp điều trị bằng thuốc hoặc trị liệu sinh học. Bạn sẽ được hướng dẫn cách chăm sóc da, tư vấn điều nên làm và không nên làm giúp kiểm soát và chung sống hòa bình với bệnh. <br>', 12, NULL, NULL, 1),
(14, 20, 'Tác hại và cách phòng tránh bệnh mề đay', 'Bệnh mề đay là phản ứng của da do viêm, gây ra do nhiều cơ chế phức tạp với vai trò chủ yếu của các chất hóa học trung gian như histamin, Serotonine. Thương tổn cơ bản là sẩn phù xuất hiện nhanh và cũng nhanh biến mất. Căn nguyên thông thường nhất là do thuốc, thức ăn, ký sinh trùng. Cũng có thể do yếu tố vật lý, tì đè, nóng, lạnh, tiếp xúc với lông động vật, phấn hoa…\n Triệu chứng:\n &#8211; Thương tổn cơ bản là sần, phù hoặc mảng sẩn phù hình tròn, hình bầu dục, có giới hạn rõ ràng, kích thước to nhỏ khác nhau, màu đỏ, ở giữa thương tổn có màu trắng ngà, ấn kích mất màu. Thương tổn có thể lây lan đến các vùng xung quanh, số lượng từ vài chiếc tới hàng trăm chiếc tùy từng bệnh nhân. Vị trí có thể gặp ở bất kì chỗ nào của cơ thể.\n &#8211; Dùng vật đầu tù vạch lên da bất kì chỗ nào sẽ thấy 1 dải màu trắng ngà xuất hiện nhanh, tiếp theo chuyển màu đỏ, phù ở rìa đường vạch, tại đường vạch có thâm nhiễm nổi gờ lên.\n &#8211; Bệnh nhân sẽ cảm thấy ngứa, nóng rát rất khó chịu\n &#8211; Cơ thể có thể bị sốt, đau bụng, buồn nôn, khó thở…<br>Bệnh mề đay có lây không ?\n Bệnh nổi mề đay có lây không là điều mà đa số bệnh nhân quan tâm. Theo các bác sĩ phòng khám da liễu Hà Nội khẳng định bệnh nổi mề đay không lây nhiễm. Khi chữa trị không triệt để bệnh chỉ có thể tái phát và biến chuyển thành bệnh mề đay mãn tính chứ không thể lây từ người này sang người khác. Bởi dựa vào những nguyên nhân gây bệnh trên thì bệnh mề đay phụ thuộc rất nhiều vào cơ địa của từng người. Với những người có cơ địa dễ bị dị ứng với những dị nguyên gây bệnh thì sẽ rất hay bị dị ứng nổi mề đay.\n Tác hại:\n Bệnh mề đay khiến cho người bệnh ngứa, khó chịu và tự ti do đó làm giảm chất lượng cuộc sống. Không những vậy, bệnh còn biến chứng dẫn đến một số bệnh khác nguy hiểm, thậm chí là ảnh hưởng đến tính mạng.\n &#8211; Ảnh hưởng đến chất lượng cuộc sống: Mề đay nếu phát bệnh nhưng không kịp thời trị liệu, kéo dài đến vài tháng hoặc đến hàng năm, sáng dậy và trước khi ngủ ngứa càng dữ dội, ảnh hưởng đến cuộc sống và tâm lí thường ngày. Vì vậy mề đay mãn tính không được để kệ nó phát triển, sẽ dẫn đến hậu quả nghiêm trọng về sau.\n &#8211; Dẫn đến các bệnh khác: Phát bệnh mề đay kèm theo các triệu chứng đường tiêu hóa, dẫn đến rối loạn chức năng đường ruột như buồn nôn, nôn, đi ngoài và đau bụng; dẫn đến viêm thận, cường giáp miễn dịch của cơ thể, thậm chí chức năng điều tiết miễn dịch bị giảm gây nên u bướu ác tính. Chị em trong giai đoạn sinh sản vẫn có thể bị vô sinh và sảy thai do miễn dịch.\n &#8211; Nguy hiểm tính mạng: Mề đay nếu ở cổ họng và khí quản, sẽ dẫn đến phù cổ họng tái diễn nhiều lần, cảm giác dắt cổ họng, khó thở, đau ngực, nghiêm trọng khiến người bệnh thiếu khí mà chết; bệnh tình nghiêm trọng, kèm theo triệu chứng sốc dị ứng như hồi hộp, lo lắng, nôn, thậm chí huyết áp hạ, nguy hiểm đến tính mạng.\n Cách phòng bệnh:\n Phòng bệnh bao giờ cũng hơn chữa bệnh. Muốn không bị bệnh mề đay bạn nên thực hiện một số phương pháp sau:\n &#8211; Trong trường hợp bạn bị nổi mề đay do trời lạnh thì nên mặc ấm, không nên tiếp xúc trực tiếp với gió lạnh.\n &#8211; Nếu bạn bị dị ứng nổi mề đay do thực phẩm thì tránh xa những thức ăn gây dị ứng như hải sản, thịt gà, trứng…\n &#8211; Hạn chế dùng mỹ phẩm, nếu dùng thì cần phải chọn loại phù hợp với làn da của bạn, không nên sử dụng tùy tiện dễ gây dị ứng, mẩn đỏ,ngứa trên da.\n -Nếu phải làm việc trong môi trường có nhiều chất độc hại thì cần phải trang bị bảo hộ lao động.\n &#8211; Luôn luôn giữ vệ sinh sạch sẽ, chú ý chế độ ăn uống để tăng sức đề kháng cho cơ thể, vi khuẩn, virus gây bệnh không thể xâm nhập.\n &#8211; Không nên ăn chất cay nóng, đồ uống có cồn và chất kích thích.\n &#8211; Khi uống thuốc đông y hoặc tây y phải theo sự chỉ định của bác sĩ. Bởi với một số thuốc sẽ gây ra tác dụng phụ, gây chóng mặt hoặc nặng hơn là ngộ độc thuốc\n &gt;&gt; Bệnh mày đây không phải là căn bệnh xa lạ, dấu hiệu dễ dàng nhận biết. Chính vì vậy, khi đã thực hiện các phương pháp phòng bệnh nhưng vẫn không tránh khỏi, hãy chủ động chữa trị bằng các mẹo dân gian. Nếu bệnh không thuyên giảm, hãy đến gặp bác sĩ để được khám và điều trị tận gốc căn bệnh hay tái phát này.\n Phòng khám da liễu Hà Nội với đội ngũ bác sĩ chuyên khoa da liễu kinh nghiệm, tận tâm sẽ giúp bạn xóa bỏ nỗi lo lắng về mề đay – căn bệnh có thể ảnh hưởng đến tình mạng nếu không điều trị kịp thời.\n Liên hệ với chúng tôi nếu bạn hoặc người thân đang phải sống chung với mề đay.\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 12, NULL, NULL, 1),
(15, 20, 'Đâu là thực đơn cho bệnh nhân mề đay ?', 'Chế độ dinh dưỡng trong thực đơn của mỗi bữa ăn có tác động rất lớn đến sự tiến triển của bệnh mề đay. Thực phẩm nào nên ăn và thực phẩm nào nên kiêng là vấn đề được nhiều người quan tâm. Để giải đáp thắc mắc của bệnh nhân, bác sĩ tại Phòng khám Da liễu Hà Nội sẽ giới thiệu thực đơn phù hợp với những người bị mề đay.<br>Theo các kết quả nghiên cứu dinh dưỡng cho biết, hải sản là nhóm thực phẩm dễ khiến chúng ta gặp phải tình trạng dị ứng cao, mẩn ngứa, phát ban: tôm, cua, mực, ghẹ… Bởi trong hải sản có chứa hàm lượng protein parvalbumin. Đây là loại chất gây dị ứng khá mạnh, nhất là những người có cơ địa dị ứng, mẫn cảm nhanh chóng.<br>Khi người nhạy cảm như các bệnh nhân nổi mề đay thì việc ăn hải sản vào sẽ càng tăng mức ngứa lên cao, cơ thể nóng bừng và phát ban đỏ ra bên ngoài, nặng người bệnh có thể bị sưng phù toàn thân.<br>Đã có nhiều người sai lầm khi nghĩ rằng hải sản nấu chín ở nhiệt độ cao thì protein paravalbumin sẽ được chuyển hóa hoàn toàn và không bị dị ứng. Nhưng thực chất thì hàm lượng này có trong hải sản cao và sẽ không bị chuyển hóa trong nhiệt độ cao.<br>Chính vì vậy, người bị nổi mề đay không được ăn hải sản để bảo vệ sức khỏe và giúp cho quá trình hỗ trợ điều trị bệnh thuận tiện hơn.<br>Tại sao thịt bò và sữa bò chứa hàm lượng chất dinh dưỡng cao như vậy mà những người bị nổi mề đay cần kiêng ăn? Rất đơn giản nhưng cũng không phải ai cũng biết, trong thịt bò và sữa bò có chứa 1 loại protein gây dị ứng là casein và huyết thanh. Chính vì vậy mà không ít người bị nổi mề đay khi ăn vào bệnh sẽ ngày càng nặng hơn. Còn đối với trẻ em nhạy cảm khi ăn hoặc uống vào sẽ tăng nguy cơ bị nổi mề đay nhanh chóng, các cơn ngứa càng kéo dài và rất khó chịu cho người bệnh.<br>Vì thế người bệnh mề đay kiêng ăn thịt bò và uống sữa bò.<br>Từ trước đến nay, bia rượu luôn là chất kích thích khá mạnh đối với một số đối tượng hoặc một số loại nước ngọt có ga giúp cho bữa ăn được ngon miệng hơn. Nhưng lợi dụng rượu bia nước ngọt có ga sẽ ảnh hưởng khá lớn đến sức khỏe của chúng ta, nhất là đối với những người bệnh nổi mề đay. Vì theo nghiên cứu vitamin nhóm B có trong các loại nước này dễ kích thích các tế bào thần kinh và gây ra hiện tượng mẩn ngứa, phát ban đỏ ảnh hưởng rất không tốt đến người bệnh. Do vậy, nổi mề đay cần tránh xa rượu bia nước ngọt có ga, thức uống chứa chất kích thích.<br>Đậu phộng hay còn gọi là lạc, đây là một trong những tác nhân gây ra bệnh nổi mề đay. Bởi trong lạc có hỗ trợ chữa chất gây dị ứng như protein dự trữ là vicilin và albumin. Hai loại chất này rất bền vững ở nhiệt độ cao nên không bị biến đổi chất. Thông thường, ăn lạc nướng sẽ bị dị ứng nhanh hơn và mạnh hơn lạc luộc.<br>Theo viện nghiên cứu dinh dưỡng thì 1 củ lạc cũng có thể làm khởi phát được căn bệnh nổi mề đay này. Nên người bị nổi mề đay cần kiêng ăn đậu phộng để đem lại sự an toàn nhất cho sức khỏe bản thân.<br>Ngoài những thực phẩm chính ở trên thì nằm trong danh sách những thực phẩm gây bệnh mề đay là: nấm, bơ, trứng, măng…<br>Có thể thấy rằng, có vô vàn những loại thức ăn thực phẩm có thể gây lên bệnh nổi mề đay. Vì vậy, người bệnh nên có 1 chế độ ăn sao cho hợp lý tránh xa những thực phẩm cần kiêng ăn để tránh những nguy hại cho sức khỏe và làm bệnh ngày càng nặng. Đồng thời người bệnh mề đay cũng cần kết hợp với các bài tập thể dục để tăng cường sức khỏe và sức đề kháng chống lại các tác nhân gây mề đay.<br>Các lưu ý khác <br>Nổi mề đay có kiêng nước không ?<br>Quan niệm kiêng nước khi bị nổi mề đay xuất phát từ xa xưa bắt nguồn từ việc sợ nhiễm phong hàn. Có một số trường hợp nổi mề đay do dị ứng với thời tiết lạnh nên sự chênh lệch của nhiệt độ môi trường với nhiệt độ cơ thể sinh ra phản ứng dị ứng. Người ta cho đây là bệnh nhiễm phong hàn vì cứ tiếp xúc với nước là bệnh nặng hơn.<br>Tuy nhiên đây là một quan niệm không đúng hoàn toàn. Nguyên nhân gây nên mề đay ở mỗi người không giống nhau và không phải mọi trường hợp bệnh đều do phong hàn mà ra. Vì thế với những tác nhân như môi trường, thực phẩm, mỹ phẩm… thì cơ địa dù có tiếp xúc với nước vẫn không bùng phát bệnh một cách trầm trọng.<br>Việc kiêng nước khi bị nổi mề đay chỉ nên áp dụng với các trường hợp dị ứng với thời tiết lạnh mà thôi. Các trường hợp khác nếu kiêng nước là hoàn toàn sai lầm bởi nếu không được tắm rửa sạch sẽ thậm chí còn bị gia tăng nguy cơ nhiễm khuẩn khiến bệnh nặng hơn và bị viêm nhiễm ngoài da.<br>Bệnh mề đay có nhiều biến chứng nguy hiểm, hy vọng với những thông tin mà bác sĩ của chúng tôi chia sẻ sẽ giúp bạn có thêm kiến thức để chuẩn bị cho mình và gia đình những món ăn ngon, bổ dưỡng có thể bảo vệ mình khỏi những tác hại mà bệnh mề đay gây ra đồng thời ngăn chặn bệnh quay trở lại.<br>NẾU CÒN BẤT CỨ THẮC MẮC NÀO, hãy liên hệ với chúng tôi để được tư vấn:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Số 447 Hoàng Quốc Việt, Hà Nội<br> Hotline: 098.123.1234<br>&nbsp;<br>&nbsp; <br>', 11, NULL, NULL, 1),
(16, 20, 'Điều trị nổi mề đay mẩn ngứa khắp người', 'Bệnh mề đay là một dạng dị ứng ở ngoài da, đây là một phản ứng cấp hoặc mãn tính của mao mạch do dị ứng làm phù ở da hoặc niêm mạc. Biểu hiện của bệnh rất dễ nhận biết, khi bị nổi mề đay, vùng da nổi gồ lên từng mảng có nhiều hình dạng khác nhau, gây ngứa, lúc mất chỗ này lại mọc chỗ kia, càng gãi càng ngứa và càng nổi thêm nốt mới. các nốt sần nổi đột ngột thành đám, dần dần nhẹ đi và lặn.<br>Mề đay được chia làm 2 loại\n Mề đay cấp tính: Bệnh biểu hiện đột ngột ở bất cứ vùng nào trên cơ thể, thể hiện trên những sần, phù nề, ngứa dữ dội. Cơn xảy ra trong vài phút hoặc vài giờ rồi lặn hoặc có thể từng đợt kế tiếp nhau. Trong cơn mề đay cấp có thể kèm theo sốt cao, nôn mửa, đau quặn bụng, khó thở…\n Mề đay mãn tính: Đó là khi tình trạng nổi mề đay kéo dài trên 8 tuần, không kể nhiều hay ít, có khi ngắt quãng nhiều ngày, có thể gặp các dạng khác nhau.\n + Mề đay thành vệt dài, thành vòng &#8211; mề đay xuất huyết.\n + Mề đay sần ở trẻ em &#8211; mề đay mụn nước, phòng nước.\n + Mề đay khổng lồ &#8211; đó là phù nổi đột ngột làm sưng phù mặt, mi mắt, môi hoặc bộ phận sinh dục, thường sau vài giờ thì lặn, không ngứa chỉ gây cảm giác căng tức khó chịu. Mức độ nguy hiểm của dạng phù này là có thể làm phù đường hô hấp trên, gây hẹp thanh quản và họng dẫn tới khó thở, phải cấp cứu.\n + Mề đay cấp tiết Cholin: Xuất hiện khi đi ra nắng, vận động thể lực, xúc cảm hay gặp ở người trẻ tuổi. Mề đay nổi đột ngột rầm rộ khắp cơ thể gây cảm giác rất ngứa.<br>Mề đay khắp cơ thể<br>Nguyên nhân hình thành bệnh\n &#8211; Do yếu tố vật lý như chấn thương, cọ xát, lạnh nắng.\n &#8211; Do tiếp xúc các vật lạ qua da, qua đường hô hấp, do ăn uống, thuốc, mỹ phẩm, côn trùng, ký sinh trùng, vi khuẩn…\n &#8211; Do di truyền, chủ yếu là chứng dị ứng do lạnh.\n &#8211; Do các bệnh hệ thống: Có thể gặp mề đay kết hợp với bệnh Luput ban đỏ hệ thống, u ác tính, cường giáp trạng…cũng có nhiều trường hợp nổi mề đay nhưng không xác định được nguyên nhân.\n &#8211; Thực phẩm, thức uống, gia vị: Thức ăn hay bị &#8220;đổ thừa&#8221; nhất là đồ biển như: sò, nghêu, cua, ghẹ, cá biển. Các loại sôcôla, sữa, bơ, phó mát. Nhóm thực vật là dưa gang, dưa tây, cà chua, trái dâu, kể cả hành, tỏi. Tuy nhiên, cần phải nhớ rằng thức ăn thông thường nhất, &#8220;lành nhất&#8221; cũng có thể gây bệnh.\n &#8211; Các chất phụ gia: Cũng là yếu tố quan trọng. Chúng có thể là chất tự nhiên như các loại men, con giấm hoặc chất hóa học dùng để bảo quản và nhuộm màu thực phẩm.\n &#8211; Thuốc men: Có rất nhiều thứ thuốc có thể gây dị ứng và nổi MÐ. Ðáng kể nhất là Penicilline rồi đến Aspirine, Sulfamides; các loại thuốc ngủ, thuốc an thần, thuốc trị đau nhức xương khớp, thuốc ngừa thai và còn nhiều loại khác.\n &#8211; Nhiễm trùng: Các ổ nhiễm trùng, nhiễm nấm tiềm tàng thường gây bệnh MÐ mãn tính như viêm xoang, sâu răng, nhiễm trùng đường tiểu, đường hô hấp trên như viêm mũi &#8211; họng.\n &#8211; Các loại bụi nhà, bụi phấn hoa, bụi lông thú và các loại ky sinh trùng cũng thường là nguyên nhân của MÐ mãn tính.\n &#8211; Các yếu tố xúc cảm, thay đổi nhiệt độ, áp lực cọ xát do quần áo chật bó cũng có thể làm nổi MÐ.\n Vì có quá nhiều nguyên nhân gây bệnh, người bệnh cần phải thật chú ý mới hy vọng giúp thầy thuốc tìm ra nguyên nhân chính gây bệnh để trị tận gốc.<br>Mề đay ở trẻ<br>Điều trị\n Phương pháp trị bệnh mề đay có bài mẹo dân gian, đông y, tây y, thuốc nam…vv\n Nhưng để điều trị hiệu quả mề đay bạn cần nhớ được nguyên nhân gây bệnh, tùy vào mức độ bác sĩ sẽ điều trị bằng thuốc nam hoặc thuốc tây. Nếu bệnh nặng hoặc xuất hiện thường xuyên, người bệnh phải đi khám ở các cơ sở y tế để có hướng xử trí tốt nhất.<br>Phòng bệnh\n Những người dễ bị nổi mề đay thường là có cơ địa nhạy cảm, do vậy để phòng bệnh cần lưu ý những vấn đề sau:\n &#8211; Đối với nổi mề đay do lạnh, luôn chú ý mặc ấm, hạn chế tối đa tiếp xúc với môi trường lạnh.\n &#8211; Nếu do ăn uống nổi mề đay thì không nên ăn những thức ăn đó (như thịt gà, cá chép, tôm, cua…).\n &#8211; Phụ nữ khi sử dụng mỹ phẩm phải thận trọng, lụa chọn những loại mỹ phẩn thích hợp với loại da của mình.\n &#8211; Khi tiếp xúc với môi trường có nhiều chất độc hại phải đeo khẩu trang và mặc quần áo bảo hộ lao động.\n &#8211; Cần giữ gìn vệ sinh cơ thể sạch sẽ, hạn chế sự xâm nhập của các loại ký sinh trùng như bọ chét, chấy rận.\n &#8211; Đặc biệt khi sử dụng các loại thuốc đông &#8211; tây y mà không được hướng dẫn của bác sỹ, không chỉ gây ra chứng mề đay mà còn có thể bị ngộ độc thuốc. <br>', 15, NULL, NULL, 1),
(17, 42, 'Bệnh lang ben ở trẻ nhỏ', 'Lang ben là bệnh ngoài da với các chấm màu nâu hoặc màu trắng có thể xuất hiện ở nhiều vị trí khác nhau như mặt, tay, chân và...<br>Khi nhắc đến lang ben chúng ta thường nghĩ đến những vùng da bị thay đổi màu sắc, có thể là màu trắng, màu nâu xuất hiện nhiều...<br>Bệnh lang ben ngày càng phổ biến và ai cũng có nguy cơ mắc phải căn bệnh khó chịu này. Không chỉ khó điều trị dứt điểm bệnh...<br>Lang ben là bệnh ngoài da với biểu hiện là các chấm màu nâu hoặc màu trắng, có thể xuất hiện ở nhiều vị trí khác nhau như...<br>Lang ben là bệnh nhiễm nấm ngoài da thường gặp. Bệnh thườg lan rộng trong môi trường nhiệt đới nóng ẩm và có thể dễ dàng lây từ...<br>', 11, NULL, 1, 1),
(18, 42, 'Hậu quả do chủ quan với bệnh lang ben', 'Khi nhắc đến lang ben chúng ta thường nghĩ đến những vùng da bị thay đổi màu sắc, có thể là màu trắng, màu nâu xuất hiện nhiều ở các vị trí như ngực, bụng, lưng, chân tay. Các triệu chứng của bệnh lang ben khá nhẹ nên người bệnh thường chủ quan và không để ý khiến cho tình trạng bệnh nặng hơn, khó chữa trị. Do đó, khi nhận thấy dấu hiệu của bệnh lang ben bạn cần chủ động chữa trị để bệnh không lây lan và tái phát.<br>1. Triệu chứng<br>Vị trí xuất hiện của lang ben chủ yếu ở mặt, tay, ngực…nên dễ nhận biết khi mắc bệnh. Triệu chứng của bệnh gao gồm:\n &#8211; Đốm lạ xuất hiện từ từ trên làn da và kích thước dần dần tăng lên;\n &#8211; Đốm có màu sáng hơn hoặc tối hơn so với khu vực xung quanh;\n &#8211; Đốm có màu trắng, hồng, đỏ, nâu;\n &#8211; Các đốm có thể xuất hiện bất cứ nơi nào trên cơ thể, nhưng thường xuyên xuất hiện trên cổ, ngực, lưng và cánh tay;\n &#8211; Đốm có thể khô, có vảy và gây cảm giác ngứa;\n &#8211; Khác với da khỏe mạnh, nấm men làm da không tiếp xúc được với ánh nắng;\n &#8211; Khi nấm men phát triển, các đốm có thể kết hợp và tạo thành những mảng sáng hơn (hoặc tối hơn) da;\n &#8211; Các đốm có thể biến mất khi nhiệt độ giảm xuống. Tuy nhiên, nó sẽ trở lại vào mùa xuân hoặc mùa hè khi không khí trở nên ấm áp và ẩm ướt.\n Ngoài những triệu chứng kể trên, nếu thấy có bất cứ dấu hiệu bất thường nào trên cơ thể, bạn nên thăm khám bác sĩ để được tư vấn và chữa trị kịp thời.<br>2. Tác hại<br>Mặc dù dễ nhận biết, song một số người chủ quan vì nghĩ rằng bệnh không nguy hiểm. Nhưng họ lại không biết được rằng, lang ben càng để lâu càng lây lan rộng ra các bộ phận khác của cơ thể, gây khó khăn trong việc chữa trị. Không những thế, lang ben rất dễ tái phát nếu không chữa trị đúng cách. Các bác sĩ tại Phòng khám Da liễu Hà Nội đã nghiên cứu và chỉ ra một số tác hại mà lang ben gây ra dưới đây:\n &#8211; Bệnh lang ben gây nên những mảng da không đều màu làm mất đi tính thẩm mỹ trên da cho người mắc phải. Đôi khi, bệnh cũng gây nên những phiền toái khác như: ngứa rát nhẹ khi bị chảy mồ hôi, châm chích khó chịu. Tuy bệnh lang ben không có những biến chứng nguy hiểm nhưng nếu không được điều trị đúng cách, bệnh sẽ rất dễ tái phát và lây lan rất nhanh.\n &#8211; Bệnh thường tiến triển âm thầm và lâu dài từ nhiều tháng đến nhiều năm. Bệnh gây tổn thương trên da chủ yếu ở vị trí 1/2 phía trên thân người như mặt, cổ, lưng, ngực…\n + Tổn thương trên da thường xếp thành từng đám, có vảy. Khi ra nắng tiếp xúc với ánh sáng mặt trời hoặc khi có mồ hôi đổ ra sẽ gây ngứa ngáy, râm ran khó chịu, có cảm giác như kim đâm.\n + Lang ben dễ lây lan từ vùng da cố định ra diện tích rộng, đặc biệt với các vùng da hở, tiếp xúc với ánh sáng mặt trời.\n + Lang ben rất dễ lây nhiễm từ người này sang người khác và khó phòng tránh.\n + Bệnh lang ben tuy không khó trị liệu tuy nhiên lại rất dễ tái nhiễm trở lại, dai dẳng nếu không được trị liệu đúng cách và có cách phòng chống tích cực.<br>3. Cách chữa trị<br>Vì bệnh lang ben rất dễ bị tái phát cho nên việc điều trị lang ben cũng không hề dễ dàng và cần có phương pháp điều trị bệnh tận gốc thì bệnh mới không tái phát nhiều lần.. tốt nhất vẫn là điều trị sớm và kịp thời khi mới phát hiện ra bệnh.. Nếu trường hợp nặng và tình trạng lang ben bắt đầu lan rộng thì phải kết hợp song song giữa thuốc bôi ngoài và thuốc uống..\n Tùy thuộc vào mức độ nặng, nhẹ của bệnh để sử dụng phương pháp chữa trị phù hợp. Nếu bệnh vừa mới bắt đầu, bạn có thể sử dụng một số mẹo dân gian như: dùng lá rau răm, chuối xanh, củ riềng…để chữa trị tại nhà, vừa đơn giản vừa hiệu quả. Tuy nhiên, bạn cần phải kiên trì khi sử dụng cách này.\n Khi nhận thấy bệnh có dấu hiệu lây lan, bạn cần uống thuốc và kết hợp bôi ngoài da để đem lại hiệu quả nhanh chóng. Để đảm bảo an toàn, bạn nên đến gặp bác sĩ để được khám và chỉ định dùng đúng thuốc, tránh trường hợp sử dụng sai thuốc gây kích ứng và tác dụng phụ.\n Lang ben là bệnh điều trị tương đối khó, do vậy các bác sĩ khuyên những người chưa mắc bệnh nên vệ sinh thường xuyên, môi trường sống sạch sẽ và tuyệt đối không sử dụng chung đồ dùng cá nhân như khăn mặt, chăn màn với người khác. Bên cạnh đó, nên có chế độ dinh dưỡng đảm bảo, hạn chế ăn các đồ ăn chứa nhiều dầu, mỡ…Nên đi kiểm tra sức khỏe định kỳ để chủ động trong phòng và chữa bệnh.\n Mặc dù là bệnh phổ biến, khó chữa trị tuy nhiên bạn không nên quá lo lắng vì bệnh không gây nguy hiểm cho sức khỏe, chỉ ảnh hưởng đến yếu tố thẩm mỹ. Khi nhận thấy dấu hiệu, không được chủ quan mà nên đến ngay các cơ sở y tế để gặp bác sĩ.<br>HÃY LIÊN HỆ VỚI CHÚNG TÔI NẾU CẦN TƯ VẤN<br> PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 12, NULL, NULL, 1),
(19, 43, 'Giải pháp tối ưu để phòng tránh bệnh bạch biến thường xuất hiện ở mùa xuân? ', '1. Bệnh bạch biến có lây không ?\n Những người bị bệnh bạch biến nếu không được chữa trị kịp thời sẽ lan rộng ra khắp cơ thể, song bệnh không lây từ người bệnh sang những người lành, chính vì thế không nên có sự kỳ thị với người bệnh mà làm cho người bệnh căng thẳng và mệt mỏi hơn.\n Bệnh bạch biến có tính chất di truyền cao, khoảng 30% người bệnh có người thân trong gia đình cũng bị bệnh bạch biến.\n Nếu không may mắc phải căn bệnh này, bệnh nhân không nên quá căng thẳng và sợ hãi mà khiến cho bệnh thêm nặng hơn. Người bệnh nên đến các bệnh viện, phòng khám y tế chuyên khoa có uy tín để được thăm khám, kiểm tra và chẩn đoán bệnh chính xác, từ đó đưa ra các phác đồ điều trị bệnh phù hợp, hiệu quả nhất.\n Vì bệnh bạch biến không lây truyền từ người bệnh sang người lành do đó, những người xung quanh hay bạn bè không nên sợ hãi mà hắt hủi, xa lánh người bệnh. Nên động viên tinh thần để người bệnh vững tin hơn vào quá trình chữa trị.\n 2. Bạch biến có chữa được không ?\n Bệnh bạch biến là chứng bệnh rất khó điều trị, đã có nhiều phương pháp điều trị được áp dụng, tuy nhiên không phải phương pháp nào cũng có hiệu quả, giúp người bệnh chữa khỏi bệnh hoàn toàn. Người bệnh có thể sử dụng một số phương pháp điều trị như dùng thuốc đặc trị, phẫu thuật cấy tế bào hắc tố, điều trị quang học,… Tuy nhiên, việc áp dụng phương pháp nào còn phải dựa vào tình trạng bệnh cụ thể của từng người bệnh, từ đó các bác sĩ mới đưa ra được phác đồ điều trị phù hợp nhất với mỗi người bệnh.\n Điều trị bệnh bạch biến đòi hỏi phải thực hiện trong thời gian dài bởi quá trình điều trị căn bệnh này rất khó khăn, do đó người bệnh cần kiên nhẫn thực hiện điều trị theo sự chỉ định của bác sĩ. Ngoài ra, người bệnh cũng nên hạn chế căng thẳng và suy nghĩ quá nhiều khiến cho tâm trạng không tốt.\n 3. Thực đơn cho bệnh nhân bạch biến\n Bạch biến là bệnh ngoài da có thể gặp ở mọi lứa tuổi. Đây là bệnh về da do mất sắc tố melanin không rõ nguyên nhân và thường biểu hiện ở những vùng da bạc màu không có tế bào hắc tố. Để hạn chế nguy cơ lây lan bệnh trên da, các bác sĩ đã đưa ra lời khuyên về chế độ ăn uống cho những người mắc căn bệnh này, cụ thể như nên ăn gì và không ăn gì khi bị bệnh. Cụ thể như sau:\n Bệnh bạch biến kiêng ăn gì ?\n Khi bị bệnh bạch biến bạn nên kiêng các món ăn sau:\n – Kiêng những thực phầm chứa gluten như lúa mì, yến mạch… Khi bệnh bạch biến nếu bạn ăn những thực phẩm này sẽ khiến bệnh nặng thêm và quá trình điều trị bệnh cũng khó khăn hơn nếu ăn các thực phẩm này.\n – Kiêng uống đồ uống chứa chất kích thích như cà phê, rượu bia, trà đặc, thuốc lá.\n Nếu bạn đang băn khoăn không biết bạch biến kiêng ăn gì thì cách tốt nhất là hãy tránh xa những thực phẩm và đồ ăn trên.\n Bệnh bạch biến nên ăn gì ?\n Ngoài việc kiêng những thực phẩm lúa mì, yến mạch hay các đồ uống có chất kích thích, bạn nên thực hiện chế độ ăn sau để bệnh không lây lan, phát triển nặng và quá trình điều trị bệnh cũng nhanh chóng, dễ khỏi hơn:\n – Ăn những thực phẩm giàu vitamin B12, B1, B6 và axit folic. Những thực phẩm này có nhiều trong gan, cá hồi, sò, tỏi, đậu xanh, gạo,…. và các khoáng chất như phốt pho, kali, , canxi, sắt, đồng. Các thực phẩm giàu axit folic mà người bị bệnh bạch biến nên ăn là: rau lá xanh (như cải bó xôi, súp lơ), đậu đen, măng tây và các thực phẩm giàu vitamin C.\n – Nên ăn nhiều rau xanh và hoa quả chứa nhiều vitamin C như (ổi, bưởi, cam, dưa đỏ, cà chua, chanh, dâu tây, khoai tây) và những thực phẩm giàu kẽm như hàu, cua, thịt bò, tôm hùm, thịt gà, các loại đậu, hạt, thịt lợn và các thực phẩm từ sữa.\n Việc áp dụng chế độ dinh dưỡng phù hợp kết hợp kiêng những món ăn chống chỉ định sẽ góp phần cải thiện nhanh chóng tình trạng bệnh bạch biến. Chính vì vậy khi mắc căn bệnh này và đặc biệt là trong quá trình điều trị bệnh bạn nên tuân thủ nguyên tắc về chế độ dinh dưỡng trên. Điều này vừa giúp tăng cường sức khỏe vừa ngăn ngừa sự tiến triển và tái phát của bệnh.\n 4. Cách phòng tránh\n Chia sẻ về việc phòng bệnh bạch biến, các bác sĩ tại phòng khám da liễu Hà Nội cho biết, hạn chế và có biện pháp xử trí kịp thời những sang chấn về tinh thần hoặc thể chất có tác dụng rất tốt để ngừa căn bệnh này. Bên cạnh đó, thực hiện chế độ sinh hoạt lành mạnh và tăng cường bổ sung một số loại thực phẩm sau cũng sẽ giúp hạn chế sự xuất hiện của bệnh:\n – Rau bina chứa nhiều nhiều Omega¬3, các loại vitamin B và các khoáng chất như magie, sắt… đồng thời chứa chất phytochemical giúp ngăn ngừa thoái hóa và bảo vệ da nên có thể hỗ trợ phòng bạch biến hiệu quả.\n – Cà chua có nhiều vitamin C nên sẽ giúp chống oxy hóa cho da.\n – Các loại trái cây, nhất là việt quất chứa nhiều vitamin, chất xơ rất tốt với hệ thống miễn dịch.\n Những cách phòng bệnh bạch biến trên đây chỉ có tính chất tham khảo, không được xem là giải pháp điều trị và ngăn chặn bệnh. Vì thế, nếu mắc phải căn bệnh này người bệnh cần sớm gặp bác sĩ để thăm khám và được tư vấn hướng điều trị hiệu quả và phòng ngừa bệnh tái phát.\n LIÊN HỆ VỚI CHÚNG TÔI NẾU BẠN CẦN TƯ VẤN VỀ BỆNH BẠCH BIẾN\n  PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 13, 1, NULL, 1),
(20, 43, 'Địa chỉ chữa bệnh bạch biến uy tín', 'Bạch biến là bệnh ngoài da, tiến triển không theo quy luật, phát bệnh ở bất cứ độ tuổi nào. Đây là bệnh mạn tính, dai dẳng trong nhiều năm khiến cho người bệnh tự ti, ngại giao tiếp và ảnh hưởng đến chất lượng cuộc sống.\n 1. Bệnh bạch biến là gì ?\n Bệnh bạch biến là bệnh rối loạn sắc tố mắc phải ở da và niêm mạc, biểu hiện sự đặc trưng là những mảng da trắng hoàn toàn do tiêu hủy và mất các tế bào sắc tố. Bệnh chiếm khoảng 1% dân số, nữ hay gặp nam. Bệnh gặp ở bất cứ tuổi nào nhưng có tới 50% dưới 20 tuổi.\n 2. Nguyên nhân\n Cho đến nay vẫn chưa có kết luận chính thức về căn nguyên gây bệnh, nhưng theo 1 số nghiên cứu các bác sĩ đưa ra giả thuyết về nguyên nhân của bệnh bạch biến như sau:\n &#8211; Yếu tố di truyền: 30% bệnh nhân có người thân trong gia đình cũng bị bệnh bạch biến\n &#8211; Do một số tế bào lympho hoạt hóa phá hủy chọn lọc các tế bào sắc tố\n &#8211; Do sự rối loạn hệ thống chống oxy hóa-kháng oxy hóa\n &#8211; Khiếm khuyết của các tế bào sắc tố\n &#8211; Các yếu tố khác: Sang chấn tâm lý, tác động của hóa chất, rối loạn chức năng 1 số phủ tạng\n 3. Vì sao nên chữa trị bạch biến sớm\n Tuy bệnh bạch biến không gây ra nguy hại cho sức khỏe và tính mạng nhưng nó lại có khả năng lan ra toàn thân, mất thẩm mỹ, tác động trực tiếp đến tâm lí và trở thành rào cản đối với cuộc sống của người bệnh. Sự xuất hiện của các đốm bạch biến trên da khiến người bệnh tự ti, mặc cảm trong giao tiếp vì thế họ dễ khép mình, tránh tiếp xúc với những người xung quanh. Tình trạng này càng kéo dài càng tạo ra sang chấn lớn về tâm lí, thậm chí còn là nguyên nhân gây ra bệnh trầm cảm.\n 4. Cách chữa trị\n Bệnh bạch biến là một trong những bệnh khó trị liệu, bệnh thường diễn biến dai dẳng và dễ quay trở lại. Lại càng khó hơn khi nguyên nhân gây bệnh không rõ ràng. Cách chữa trị bệnh bạch biến chủ yếu là điều trị theo cơ chế và triệu chứng bằng nhiều phương pháp khác nhau như dùng thuốc bôi, thuốc uống và điều trị bằng công nghệ laser đang là phương pháp được thí điểm ở nhiều cơ sở thẩm mỹ.\n Tùy vào độ tuổi, mức độ bệnh, vị trí thương tổn, thời gian xuất hiện bệnh…mà có phương pháp điều trị bệnh bạch biến khác nhau. Để mang lại hiệu quả cao, an toàn và thời gian điều trị nhanh chóng tình trạng bạch biến, người bệnh nên tới các phòng khám da liễu uy tín để được khám và tư vấn. Đặc biệt, Phòng khám Da liễu Hà Nội bạn sẽ được bác sĩ soi da, khám bệnh và tư vấn hoàn toàn miễn phí.\n 5. Chữa trị bạch biến ở đâu hiệu quả, an toàn ?\n Với sự bùng nổ khoa học, công nghệ đặc biệt là Internet đã giúp cho con người dễ dàng tìm kiếm những cơ sở chữa bệnh khác nhau, tuy nhiên không phải thông tin nào bạn tìm thấy cũng đáng tin cậy. Hơn nữa, ngày càng có nhiều trung tâm y tế, các phòng khám ra đời nhưng chất lượng rất khó để kiểm chứng thông qua vài bài quảng cáo. Có không ít người bệnh không tìm hiểu kĩ đã vội vàng đến khám chữa bệnh để rồi nhận lại sự thất vọng.\n Vì sao phòng khám da liễu Hà Nội lại nhận được sự tin tưởng và làm hài lòng rất nhiều “thượng đế” khó tính:\n &#8211; Đội ngũ bác sĩ chuyên khoa da liễu chuyên môn giỏi, kinh nghiệm lâu năm, tận tâm\n &#8211; Thủ tục thăm khám đơn giản, nhanh chóng\n &#8211; Máy móc, thiết bị y tế hiện đại, được khử trùng trước khi đưa vào điều trị\n &#8211; Chi phí khám bệnh cạnh tranh\n &gt;&gt; Bạch bạch biến nếu biến chứng sẽ rất khó chữa trị càng khó hơn khi nguyên nhân gây bệnh chưa rõ ràng. Nếu nhận thấy có dấu hiệu bệnh xuất hiện, hãy đến ngay cơ sở y tế uy tín để khám và điều trị sớm. Mong rằng những chia sẻ trên đây có thể giúp bạn dễ dàng hơn trong việc đưa ra quyết định về địa chỉ chữa bệnh bạch biến ở Hà Nội.\n Để tìm hiểu thêm thông tin về bệnh bạch biến, xin vui lòng liên hệ với bác sĩ của chúng tôi để được tư vấn:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n  Số 447 đường Hoàng Quốc Việt, Hà Nội\n   Hotline: 098.123.1234 <br>', 15, NULL, NULL, 1),
(21, 43, 'Bạch biến và những tác hại của nó để lại cho người bệnh', 'Bạch biến là bệnh ngoài da khiến cho da bị mất màu , màu da bị mất theo từng mảng, tiến triển không theo quy luật, phát bệnh ở bất cứ độ tuổi nào. Đây là bệnh mãn tính, dai dẳng trong nhiều năm khiến cho người bệnh tự ti, ngại giao tiếp và ảnh hưởng đến chất lượng cuộc sống. Bệnh này không gây nguy hiểm và có thể chữa trị được, nhưng một số loại sắc tố da có thể tái phát ở mặt và cổ. Bệnh bạch biến đôi khi có liên quan đến các loại bệnh khác như bệnh tuyến giáp.\n 1. Dấu hiệu của bệnh \n Những vùng da nhỏ thường mất màu, sắc tố và trở thành cùng màu trắng. Các mảng da bị bạch biến thường không có cảm giác khi tiếp xúc. Chúng không gây đau hoặc ngứa và đa dạng về kích thước, có thể rộng đến 1,5 cm. Các mảng da này thường lan rộng và hình thành nên các mảng bạch biến lớn hơn không có hình thù xác định. Chúng thường xuất hiện ở hai bên đối xứng trên cơ thể. Đôi khi lông, tóc ở những vùng bị bạch biến cũng bị mất sắc tố.<br><br>Tình trạng bạch biến lan khắp người<br>2. Triệu chứng\n &#8211; Trên da xuất hiện các vệt màu trắng hình tròn hay bầu dục, ranh giới rõ rải rác khắp cơ thể\n &#8211; Bề mặt tổn thương nhẵn, bằng phẳng với da, không có vảy, không thâm nhiễm\n &#8211; Thương tổn xuất hiện ở niêm mạc, lòng bàn tay, bàn chân, kích thước lúc đầu nhỏ sau đó liên kết với nhau thành đám\n &#8211; Bệnh nhân có thể bị thương tổn trên da đầu nhưng được tóc trắng trên thương tổn che lấp<br>3. Nguyên nhân\n Cho đến nay vẫn chưa có kết luận chính thức về căn nguyên gây bệnh, nhưng theo 1 số nghiên cứu các bác sĩ đưa ra giả thuyết về nguyên nhân của bệnh bạch biến như sau:<br>4. Nguy cơ những người dễ mắc phải bệnh <br>Bệnh bạch biến có ảnh hưởng đến tất cả các nhóm chủng tộc và sắc tộc. Bệnh thường được thấy rõ hơn ở những người có màu da sậm. Bạch biến có thể xuất hiện ở bất kỳ độ tuổi nào, nhưng gần một nửa các trường hợp mắc bệnh bạch biến thường xảy ra ở những người nhỏ hơn 20 tuổi.<br>5. Tác hại\n Bệnh bạch biến không đe dọa tính mạng hoặc truyền nhiễm. Tuy nhiên, bệnh khiến cho người bệnh tự ti và gây mất thẩm mỹ.\n &#8211; Bạch biến có thể kết hợp với một số bệnh khác như đái đường, các bệnh ác tính, thiếu máu kéo dài, bệnh tuyến giáp, xơ gan…\n &#8211; Bệnh tuyến giáp: Bạch biến kèm theo các bệnh khác như cường tuyến giáp, viêm tuyến giáp trạng, bệnh tiểu đường, thiếu máu ác tính, viêm khớp dạng thấp…\n &#8211; Bệnh về răng: Bạch biến ở đầu mặt kèm theo các bệnh về răng như sâu răng, răng dị dạng, lệch, hỏng răng.\n &#8211; U bướu: Về lâm sàng người bệnh u hắc tố ác tính phát sinh bạch biến tương đối thường gặp, bạch biến kèm với u hắc tố thường là u hắc tố có trước, bạch biến có sau.\n &#8211; Rụng tóc: Bạch biến dẫn đến rụng tóc chủ yếu trong giai đoạn bạch biến phát triển và lây lan. Bạch biến có thể kèm theo rụng hết tóc hoặc ở da đầu, râu rụng đơn phát hoặc đa phát.<br><br>Người mẫu bạch biến <br>6. Cách chữa trị\n Bệnh bạch biến là một trong những bệnh khó trị liệu, bệnh thường diễn biến dai dẳng và dễ quay trở lại. Lại càng khó hơn khi nguyên nhân gây bệnh không rõ ràng. Cách chữa trị bệnh bạch biến chủ yếu là điều trị theo cơ chế và triệu chứng bằng nhiều phương pháp khác nhau như dùng thuốc bôi, thuốc uống và điều trị bằng công nghệ laser đang là phương pháp được thí điểm ở nhiều cơ sở thẩm mỹ.\n Tùy vào độ tuổi, mức độ bệnh, vị trí thương tổn, thời gian xuất hiện bệnh…mà có phương pháp điều trị bệnh bạch biến khác nhau. Để mang lại hiệu quả cao, an toàn và thời gian điều trị nhanh chóng tình trạng bạch biến, người bệnh nên tới các phòng khám da liễu uy tín để được khám và tư vấn. Đặc biệt, Phòng khám Da liễu Hà Nội bạn sẽ được bác sĩ soi da, khám bệnh và tư vấn hoàn toàn miễn phí.\n Để tìm hiểu thêm thông tin về bệnh bạch biến, xin vui lòng liên hệ với bác sĩ của chúng tôi để được tư vấn:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số 447 đường Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 12, NULL, NULL, 1),
(22, 44, 'Dị ứng và cách điều trị hiệu quả', 'Dị ứng da thường mang lại cảm giác ngứa, khó chịu, mặc dù không ảnh hưởng nhiều đến sức khỏe nhưng lại khiến bạn bất tiện. Hãy tìm hiểu về triệu chứng này để tìm ra cách điều trị hiệu quả và nhanh nhất.<br>Dị ứng da còn được gọi là Chàm tiếp xúc, thường hay sảy ra khi da tiếp xúc với các vật khiến cho da nhạy cảm hoặc bị dị ứng. Bên cạnh đó khi bạn bị dị ứng ngứa ngoài da với một số thứ khác như thực phẩm hoặc là protein khi hít thở vào có khả năng gây ra nổi mẩn. Các dấu hiệu dễ nhận ra dị ứng da là: sưng, đỏ ngứa và nổi mẩn.<br>Bệnh dị ứng<br>Dị ứng là tình trạng hệ miễn dịch của bạn phản ứng quá mức so với bình thường đối với những chất không gây hại. Các loại bệnh dị ứng thường gặp là:<br>Sốc phản vệ: là một phản ứng dị ứng nặng, có thể gây tử vong. Nó gây tổn thương đến nhiều bộ phận của cơ thể và có thể xuất hiện rất nhanh;<br>Bệnh hen suyễn: là một bệnh mãn tính làm viêm vàhẹp đường dẫn khí của phổi, gây ra tình trạng thở khò khè, khó thở, đau thắt ngực, và ho. Ở những người bị dị ứng hen suyễn, các triệu chứng hen suyễn có thể được kích hoạt khi tiếp xúc với một chất gây ra dị ứng, hoặc chất gây dị ứng;<br>Viêm da dị ứng (chàm): còn được gọi là bệnh chàm, là một tình trạng viêm da không lây nhiễm. Đặc trưng của bệnh là tình trạng khô, ngứa da có thể chảy dịch khi trầy xước;<br>Dị ứng do môi trường: xảy ra khi hệ thống miễn dịch của cơ thể phản ứng bất thường với một chất vô hại như phấn hoa hay lông thú. Triệu chứng có thể là phản ứng dị ứng trong mũi (viêm mũi dị ứng, hoặc sốt cỏ khô) và trong phổi (bệnh suyễn);<br>Dị ứng thực phẩm: dị ứng thực phẩm xảy ra khi hệ thống miễn dịch phản ứng với một thực phẩm vô hại. Các loại thực phẩm thường gây ra phản ứng dị ứng là sữa, trứng, đậu phộng, hạt cây, đậu nành, lúa mì, cá và sò ốc.<br><br>Nguyên nhân gây dị ứng da<br>Các thành phần độc hại trong không khí: trong một môi trường có quá nhiều khói bụi, tạp chất hóa học…thì không chỉ có hệ hô hấp bị ảnh hưởng mà ngay cả bề mặt da cũng bị kích ứng. Nếu nhẹ thì chỉ bị ngứa ít còn đối với những người da mẫn cảm thì sẽ nghiêm trọng hơn như da bị phồng rộp, nổi mẩn đỏ khắp người, cảm thấy khó thở,…<br>Sử dụng thuốc: tình trạng dị ứng da xảy ra nhiều nhất là khi dùng những loại thuốc có thành phần giảm đau, kháng viêm hoặc các loại thuốc kháng sinh chữa bệnh đau xương khớp.<br>Một số loại thực phẩm: không phải ai cũng biết được mình bị dị ứng với loại thực phẩm nào đó mà chỉ có thể biết được khi đã từng ăn qua. Dấu hiệu nhận biết người bị dị ứng da ngứa do thực phẩm là trong lúc ăn hoặc sau khi ăn người bệnh bị nổi ban, trong người cảm thấy nóng ran và ngứa dữ dội.<br>Các loại nấm vi khuẩn: có thể bạn bị khi đi đến các nơi bị ô nhiễm hoặc bị lây lan từ người khác. Khi dị ứng da mẩn ngứa do nấm hoặc vi khuẩn bạn hoàn toàn có thể khắc phục bằng cách sử dụng những loại thuốc bôi chống khuẩn và làm dịu cơn ngứa cho da.<br>Bị côn trùng đốt, chích: như kiến, nhện, ong, bọ cạp. Nhiều loại côn trùng có nọc vô cùng độc. Nếu may mắn bị nhẹ người bệnh chỉ bị nổi mẩn đỏ ở trên da một vài ngày là hết, trường hợp nặng hơn có khả năng bị phồng rộp nước, chảy mủ rồi sau đó lan tỏa rộng ra những vùng da khác hình thành nhiễm trùng.<br>Do dùng mỹ phẩm bừa bãi không đủ chất lượng: hiện nay việc mua bán sử dụng tràn lan các loại mỹ phẩm không rõ nguồn gốc chính là một trong những nguyên nhân chính dẫn đến bệnh dị ứng da. Bệnh nhân có thể bị bào mỏng da gây ra chứng ửng đỏ hoặc bong tróc da, mụn nổi chi chít. Nặng hơn là thương tổn da vĩnh viễn.<br><br>Triệu chứng của bệnh.<br>Các phần của cơ thể mà tiếp xúc với chất gây dị ứng đều ảnh hưởng đến sự biểu hiện của những triệu chứng. Ví dụ:<br>Có thể có các triệu chứng và dấu hiệu khác không được đề cập. Nếu bạn có bất kỳ thắc mắc nào về các dấu hiệu bệnh, hãy tham khảo ý kiến bác sĩ.<br>Cách phòng bệnh<br>Những người có nguy cơ mắc bệnh<br>Phương pháp điều trị<br>Sau khi được xét nghiệm và bác sĩ chẩn đoán tình trạng, bạn sẽ được chỉ định điều trị bằng phương pháp phù hợp, sau đây là một số phương thức điều trị:<br>Những biến chứng có thể xảy ra<br> <br>', 15, NULL, NULL, 1),
(23, 45, 'Ngăn chặn bệnh tổ đỉa tái phát vào mùa nóng ẩm ở người trưởng thành', 'Hiện nay, số người mắc bệnh tổ đỉa ngày càng tăng và khiến nhiều người không ít lo lắng. Đây là một bệnh ngoài da khá phổ biến và thường khu trú tại lòng bàn tay và lòng bàn chân, khiến nhiều người gặp không ít khó khăn trong sinh hoạt cũng như trong công việc hàng ngày. Bệnh khó điều trị nhưng lại dễ tái phát, do đó khi chữa trị bệnh cần tuân thủ các nguyên tắc điều trị để bệnh không có cơ hội quay lại.\n Triệu chứng<br>Bệnh tổ đỉa là căn bệnh thường gặp với vị trí khu trú chủ yếu là lòng bàn tay, bàn chân. Bệnh xuất hiện ở mọi độ tuổi, ở cả nam giới và nữ giới, bao gồm một số triệu chứng hay gặp dưới đây:\n &#8211; Mụn nước màu trắng trong là triệu chứng chính, kích thước nhỏ khoảng 1mm, nằm sâu, chắc, khó vỡ, thường tập trung thành từng chùm hơi gồ trên mặt da. Đôi khi nhiều mụn nước kết tụ thành bóng nước lớn.\n &#8211; Vị trí: 90% là gặp ở lòng bàn tay và các rìa ngón tay hoặc là chỉ gặp một trong hai chỗ đã nói trên, còn ở lòng bàn chân và rìa ngón chân thì ít gặp hơn. Tổn thương thường đối xứng và bệnh thường không bao giờ vượt quá cổ tay, cổ chân.\n &#8211; Bệnh thường xảy ra từng đợt, trước khi nổi mụn nước thường có cảm giác ngứa, rát, một số trường hợp kèm tăng tiết mồ hôi. Mụn nước của bệnh tổ đỉa thường có xu hướng khô ít khi tự vỡ, rồi để lại một điểm dày sừng màu vàng đục, tróc da.\n &#8211; Khi bị nhiễm khuẩn thì mụn nước hoặc bóng nước sẽ đục, sưng đỏ kèm theo sưng hạch bạch huyết ở vùng kế cận và người bệnh nóng sốt.\n &#8211; Bệnh tổ đỉa được các bác sĩ da liễu coi như một loại chàm (eczema). Nhưng khác eczema, tổ đỉa chỉ nổi ở lòng bàn tay, lòng bàn chân và ria ngón tay chân, còn eczema thì có thể gặp ở bất cứ vị trí nào trên da. Mặt khác, mụn nước tổ đỉa thường to, sâu, chắc, khó vỡ hơn mụn nước eczema.\n Chữa trị Tây y\n Bệnh tổ đỉa sẽ tiến triển dai dẳng, hay tái phát theo chu kỳ thành mạn tính, kéo dài nhiều tháng, nhiều năm, trở ngại nhiều cho sinh hoạt, lao động nếu không được điều trị đúng đắn. Khi mắc bệnh tổ đỉa, bệnh nhân có thể sử dụng thuốc uống hoặc thuốc bôi ngoài da tùy vào mức độ thương tổn mà bệnh gây ra.\n &#8211; Bôi các loại kem và mỡ: Sicorten, Lorinden, Halog, Diproson, Fucicort, Flucina…\n &#8211; Uống một trong các loại thuốc: Clarytine, Histalong, Hismanal, Zirtine, Cézil: Viên 10 mg, ngày uống 1 viên.\n &#8211; Nếu có bội nhiễm, nên dùng một đợt kháng sinh Erythromycin 0,5 g x 3-4 viên/ngày trong 5-7 ngày.\n Bệnh nhân hạn chế gãi hoặc chà xát lên vùng tổn thương. Và đặc biệt KHÔNG tự ý uống, bôi thuốc mà phải theo chỉ định của bác sĩ.\n Mẹo dân gian\n Nhiều người khi bị bệnh tổ đỉa đã không khỏi lo lắng và tìm nhiều cách để chữa trị bệnh dứt điểm. Sử dụng khá nhiều bài thuốc từ đông y cho đến tây y nhưng vẫn không giảm và bệnh ngày càng phát triển, khiến nhiều người lại càng lo lắng hơn. Từ đó, nhiều người chuyển sang sử dụng các bài thuốc từ dân gian để chữa bệnh tổ đỉa với mong muốn sẽ khỏi bệnh. Phòng khám da liễu Hà Nội sẽ giới thiệu một số cách chữa trị dân gian để các bạn tham khảo:\n &#8211; Lá đào tươi\n Chuẩn bị một nắm lá đào tươi vừa đủ, đem rửa sạch, giã nhỏ chúng, và dùng đắp vào những vùng bị bệnh tổ đỉa trong 30 phút, rồi rửa sạch lại. Lưu ý, nên vệ sinh vùng da trước khi đắp thuốc vào. Người bệnh nên kiên trì sử dụng bài thuốc này 2 lần mỗi ngày và trong 1 tuần bạn sẽ cảm nhận được hiệu quả của nó mang lại\n &#8211; Lá ổi\n Bạn có thể sử dụng lá ổi, lá trà xanh  vò nát rồi đem nấu thành nước thuốc. Sử dụng nước thuốc này bằng cách ngâm vùng da bị bệnh tổ đỉa vào và chà rửa thật nhẹ nhàng, tránh chà mạnh và làm ảnh hưởng đến da. Ngâm rửa trong 20 phút rồi thì lau khô và có thể bôi thuốc theo chỉ định của bác sĩ\n &#8211; Khế chua\n Bài thuốc này sử dụng vào mỗi buổi tối trước khi đi ngủ là tốt nhất. Đầu tiên bạn dùng khế chua cắt lát rồi đem nướng trên than đỏ, khi thấy lát khế chua  đã thật nóng thì đặt vùng chân hoặc vùng tay bị bệnh tổ đỉa lên trên lát khế và giữ cho đến khi chúng nguội hẳn. Để nguyên như vậy và đi ngủ. Áp dụng thường xuyên vào mỗi tối sẽ giúp làm giảm những triệu chứng ngứa ngáy khó chịu của bệnh tổ đỉa, từ đó sẽ nhanh chóng khỏi bệnh.<br>Bệnh nhân lưu ý, trên đây chỉ là một số mẹo dân gian mang tính chất tham khảo, nếu áp dụng phải chú ý đến yếu tố vệ sinh để đảm bảo không bị viêm nhiễm và khiến bệnh nặng hơn. Khi nhận thấy bất cứ dấu hiệu bất thường nào của bệnh, phải đến ngay cơ sở y tế uy tín để khám và điều trị kịp thời.\n Cách phòng bệnh quay lại\n Tổ đỉa rất khó chữa trị bởi nguyên nhân gây bệnh chưa gây bệnh chưa rõ ràng, vì vậy sau khi điều trị bệnh thuyên giảm hoặc khỏi hẳn thì bệnh nhân vẫn phải tuân thủ nguyên tắc điều trị mà bác sĩ đưa ra, dưới đây là một số lưu ý để tránh bệnh tái phát:\n &#8211; Những người có cơ địa yếu và dễ bị dị ứng thì nên tránh tiếp  xúc với các hóa chất như nước rửa chén, xà phòng, chất tẩy rửa&#8230; nên sử dụng đồ bảo hộ như găng tay cao su để hạn chế những vùng da nhạy cảm tiếp xúc với những chất độc hại này.\n &#8211; Khi làm việc trong môi trường độc hại, tiếp xúc thường xuyên với hóa chất thì bạn nên mang đồ bảo hộ lao động, đeo khẩu trang chống độc&#8230;\n &#8211; Giữ cho bàn chân luôn sạch sẽ và khô thoáng, tránh bịẩm ướt và làm hại đến làn da, đặc biệt là các kẽ ngón chân.\n =&gt; “Dai dẳng như tổ đỉa” là câu nói vui mà các bác sĩ thường dùng khi nhắc đến căn bệnh này. Bệnh thường dai dẳng, khó chữa trị chính vì vậy trong và sau quá trình điều trị, người bệnh không được chủ quan, cần chủ động thực hiện các biện pháp phòng chống bệnh tái phát.\n Bạn đang mắc bệnh tổ đỉa và không biết đâu là cơ sở y tế chữa bệnh uy tín, hiệu quả ?? Phòng khám da liễu Hà Nội với thâm niên gần 10 năm chắc chắn sẽ là nơi bạn có thể gửi trọn niềm tin.\n LIÊN HỆ VỚI CHÚNG TÔI ĐỂ ĐẶT LỊCH KHÁM\n  PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 14, 3, 7, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(24, 19, 'Triệu chứng và các giai đoạn chuyển biến của bệnh chàm', 'Chàm là căn bệnh ngoài da thường xảy ra ở trẻ em, song thực tế người lớn cũng bị. Chàm được biết đến là căn bệnh viêm da dị ứng và đặc điểm của nó là viêm và nổi mụn nhỏ. Da sẽ ngứa ngáy kinh khủng và loét ra. Cho dù xảy ra ở người lớn hay trẻ em, thì đây cũng là căn bệnh rất khó chữa.<br>CÁC GIAI ĐOẠN CHUYỂN BIẾN<br>Bệnh chuyển biến thành nhiều giai đoạn, mỗi giai đoạn sẽ có những biểu hiện khác nhau:<br>&#8211; Giai đoạn tấy đỏ: Bắt đầu ngứa hay là cảm giác nóng rồi trở thành đỏ phù và nóng. Có thể phù ở những vùng da lỏng lẻo như mi mắt, bao quy đầu. Trên bề mặt xuất hiện những hạt nhỏ màu trắng mà sau này sẽ tạo thành mụn nước.<br>&#8211; Giai đoạn nổi mụn nước: Ban đầu, da đỏ lên và các mụn nước li ti được hình thành và lan rộng ra các vùng da lành khác. Mụn nước có chứa dịch trong, xuất hiện dày đặc gây cảm giác ngứa, rát.\n \n &#8211; Giai đoạn chảy nước: Mụn nước có thể vỡ đi do bệnh nhân gãi hoặc vỡ dập tự nhiên, nước vàng chảy ra, khi thì từng giọt, khi thì dính vào quần áo. Đến giai đoạn này, mảng chàm lổ chổ nhiều vết hình tròn còn gọi là giếng chàm (giai đoạn này dễ bị bội nhiễm). Huyết thanh thấm ra ngoài, nếu lấy một vật gì đậy lại thì sau một thời gian huyết thanh sẽ tạo thành một mảng dày.<br>&#8211; Giai đoạn da nhẵn: Đó là khi sau một thời gian lớp vảy của huyết thanh đọng trên da bị bong ra và để lại lớp da mỏng, nhẵn bóng.<br>&#8211; Giai đoạn bong vảy da: Lớp da mỏng trên rạn nứt và bong vảy sau đó tăng sắc tố da và dày hơn. Sau thương tổn da sẽ trở lại bình thường và không dể lại sẹo trên da.<br>Chàm bớt<br>TRIỆU CHỨNG CỦA BỆNH CHÀM<br>Ngứa: Đây là triệu chứng thường gặp nhất ở bệnh nhân mắc bệnh chàm. Ngứa cũng là dấu hiệu đầu tiên khi các thương tổn da xuất hiện. Khi bệnh nhân càng gãi, cơn ngứa càng kéo dài hơn và làm cho da trở nên trầm trọng hơn. Tình trạng ngứa cũng khó chịu hơn về đêm và khi thời tiết chuyển sang mùa lạnh.<br>Sẩn đỏ và mụn nước : Đây cũng là những dấu hiệu khởi phát khi mới mắc bệnh. Trên da sẽ xuất hiện những mụn đỏ không rõ ranh giới cùng với đó là các đám sẩn và mụn nước có dịch trong. Bệnh nhân sẽ có cảm giác nóng rát và ngứa ngáy ở vùng da bị nhiễm bệnh chàm.<br>Chảy dịch và đóng vảy tiết: Sau một thời gian xuất hiện, các đám mụn nước sẽ bắt đầu có hiện tượng chảy dịch do gãi, tác động từ bên ngoài. Các vết trợt do mụn nước vỡ sẽ để lại trên da, có khả năng hình thành bội nhiễm tạo thành các mụn mủ. Sau khi dịch tiết vỡ ra khô lại, trên da của bệnh nhân sẽ có hiện tượng đóng vảy tiết.\n Trong giai đoạn mụn nước vỡ, bệnh nhân càng gãi sẽ càng làm cho dịch tiết lan nhanh ra những vùng da lành.<br>Bong tróc da: Sau khi các vảy tiết bị tróc đi, da sẽ trở nên nhẵn và hơi cứng. Lớp da này sẽ nhanh tróng dày lên và tróc đi khiến cho bệnh nhân mất tự tin vào bản thân, ảnh hưởng đến thẩm mỹ. Các triệu chứng trên có thể lặp lại và đan xen với nhau đối với bệnh nhân mắc bệnh chàm cơ địa mãn tính.\n \n CẦN LÀM GÌ KHI BỊ CHÀM DA<br>&#8211; Tránh tiếp xúc trực tiếp hóa chất, nên có biện pháp bảo vệ khi tiếp xúc hóa chất như găng tay. Thay thế các loại sữa tắm dễ kích ứng da.\n &#8211; Tránh sử dụng mỹ phẩm khi không cần thiết. Nếu cần sử dụng mỹ phẩm nên thử trên một vùng da nhỏ để xem có kích ứng không.\n &#8211; Không nên ăn các món ăn lạ. Hạn chế sử dụng một số thực phẩm như hải sản, các món cay nóng, các món có mùi tanh,…\n &#8211; Hạn chế lạm dụng các loại thuốc trị bệnh chàm cơ địa. Đây là biện pháp để không làm phức tạp thêm tình trạng bệnh.\n Bệnh chàm là một trong số những bệnh ngoài da khó chữa và dễ tái phát. Tuy nhiên nếu kiên trì và có liệu trình điều trị phù hợp bệnh sẽ được chữa trị triệt để. Để biết chính xác phương pháp điều trị, bệnh nhân cần đến gặp bác sĩ dể được tư vấn.<br>Cần thêm thông tin về bệnh chàm, xin vui lòng liên hệ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 13, NULL, NULL, 1),
(25, 19, 'Bệnh chàm da, nguyên nhân hình thành bệnh và cách điều trị', 'Chàm da là bệnh ngoài da gây mất thẩm mỹ, bệnh không nguy hại đến sức khỏe nhưng lại ảnh hưởng đến giá trị cuộc sống, khiến bệnh nhân mất tự tin và mặc cảm.\n Khoảng 10% dân số trên thế giới mắc bệnh chàm. Ở Việt Nam bệnh chàm chiếm 25% trong tổng số các bệnh ngoài da. Chàm là một loại bệnh có sự phối hợp với những biến đổi về mặt địa lý, về chủng tộc và tính &#8220;mãn tính&#8221; của nó. Tỷ lệ tăng ở những quốc gia, ở vùng nhiệt đới. Tại London 18% chàm được tìm thấy trong các đối tượng đến khám bệnh. Một số điều tra về các bệnh da tại phòng khám tổng quát phát hiện 33% và 23% trong tất cả các trường hợp tại Glasgow và Oxford. Chàm chiếm 17% trong tất cả các bệnh tại Hylạp.<br>Chàm da ở các khu vực và độ tuổi khác nhau<br>Nguyên nhân gây bệnh chàm\n Nguyên nhân gây bệnh chàm khá đa dạng nhưng nhìn chung đều xuất phát từ 3 nguyên nhân chính sau.\n 1. Do cơ địa, cơ thể bệnh nhân\n + Bệnh thường có tính chất gia đình, di truyền: tiền sử trong gia đình bệnh nhân có người bị bệnh chàm thì nguy cơ mắc phải căn bệnh này càng cao;\n + Do rối loạn các hoạt động của cơ thể: các cơ quan trong cơ thể hoạt động bị rối loạn chức năng như bài tiết, thần kinh, tiêu hóa, nội tiết cơ thể thay đổi cũng là nguyên nhân gây ra bệnh chàm rất phổ biến;\n + Bệnh nhân mắc phải một số căn bệnh: suyễn, viêm mũi xoang, viêm gan, viêm đại tràng, các bệnh về thận, viêm tai,…\n 2. Nguyên nhân dị nguyên\n + Do nghề nghiệp phải tiếp xúc với các hóa chất gây bệnh như xi măng, thuốc nhuộm, nguyên liệu làm cao su, sơn xe, dầu mỡ, phân bón hóa học, thuốc trừ sâu,…\n + Do tiếp xúc với đồ dùng hàng ngày gây dị ứng: quần áo, chăn màn, giày dép, khăn len, mực in từ các tờ báo, kem bôi mặt, kem cạo râu,…\n + Do ăn phải các thức ăn lạ, không phù hợp cơ địa: cá biển (đặc biệt là cá ngừ), mực, trăn, tôm, cua,…\n 3. Do sức đề kháng yếu, ăn uống – sinh hoạt thiếu khoa học\n + Sức khỏe và khả năng đề kháng của bạn hạn chế là nguyên nhân khiến bệnh dễ phát sinh và nhanh chóng lây lan trên diện rộng của bề mặt da;\n + Chế độ ăn uống thiếu cân bằng, thiếu hụt vitamin, ăn nhiều các thức ăn có đạm cao như tôm, cua, bò, gà, vịt xiêm, ba ba, ăn nhiều gia vị có tính cay nóng,…<br>Triệu chứng của bệnh\n Hiện tượng ngứa người và lên các mụn nước bề mặt da. Mụn nước thường tập trung thành từng chùm trên nền da đỏ còn gọi là hồng ban. Bệnh tiến triển theo 5 giai đoạn chính như sau:\n &#8211; Giai đoạn tấy đỏ, bắt đầu thấy ngứa và trên về mặt da có những hạt nhỏ màu trắng, sau đó lên thành mụn nước.\n &#8211; Giai đoạn nổi mụn nước, các mụn bắt đầu có bọng nước.\n &#8211; Giai đoạn chảy nước, mụn bị vỡ do gãi hay va đập tự nhiên và rất dễ bị bội nhiễm.\n &#8211; Giai đoạn da nhẵn, sau khi mụn vỡ sẽ tiết ra nước vàng khiến mụn đóng vẩy\n &#8211; Giai đoạn bong vẩy da.<br>Chàm lan rộng cánh tay<br>Chẩn đoán bệnh chàm\n 1. Chẩn đoán xác định: Thường dễ chẩn đoán xác định nhờ :\n &#8211; Thương tổn cơ bản là mụn nước.\n &#8211; Mụn nước tập trung từng đám, trên nền da đỏ và phù nề.\n &#8211; Rất ngứa.\n &#8211; Bệnh dai dẳng và hay tái phát.\n 2. Chẩn đoán phân biệt :\n Giai đoạn bệnh có mụn nước :\n &#8211; Mụn nước trong hắc lào (nấm da) : Các mụn nước sắp xếp ở bờ thương tổn, có xu hướng lành ở giữa, da nhạt màu hay sậm màu, ngứa khi tăng tiết mồ hôi, ra nắng, xét nghiệm nấm, (dương tính).\n &#8211; Ghẻ : mụn nước khu trú ở kẽ ngón tay, chân, mông, bệnh có tính chất dịch tể, ngứa nhiều về ban đêm, có thể phát hiện được ký sinh trùng gây bệnh.\n &#8211; Rôm : Xuất hiện mụn nước ở vùng da hở, nhanh chóng hóa mủ, các thương tổn tăng khi trời nóng và lặn khi mát trời.\n &#8211; Chốc hạt kê : Giống các thương tổn chàm bội nhiễm, nhưng khác ở chỗ có vẩy tiết màu nâu như mật ong, cấy dịch mụn nước có vi khuẩn gây bệnh.\n Giai đoạn khô và bong vảy :\n &#8211; Vảy nến.\n &#8211; Vảy phấn hồng\n &#8211; Á sừng dạng vảy nến.<br>Điều trị bệnh chàm\n Để điều trị được bệnh chàm trước tiên cần tìm ra được nguyên nhân, bác sĩ sẽ kê thuốc bôi kết hợp với chế độ ăn uống nghỉ ngơi. Tùy theo mức độ của bệnh bác sĩ sẽ có liệu trình phù hợp với thể trạng của bạn. <br>', 15, NULL, NULL, 1),
(26, 24, 'Điều trị dứt điểm rụng tóc hiệu quả, an toàn', 'Trong sự phát triển bình thường của cơ thể, tóc và lông phát triển theo chu kì tăng trưởng =&gt; thoái hóa =&gt; rụng. Thế nhưng, rụng tóc quá nhiều khiến cho rất nhiều chị em vô cùng lo lắng và không biết phải làm gì để ngăn chặn tình trạng đó. Trong bài viết này, các bác sĩ tại Phòng khám Da liễu Hà Nội sẽ giải đáp các thắc mắc về nguyên nhân rụng tóc và cách chữa trị.\n Dấu hiệu bệnh rụng tóc\n &#8211; Tóc rụng nhiều khi gội đầu, chải đầu thậm chí khi vuốt tóc\n &#8211; Rụng trên 100 sợi 1 ngày\n &#8211; Tóc rụng diện rộng nhưng tập trung vào 1 vùng\n &#8211; Tóc mỏng, thưa thớt ở nữ giới và rụng thành mảng gây hói ở nam giới<br>Phân loại: Dựa vào căn nguyên gây bệnh, người ta chia rụng tóc thành các loại sau:\n &#8211; Rụng tóc sinh lý\n &#8211; Rụng tóc do yếu tố hóa học, lý học\n &#8211; Rụng tóc do sinh đẻ\n &#8211; Rụng tóc do mắc các bệnh mạn tính\n Nguyên nhân rụng tóc\n Có rất nhiều nguyên nhân gây bệnh rụng tóc như do da đầu tăng tiết bã nhờn, do ăn uống không đủ chất, vitamin, do căng thẳng stress, do nguồn nước, dầu gội không phù hợp. Cũng có khi do thời kì phụ nữ sau sinh, tiền mãn kinh.\n Mặt khác rụng tóc có thể do bạn tiếp xúc với các chất hóa mỹ phẩm, thói quen làm đẹp không đúng cách, duỗi, uốn, nhuộm nhiều lần, các dầu gội đầu trị gàu mạnh cũng có thể khiến tóc bị gãy rụng nhiều.\n Ngoài ra có một nguyên nhân nữa khiến tóc rụng nhiều là việc mất cân bằng giữa DHT (dihyrotestosterone) và Testosterone trong máu. Khi nồng độ DHT trong máu tăng cao, sẽ dẫn đến việc ức chế hành tóc không nhận được đủ các chất dinh dưỡng, tóc không được nuôi dưỡng sẽ mỏng đi, yếu, dễ rụng và khó mọc lại. Hơn thế, DHT còn làm tuyến bã nhờn tăng tiết, khiến cho chân tóc bị bít lại, yếu và càng dễ rụng.\n Tuy nhiên, rụng tóc nhiều không phải là triệu chứng của bệnh ung thư . Các bệnh nhân ung thư chỉ bị rụng tóc khi có sự tác động của quá trình hóa, xạ trị hoặc dùng thuốc chống ung thư.\n Cách chữa trị\n Để điều trị rụng tóc, trước hết cần phải tìm hiểu căn nguyên gây bệnh để lựa chọn cách chữa trị hiệu quả.\n Cách tốt nhất để chữa bệnh rụng tóc là bạn phải điều trị từ bên trong và bên ngoài. Điều trị bên ngoài là thay đổi thói quen chăm sóc tóc, không cột tóc quá chặt, đổi dầu gội đầu, bạn có thể dùng bồ kết hoặc các loại mặt nạ chăm sóc tóc từ trái cây, tránh xa các tiệm ép , nhuộm tóc khi tóc đang còn yếu.\n Điều trị dứt điểm bệnh rụng tóc từ bên trong bằng cách bổ sung các vitamin và khoáng chất tăng cường độ chắc khỏe cho tóc. Việc chữa trị bệnh rụng tóc có thể mất thời gian nhưng bù lại sẽ an toàn , bền vững và phải không có tác dụng phụ.\n Điều trị tại nhà\n &#8211; Nếu bạn bị rụng tóc do căng thẳng thì cần phải cân bằng lại tinh thần, bổ sung các loại thực phẩm chứa nhiều vitamin và hạn chế ăn các đồ ăn nhiều dầu mỡ.\n &#8211; Gội đầu bằng dầu dừa, dầu oliu…để bổ sung dưỡng chất giúp tóc mềm mượt và chắc khỏe hơn\n &#8211; Sử dụng một số sản phẩm để kích thích tóc mọc nhiều như tinh dầu bưởi…\n &#8211; Lựa chọn loại dầu gội hợp với tóc và có phương pháp gội đầu đúng cách\n Sử dụng thuốc theo chỉ định của bác sĩ\n Nếu thực hiện các cách điều trị tại nhà vẫn không chấm dứt tình trạng rụng tóc, nên đến gặp bác sĩ để được tư vấn. Bên cạnh đó, các trường hợp rụng tóc do thay đổi nội tiết tố thường gặp ở phụ nữ sau sinh, thời kỳ tiền mãn kinh, chu kỳ phát triển của tóc bị giảm sút dẫn đến chân tóc bị teo lại , tóc con không mọc lại được nữa cũng nên đến gặp bác sĩ để có cách chữa trị an toàn, hiệu quả.\n Chị em vẫn sẽ lo lắng nếu rụng tóc vẫn tiếp diễn, bởi vì rụng tóc không chỉ làm mất đi vẻ đẹp mà còn tiềm ẩn một căn bệnh nào đó. Đến với Phòng khám Da liễu Hà Nội, sở hữu mái tóc khỏe, bồng bềnh sẽ không còn là mơ ước.\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n  Số 447 Hoàng Quốc Việt, Cổ Nhuế, Hà Nội\n   Hotline: 098.123.1234 <br>', 15, NULL, NULL, 1),
(27, 24, 'Rụng tóc và những dấu hiệu nhận biết về bệnh', 'Tóc không chỉ là một phần của cơ thể mà còn là một biểu tượng của vẻ đẹp, tình trạng sức khỏe. Những người khỏe mạnh thì thường sở hữu một mái tóc đẹp, mềm mượt, chắc khỏe. Ngược lại những người có vấn đề sức khỏe thì mái tóc sẽ trở nên khô, xơ, và gãy rụng, dựa vào tình trạng gãy rụng của tóc chúng ta có thể đi khám để sớm phát hiện bệnh.\n Bình thường chúng ta có khoảng 100.00 -200.000 sợi tóc. Trong đó có khoảng 90% tóc luôn ở giai đoạn mọc kéo dài 2-6 năm, và 10% tóc ở dạng không mọc kéo dài 2-3 tháng, ở giai đoạn này tóc sẽ rụng.Khi 1 sợi tóc rụng thì sẽ có ngay một mầm tóc mới thay thế sợi tóc cũ . Tóc mọc khoảng 1.2cm/1 tháng thì mọc chậm dần.\n Thông thường một ngày có khoảng 30-60 sợi tóc rụng và cũng có lượng ấy tóc mới mọc lên. Nhưng khi số tóc rụng này vượt quá 100 sợi mà không có dấu hiệu mọc lại thì có thể mắc bệnh rụng tóc<br>&nbsp;<br>Rung tóc nhiều<br>Dấu hiệu của bệnh rụng tóc<br>Bạn có thể gặp các triệu chứng khác không được đề cập. Nếu bạn có bất kỳ thắc mắc nào về các dấu hiệu bệnh, hãy tham khảo ý kiến bác sĩ.<br>Rụng tóc gây hói<br>Nguyên nhân gây bệnh rụng tóc\n &#8211; Tiền sử gia đình: Nếu người thân của bạn bị rụng tóc, bạn cũng sẽ có nguy cơ cao bị tình trạng này. Tiền sử gia đình cũng sẽ xác định tuổi rụng tóc của bạn và khả năng tiến triển nó;\n &#8211; Hormone: thay đổi và sự mất cân bằng nội tiết tố có thể gây rụng tóc tạm thời. Điều này bao gồm những thay đổi nội tiết tố do mang thai, sinh con hoặc bước vào thời kỳ mãn kinh. Tuyến giáp cũng có thể ảnh hưởng đến nồng độ hormone, gây ra rụng tóc;\n &#8211; Các bệnh da: nhiễm trùng da đầu hoặc các bệnh về da như bệnh lupus, liken phẳng có thể gây rụng tóc;\n &#8211; Do thuốc: các thuốc điều trị ung thư, viêm khớp, trầm cảm, bệnh tim và tăng huyết áp có thể gây rụng tóc. Thuốc tránh thai và sử dụng quá nhiều vitamin A cũng có thể gây rụng tóc;\n &#8211; Rối loạn cưỡng chế kéo tóc: khiến cho người ta luôn có xu hướng kéo tóc trên da đầu, lông mày hoặc các bộ phận khác của cơ thể.\n &gt;&gt;&gt; Bạn nên gặp bác sĩ nếu tình trạng rụng tóc không được cải thiện hoặc tồi tệ hơn. Rụng tóc đột ngột có thể là một dấu hiệu của một bệnh lý nào đó mà cần phải điều trị. Vì vậy, tìm ra nguyên nhân quan trọng hơn là việc tìm kiếm phương pháp điều trị. Hãy thông báo cho bác sĩ của bạn nếu bạn nhận thấy tóc rụng nhiều hơn bình thường khi chải hoặc gội đầu.<br>Điều trị hiệu quả\n Các phương pháp điều trị rụng tóc có thể bao gồm thuốc, phẫu thuật, điều trị bằng laser hoặc tóc giả. Các bác sĩ có thể chỉ định và hướng dẫn bạn sử dụng một hoặc kết hợp các phương pháp để tạo ra kết quả điều trị hiệu quả nhất.\n &#8211; Điều trị bằng thuốc\n &#8211; Phẫu thuật\n &#8211; Điều trị Laser <br>', 13, NULL, NULL, 1),
(28, 3, 'Bệnh vảy cá nếu không điều trị kịp thời bạn sẽ phải sống với nó cả đời', 'Bệnh vảy các là căn bệnh về da có tính di truyền cao và biểu hiện đặc trưng của bệnh là nhiều vảy lan rộng. Đối với những người bị vảy các di truyền, khi mới sinh ra đã mắc bệnh và tồn tại suốt cả cuộc đời. Là căn bệnh ngoài da lành tính, nếu điều trị đúng cách và kịp thời sẽ khỏi, có những người phải sống cả đời với căn bệnh này.<br>Phân loại vảy cá\n 1. Bệnh vảy các di truyền\n &#8211; Bệnh vảy các di truyền trội\n &#8211; Bệnh vảy cá di truyền lặn liên quan đến nhiễm sắc thể X\n &#8211; Đỏ da toàn thân dạng vảy cá không có bọng nước\n &#8211; Bệnh vảy cá vảy lá (collodion baby)\n &#8211; Đỏ da toàn thân dạng vảy cá bọng nước\n &#8211; Bệnh vảy cá bọng nước\n 2. Bệnh vảy cá mắc phải\n Nguyên nhân gây bệnh<br>Bị vảy cá bẩm sinh<br>Di truyền:\n &#8211; Di truyền là nguyên nhân gây ra bệnh vảy cá phổ biến nhất. Bệnh vảy cá mang tính chất gia đình, là kết quả của đột biến di truyền. Gen đột biến trong bệnh vảy cá được tìm thấy trên nhiễm sắc thể Iq21, có liên quan đến 1 loại protein (gọi là filaggrin).<br>Dinh dưỡng kém:\n &#8211; Đây cũng là một nguyên nhân khác hình thành căn bệnh vảy cá. Bổ sung dinh dưỡng không đủ sẽ không thể chống chọi lại với tác nhân gây bệnh bên ngoài.\n &#8211; Cơ thể suy nhược, mắc bệnh ung thư, suy thận mãn tính, bệnh tuyến giáp,…cũng làm xuất hiện vảy cá.\n Bị mắc các bệnh ngoài da khác:\n &#8211; Vảy cá có thể hình thành khi da bị các tổn thương khác, nhất là tổn thương do vảy nến, viêm da dị ứng, dày sừng pilaris,…<br>Cách phòng bệnh\n Vảy cá là căn bệnh khó chữa, phải kiên trì trong thời gian dài mới có thể khỏi được bệnh, quan trọng hơn cả bạn nên biết để phòng bệnh sẽ tốt hơn.\n + Nếu trong gia đình có người thân bị bệnh vảy cá, nên được chữa trị ngay. Cần thiết chữa bệnh trước khi kết hôn để ngăn bệnh lây truyền cho thế hệ sau.\n + Tránh môi trường độc hại, chất độc hóa học để không bị các bệnh ngoài da như vảy cá, vảy nến, á sừng, dày sừng, viêm da,…\n + Bổ sung đầy đủ dinh dưỡng mỗi ngày cho cơ thể khỏe mạnh. Mỗi người nên tự bảo vệ bản thân bằng cách ăn uống đủ chất, tránh các chất kích thích gây hại.\n +Vệ sinh cơ thể sạch sẽ mỗi ngày, tập thể dục thể thao giúp tăng cường đề kháng.\n + Cần khám sức khỏe định kỳ 6 tháng/ 1 lần để sớm phát hiện và chữa trị mọi căn bệnh.<br>Bệnh vảy các là căn bệnh về da có tính di truyền cao và biểu hiện đặc trưng của bệnh là nhiều vảy lan rộng. Đối với những người bị vảy các di truyền, khi mới sinh ra đã mắc bệnh và tồn tại suốt cả cuộc đời. Là căn bệnh ngoài da lành tính, nếu điều trị đúng cách và kịp thời sẽ khỏi, có những người phải sống cả đời với căn bệnh này.<br>Phân loại vảy cá<br>Vảy cá đang tróc da<br>Nguyên nhân gây bệnh<br>Di truyền:<br>Dinh dưỡng kém:<br>Bị mắc các bệnh ngoài da khác:<br>Cách phòng bệnh <br>Vảy cá là căn bệnh khó chữa, phải kiên trì trong thời gian dài mới có thể khỏi được bệnh, quan trọng hơn cả bạn nên biết để phòng bệnh sẽ tốt hơn.<br>+ Nếu trong gia đình có người thân bị bệnh vảy cá, nên được chữa trị ngay. Cần thiết chữa bệnh trước khi kết hôn để ngăn bệnh lây truyền cho thế hệ sau.<br>+ Tránh môi trường độc hại, chất độc hóa học để không bị các bệnh ngoài da như vảy cá, vảy nến, á sừng, dày sừng, viêm da,…<br>+ Bổ sung đầy đủ dinh dưỡng mỗi ngày cho cơ thể khỏe mạnh. Mỗi người nên tự bảo vệ bản thân bằng cách ăn uống đủ chất, tránh các chất kích thích gây hại.<br>+Vệ sinh cơ thể sạch sẽ mỗi ngày, tập thể dục thể thao giúp tăng cường đề kháng.<br>+ Cần khám sức khỏe định kỳ 6 tháng/ 1 lần để sớm phát hiện và chữa trị mọi căn bệnh.<br>Điều trị bệnh<br>Gồm có vảy cá thông thường và vảy cá di truyền lặn liên quan tới giới tính.<br>Biểu hiện lâm sàng và mô bệnh học cũng như bệnh vảy cá thông thường. Tuy nhiên, bệnh có những điểm khác là các nếp gấp bị tổn thương dày sừng hơn, có ngứa. Hiếm gặp, có thể chỉ là một triệu chứng của bệnh di truyền tiềm ẩn hoặc gặp trong các bệnh suy dinh dưỡng, phản ứng thuốc, các bệnh hệ thống (lupus đỏ, suy thận, phong, AIDS…). Bệnh có thể điều trị khỏi.<br>+ Mỡ axit salicylic 5% (chú ý không dùng cho trẻ mới đẻ vì nguy cơ gây ngộ độc nặng).<br>+ Kem hoặc mỡ urea 10-12%<br>+ Kem hoặc mỡ axit lactic 5-10%.<br>+ Dày sừng khu trú dùng dung dịch propylen glycol 40% băng bịt ban đêm rất có hiệu quả. Không dùng cho trẻ em vì sẽ hấp thụ một lượng lớn vào cơ thể gây ngộ độc thận và có thể gây viêm da tiếp xúc.<br>Vitamin A axit, người lớn 25-35mg/ngày, trẻ em 0,5mg/kg/ngày cải thiện bệnh rõ rệt.<br>&nbsp; <br>', 15, NULL, NULL, 1),
(29, 47, 'Bệnh ghẻ', 'Ghẻ là bệnh ngoài da thường gặp tại Việt Nam, đặc biệt là những vùng dân cư đông đúc, nhà cửa chật hẹp và không vệ sinh sạch...<br>Bệnh ghẻ là một bệnh ngoài da gây ngứa, do ngứa gãi gây nhiễm khuẩn thứ phát và có thể gặp biến chứng viêm cầu thận. Nếu không...<br>', 11, NULL, NULL, 1),
(30, 47, 'Nhận diện bệnh ghẻ để điều trị sớm', 'Ghẻ là bệnh ngoài da thường gặp tại Việt Nam, đặc biệt là những vùng dân cư đông đúc, nhà cửa chật hẹp và không vệ sinh sạch sẽ. Bệnh dễ lây lan từ người này sang người khác do sử dụng chung đồ dùng cá nhân như quần áo, chăn màn hoặc dùng chung nguồn nước. Chính vì vậy, cần phải trang bị những kiến thức về bệnh để có thể nhận biết và chữa trị kịp thời, đem lại hiệu quả cao.\n Dấu hiệu nhận biết bệnh ghẻ\n Bệnh ghẻ là một bệnh ngoài da gây ngứa, do ngứa gãi gây nhiễm khuẩn thứ phát và có thể gặp biến chứng viêm cầu thận. Vì là bệnh ngoài da nên dấu hiệu nhận biết của bệnh ghẻ khá dễ:\n &#8211; Trên bề mặt da xuất hiện các mụn nước sắp xếp rải rác ở kẽ ngón tay, đường chỉ lòng bàn tay&#8230;Ở trẻ sơ sinh mụn nước có thể xuất hiện ở lòng bàn chân, quy đầu&#8230;\n &#8211; Có những vết xước, vảy da, đỏ da có thể chàm hóa, mụn mủ\n &#8211; Người bệnh ngứa khó chịu, nhất là về đêm do ghẻ cái đào hầm vào ban đêm\n &#8211; Bệnh biến chứng thành nhiều dạng:\n + Chàm hóa: người bệnh bị ngứa, mụn nước tập trung thành đám\n + Bội nhiễm: các mụn nước xen kẽ mụn mủ có thể phù nề, loét\n + Lichen hóa: ngứa nhiều, da dày,có màu thâm.\n Triệu chứng kèm theo của bệnh ghẻ\n &#8211; Xuất hiện vết máu trầy xước. Do khi bị bệnh ghẻ, tình trạng ngứa vô cùng dữ dội, do đó người bệnh vô tình gãi, cào xước, khiến da bị trầy xước và chảy máu\n &#8211; Chàm hóa thứ cấp: Do cái ghẻ sinh ra các chất bài tiết gây kích thích da cộng với việc gãi ngứa, khiến da xuất hiện những tổn thương nốt ban đỏ, mụn nước, đó chính là hiện tượng chàm hóa thứ cấp.\n &#8211; Nhiễm trùng thứ cấp: Triệu chứng bệnh ghẻ này xuất hiện do người bệnh gãi nhiều, thời tiết nóng, điều kiện vệ sinh kém, hoặc ra nhiều mồ hôi, khiến làn da rất bị nhiễm trùng thứ cấp, làm xuất hiện chứng bệnh chốc lở thứ cấp, mụn nhọt, viêm nang lông, hạch bạch huyết sưng to ở trên da.\n &#8211; Triệu chứng bệnh ghẻ viêm thận cấp tính: Do nhiễm trùng ghẻ thứ cấp, mà các chất bài tiết của cái ghẻ và các nhiễm trùng tế bào có tác dụng kháng nguyên với cơ thể, từ đó xuất hiện hiện tượng viêm thận cấp tính khiến người bệnh cảm thấy mệt mỏi, đau lưng, phù nề.\n Cách chữa trị\n Bệnh ghẻ sẽ có thể chữa trị nếu phát hiện sớm và có phương pháp điều trị phù hợp.\n &#8211; Có thể dùng lá cây ba chạc đen tắm hoặc dầu hạt máu chó cũng có hiệu quả trong điều trị ghẻ\n &#8211; Bôi thuốc tại chỗ hoặc toàn thân: bệnh nhân phải tắm rửa sạch sẽ bằng xà phòng, sau đó bôi thuốc vào các thương tổn, mỗi ngày 1 lần vào buổi tối.\n &#8211; Nếu ghẻ chàm hóa hoặc bội nhiễm phải bôi thêm dung dịch màu milian hoặc castellani và uống kháng hastamin tổng hợp.\n Để có thể điều trị dứt điểm, nhanh chóng bệnh nhân nên đến gặp bác sĩ để biết tình trạng bệnh và chỉ định sử dụng thuốc an toàn, không gây tác dụng phụ. Tuyệt đối không tự ý sử dụng thuốc vì có thể làm cho bệnh nặng hơn, thậm chí gây nguy hiểm đến tính mạng.\n Lời khuyên của bác sĩ cho bệnh nhân bị ghẻ\n Trong quá trình sử dụng thuốc chữa bệnh ghẻ, người bệnh cần chú ý những điều sau:\n &#8211; Chỉ bôi thuốc chữa bệnh ghẻ lên vùng da bị bệnh, tuyệt đối không bôi lên mắt, và những vùng da không bị bệnh. Bôi thuốc sau khi đã tắm, lau khô người và đã thay quần áo mới. Có thể sử dụng thuốc chữa bệnh ghẻ 1 lần/ngày trước khi tắm xong vào mỗi buổi tối, hoặc 2-3 lần/ngày tùy vào hướng dẫn của bác sĩ\n &#8211; Nên bôi liên tục cho tới khi khỏi bệnh, sau khi bệnh khỏi có thể bôi thêm 1 thời gian ngắn nữa để đề phòng bệnh ghẻ nước tái phát\n &#8211; Người bệnh cần dùng thêm những thuốc chữa bệnh ghẻ như: lvermetin, nó có tác dụng gây bất động và đào thải các ấu trùng qua đường huyết\n &#8211; Sử dụng thuốc kháng histamin, và vitamin B, C theo chỉ dẫn\n &#8211; Người bị ghẻ tuyệt đối không được tắm với lá xoan, lá đào, lá xà cừ vì rất dễ bị dị ứng, nhiễm độc\n Cách phòng ngừa bệnh tái phát\n Ghẻ là một trong số các bệnh ngoài da dễ tái phát sau điều trị, do đó bệnh nhân và người nhà cần chủ động phòng ngừa bệnh quay lại bằng cách thực hiện các lưu ý sau:\n &#8211; Chú ý phát hiện và điều trị sớm, bệnh sẽ chóng khỏi và ít lây lan. Không gãi, không tự ý dùng các loại thuốc bôi như thuốc rầy, thuốc súng… rất nguy hiểm.\n &#8211; Quần áo, chăn màn, chiếu, gối nên giặt tẩy thật sạch. Người bệnh cũng có thể nhúng nước sôi, phơi nắng cho thật khô, ủi nóng trước khi mặc quần áo hay sử dụng. Việc này giúp diệt hết cái ghẻ và trứng, đề phòng tái nhiễm hoặc lây lan bệnh. Tránh dùng chung quần áo và các vật dụng cá nhân.\n &#8211; Bệnh có thể tái phát từng đợt theo chu kỳ 3 tuần do trứng còn sống sót và phát triển thành cái ghẻ trưởng thành. Bắt buộc điều trị lại theo đúng phương pháp.\n &#8211; Phải điều trị đồng thời cho tất cả mọi người cùng bị ngứa đang sinh hoạt, chung sống trong gia đình, lớp học, ký túc xá… nhằm tránh tình trạng tái lây nhiễm lẫn nhau.\n Để đảm bảo an toàn, bệnh nhân nên đến gặp bác sĩ uy tín để khám và được lên phác đồ điều trị nếu bệnh đã biến chứng. Bên cạnh điều trị bằng thuốc, người bệnh nên chú ý đến các yếu tố ngoại cảnh khác để quá trình điều trị nhanh chóng và hiệu quả.\n Bạn đang có nhu cầu chữa bệnh ghẻ, liên lạc với chúng tôi để được tư vấn:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n  Add: Số 447 Hoàng Quốc Việt, Hà Nội\n   Hotline: 098.123.1234 <br>', 13, NULL, NULL, 1),
(31, 47, 'Bệnh ghẻ và những điều nên biết ở người trưởng thành', 'Bệnh ghẻ là một bệnh ngoài da gây ngứa, do ngứa gãi gây nhiễm khuẩn thứ phát và có thể gặp biến chứng viêm cầu thận. Nếu không được chẩn đoán và điều trị đúng cách, bệnh kéo dài, ngứa gãi gây mất ngủ, suy nhược thần kinh, mặt khác bệnh có thể lây lan trong gia đình, tập thể thậm chí thành dịch. Cũng như các bệnh ngoài da khác, bệnh ghẻ không gây chết người nhưng ảnh hưởng tới sức khoẻ, học tập và công tác.<br>Bệnh ghẻ là gì ?\n Ghẻ là một bệnh ở da khá phổ biến ở Việt Nam, thường xuất hiện ở những vùng có dân cư đông đúc, nhà ở chật hẹp, không vệ sinh sạch sẽ. Bệnh lây từ người này sang người khác thông qua tiếp xúc trực tiếp hoặc qua quần áo, chăn màn&#8230;<br>Tình trạng ghẻ lan khắp cơ thể<br>Cơ chế hình thành ghẻ.\n Ký sinh trùng gây bệnh có tên khoa học là Sarcoptes scabiei hominis. Ghẻ cái có hình bầu dục, bụng có 8 chân, lưng có gai xiên về phía sau, đầu có vòi hút thức ăn đồng thời để đào hầm vào lớp da thượng bì. Mỗi ngày ghẻ cái để 1-5 trứng, sau 3-7 ngày trứng nở thành ấu trùng, ấu trùng lột xác nhiều lần thành ghẻ trưởng thành<br>Cơ chế hình thành ghẻ<br>Dấu hiệu nhận biết\n &#8211; Trên bề mặt da xuất hiện các mụn nước sắp xếp rải rác ở kẽ ngón tay, đường chỉ lòng bàn tay&#8230;\n &#8211; Có những vết xước, vảy da, đỏ da có thể chàm hóa, mụn mủ\n &#8211; Người bệnh ngứa khó chịu, nhất là về đêm do ghẻ cái đào hầm vào ban đêm\n &#8211; Bệnh biến chứng thành nhiều dạng:\n  Chàm hóa: người bệnh bị ngứa, mụn nước tập trung thành đám\n  Bội nhiễm: các mụn nước xen kẽ mụn mủ có thể phù nề, loét\n  Lichen hóa: ngứa nhiều, da dày,có màu thâm.<br>Làm gì khi bị ghẻ\n Khi bị ghẻ, trước hết bệnh nhận hoặc người nhà cần tuân thủ các nguyên tắc sau:\n &#8211; Điều trị cho những người trong gia đình, tập thể bị ghẻ\n &#8211; Bôi thuốc đúng cách\n &#8211; Quần áo, chăn màn phải giặt sạch, phơi khô.<br>Đối với bệnh nhân bị ghẻ cần:\n + Thực hiện các xét nghiệm, chuẩn đoán chính xác vị trí cái ghẻ tồn tại, hang ổ của ghẻ . Có xác định đúng vị trí ghẻ cái gây bệnh, lầm tổ mới có thể diệt được ghẻ hiệu quả.\n + Với trẻ em: Sử dụng các dạng thuốc mỡ diêm sinh có tính sát khuẩn, diệt ghẻ tuy nhiên phải đảm bảo không gây kích ứng lên làn da mẫn cảm của trẻ.\n + Với phụ nữ mang thai: Thường chỉ dùng các loại thuốc bôi, không dùng thuốc uống và thuốc kháng sinh tránh ảnh hưởng tới thai nhi.\n + Với người lớn có thể kết hợp nhiều loại thuốc bôi, thuốc uống, kem ngoài da để đạt được kết quả tốt nhất.\n Để đảm bảo an toàn, bệnh nhân nên đến gặp bác sĩ uy tín để khám và được lên phác đồ điều trị nhất là khi bệnh đã biến chứng.<br>PHÒNG KHÁM DA LIỄU HÀ NỘI\n Add: Số 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 11, NULL, 7, 1),
(32, 48, 'Bệnh zona: Triệu chứng và biến chứng', 'Zona là bệnh lý ngoài da, sẽ rất nguy hiểm khi biến chứng, đặc biệt khi bệnh lây lan đến mắt, mồm..Hiện nay đã có nhiều phương pháp hiện đại trong điều trị zona, giúp bệnh nhân tiết kiệm thời gian điều trị. Để giúp khách hàng hiểu rõ hơn về căn nguyên gây bệnh từ đó hướng dẫn các phòng và chữa zona, các bác sĩ tại Phòng khám Da liễu Hà Nội sẽ giải đáp những thắc mắc liên quan đến căn bệnh này.\n Bệnh zona là gì ?\n Zona hay còn gọi là bệnh giời leo, gây ra do sự tái hoạt virut Varicella zoster tiềm ẩn ở rễ thần kinh cảm giác cạnh cột sống trong quá khứ.\n Đây là bệnh nhiễm trùng da với biểu hiện là các ban đỏ, mụn nước, mụn mủ lõm giữa tập trung thành đám dọc theo đường phân bố của thần kinh ngoại biên. Bệnh gặp ở mọi lứa tuổi, thường gặp hơn ở người già, người suy giảm miễn dịch, đặc biệt người bị nhiễm HIV/AIDS.\n Nguyên nhân\n &#8211; Hệ miễn dịch suy giảm: Đối với nguyên nhân này thường rơi vào đối tượng là trẻ em. Trẻ em thường có nguy cơ mắc bệnh thủy đậu trước đó và dẫn tới bệnh zona sau này bởi hệ miễn dịch của trẻ chưa được hoàn thiện nên khả năng virus tái hoạt động gây bệnh là rất cao.\n &#8211; Hệ miễn dịch suy yếu: Với nguyên nhân này thường rơi vào đối tượng là người già. Tỷ lệ bệnh zona xuất hiện ở lứa tuổi này nhiều nhất là trên 50 tuổi do hệ miễn dịch của họ đang bị suy yếu, mất khả năng ức chế virus gây bệnh.\n &#8211; Do stress kéo dài: Căng thẳng thần kinh là điều kiện thuận lợi để virut phát triển và khiến cho bệnh nặng hơn.\n Triệu chứng và biến chứng của bệnh\n Bệnh zona thông thường có những triệu chứng sau:\n &#8211; Trước 1-5 ngày trước khi phát bệnh, bệnh nhân có cảm giác khó chịu tại một vùng da như bỏng, nóng rát..\n &#8211; Bề mặt da xuất hiện các nốt đỏ nhỏ, tập trung thành mảng khoảng vài cm, sắp xếp dọc theo đường đi của 1 dây thần kinh ngoại biên\n &#8211; Xuất hiện các mụn nước, các bọng nước tập trung thành đám, sau vài ngày hơi lõm xuống, đục và dập vỡ rồi đóng vảy.\n &#8211; Mức độ đau rất đa dạng, đau nhẹ như cảm giác bỏng rát, âm ỉ từng cơn nếu nặng sẽ như kim châm, giật từng cơn.\n Biến chứng\n Zona là bệnh có tiến triển không theo chu kì và có những biến chứng khó lường, điển hình là\n &#8211; Biến chứng thường gặp nhất là đau dây thần kinh sau bệnh zona, nhất là ở người cao tuổi. Các cơn đau đôi khi kéo dài cả năm sau khi vết thương trên da đã lành.\n &#8211; Nhiều trường hợp bị biến chứng gây bội nhiễm da, tạo thành mụn mủ loét sâu, sưng bóng lên và rất đau; viêm màng não, viêm tụy cắt ngang, xuất huyết giảm tiểu cầu… do xử lý sai bệnh zona.\n &#8211; Nguy hiểm hơn khi zona tấn công dây thần kinh tam thoa ở mặt, mụn nước sẽ mọc trên mặt, trong miệng và mắt.\n &#8211; Nếu mụn mọc gần mắt, cần đi bác sĩ nhãn khoa ngay vì zona có thể làm giảm thị lực. Nếu tổn thương vào dây thần kinh thị giác sẽ gây mù mắt. Zona tấn công vào tai, có thể giảm thính lực.\n &#8211; Phụ nữ mang thai mắc bệnh zona có thể gây hại cho thai nhi\n Cách chữa trị\n Hiện nay có rất nhiều phương pháp được áp dụng để chữa trị zona, cùng điểm qua một số cách được nhiều người thực hiện dưới đây:\n &#8211; Sử dụng một số loại nguyên liệu từ thiên nhiên và dễ tìm như tỏi, nha đam và mật ong để chữa trị zona. Tỏi các tác dụng diệt khuẩn sẽ giúp cho vết thương nhanh lành, các tinh chất từ sữa có tác dụng trong việc làm lành vết thương và ngăn ngừa mụn, nha đam lành tính giúp làm dịu và giảm ngứa. Đây là cách làm đơn giản nhưng đòi hỏi người bệnh phải kiên trì.\n &#8211; Sử dụng thuốc uống kết hợp bôi ngoài da để nhanh lành vết thương, giảm số thương tổn mới và giảm đau sau zona. Thuốc nên được sử dụng sớm, tốt nhất trong 72 giờ đầu. Bệnh nhân tuyệt đối không tự ý sử dụng thuốc mà phải theo đơn mà bác sĩ chỉ định.\n &#8211; Trường hợp bệnh nặng, có thương tổn ở vùng mắt phải đến gặp ngay bác sĩ chuyên khoa để khám và xây dựng phác đồ điều trị ngay để bệnh không tiến triển xấu và để lại hậu quả.\n Sau khi điều trị, bệnh nhân cần tuân thủ nguyên tắc điều trị mà bác sĩ yêu cầu đồng thời kết hợp sử dụng một số loại thuốc kháng virut.\n Những lưu ý khi bị zona\n Để tránh không bị lang ben và làm cho bệnh không nặng hơn, cần lưu ý:\n &#8211; Khi nhận thấy các dấu hiệu của bệnh zona, người bệnh không được chủ quan mà phải đến ngay cơ sở y tế, phòng khám để khám và điều trị kịp thời.\n &#8211; Tuyệt đối tuân thủ nguyên tắc điều trị và sử dụng thuốc theo đúng chỉ định của bác sĩ.\n &#8211; Chăm sóc và bảo vệ cẩn thận, luôn vệ sinh cơ thể và nơi ở sạch sẽ, thoáng mát\n &#8211; Có chế độ ăn uống, nghỉ ngơi khoa học, đảm bảo cung cấp đủ chất dinh dưỡng cho cơ thể.\n &#8211; Tránh sử dụng một số thực phẩm như socola, ngũ cốc, rượu bia…\n Nhiều bệnh nhân lo lắng về biến chứng của bệnh zona là điều hoàn toàn dễ hiểu. Nhưng thay vì để bệnh tiến triển xấu hãy đến gặp bác sĩ để chữa trị khi vừa mới chớm bệnh. Với đội ngũ bác sĩ chuyên khoa kinh nghiệm trong nghề, Phòng khám Da liễu Hà Nội hứa hẹn sẽ mang lại cho bạn không gian chữa bệnh thoải mái, an toàn và hiệu quả.\n Nếu vẫn còn lo lắng và cần tư vấn, hãy liên lạc với chúng tôi:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n  Add: 447 Hoàng Quốc Việt, Hà Nội\n   Hotline: 098.123.1234 <br>', 12, NULL, NULL, 1),
(33, 48, 'Những điều cần biết về bệnh Zona thần kinh', 'Bệnh Zola thần kinh là bệnh do virut gây nên, tấn công chủ yếu lên da và thần kinh ở vùng da đó, bệnh thường khởi phát đột ngột, diễn biến cấp tính. Đa số những người bị Zona đều là những người khỏe mạnh, triệu chứng sẽ là ban đỏ, biến chuyển thành mụn nước với cảm giác ngứa, nóng và rát. Khoảng hai đến bốn tuần sau thì lành da nhưng cảm giác đau nóng có thể kéo dài khá lâu.\n Tỷ lệ người mắc bệnh zona mỗi năm là 1,2 &#8211; 3,4 ca mỗi 1.000 người. Tỷ số này tăng lên 3,9 &#8211; 11,8 đối với người trên 65 tuổi.\n Zona là một bệnh do vi rút gây nên, tấn công chủ yếu lên da và thần kinh ở vùng da đó. Bệnh thường khởi phát đột ngột, diễn biến cấp tính. Tuy zona là bệnh không nguy hiểm nhưng nếu không chữa trị kịp thời, để bệnh nặng sẽ dẫn đến nhiều biến chứng, bệnh quá nặng thì vi rút sẽ tiêu hủy tế bào thần kinh tủy sống và làm rối loạn chức năng dẫn truyền tín hiệu từ ngoài da.<br>Triệu chứng của bệnh\n Trước khi tổn thương mọc 2-3 ngày thường sẽ có cảm giác báo hiệu như: rát dấm dứt, đau vùng sắp mọc tổn thương kèm theo triệu chứng toàn thân ít hoặc nhiều như mệt mỏi, đau đầu&#8230; Hạch ngoại vi lân cận có thể sưng và đau.\n + Vị trí : thường khu trú tập trung ở những vị trí đặc biệt và chỉ có một bên của cơ thể dọc theo các đường dây thần kinh , nhưng cá biệt có thể bị cả hai bên hay lan toả.\n + Tổn thương cơ bản: thường bắt đầu là các mảng đỏ, nề nhẹ, gờ cao hơn mặt da, hình tròn, bầu dục lần lượt nổi dọc dây thần kinh , rải rác hoặc cụm lại thành dải , thành vệt, sau 1-2 giờ trên mảng đỏ xuất hiện những mụn nước chứa dịch trong, căng khó vỡ, các mụn nước tập trung thành cụm ( như chùm nho), về sau đục, vỡ, xẹp để lại sẹo ( nếu nhiễm khuẩn). Trước hoặc cùng với mọc tổn thương ở da thường nổi hạch sưng và đau ở vùng tương ứng và là dấu hiệu quan trọng để chẩn đoán.<br>Zona thần kinh ở phụ nữ mang thai<br>Nguyên nhân gây bệnh\n Bệnh zona là một bệnh do virus varicella-zoster, cũng là virus gây bệnh thủy đậu. Bất kỳ ai bị bệnh thủy đậu có thể mắc bệnh zona. Sau khi phục hồi từ bệnh thủy đậu, virus sau đó có thể xâm nhập vào hệ thần kinh và ngủ trong đó nhiều năm. Cuối cùng, nó có thể kích hoạt lại và đi dọc theo đường thần kinh đến da và tạo ra các bóng nước. Cho đến nay, nhà khoa học vẫn chưa biết được lý do bệnh tái hoạt lại là gì, có thể là do khả năng miễn dịch phản ứng với nhiễm trùng bị suy giảm khi bạn già đi. Varicella-zoster là một phần của một nhóm vi khuẩn được gọi là virus herpes, bao gồm các loại virus gây mụn rộp và herpes sinh dục. Vì vậy, bệnh zona còn được gọi là herpes zoster. Tuy nhiên, virus gây bệnh thủy đậu và bệnh zona không phải là virus gây ra mụn rộp hoặc herpes sinh dục, một tình trạng nhiễm trùng lây truyền qua đường tình dục.<br>Những người dễ mắc bệnh Zona thần kinh\n Bệnh thường xảy ra khi sức đề kháng của bạn suy giảm, thường gặp ở những người lớn tuổi và hệ miễn dịch kém.\n Một số yếu tố điển hình có thể làm tăng nguy cơ mắc bệnh zona bao gồm:\n &#8211; Trên 50 tuổi. Bệnh zona thường gặp nhất ở những người lớn hơn 50 tuổi. Nguy cơ này tăng theo tuổi tác. Một số chuyên gia ước tính rằng một nửa những người 80 tuổi trở lên sẽ bị bệnh zona;\n &#8211; Một số bệnh nhất định. Bệnh làm suy yếu hệ miễn dịch, chẳng hạn như HIV/AIDS và ung thư, có thể làm tăng nguy cơ mắc bệnh zona;\n &#8211; Điều trị ung thư. Xạ trị hoặc hóa trị có thể làm giảm sức đề kháng với bệnh và có thể gây ra bệnh zona;\n &#8211; Thuốc được dùng để ngăn thải ghép có thể làm tăng nguy cơ mắc bệnh zona nếu sử dụng lâu dài, chẳng hạn như thuốc steroid prednisone.<br>Zona thần kinh ở trẻ nhỏ<br>Điều trị bệnh Zona thần kinh\n Hầu hết những người mắc bệnh này bác sĩ đều chẩn đoán được khi khám lâm sàng, việc điều trị sớm sẽ tránh được những biến chứng về sau.\n &#8211; Điều trị zona tốt nhất là trong vòng 48 giờ tính từ khi có tổn thương da. Nếu điều trị trong vòng 1 tuần đầu thì kết quả cũng khá tốt. Điều trị càng muộn, nguy cơ biến chứng càng nhiều. Trường hợp điều trị muộn hoặc điều trị sớm mà không đúng thuốc hoặc đúng thuốc mà không đủ liều thì coi như chưa được điều trị.\n &#8211; Liệu pháp đầy đủ trị bệnh zona thần kinh gồm: dùng thuốc giảm đau, hạ sốt, chống viêm; thuốc làm dịu da; thuốc chống nhiễm khuẩn và thuốc kháng virut.\n &#8211; Thuốc kháng virut cần dùng càng sớm càng tốt từ khi có các dấu hiệu đầu tiên của bệnh zona để đạt hiệu quả điều trị cao. Uống thuốc kháng virut như: acyclovir, valacyclovir và famciclovir, có thể là giảm thời gian phát ban và đau, bao gồm cả đau sau tổn thương.Thuốc có tác dụng phụ như nôn hoặc tiêu chảy ở khoảng 3-4% các trường hợp. Bệnh nhân suy giảm chức năng thận thì phải chỉnh liều cho phù hợp.\n &#8211; Dùng thuốc giảm đau như: acetaminophen và ibuprofen, naproxen&#8230; Nếu bệnh nhân đau cấp trong lúc bệnh đang tiến triển thì có thể uống thêm các thuốc giảm đau thần kinh phối hợp như: gabapentin hoặc pregabalin trong thời gian từ 1-3 tuần.\n &#8211; Uống thuốc kháng histamin (clopheniramin, diphenhydramin, promethazin, dimenhydrinat&#8230;), có thể dùng kèm kem chống ngứa hoặc lotion calamin để làm dịu các cơn ngứa tại tổn thương da.\n &#8211; Các corticoid đường uống và bôi có thể được dùng để giảm viêm.\n &#8211; Khi tổn thương da ướt, tiết dịch nhiều thì bôi các chế phẩm dạng dung dịch như jarish, dalibour, các dung dịch kháng sinh. Khi tổn thương da khô hơn thì có thể bôi kem acyclovir. Nếu có nhiễm trùng thì bôi thêm các mỡ kháng sinh như foban, bactroban. Có thể chiếu laser He-Ne một đợt để góp phần giảm viêm, giảm đau và hạn chế sẹo.\n &#8211; Thuốc chủng ngừa varicella-zoster có thể giúp ngăn ngừa bệnh zona ở người trên 60 tuổi, đã bị bệnh thủy đậu. Tuy nhiên, nó không nhằm để chữa trị cho những người đang có bệnh. <br>', 11, NULL, NULL, 1),
(34, 20, 'Biểu hiện và cách điều trị bệnh Lupus ban đỏ khi bắt đầu vào đông', 'Lupus ban đỏ  là một bệnh tự miễn trong đó hệ thống miễn dịch của con người, tấn công chính những cơ quan và các tế bào của cơ thể, làm chúng bị tổn thương và rối loạn chức năng. Lupus ban đỏ hệ thống là bệnh lý của mô liên kết có tổn thương nhiều cơ quan do hệ thống miễn dịch của cơ thể bị rối loạn, đặc trưng bởi sự có mặt của kháng thể kháng nhân và nhiều tự kháng thể khác. Các cơ quan thường bị tổn thương bao gồm khớp, da, thận, tế bào máu, tim, phổi, thần kinh…<br>Bệnh lupus ban đỏ<br>Biểu hiện của bệnh<br>Các triệu chứng của lupus ban đỏ  có thể xuất hiện đột ngột hoặc từ từ sau nhiều tháng, nhiều năm. Do ảnh hưởng đến hầu hết các cơ quan trong cơ thể nên triệu chứng của bệnh hết sức đa dạng và thường nặng lên vào các tháng mùa đông. Có lẽ do hậu quả của sự tiếp xúc nhiều với ánh nắng mặt trời trong thời gian mùa hè trước đó.<br>Hơn 90% số bệnh nhân đến khám có các biểu hiện không đặc hiệu như gầy sút, mệt mỏi, sốt nhẹ, rụng tóc, viêm loét miệng, đau các khớp nhỏ, đau mỏi cơ, rối loạn kinh nguyệt. Khoảng 3/4 số bệnh nhân thấy nổi các ban đỏ bất thường trên da, trong đó hay gặp nhất là ban cánh bướm ở mặt, một dấu hiệu rất đặc trưng của lupus ban đỏ hệ thống (ban đỏ ở 2 gò má bắc cầu qua sống mũi).<br>Những tổn thương nội tạng như ở tim (tràn dịch màng tim, viêm cơ tim), ở phổi (tràn dịch màng phổi, viêm phổi), ở thận (viêm cầu thận), ở hệ thần kinh (co giật, rối loạn tâm thần), ở hệ tạo máu (thiếu máu, xuất huyết) thường xuất hiện trong giai đoạn toàn phát của bệnh ở khoảng 50-85% số bệnh nhân và là những nguyên nhân chủ yếu gây tử vong. Các triệu chứng này thường diễn biến thành từng đợt xen kẽ giữa những thời gian lui bệnh.<br>Trong giai đoạn đầu của bệnh, các triệu chứng thường mơ hồ và giống với nhiều bệnh lý khác cho nên kể từ lúc có những triệu chứng đầu tiên cho đến khi bệnh được chẩn đoán chính xác có thể phải mất vài năm.<br>Cách điều trị<br>Lupus ban đỏ không thể điều trị khỏi hoàn toàn nhưng có thể kiểm soát được nếu điều trị đúng. Mục đích chính của việc điều trị này là nhằm giảm thiểu triệu chứng và hạn chế các tổn thương nội tạng nặng. Trong giai đoạn bệnh nặng người bệnh cần được tăng cường nghỉ ngơi nhưng vẫn cần một chế độ vận động hợp lý để tránh teo cơ và cứng khớp.<br>Cho đến nay, chưa có một loại thuốc đông dược nào chứng minh được hiệu quả rõ rệt trong điều trị lupus ban đỏ hệ thống. Do đó, người bệnh nên hết sức thận trọng khi sử dụng các thuốc này vì chúng cũng có thể gây ra nhiều tác dụng không mong muốn, thậm chí có thể đe doạ tính mạng của họ.\n \n Các phương pháp phòng ngừa bệnh bao gồm:<br>Do nguyên nhân của bệnh chưa được xác định 1 cách rõ ràng nên chưa có nhiều các biện pháp phòng tránh. Tuy nhiên, bạn có thể thực hiện một số việc sau để ngăn ngừa bệnh:\n &#8211; Hạn chế tiếp xúc với ánh nắng mặt trời, đặc biệt là vào giờ cao điểm.\n &#8211; Bôi kem chống nắng, đội mũ và mặc áo dài tay khi ra ngoài.\n &#8211; Ăn nhiều thực phẩm giàu kẽm, sắt, vitamin…\n &#8211; Hoạt động và làm việc vừa sức, hạn chế lo lắng, mất ngủ.<br>Bệnh lupus ban đỏ vẫn chưa có biện pháp điều trị, do đó bạn cần chủ động phòng tránh căn bệnh này. Nếu cần tư vấn  thêm, xin vui lòng liên hệ với chúng tôi:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số: 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 13, 6, NULL, 1),
(35, 20, 'Sự nguy hiểm của bệnh Lupus ban đỏ', 'Bệnh Lupus ban đỏ là căn bệnh nguy hiểm ít được biết đến, cơ thể có hệ thống miễn dịch giúp chống lại sự xâm nhập của các tác nhân lạ. Mắc Lupus ban đỏ hệ thống khiến hệ thống miễn dịch mất khả năng phân biệt “lạ &#8211; quen” và chống lại cơ thể\n Lupus ban đỏ hệ thống thường được gọi đơn giản là bệnh lupus, được mô tả lần đầu tiên vào năm 1845 bởi Hebra. Đây là một bệnh tự miễn mạn tính, không rõ nguyên nhân, gây tổn thương hầu hết các hệ cơ quan trong cơ thể và trong trường hợp nặng, có thể đe dọa tính mạng người bệnh. Ở người mắc lupus ban đỏ hệ thống, hệ miễn dịch không giúp cơ thể chống lại sự xâm nhập của các tác nhân lạ, gây bệnh mà quay ra chống lại cơ thể bằng cách sinh ra các kháng thể kháng lại các tế bào của hầu hết các cơ quan.\n Lupus ban đỏ hệ thống là vấn đề toàn cầu, với hàng triệu người mới mắc hàng năm trên thế giới nhưng xã hội còn ít biết đến sự tồn tại của nó. Theo nghiên cứu của Hội Lupus Mỹ, hiện nay nước này có khoảng 2 triệu người bệnh lupus ban đỏ hệ thống, số người chết do bệnh tăng từ 879 năm 1979 lên 1.046 năm 2002 và 40% bệnh nhân đã phải nghỉ việc.\n Ở Việt Nam, theo nghiên cứu của Trung tâm Dị ứng &#8211; Miễn dịch lâm sàng, Bệnh viện Bạch Mai, nơi chủ yếu tiếp nhận điều trị bệnh nhân lupus ban đỏ hệ thống ở các tỉnh phía bắc, bệnh nhân mắc bệnh này vào điều trị tại trung tâm luôn chiếm số lượng đông nhất với 400-500 người mỗi năm, chiếm hơn 1/3 tổng số bệnh nhân điều trị nội trú.<br>Bệnh Lupus ban đỏ<br>Nguyên nhân gây bệnh Lupus ban đỏ\n Cho đến nay, nguyên nhân gây bệnh và cơ chế bệnh sinh chính xác của lupus ban đỏ hệ thống chưa được biết rõ. Cơ chế bệnh sinh của bệnh là một quá trình phức tạp với sự tham gia của nhiều yếu tố như di truyền, miễn dịch thể dịch, miễn dịch tế bào và yếu tố môi trường. Một số gen quy định phức hợp kháng nguyên phù hợp tổ chức như HLA-DR2,3,8, các gen mã hóa bổ thể C1q, C2, C4 và một số cytokin có liên quan đến tỷ lệ mắc bệnh cao. Nguyên nhân gây bệnh chính thức không được biết rõ, người ta cho rằng lupus ban đỏ hệ thống được gây ra do sự phối hợp của nhiều yếu tố, trong đó đặc biệt quan trọng là các yếu tố:<br>&#8211; Di truyền: Anh chị em ruột của các bệnh nhân lupus ban đỏ hệ thống có nguy cơ mắc bệnh cao gấp 20 lần so với người thường.\n &#8211; Môi trường: Do nhiễm khuẩn, tiếp xúc với hoá chất, ánh nắng mặt trời…\n &#8211; Nội tiết: Bệnh gặp chủ yếu ở phụ nữ trong độ tuổi sinh đẻ (nhiều hơn gấp 9 lần so với nam giới). Sau khi mãn kinh, cả tỷ lệ mắc và mức độ của bệnh đều giảm rõ rệt, còn trong thời kỳ mang thai bệnh thường nặng lên.<br>Sự hình thành của bệnh<br>Biểu hiện của bệnh\n Dấu hiệu của bệnh có thể xuất hiện từ từ theo tháng hay năm, triệu chứng của bênh khá đa dạng và thường nặng lên vào mùa đông.\n Bệnh lupus có nhiều triệu chứng khác nhau, nhưng một số triệu chứng phổ biến nhất của bệnh lupus đó là:\n &#8211; Đau hoặc sưng khớp\n &#8211; Đau cơ\n &#8211; Sốt không rõ nguyên nhân\n &#8211; Ban đỏ, thường ở trên mặt\n &#8211; Đau ngực khi hít thở sâu\n &#8211; Rụng tóc\n &#8211; Ngón tay hoặc ngón chân tái nhợt hoặc tím bầm\n &#8211; Nhạy cảm với ánh sáng mặt trời\n &#8211; Sưng ở chân hoặc xung quanh mắt\n &#8211; Miệng loét\n &#8211; Phình tuyến\n &#8211; Cảm thấy rất mệt.\n Các triệu chứng ít gặp hơn bao gồm:\n &#8211; Thiếu máu (giảm tế bào hồng cầu)\n &#8211; Nhức đầu\n &#8211; Chóng mặt\n &#8211; Cảm thấy buồn\n &#8211; Bối rối\n &#8211; Co giật.\n Các triệu chứng có thể xuất hiện và biến mất. Những thời điểm một người biểu hiện các triệu chứng được gọi là bùng phát, mức độ có thể từ nhẹ đến nặng. Triệu chứng mới có thể xuất hiện bất cứ lúc nào.Trong giai đoạn đầu của bệnh các triệu chứng rất mơ hồ và giống với nhiều bệnh lý khác nên từ khi có dấu hiệu bệnh đến khi chẩn đoán ra bệnh có thể mất đến vài năm.<br>Điều trị bệnh Lupus ban đỏ\n Bệnh không thể điều trị khỏi hoàn toàn nhưng có thể kiểm soát được khi điều trị đúng, việc điều trị với mục đích giảm thiểu các triệu chứng và hạn chế các tổn thương nội tạng nặng. Người bệnh cần tăng cường nghỉ ngơi trong giai đoạn cấp, cần chế độ vận động hợp lý tránh teo cơ và cứng khớp.\n &#8211; Sử dụng thuốc chống viêm giảm đau riêng cho bệnh\n &#8211; Thuốc chống sốt rét\n Cho đến nay, chưa có một loại thuốc đông dược nào chứng minh được hiệu quả rõ rệt trong điều trị lupus ban đỏ hệ thống. Do đó, người bệnh nên hết sức thận trọng khi sử dụng các thuốc này vì chúng cũng có thể gây ra nhiều tác dụng không mong muốn, thậm chí có thể đe doạ tính mạng của họ.<br>Đối với người mắc bệnh Lupus ban đỏ\n Người bị mắc bệnh lupus ban đỏ hệ thống cần có một cuộc sống lành mạnh, năng vận động, ít sang chấn tâm lý. Ngoài ra, tia tử ngoại trong ánh nắng mặt trời thường khởi phát hoặc làm nặng các đợt cấp của bệnh nên cần được tránh tiếp xúc tối đa. Việc ngừng đột ngột các thuốc, đặc biệt corticosteroid cũng là một nguyên nhân quan trọng dẫn đến các đợt cấp của bệnh và do đó cũng cần được tránh <br>', 12, NULL, NULL, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(36, 49, 'Nguyên nhân và cách chữa dứt điểm bệnh viêm nang lông', 'Viêm nang lông tuy không nguy hiểm nhưng lại dai dẳng, gây cảm giác ngứa ngáy khó chịu, khiến người người mắc phải mất tự tin do da sần sùi mất thẩm mỹ. Nhiều bạn trẻ bế tắc vì nghĩ rằng phải sống chung với “viêm nang lông” cả đời liệu có đúng không? Đầu tiên hãy cùng các bác sĩ tại Phòng khám da liễu Hà Nội tìm hiểu xem đây là bệnh gì, nguyên nhân và cách điều trị như thế nào?<br>Viêm nang lông<br>Bệnh viêm nang lông là gì?<br>Viêm nang lông là tình trạng viêm nhiễm nông ở da, có thể chỉ một vài nang lông hoặc nhiều nang lông cùng bị viêm. Bệnh thường ảnh hưởng tới các vùng da của cơ thể như vùng mọc râu, cánh tay, lưng, mông và chân.<br>Bệnh viêm nang lông có thể gặp ở cả nam và nữ, ở mọi lứa tuổi, thường xuất hiện ở bắp chân, tay, đùi, mông, nách. Khi bị bệnh, nếu không chữa trị sớm, bệnh sẽ phát triển sâu hơn trở thành mụn đinh, bị chàm hóa do gãi nhiều, nhiễm trùng, ảnh hưởng nghiêm trọng đến sức khỏe. Người bệnh cần phát hiện sớm để điều trị kịp thời.<br>Nguyên nhân gây bệnh<br>Để điều trị bệnh đứt điểm bệnh, trước hết cần xác định được nguyên nhân gây bệnh. Đối với bệnh viêm nang lông có thể kể đến một số nguyên nhân sau:<br>&#8211; Do rối loạn tuyến dầu: Tuyến dầu hoạt động mạnh quá mức hoặc chất dầu nhiều, gây bức bí và bịt kín các nang lông, cản trở sự phát triển của sợi lông. Khi lỗ chân lông bị bịt kín đã tạo điều kiện cho các vi khuẩn liên cầu hoặc tụ cầu, nấm, virut, ký sinh trùng cư trú tại các lỗ chân lông gây bệnh viêm nang lông.<br>&#8211; Do cạo, nhổ hoặc tẩy lông và vệ sinh không đúng cách: Sau khi cạo, nhổ lông khiến lông bị nhiễm trùng gây ra tình trạng viêm nhiễm các lỗ chân lông, gây bệnh viêm nang lông.<br>&#8211; Sử dụng quần áo bó sát bằng sợi tổng hợp, cọ xát mạnh vào da…cũng là nguyên nhân gây viêm nang lông.<br>&#8211; Sử dụng thuốc kháng sinh lâu dài cũng tạo điều kiện cho các vi khuẩn kỵ khí phát triển.<br>&#8211; Ngoài ra, rối loạn nội tiết, người ra nhiều mồ hôi, khí hậu nóng, độ ẩm cao, môi trường bị ô nhiễm là những yếu tố tác động trực tiếp lên da gây nên hiện tượng viêm nang lông.<br>&#8211; Những người bị suy yếu hệ miễn dịch như đái tháo đường, suy thận, da bị tổn thương sẵn, dùng thuốc kháng sinh lâu ngày…cũng hay bị viêm nang lông hơn người bình thường.<br>Cách chữa trị<br>Với sự phát triển mạnh mẽ của y học, các bác sĩ cho rằng viêm nang lông có thể trị và phòng ngừa được. Vì vậy, đừng lo lắng khi thấy những hình ảnh đáng sợ của viêm nang lông trên internet, hoặc nghe ai đó truyền tai nhau nói viêm nang lông không trị được. Viêm nang lông nhẹ và trung bình thường mất nhanh chóng và không để lại sẹo nếu điều trị đúng cách. Tuy nhiên, giống như tất cả các bệnh nhiễm trùng khác, luôn luôn có phần trăm nhỏ bệnh không thuyên giảm mà phát triển nghiêm trọng hơn. Viêm nang lông nghiêm trọng có thể dẫn đến nhiễm trùng sâu thành viêm mô tế bào, để lại sẹo, nếu ở da đầu có thể gây rụng tóc vĩnh viễn.<br>Tùy thuộc vào mức độ nặng nhẹ, bác sĩ sẽ kê một loại kháng sinh uống chẳng hạn như cephalexin hoặc erythromycin. Một loại không thể thiếu trong toa thuốc là thuốc bôi có chứa kháng sinh như erythromycin, clindamycin, hoặc mupirocin. Đôi khi ngoài thuốc thoa kháng sinh, bác sĩ có thể cho thoa thêm hydrocortisone 1% trong 3-5 ngày để giảm ngứa nhanh hơn. Nếu bác sĩ nghi ngờ viêm nang lông có kèm với tình trạng nhiễm nấm, bạn sẽ được cạo tìm nấm, để sử dụng thuốc kháng nấm thích hợp.<br>Tuy nhiên, để biết chính xác tình trạng bệnh và phương pháp điều trị, bạn nên đến gặp trực tiếp bác sĩ để được thăm khám và chỉ định dùng thuốc đúng bệnh và phù hợp với cơ địa.<br>Để ngăn ngừa bệnh xuất hiện và tái phát, bạn nên giữ thói quen sinh hoạt khoa học, luôn giữ cho cơ thể sạch sẽ. Không nên mặc quần áo bó sát khiến lỗ chân lông bị bít. Bên cạnh đó, khi wax hoặc cạo râu bạn nên chú ý cẩn thận để không làm da bị tổn thương.<br>Nếu cần thêm thông tin về bệnh viêm nang lông, liên hệ với bác sĩ của chúng tôi để được tư vấn miễn phí.<br>Thông tin liên hệ: <br>PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số 447 Hoàng Quốc Việt, Hà Nội\n  Hotline: 098.123.1234 <br>', 15, NULL, NULL, 1),
(37, 49, 'Điều trị viêm nang không khó như bạn nghĩ', 'Viêm nang lông gây phiền toái cho cơ thể bạn, chúng khiến da bạn khô ráp nhất là vào mùa đông, bạn sẽ thấy ngứa do lớp da khô đang bong tróc. Hiện tượng viêm xảy ra ở một vài nang lông nhưng cũng có thể ở nhiều nang lông cùng lúc. Viêm nang lông có thể xuất hiện ở mọi vùng da trên cơ thể, từ vùng mặt, râu, vùng da đầu, vùng gáy, cánh tay, chân, thân mình, vùng mông…<br>Những biểu hiện của bệnh thường gặp\n Đây là căn bệnh ngoài da phổ biến, bạn có thể thấy những dấu hiệu sau<br>Biểu hiện viêm nang lông<br>Nguyên nhân gây viêm nang lông\n &#8211; Di truyền: Theo các thống kê thì có tới 60% những người mắc bệnh viêm nang lông đều có người thân mắc bệnh này trước đó. Tuy vẫn chưa chắc chắn 100% là bệnh viêm nang lông viêm lỗ chân lông có lây qua di truyền hay không nhưng tốt nhất mọi người vẫn nên cảnh giác và phòng bệnh từ sớm.\n &#8211; Do vệ sinh kém: Vệ sinh da kém sẽ làm cho các tế bào chết và dịch tiết bã nhờ tích tụ trong lỗ chân lông, đây chính là nơi ở thuận lợi cho vi khuẩn sinh sôi và phát triển gây nên viêm mủ chân lông.\n &#8211; Do tẩy lông không đúng cách: Việc cạo, nhổ, tẩy lông không đúng cách chính là thủ phạm gây nên viêm nang lông viêm lỗ chân lông. Việc cạo nhổ lông không đúng có thể khiến da bị trầy xước, tổn thương và gây nhiễm trùng da khiến vùng chân lông bị viêm nhiễm trầm trọng. Thường bị ở vùng nách, chân lông, vùng kín.\n &#8211; Do thuốc kháng sinh: Việc sử dụng nhiều loại thuốc kháng sinh trong một thời gian dài sẽ làm cho các vi khuẩn có lợi trong cơ thể bị tiêu diệt và dễ tạo điều kiện cho các vi khuẩn kỵ khí phát triển gây nên tình trạng viêm da.\n &#8211; Do tuyến bã dầu hoạt động mạnh: ở một số cơ địa có tuyến mồ hôi dầu hoạt động mạnh tiết ra nhiều chất dầu làm bịt kín các nang lông, ngăn cản sự phát triển của sợi lông chính điều này đã tạo cơ hội cho vi khuẩn, nấm, virus dưới da phát triển và gây bệnh.<br>Sự hình thành viêm nang lông<br>Cách điều trị viêm nang lông\n Viêm nang lông, viêm lỗ chân lông nếu như không được điều trị sớm thì bệnh có thể nặng hơn gây mụn đinh vô cùng khó điều trị. Tình trạng nhiễm trùng nặng kéo dài sẽ làm tổn thương da trầm trọng ảnh hưởng tới sức khỏe cũng như thẩm mỹ làn da. Vì vậy tìm cách điều trị viêm lỗ chân lông sớm là việc vô cùng cần thiết, dưới đây là một số cách điều trị mà bạn có thể tham khảo như sau:<br>Diễn biến viêm nang lông dai dẳng, hay tái phát do những yếu tố thuận lợi như môi trường ô nhiễm, nóng và ẩm. Cải thiện môi trường sẽ giúp cho điều trị hiệu quả hơn và tránh tái phát bệnh. Rất nhiều bệnh nhân bị tái phát sau điều trị hoặc bệnh trở nên dai dẳng khó điều trị. Biến chứng có thể xảy ra nặng hơn là gây nhọt, nhọt cụm và viêm mô dưới da.Có thể sử dụng các loại xà phòng diệt khuẩn hoặc benzoyl peroxide để phòng ngừa tái phát.<br>Điều trị theo bác sĩ\n &#8211; Điều trị tại chỗ: Có thể dùng các thuốc bôi chống nhiễm trùng như betadin, cồn iode, các loại kem hoặc mỡ kháng sinh như bactroban, fucidin&#8230;\n &#8211; Điều trị toàn thân: trường hợp viêm nặng và tái phát có thể dùng thuốc đường toàn thân.\n &#8211; Kháng sinh: trong trường hợp viêm nang lông do tụ cầu có thể sử dụng kháng sinh đường toàn thân khi cần thiết. Các kháng sinh thuộc nhóm õ-lactamin, amoxillin, nhóm cephalosporin, cyclin, co-trimoxazol, ciprofloxacin và metronidazol. Liều lượng và cách sử dụng theo tình trạng bệnh và hướng dẫn của nhà sản xuất.\n &#8211; Viêm nang lông do vi khuẩn Gram âm: cần phải ngừng kháng sinh đang sử dụng, rửa benzoyl peroxide và cho ampixillin hoặc co-trimoxazol. Trong một số trường hợp phải cho Isotretinoin. Tuy nhiên, isotretinoin là thuốc có thể gây một số tác dụng phụ nguy hiểm, đặc biệt có thể gây quái thai ở phụ nữ khi sử dụng thuốc này nên cần phải có chỉ định của thầy thuốc.\n &#8211; Viêm nang lông do nấm: sử dụng các thuốc chống nấm bôi và phối hợp với thuốc uống. Thuốc bôi như Nizoral, Canesten, Mycoster&#8230;. Có nhiều loại thuốc chống nấm đường uống như itraconazole 100mg uống 2viên/ngày trong 14 ngày hoặc terbinafine uống 250mg/ngày trong 14 ngày. Đối với nấm men candida dùng itraconazole 100mg uống 2viên/ngày trong 14 ngày, hoặc fluconazol 150mg uống 2viên/ngày trong 14 ngày.\n &#8211; Viêm nang lông do vi rút herpes, có thể bôi kem acyclovir 6lần/ngày và uống acyclovir 400mg 3lần/ngày hoặc 200mg 5lần/ngày, hoặc valacyclovir 500mg uống 2 lần/ngày.\n &#8211; Viêm nang lông do demodex: có thể dùng kem permethrin bôi hoặc kem metronidazol phối hợp với uống metronidazol 1g/ngày trong 1 tuần.\n Chú ý: đối với viêm nang lông hay tái phát cần tìm nguyên nhân, phát hiện các ổ vi trùng ở các hốc mũi, hậu môn&#8230; và tránh làm xây xước da do cạo râu bằng cách cắt râu bằng kéo.\n Khi mắc bệnh, dù chỉ rất nhẹ cũng nên đến khám bệnh để có phương pháp điều trị đúng đắn, chớ coi thường vì dân gian có câu: “Cái sảy nảy cái ung” để nói đến trường hợp lúc đầu chỉ là viêm nang lông thông thường nhưng có thể gây biến chứng nặng thành nhọt, đinh râu. <br>', 15, NULL, NULL, 1),
(38, 50, 'Sai lầm của phụ huynh khi con bị thủy đậu', 'Mùa Đông đến kéo theo nguy cơ mắc bệnh ngoài da ở trẻ nhỏ như bệnh viêm da, thủy đậu&#8230;Thế nhưng, không phải ông bố, bà mẹ nào cũng biết cách để chữa trị cho con cái khi mắc bệnh. Có nhiều trường hợp, do bố mẹ thiếu hiểu biết nên đã mắc phải những sai lầm khiến bệnh của con nặng hơn. Cùng nghe các bác sĩ tại Phòng khám da liễu Hà Nội chỉ ra những lỗi hay gặp của bố mẹ trong chữa bệnh thủy đậu.<br>1. Kiêng tắm, kiêng gió\n Khi trẻ bị thủy đậu, nhiều cha mẹ đã tuyệt đối kiêng nước, kiêng gió nên không tắm rửa, lau người cho bé. Đây là cách chăm con sai lầm khiến nhiều trẻ bị biến chứng bị bội nhiễm, có trường hợp bị nhiễm trùng huyết do vi khuẩn xâm nhập vào máu, gây nguy hiểm cho trẻ.\n Lời khuyên được đưa ra, cha mẹ nên tắm cho bé bằng nước ấm, trong phòng tắm kín và không được tắm quá lâu.\n Bệnh thủy đậu kiêng tắm, kiêng gió là quan niệm sai lầm\n \n 2. Bôi thuốc xanh methylen chi chít khắp người\n Nhiều người thấy con bị thủy đậu lập tức bôi xanh methylen vào các nốt đậu, cả nốt vỡ và chưa vỡ. Điều này là không cần thiết, chỉ bôi vào mụn thủy đậu đã vỡ sẽ là khô nhanh và ngăn ngừa bị bội nhiễm vi khuẩn.\n Lưu ý không được bôi mỡ penicillin và tetracyclin, không nên làm vỡ nốt phỏng sẽ làm lây lan và dễ bị bội nhiễm.\n Trên một vùng da có thể có nhiều nốt đậu: mới mọc, phỏng nước hoặc đã vỡ. Khi nốt phỏng vỡ để lại vết xước trên da, nếu không nhiễm trùng sẽ không để lại sẹo.<br>3. Tắm lá cho trẻ nhỏ bị thủy đậu\n Theo khuyến cáo của các chuyên gia, các bậc phụ huynh không nên mua các loại lá về tắm cho trẻ nhanh khỏi. Bởi lẽ, da trẻ nhỏ, nhất là trẻ sơ sinh rất mỏng, cấu trúc chưa ổn định nên dễ bị tổn thương, dị ứng và nhiễm trùng.\n Theo thầy thuốc nhân dân Nguyễn Xuân Hướng, các loại lá như lá bàng, lá chè xanh mà cha mẹ hay dùng để tắm cho trẻ cũng không hề tốt, có thể khiến trẻ mắc bệnh. Do trong hai loại lá này có chất chát dễ làm cho da bé bị tổn thương.\n Lá tre tuy không có hại nhưng cũng không nên dùng để tắm cho trẻ vì lá tre có lông, khiến bé bị ngứa. Ngoài ra, lá trúc đào, lá bạch hoa trà thiết thảo tuyệt đối không được sử dụng nấu nước tắm cho trẻ do các loại lá này có chứa chất chất đội dễ gây viêm da và nhiễm trùng nặng.\n Việc tắm lá cho trẻ em và uống thuốc khi bị thủy đậu phải tuân theo chỉ định của bác sỹ.<br>4. Không cách ly trẻ bị thủy đậu\n Bệnh thủy đậu là bệnh có khả năng lây nhiễm cao qua đường hô hấp, tiếp xúc trực tiếp. Ngay cả giai đoạn ủ bệnh, trước khi phát ban, cũng có thể lây. Tuy nhiên, vì thương con nên các bậc phụ huynh không cách ly trẻ sang phòng thoáng đãng khác. Đây là người nhân khiến người lớn có thể bị lây bệnh hoặc trở thành trung gian truyền bệnh. Bệnh thủy đậu ở người lớn thường diễn biến nặng hơn rất nhiều so với trẻ nhỏ. Tỷ lệ tử vong ở người lớn cao hơn gấp 30 – 40 lần so với ở trẻ 5 – 9 tuổi.\n Vì vậy, khi trẻ bị bệnh cha mẹ cần cách ly trẻ với những người khác chưa có miễn dịch. Quần áo, khăn mặt của trẻ phải ngâm giặt bằng xà phòng, phơi nắng,…<br>Chăm sóc và bảo vệ con cái là trách nhiệm của bậc làm cha mẹ. Đừng để sự hiểu biết của bản thân khiến con trẻ phải gánh chịu hậu quả. Bệnh thủy đậu hoàn toàn có thể chữa khỏi nếu có phương pháp đúng. Hãy đến các cơ sở y tế uy tín gặp bác sĩ để chữa trị kịp thời.<br>Nếu cần đặt lịch thăm khám bác sĩ, vui lòng liên hệ:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Số 447 Hoàng Quốc Việt, hà Nội<br> Hotline: 098.123.1234  <br>', 11, NULL, 7, 1),
(39, 16, 'Bệnh thủy đậu và cách điều trị nhanh khỏi không để lại sẹo', 'Bệnh Thủy đậu là một bệnh do một loại virus mang tên Varicella Zoster Virus gây ra và chiếm trên 90% số đối tượng chưa tiêm phòng vacxin có khả năng mắc bệnh. Bệnh có thể rải rác hoặc bùng phát thành các vụ dịch nhỏ ở những nơi đông dân cư, điều kiện vệ sinh kém. Biểu hiện ban đầu của bệnh là các mụn nước, bọng nước mọc ở toàn thân rồi dần dần lan lên mặt, nếu không   biết cách chăm sóc thì sẽ làm cho bệnh lây lan nhanh hơn ngoài ra còn để lại sẹo rất xấu gây ảnh hưởng rất lớn đến cuộc sống.<br>Đây là một bệnh rất dễ lây truyền. Khi 1 người đang mắc bệnh thủy đậu thực hiện các hành vi nói, ho, hắt hơi… thì virus theo nước bọt, nước mũi bắn ra ngoài tan thành bụi. Thủy đậu ở người lớn nguy hiểm hơn rất nhiều so với trẻ nhỏ, vì vậy cách chữa trị đặc biệt cần chú ý hơn.<br>Một số quan niệm sai lầm về thủy đậu<br>Nguyên nhân hình thành bệnh thủy đậu <br>Bệnh thủy đậu do một loại vi rut mang tên Varicella Zoster Virus, thường bùng phát dịch vào các mùa trong năm. Bệnh rất dễ lây và thường có các triệu chứng chóng mặt, sốt nhẹ, khắp người nổi mụn. Khi đã mắc thủy đậu, cần cách ly ít nhất 5 &#8211; 7 ngày, dịch thường xảy ra trong gia đình và trường học. Thủy đậu là một bệnh rất dễ lây truyền. Khi một người mang vi khuẩn thủy đậu nói, hắt hơi, chảy nước mũi hoặc ho&#8230; thì các vi khuẩn đó theo nước bọt, nước mũi bắn ra ngoài lẫn trong bụi. Người khác hít phải bụi đó sẽ lây bệnh ngay. Thông thường, từ lúc nhiễm phải vi khuẩn, đến lúc phát ra bệnh &#8211; được gọi là thời gian ủ bệnh là khoảng 2 -3 tuần.<br>Các triệu chứng của bệnh<br>Mụn nước lúc đầu chứa một chất dịch trong, sau đó dịch đó trở nên đục như mủ rồi đóng vẩy.Chúng ta  không cần kiêng khem quá mức chỉ cần chú ý giữ vệ sinh thật tốt cho thể. Các vẩy đó sẽ rụng dần và nếu không có biến chứng gì thì sẽ không để lại sẹo.<br>Đặc điểm của các mụn nước đó là chúng mọc làm nhiều đợt khác nhau. Do đó, cùng trên  một vùng da, có thể thấy nhiều dạng khác nhau: hoặc ban đỏ, hoặc mụn nước trong, mụn nước đục, mụn đóng vẩy&#8230; trong cùng 1 thời gian. Và bệnh có thể khỏi sau 1-2 tuần. Cũng do thời gian tiến triển của bệnh tương đối ngắn, nên một số người đã cho thủy đậu là một bệnh nhẹ, hoàn toàn không nguy hiểm.<br>Biểu hiện của bệnh thủy đậu<br>Biến chứng mà thủy đậu có thể gây ra cho người bệnh <br>Biện pháp phòng ngừa bệnh <br>Vaccine chống bệnh thủy đậu có hiệu quả cao và lâu dài, giúp cơ thể tạo kháng thể chống lại virus thủy đậu, được áp dụng đối với các đối tượng sau:<br>&#8211; Tất cả trẻ em từ 12-18 tháng tuổi được tiêm 1 lần.<br>&#8211; Trẻ em từ 19 tháng tuổi đến 13 tuổi chưa từng bị thủy đậu lần nào, cũng tiêm 1 lần.<br>&#8211; Trẻ em trên 13 tuổi và người lớn chưa từng bị thủy đậu lần nào, thì nên tiêm 2 lần, nhắc lại cách nhau từ 4-8 tuần.<br>Cách điều trị bệnh thủy đậu hiệu quả<br>Hiện chưa có thuốc đặc trị bệnh mà cách điều trị bệnh thủy đậu tùy thuộc vào sự phát hiện bệnh sớm trong 24 giờ đầu. Cần cho người bệnh đi khám bệnh ngay. Căn cứ vào tình trạng bệnh, bác sĩ sẽ cho điều trị nội trú hoặc điều trị tại nhà.<br>Điều quan trọng nhất trong điều trị, chăm sóc bệnh nhân thủy đậu là làm sạch da và vệ sinh thân thể:<br>&#8211; Cho người bệnh nằm nghỉ trong 1 phòng thoáng mát, sạch sẽ, ăn các chất dễ tiêu.<br>&#8211; Chú ý cắt ngắn móng tay và giữ sạch tay.<br>&#8211; Trẻ nhỏ phải cho mang bao tay, xoa bột tan (talc) vô khuẩn hoặc phấn rôm khắp người để trẻ đỡ ngứa.<br>&#8211; Tắm rửa bằng các dung dịch sát khuẩn<br>&#8211; Tránh cọ xát làm các bóng nước bị vỡ.<br>Điều trị triệu chứng:<br>&#8211; Tại chỗ nốt đậu dập vỡ nên chấm dung dịch xanh metylen.<br>&#8211; Chống ngứa để bệnh nhân đỡ cào gãi bằng các thuốc kháng histamin như: chlopheniramin, loratadine…<br>&#8211; Khi bệnh nhân đau và sốt cao, có thể cho dùng acetaminophen. Không bao giờ được dùng aspirin hoặc những thuốc cảm có chứa aspirin cho trẻ em do nguy cơ xảy ra hội chứng Reye (một bệnh chuyển hoá nặng gồm tổn thương não và gan dẫn đến tử vong).<br>&#8211; Mỗi ngày 2-3 lần nhỏ mắt, mũi thuốc sát khuẩn như chloramphenicol 0,4% hoặc acgyrol 1%.<br>&#8211; Khi nốt phỏng vỡ, chỉ nên bôi thuốc xanh metilen; không được bôi mỡ tetraxiclin, mỡ penixilin hay thuốc đỏ.<br>Điều trị bệnh thủy đậu bằng thuốc kháng virus<br>&#8211; Trong vòng 24 giờ đầu khi xuất hiện nốt đậu dùng kháng sinh chống virut loại acyclovir để giúp rút ngắn thời gian và mức độ nghiêm trọng của bệnh. Liều lượng phụ thuộc lứa tuổi hoặc cân nặng (đối với trẻ nhỏ).<br>&#8211; Trường hợp nặng hơn hoặc có biến chứng như viêm màng não, trẻ suy giảm miễn dịch, có thể dùng acyclovir đường tĩnh mạch.<br>&#8211; Tuân thủ chỉ định dùng thuốc của bác sĩ. Không tự ý dùng thuốc hoặc nghe lời mách bảo mà dùng thuốc sai lầm, dẫn đến thủy đậu bội nhiễm nặng.<br>Giai đoạn vừa mới lành bệnh, cần phải tuyệt đối chống nắng và tránh cào gãi gây trầy xước. Bổ sung đầy đủ các vitamin và yếu tố vi lượng cho da thông qua chế độ ăn hoặc thuốc uống. Giữ vệ sinh cơ thể sạch sẽ và nghỉ ngơi nhiều.<br>&nbsp;<br>  <br>', 15, NULL, NULL, 1),
(40, 4, 'Lấy nhân mụn chuẩn y khoa &#8211; Xóa tan nỗi lo về mụn', 'Bất chấp lời cảnh báo của các Bác sĩ da liễu, rất nhiều bạn trẻ vẫn duy trì thói quen tự nặn mụn ở nhà và đã phải chịu hậu quả nặng hơn như sẹo thâm, sẹo rỗ và mụn ngày một trầm trọng hơn…<br>Đừng vội lo lắng vì Phòng Khám Da Liễu Hà Nội luôn thấu hiểu và mong muốn được chia sẻ nỗi lo cùng bạn. Đặc biệt, khi đến phòng khám, bạn sẽ được soi da và được các Bác sĩ thăm khám hoàn toàn MIỄN PHÍ. Ngoài ra, bạn còn được trải nghiệm các dịch vụ trị liệu mụn với phương pháp chuẩn y khoa từ Bác sĩ chuyên khoa da liễu hàng đầu.<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Trước hết, cùng tìm hiểu về dịch vụ lấy mụn chuẩn Y Khoa tại Phòng Khám Da liễu Hà Nội &#8211; Nơi gửi gắm niềm tin của hàng trăm nghìn bệnh nhân đang gặp các vấn đề về da.<br>Lấy nhân mụn là phương pháp hỗ trợ việc điều trị mụn, giúp lấy sạch nhân mụn, nhằm hạn chế tình trạng mụn phát triển. Đồng thời phương pháp này giúp rút ngắn thời gian điều trị mụn.<br>TÁC DỤNG CỦA PHƯƠNG PHÁP LẤY NHÂN MỤN Y KHOA<br>Lấy sạch nhân mụn, tránh tình trạng mụn chuyển từ thể nhẹ sang thể nặng.<br>Lấy nhân mụn chuẩn y khoa &#8211; Xóa tan nỗi lo về mụn<br>ƯU ĐIỂM CỦA VIỆC LẤY NHÂN MỤN Y KHOA<br>CÁC TRƯỜNG HỢP BÁC SĨ CHỈ ĐỊNH LẤY NHÂN MỤN Y KHOA<br>QUY TRÌNH LẤY NHÂN MỤN CHUẨN Y KHOA<br>Mụn vốn là nguyên nhân gây ra sự tự ti cho nhiều bạn trẻ, các bạn luôn chần chừ việc điều trị vì cho rằng việc điều trị rất khó &#8211; Điều đó hoàn toàn có cơ sở nếu bạn không gặp đúng Bác sĩ chuyên khoa.<br>Sau khi các Bác sĩ khám sẽ kết luận tình trạng tổn thương của da, cơ địa, sức khỏe… để xây dựng Phác đồ trị liệu riêng đối từng bệnh nhân. Tùy thuộc vào mức độ nặng hay nhẹ mà quy trình có sự chênh lệch nhau 1-2 bước. Thông thường, quy trình lấy nhân mụn y khoa tại Phòng Khám Da liễu Hà Nội được xây dựng gồm những bước như sau:<br>Bước 1: Bác sĩ thăm khám, lên phác đồ điều trị phù hợp<br>Bệnh nhân sẽ được soi da nhằm xác định cấp độ mụn, tình trạng da sau đó Bác sĩ sẽ thăm khám và lên phác đồ điều trị phù hợp với tình trạng bệnh lý<br>Bước 2: Chuẩn bị dụng cụ lấy nhân mụn đã được vô trùng<br>Bước 3: Làm sạch da bằng sữa rửa mặt Papulex<br>Lí do Phòng Khám Da liễu Hà Nội lựa chọn sữa rửa mặt Papulex vì đây là loại sữa rửa mặt thích hợp với mọi loại da mụn, đồng thời nhẹ nhàng làm sạch da với công thức không chứa xà phòng; giúp làm giảm sự xuất hiện của mụn trứng cá, duy trì cảm giác tươi mát và dễ chịu cho da.<br>Bước 4: Xông hơi, hút tạp chất<br>Điều dưỡng viên sử dụng máy để xông hơi, giúp giãn nở các lỗ chân lông để việc lấy nhân mụn được dễ dàng và đạt hiệu quả cao.<br>Bước 5: Sát khuẩn bề mặt da<br>Sát khuẩn là bước vô cùng quan trọng, vì khi các lỗ chân lông giãn nở, giúp cho việc lấy mụn dễ dàng nhưng đồng thời là điều kiện để các vi khuẩn chui theo lỗ chân lông vào da. Do vậy, bước sát khuẩn giúp tiêu diệt các vi khuẩn, bảo vệ da.<br>Bước 6: Lấy nhân mụn<br>Điều dưỡng viên sử dụng dụng cụ chuyên dụng đã được vô trùng loại bỏ hoàn toàn nhân mụn, phương pháp lấy nhân mụn giúp hỗ trợ điều trị mụn và rút ngắn thời gian điều trị.<br>Bước 7: Sát khuẩn bề mặt da<br>Bước 8: Thoa thuốc kháng sinh<br>Đối với từng bệnh nhân sẽ có những nguyên nhân gây mụn khác nhau. Trên cơ sở đó, các bác sĩ sẽ chỉ định loại kháng sinh phù hợp với da.<br>Bước 9: Lắng nghe lời khuyên từ Bác sĩ<br>Trong quá trình trị liệu mụn, bệnh nhân phải đặc biệt tuân thủ đúng chỉ định của Bác sĩ để đảm bảo hiệu quả. Khuyến cáo bệnh nhân không nên tự nặn mụn vì sẽ gây ra nhiều hậu quả nghiêm trọng.<br>KẾT QUẢ TRỊ MỤN SAU 1 LIỆU TRÌNH<br>Phòng khám chuyên xử lý những ca nhiễm Corticoid do sử dụng sản phẩm không rõ nguồn gốc<br>Kết quả sau 6 buổi điều trị mụn theo quy trình Y khoa<br>Kết quả sau 5 buổi điều trị mụn theo quy trình Y khoa<br>&nbsp;<br>BẢNG GIÁ DỊCH VỤ LẤY NHÂN MỤN Y KHOA<br>CHI PHÍ TRỊ MỤN BẰNG CÔNG NGHỆ E-LIGHT 3S<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Thông tin liên hệ\n PHÒNG KHÁM DA LIỄU HÀ NỘI &#8211; Nơi gửi trọn niềm tin!\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, NULL, NULL, 1),
(41, 4, '“TRIỆT KHUẨN TẦNG SÂU ĐA SẮC” – PHÁC ĐỒ TRỊ MỤN Y KHOA TỐI ƯU', 'Có rất nhiều phương pháp trị mụn mà có thể bạn hoặc bạn bè của bạn đã thử qua nhưng không mang lại hiệu quả, mụn cứ hết rồi lại lên? Thực chất mụn là một bệnh lý thuộc về da liễu và chắc chắn rằng bạn cần phải được Bác sĩ da liễu thăm khám và lên phác đồ điều trị phù hợp khi đó mới đạt được kết quả dứt điểm như mong muốn.<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Phác đồ &#8220;Triệt Khuẩn Tầng Sâu Đa Sắc&#8221; ứng dụng các công nghệ trị mụn hiện đại nhất hiện nay như: Công nghệ E_Light 3S, Công Nghệ Plasma Lạnh, Công Nghệ Ánh Sáng Sinh Học Biolight. kết hợp điều trị bằng thuốc do Bác sĩ chuyên khoa da liễu chỉ định để điều trị giúp loại bỏ mụn từ bên trong.<br>&#xfe0f;&#x1f3af; Cam Kết Sau 1 Liệu Trình<br>&#8211; Triệt mụn tận gốc, hiệu quả thấy rõ sau mỗi buổi điều trị.<br>&#8211; Hỗ trợ xóa mờ vết tham do mụn để lại.<br>&#8211; Kích thích tăng sinh các sợi collagen và elastin tái tạo cấu trúc da, giúp da căng và sáng mịn màng.<br>Bước 1: Bác sĩ soi da, thăm khám và lên phác đồ điều trị<br>Bước 2: Chuẩn bị dụng cụ lấy nhân mụn đã được vô trùng<br>Bước 3: Làm sạch da bằng sữa rửa mặt Papulex (dành cho da mụn)\nLí do Phòng Khám Da liễu Hà Nội lựa chọn sữa rửa mặt Papulex vì đây là loại sữa rửa mặt thích hợp với da mụn, đồng thời nhẹ nhàng làm sạch da với công thức không chứa xà phòng; giúp làm giảm sự xuất hiện của mụn trứng cá, duy trì cảm giác tươi mát và dễ chịu cho da.<br>Bước 4: Xông hơi, hút tạp chất\nĐiều dưỡng viên sử dụng máy để xông hơi, giúp giãn nở các lỗ chân lông để việc lấy nhân mụn được dễ dàng và đạt hiệu quả cao.<br>Bước 5: Sát khuẩn bề mặt da\nSát khuẩn là bước vô cùng quan trọng, vì khi các lỗ chân lông giãn nở, giúp cho việc lấy mụn dễ dàng nhưng đồng thời là điều kiện để các vi khuẩn chui theo lỗ chân lông vào da. Do vậy, bước sát khuẩn giúp tiêu diệt các vi khuẩn, bảo vệ da.<br>Bước 6: Lấy nhân mụn<br>Bước 7: Sát khuẩn bề mặt da<br>Bước 8: Thoa Gel làm dịu da<br>Bước 9: Trị liệu bằng công nghệ E_Light 3S/ Công nghệ Plasma Lạnh/ Biolight<br>Với mỗi tình trạng mụn của bệnh nhân, Bác sĩ sẽ sử dụng công nghệ phù hợp hoặc kết hợp các công nghệ (Elight 3S, Plasma lạnh, Biolight) để đạt hiệu quả điều trị tốt nhất. Với năng lượng phù hợp tác động trực tiếp lên vùng da mụn giúp phá tan màng bọc bên ngoài nhân mụn, làm teo biến hoàn toàn nhân mụn và loại bỏ hoàn toàn các vi khuẩn trú ngụ tận sâu trong các nang lông mà không hề gây tổn thương cho da, đồng thời điều hòa và ổn định lượng dầu tiết trên da giúp da luôn sáng mịn.<br>Bước 10: Di thuốc kháng viêm<br>Bước 11: Thoa thuốc kháng sinh\nĐối với từng bệnh nhân sẽ có những nguyên nhân gây mụn khác nhau. Trên cơ sở đó, các bác sĩ sẽ chỉ định loại kháng sinh phù hợp với da.<br>Bước 12: Lắng nghe lời khuyên từ Bác sĩ\nTrong quá trình trị liệu mụn, bệnh nhân phải đặc biệt tuân thủ đúng chỉ định của Bác sĩ để đảm bảo hiệu quả. Khuyến cáo bệnh nhân không nên tự nặn mụn vì sẽ gây ra nhiều hậu quả nghiêm trọng.<br>CÁC TRƯỜNG HỢP MỤN ÁP DỤNG HIỆU QUẢ<br>Phòng khám chuyên xử lý những ca nhiễm Corticoid do sử dụng sản phẩm không rõ nguồn gốc. Hiệu quả của việc điều trị có thể khác biệt tùy theo thể trạng của từng người.<br>Bạn Tiến bị mụn rất nặng do sử dụng thuốc đắp mụn từ đông y và mỹ phẩm chứa Corticoid. Sau 7/10 buổi điều trị mụn tại phòng khám da liễu hà nội đã cải thiện đến 99%, da sáng và mịn màng hơn rất nhiều.  Hiệu quả của việc điều trị có thể khác nhau tùy theo thể trạng của từng người.<br><br>Bạn Lê với làn da khá nhạy cảm và mụn ẩn, mụn liti, mụn đầu trắng dày kín mặt. Sau khi được bạn bè giới thiệu đến phòng khám da liễu hà nội, sau 8 buổi điều trị tình trạng mụn đã được loại bỏ tận gốc, da khỏe và sáng mịn hơn. Tuy nhiên hiểu quả và số buổi điều trị có thể khác nhau tùy theo thể trạng của từng người.<br><br>Đây là bạn Khánh Linh với tình trạng mụn ẩn và mụn viêm tập chung chủ yếu quanh vùng miệng và trán. Đây cũng là tình trạng mụn phổ biến mà rất nhiều bạn gặp phải. Sau 6 buổi điều trị mụn bằng phác đồ &#8220;Triệt Khuẩn Tầng Sâu Đa Sắc&#8221; mụn đã hết hoàn toàn, da sáng và mịn màng hơn rất nhiều. Tuy nhiên hiểu quả và số buổi điều trị có thể khác nhau tùy theo thể trạng của từng người.<br><br>Bạn Linh Chi thay đổi đến ngỡ ngàng sau khi hết sạch mụn và vết thâm. Bạn Linh Chi ban đầu tình trạng da khá xấu, mụn ẩn, mụn viêm và đặc biệt là rất nhiều vết thâm do bạn tự cậy mụn tại nhà. Rất may là tình trạng da của bạn Chi đáp ứng rất tốt với phác đồ điều trị. Chỉ sau 6 buổi trị mụn và 2 buổi trị thâm chuyên sâu da của bạn đã hết hoàn toàn mụn và vết thâm. Tuy nhiên hiểu quả và số buổi điều trị có thể khác nhau tùy theo thể trạng của từng người.<br>Bạn Ninh Thị Huyền (Yên Bái) đã hết sạch mụn ẩn, mụn viêm chỉ sau 3 buổi. Hiệu quả của việc điều trị có thể khác biệt tùy theo thể trạng của từng người.<br>Phạm Quang Linh đã sạch hoàn toàn mụn ẩn, mụn bọc sau 6 buổi điều trị. Hiệu quả của việc điều trị có thể khác biệt tùy theo thể trạng của từng người.<br>Giải thưởng cao quý từ khách hàng bình chọn<br>Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện Đại học điện lực Hà Nội)\nCơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\nCơ sở: 114 Lương Văn Thăng, TP Ninh Bình<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, NULL, NULL, 1),
(42, 4, 'Phác đồ chữa thâm mụn hiệu quả từ Bác sĩ', 'Vết thâm sau mụn khiến làn da của bạn trở nên xỉn màu, xấu xí? Vì quá nóng vội bạn đã tìm đến các phương pháp trị thâ m mụn cấp tốc: Mỹ phẩm, chanh&#8230; nhưng lại không biết rằng nếu sử dụng sai phương pháp sẽ khiến cho tình trạng thâm nặng hơn, da bị mỏng và khó chữa hơn.<br>Phác đồ từ Bác sĩ chuyên khoa da liễu tại Phòng khám da liễu Hà Nội sẽ giúp bạn loại bỏ nhanh chóng các vết thâm sau mụn một cách an toàn và hiệu quả.<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>ĐỐI TƯỢNG PHÙ HỢP<br>PHÁC ĐỒ TRỊ THÂM MỤN CHỈ ĐỊNH TỪ BÁC SĨ DA LIỄU<br>Bước 1: Bác sĩ soi da, thăm khám và lên phác đồ điều trị<br>Bác sĩ soi da, xác định mức độ vết thâm và sức khỏe làn da để xây dựng phác đồ điều trị phù hợp với tình trạng mỗi người.<br>Bước 2. Làm sạch vùng da cần trị thâm<br>Bước 3. Sát khuẩn vùng điều trị<br>Việc sát khuẩn giúp tiêu diệt những vi khuẩn còn sót lại trên bề mặt, nhằm mục đích giảm thiếu trường hợp bị viêm nhiễm.<br>Bước 4. Lấy nhân mụn tối thiểu<br>Trước khi tiến hành điều trị thâm mụn, điều dưỡng v iên sẽ kiểm tra và lấy những nhân mụn còn sót lại (nếu có) trên da để tránh mụn xuất hiện khiến da lại xuất hiện vết thâm.<br>Bước 5. Sát khuẩn vùng điều trị<br>Sau khi lấy nhân mụn, các đầu mụn hở rất dễ tạo cơ hội để vi khuẩn xâm nhập. Do vậy, điều dưỡng viên sẽ sát khuẩn để bảo đảm vi khuẩn bị tiêu diệt và không để xảy ra tình trạng viêm nhiễm.<br>Bước 6. Điện di vitamin C<br>Với những làn da bị thâm thường bị xỉn màu, điện di vitamin C sẽ giúp da được bổ sung dưỡng chất, hỗ trợ giảm thâm hiệu quả và giúp da sáng mịn hơn.<br>  Bước 7. Ủ tê vùng điều trị thâm<br>Bước 8. Sử dụng công nghệ Laser NanoTech<br>Bác sĩ sẽ sử dụng công nghệ Laser NanoTech và điều chỉnh bước sóng  phù hợp giúp loại bỏ đi các hắc sắc tố gây thâm da ở lớp hạ bì và trung bì da.<br>\n Cận cảnh buổi trị thâm thứ 4/6 trong liệu trình điều trị vết thâm mụn vĩnh viễn tại phòng khám da liễu hà nội. Sau 4 buổi tình trạng vết thâm đã giảm được 80%, da sáng và mịn màng hơn.Bước 9. Đắp mặt nạ bạc hà<br>Mặt nạ bạc hà có tác dụng làm dịu da, giúp làn da được thanh lọc sau khi điều trị.<br>Bước 10. Rửa mặt và bôi kem chống nắng<br>Bước 11. Lắng nghe lời khuyên từ Bác sĩ<br>CAM KẾT HIỆU QUẢ SAU 1 LIỆU TRÌNH<br>* Hiệu quả khác nhau tùy vào tình trạng da của mỗi người<br>KẾT QUẢ KHÁCH HÀNG<br><br>* Hiệu quả khác nhau tùy vào tình trạng da của mỗi người<br><br>Đình đức tình trạng mụn và vết thâm rất nhiều ở 2 bên má. Sau 6 buổi điều trị mụn đã hết hoàn toàn, vết thâm cũng được mờ đi 70%. Sau khi hết mụn đình đức đăng ký thêm 4 buổi trị thâm. Và đây là kết quả sau khi điều trị hết mụn và thâm của bạn Đức.<br>* Hiệu quả khác nhau tùy vào tình trạng da của mỗi người<br><br>* Hiệu quả khác nhau tùy vào tình trạng da của mỗi người<br>&nbsp;<br>Khách hàng trị mụn và vết thâm mụn tại Phòng khám da liễu Hà Nội<br>* Hiệu quả khác nhau tùy vào tình trạng da của mỗi người<br>CHI PHÍ ĐIỀU TRỊ THÂM SAU MỤN<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 15, NULL, NULL, 1),
(43, 16, 'Mesotherapy Mix &#8211; Giải pháp đột phá trong điều trị sẹo rỗ', 'Theo Thạc sĩ, Bác sĩ Nguyễn Mai Dung: &#8220;Đối với những tình trạng sẹo rỗ, sẹo lõm lâu năm điều trị bằng các phương pháp thông thường như Vi kim (phi kim), Laser CO2, Mỹ Phẩm&#8230; sẽ không mang lại hiệu quả cao&#8221;.<br><br>Kết quả điều trị sau 6 buổi, hình ảnh thực tế 100% không hề chỉnh sửa. Hiệu quả của việc điều trị có thể khác nhau tùy theo thể trạng của từng người.<br>CÔNG NGHỆ CẤY SẸO SINH HỌC MESOTHERAPY MIX là gì?<br>Mesotherapy Mix là phương pháp đưa hoạt chất sinh học vào đúng những điểm sẹo cần điều trị, hoạt chất sinh học sẽ được nuôi cấy tự động trên mô da, kích thích các tế bào da sản sinh tế bào mới để lấp đầy vết sẹo nhanh chóng.<br>\n Xem ngay để biết kết quả sau 4 buổi điều trị. Hiệu quả của việc điều trị có thể khác nhau tùy theo thể trạng của từng người.<br>NHỮNG TÌNH TRẠNG SẸO RỖ NÀO PHÙ HỢP VỚI CẤY SẸO SINH HỌC MESOTHERAPY MIX?<br>(Đối với tình trạng sẹo rỗ mới, sẹo rỗ thể nhẹ có thể sử dụng phương pháp Trị sẹo bằng công nghệ Phi kim tế bào gốc)<br>ƯU THẾ VƯỢT TRỘI CỦA CÔNG NGHỆ MESOTHERAPY<br>QUY TRÌNH ĐIỀU TRỊ SẸO MESOTHERAPY MIX<br>Bước 1: Thăm khám và tư vấn<br>Trước khi xây dựng quy trình trị liệu, Bác sĩ sẽ kiểm tra mức độ sâu của vết sẹo, hình dáng của các điểm sẹo sau đó sẽ chỉ định số buổi điều trị phù hợp.<br>Bước 2: Chuẩn bị dụng cụ đã được tiệt trùng<br>Bước 3: Làm sạch vùng trị liệu<br>Bước 4: Sát khuẩn vùng trị liệu<br>Bước 5: Thực hiện Cấy sẹo sinh học Mesotherapy Mix<br>Bước 6: Thoa kem chống nắng<br>Bước 7: Lắng nghe chỉ định sau điều trị của Bác sĩ<br><br>Phòng khám cam kết 100% Bác sĩ sẽ thực hiện cấy sẹo sinh học cho khách hàng trong suốt liệu trình điều trị, đảm bảo an toàn tuyệt đối.<br>LIỆU TRÌNH ĐIỀU TRỊ SẸO DIỄN RA TRONG BAO LÂU?<br>Liệu trình thông thường khoảng 4 &#8211; 6 buổi (Sau khi thăm khám tùy thuộc mức độ sẹo và hình dáng sẹo Bác sĩ sẽ xây dựng phác đồ điều trị với liệu trình phù hợp)<br>CHI PHÍ ĐIỀU TRỊ CẤY SẸO SINH HỌC<br>Giá gốc: dao động 3.000.000 &#8211; 5.000.000 vnđ/buổi tùy vào mức độ nặng, nhẹ<br>Chi phí trị sẹo đang được giảm 75 % cho tất cả các đối tượng, không phân biệt HSSV và các đối tượng khác:<br>Mức giá trên đã bao gồm sản phẩm điều trị tại phòng khám.<br>KẾT QUẢ ĐẠT ĐƯỢC SAU 1 LIỆU TRÌNH<br>KẾT QUẢ CỦA KHÁCH HÀNG ĐIỀU TRỊ TẠI PHÒNG KHÁM<br><br>Phòng khám khẳng định &#8220;Phác đồ cấy sẹo sinh học&#8221; là phương pháp điều trị sẹo sâu và sẹo rỗ lâu năm hiệu quả nhất. Bất chấp khách hàng đã từng điều trị bằng lăn kim, phi kim, laser co2 nhưng không hết. Hiệu quả của việc điều trị có thể khác nhau tùy theo thể trạng của từng người.<br><br>Hiệu quả của việc điều trị có thể khác nhau tùy theo thể trạng của từng người.<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>ĐỘI NGŨ BÁC SĨ TRỰC TIẾP KHÁM VÀ ĐIỀU TRỊ SẸO CHO BẠN<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 15, NULL, NULL, 1),
(44, 16, 'Chấm dứt sẹo rỗ nhờ công nghệ Phi kim kết hợp Hoạt chất sinh học', 'Theo Thạc sĩ, Bác sĩ Nguyễn Mai Dung: &#8220;Đối với những tình trạng lỗ chân lông to, sẹo rỗ, sẹo lõm có mức độ nhẹ và mới xuất hiện có thể điều trị bằng công nghệ phi kim (vi kim) kết hợp hoạt chất sinh học&#8221;.<br>Phương pháp Phi kim (vi kim) kết hợp hoạt chất sinh học chỉ hiệu quả đối với trường hợp lỗ chân lông to, sẹo rỗ mức độ nhẹ và mới xuất hiện<br>Đối với tình trạng sẹo rỗ có mức độ rỗ trung bình đến nặng và lâu năm nên điều trị bằng phương pháp Cấy sẹo sinh học Mesotherapy Mix.<br>Sẹo rỗ là tổn thương sâu của viêm nang lông, tổn thương này lan rộng xuống trung bì sau đó thành túi mủ hoại tử. Khối mô sâu bị mất do hoại tử gây ra sẹo rỗ, mang đến hậu quả là da mặt sau khi lành mụn phải chịu tình trạng “bị rỗ”.<br>Các hình thức sẹo này rất đa dạng, có thể là sẹo lõm hình trụ, hình thang, hình lòng chảo hoặc kết hợp.<br>CÔNG NGHỆ PHI KIM KẾT HỢP HOẠT CHẤT SINH HỌC LÀ GÌ?<br>Phi kim là phương pháp vi châm kim (kim siêu nhỏ) hoặc điều trị collagen cảm ứng. Là một thủ thuật hiện đại và đột phá nhất sử dụng một thiết bị vi châm kim để tạo ra chấn thương da có kiểm soát. Cơ chế hoạt động của &#8220;Vi kim&#8221; đơn giản là dựa trên cơ chế tự làm lành vết thương của cơ thể.<br>Công nghệ phi kim kết hợp với hoạt chất sinh học sẽ giúp đẩy nhanh tiến trình điều trị sẹo và giúp da căng mịn hơn<br>Đối với công nghệ phi kim kết hợp hoạt chất sinh học tại Phòng khám Da liễu Hà Nội, các hoạt chất sinh học sẽ được đưa vào trung bì thông qua các đầu vi kim. Những tế bào xung quanh sẹo lõm đã giảm dần khả năng sinh collagen để lấp đầy các vết lõm, nên liệu pháp đưa tế bào sinh học trực tiếp vào vùng da bị rỗ sẽ giúp thay thế các tế bào già cỗi bằng những tế bào nguyên sinh có khả năng sửa chữa, tái tạo, khôi phục, chữa lành các mô bị hư hỏng, tăng cường lượng collagen, thúc đẩy các nguyên bào sợi phục hồi là giải pháp hiệu quả làm đầy vết sẹo lõm, tái tạo làn da mới trẻ khoẻ, căng mịn.<br>NHỮNG TÌNH TRẠNG SẸO RỖ NÀO PHÙ HỢP?<br>ƯU THẾ VƯỢT TRỘI CỦA CÔNG NGHỆ PHI KIM KẾT HỢP HOẠT CHẤT SINH HỌC<br>QUY TRÌNH ĐIỀU TRỊ SẸO CÔNG NGHỆ PHI KIM<br>Bước 1: Thăm khám và tư vấn điều trị<br>Trước khi xây dựng quy trình trị liệu, Bác sĩ sẽ kiểm tra mức độ sâu của vết sẹo, hình dáng của các điểm sẹo sau đó sẽ chỉ định số buổi điều trị phù hợp.<br>Bước 2: Chuẩn bị dụng cụ đã được tiệt trùng<br>Nhằm đảm bảo vệ sinh và an toàn tuyệt đối, các dụng cụ sử dụng trong quy trình sẽ được tiệt trùng trong máy hấp tiệt trùng.<br>Bước 3: Làm sạch vùng trị liệu<br>Bước 4: Khử trùng vùng trị liệu<br>Bước 5: Sử dụng công nghệ phi kim kết hợp với hoạt chất sinh học<br>Bác sĩ sử dụng công nghệ Phi kim để đưa hoạt chất sinh học xuống sâu lớp trung bì của da<br>Bước 6: Thoa kem chống nắng<br>Bước 7: Lắng nghe chỉ định sau điều trị của Bác sĩ<br>CHI PHÍ ĐIỀU TRỊ <br>Giá gốc: 1,000,000 vnđ/buổi.<br>(Chương trình áp dụng tới hết ngày 30/11)<br>KẾT QUẢ ĐẠT ĐƯỢC SAU 1 LIỆU TRÌNH<br>KẾT QUẢ CỦA KHÁCH HÀNG ĐIỀU TRỊ TẠI PHÒNG KHÁM<br>Kết quả điều trị sau 4 buổi<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>ĐỘI NGŨ BÁC SĨ TRỰC TIẾP KHÁM VÀ ĐIỀU TRỊ SẸO CHO BẠN<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, NULL, NULL, 1),
(45, 2, 'Điều trị nám chuyên sâu theo phác đồ Bác sĩ', 'Theo Bác sĩ Nguyễn Tòng Xuân: &#8220;Để điều trị nám da tận gốc trước hết chúng ta cần hiểu rõ những nguyên nhân nào gây ra nám và loại nám từ đó mới xây dựng được Phác đồ chữa trị mang lại hiệu quả triệt để&#8221;.<br>Bác sĩ chuyên khoa I &#8211; Nguyễn Tòng Xuân thăm khám khách hàng<br>NGUYÊN NHÂN GÂY NÁM DA<br>Nguyên nhân bên trong:<br>Nguyên nhân bên ngoài:<br>CÁC LOẠI NÁM DA THƯỜNG GẶP<br>Nám tàn nhang: Là những nốt nhỏ màu nâu nhạt đến nâu sẫm, xuất hiện rải rác trên da.<br>Nám mảng: Xuất hiện theo từng mảng trên diện rộng của bề mặt da, các mảng nám tương đối nhạt màu, thường tập trung ở 2 bên má, trán và mũi. Loại nám mảng được chia thành 2 loại: Nám mảng chân sâu và nám mảng trên bề mặt<br>Nám đốm hay còn gọi là nám sâu: Là loại nám thường xuất hiện theo từng đốm nhỏ, sậm màu, thường tập trung ở 2 gò má và đối xứng nhau. Nám đốm là loại nám có chân ăn sâu dưới lớp trung bì của da.<br>Nám hỗn hợp: Thường gặp ở độ tuổi tiền mãn kinh, là sự tổng hợp của nám tàn nhang, nám mảng và nám đốm.<br>PHÁC ĐỒ ĐIỀU TRỊ NÁM CHUYÊN SÂU TỪ BÁC SĨ DA LIỄU<br>Đầu tiên, Bác sĩ sẽ tiến hành soi da bằng thiết bị công nghệ hiện đại nhằm xác định chính xác loại nám, cấp độ chân sâu<br>Bác sĩ xác định nguyên nhân gây ra nám là do nguyên nhân bên ngoài hay do nội tiết (một số trường hợp có nhiều bệnh lý Bác sĩ sẽ chỉ định xét nghiệm nội tiết)<br>Sau khi xác định được nguyên nhân, loại nám, cấp độ chân sâu Bác sĩ sẽ lên phác đồ điều trị phù hợp:<br>Đối với những loại nám trên bề mặt (Nám tàn nhang, nám mảng thể nhẹ) xuất hiện do yếu tố bên ngoài:<br>Chỉ định: Điều trị bằng Rubystar Effect + Kem chống nắng<br>Đối với những loại nám trên bề mặt (Nám tàn nhang, nám mảng thể nhẹ) xuất hiện do yếu tố nội tiết:<br>Chỉ định: Điều trị bằng Rubystar Effect + Thuốc điều hòa nội tiết tố + Kem chống nắng<br>Đối với những loại nám có chân sâu (nám mảng chân sâu, nám đốm, nám hỗn hợp) xuất hiện do yếu tố bên ngoài:<br>Chỉ định: Điều trị bằng Rubystar Effect + Dược mỹ phẩm + Kem chống nắng<br>Đối với những loại nám có chân sâu (nám mảng chân sâu, nám đốm, nám hỗn hợp) xuất hiện do yếu tố nội tiết:<br>Chỉ định: Điều trị bằng Rubystar Effect + Dược mỹ phẩm + Thuốc điều hòa nội tiết tố + Kem chống nắng<br>Đối với những trường hợp tăng sinh sắc tố do laser hoặc sản phẩm lột tẩy khiến nám tăng sinh đột biến, da mỏng, yếu:<br>Chỉ định: Cần chăm sóc phục hồi cho da khỏe sau đó mới tiếp tục điều trị<br>TẠI SAO CÔNG NGHỆ Rubystar Effect LUÔN ĐƯỢC NHẮC TỚI TRONG PHÁC ĐỒ ĐIỀU TRỊ NÁM?<br>Công nghệ Rubystar Effect đóng vai trò khá quan trọng trong việc loại bỏ nhanh các hắc sắc tố trên bề mặt da và lớp trung bì của da. Tuy nhiên, việc lựa chọn công nghệ có bản chất laser không gây tổn thương sẽ giúp an toàn với làn da hơn.<br>Công nghệ Laser NanoTech được các Bác sĩ lựa chọn để loại bỏ các sắc tố nám da nhanh chóng<br>Sau nhiều năm nghiên cứu và trải nghiệm, đội ngũ Bác sĩ tại phòng khám da liễu Hà Nội đã quyết định lựa chọn công nghệ Rubystar Effect trong việc hỗ trợ điều trị nám da cho bệnh nhân. Bởi vì, điều trị bằng công nghệ Rubystar Effect mang lại hiệu quả cao hơn và thời gian nhanh hơn so với các công nghệ Laser khác.<br>LIỆU TRÌNH ĐIỀU TRỊ NÁM DIỄN RA TRONG BAO LÂU?<br>CHI PHÍ ĐIỀU TRỊ TRỰC TIẾP BÁC SĨ DA LIỄU<br>KẾT QUẢ ĐẠT ĐƯỢC SAU 1 LIỆU TRÌNH<br>KẾT QUẢ CỦA KHÁCH HÀNG ĐIỀU TRỊ TẠI PHÒNG KHÁM<br>Kết quả sau 1 lần điều trị nám tàn nhang<br>Kết quả sau 6 buổi điều trị Sạm da, nám đốm và tàn nhang<br>Kết quả sau 2 buổi điều trị tàn nhang<br>Kết quả sau 7 buổi điều trị nám đốm chân sâu<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055\n   YÊU CẦU GỌI LẠI\n\nBạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác\n\n <br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 15, NULL, NULL, 1),
(46, 2, 'Trị tàn nhang công nghệ Rubystar Effect', 'Bạn đã thử nhiều cách nhưng không hiệu quả ? Bạn vẫn đang cố gắng tìm kiếm những phương pháp hiện đại, hiệu quả và an toàn hơn hay chán nản và muốn từ bỏ công cuộc làm đẹp ??<br>Với Công nghệ Rubystar Effect hiện đại bậc nhất hiện nay, chỉ duy nhất tại Phòng Khám Da liễu Hà Nội. Chúng tôi mong muốn đồng hành cùng bạn trên con đường tìm kiếm vẻ đẹp của chính mình, giúp bạn tự tin, tỏa sáng và thành công.<br>Bác sĩ sẽ sử dụng công nghệ Rubystar Effect và điều chỉnh bước sóng  phù hợp giúp loại bỏ đi các hắc sắc tố gây thâm da ở lớp hạ bì và trung bì da.<br>Tàn nhang là gì ?<br>Là những sắc tố ở thượng bì, xuất hiện các vùng da màu đốm nâu, có màu sắc thường không đồng nhất trên gò má, mũi và trán. Các vùng da này có thể xuất hiện đối xứng cả hai bên của khuôn mặt và có đặc điểm giống nhau.<br>Là những nốt màu nâu hoặc vàng có hình tròn hoặc hình oval có kích thước từ 1-20mm, các nốt tách biệt, không liên kết với nhau. Xuất hiện nhiều vào mùa hè vì tiếp xúc với ánh nắng mặt trời<br>Bác sĩ khám tàn nhang cho bạn Kim Ngân<br>Nguyên nhân gây tàn nhang<br>Theo nghiên cứu của các chuyên gia, tàn nhang sản sinh và phát triển do một số nguyên nhân chính sau:<br>Ngoài ra, những người ở xứ lạnh hay những người da trắng sẽ dễ bị tàn nhang hơn, da dầu hoặc da khô khó bị tàn nhang hơn da bình thường.<br>Lý do bạn nên chữa trị tàn nhang sớm ?<br>Tàn nhang xuất hiện và có biểu hiện nhận biết khá dễ dàng. Do không ảnh hưởng đến sức khỏe nên nhiều chị em vẫn chần chừ trong việc trị liệu. Thế nhưng, tàn nhang để lâu ngày sẽ gây ra nhiều vấn đề đáng lo ngại cho da:<br>Ưu thế vượt trội của công nghệ Rubystar Effect tại Phòng khám Da liễu Hà Nội<br>Quy trình trị tàn nhang bằng công nghệ Rubystar Effect tại Phòng khám Da liễu Hà Nội<br>Đối với các bệnh nhân mới bị tàn nhang, các vết tàn nhang còn ít, Phòng Khám Da liễu cam kết điều trị sạch tàn nhang trong 1 lần điều trị.<br>Với các bệnh nhân tình trạng nám nhiều, nám sâu sẽ được bác sĩ khám và lên phác độ điều trị theo quy trình từ 3-5 buổi.<br>Quy trình trị tàn nhang bằng công nghệ Rubystar Effect  được thực hiện qua các bước sau:<br>Đến với phòng khám Da liễu Hà Nội, khách hàng sẽ được soi da miễn phí và được bác sĩ tư vấn về liệu trình trị liệu phù hợp với da.<br>Papulex là loại sữa rửa mặt xuất xứ từ Pháp, được chứng nhận an toàn của Bộ Y tế, là sữa rửa mặt thích hợp với mọi loại da. Bên cạnh đó, nó còn giúp làm giảm mụn, đồng thời nhẹ nhàng làm sạch da với công thức không chứa xà phòng ;giúp làm giảm sự xuất hiện của mụn trứng cá, duy trì cảm giác tươi mát và dễ chịu cho da.<br>Sau khi rửa mặt, điều dưỡng viên tiến hành loại bỏ tế bào chết, giúp cho trắng sáng và mịn màng hơn.<br>Trước khi điều trị bằng công nghệ laser, bệnh nhân được điều dưỡng viên bôi thuốc Lidocane vùng da trị liệu trong khoảng 15 phút sau đó rửa sạch mặt<br>Tại cơ sở Phòng Khám Da liễu Hà Nội, chúng tôi trị tàn nhang bằng công nghệ Rubystar Effect. Với cơ chế tia laser tập trung năng lượng vào các sắc tố làm phá vỡ mảng sắc tố và sự kết dính của nó với da, đấy các vết tàn nhang sẽ được loại bỏ sau 1 lần điều trị.<br>Trong quá trình điều trị không gây tổn thương, xâm lấn và đốt cháy như công nghệ cũ.<br>Sau khi điều trị bằng công nghệ laser, bệnh nhân sẽ được đắp mặt nạ bạc hà để giúp làm dịu và thanh lọc da sau điều trị<br>Kem chống nắng có tác dụng bảo vệ da tránh sự tác động xấu của môi trường đặc biệt là các tia UV-tác nhân khiến cho tàn nhang phát triển.<br>Trước khi rời phòng khám, bệnh nhân sẽ được các thạc sĩ, bác sĩ chuyên môn tư vấn về chế độ chăm sóc da, ăn uống và một số loại kem phù hợp với da.<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, NULL, NULL, 1),
(47, 1, 'Carbon Laser &#8211; Bí quyết níu giữ tuổi xuân', 'Carbon hoạt tính có tác dụng thanh lọc da, thải độc tố… Khi kết hợp với Laser, carbon hoạt tính giúp cho làn da được trẻ hóa và xóa bỏ những khiếm khuyết của lão hóa da.<br>Carbon Laser &#8211; Bí quyết níu giữ tuổi thanh xuân<br>CƠ CHẾ HOẠT ĐỘNG CỦA CARBON LASER TRONG ĐIỀU TRỊ TRẺ HÓA DA<br>Cơ chế hoạt động trẻ hóa da bằng carbon hoạt tính là cách làm đẹp an toàn và thúc đẩy quá trình trao đổi chất nhanh chóng. Khi sử dụng kết hợp với laser bằng các bước sóng tập trung sẽ giúp phá bỏ các tế bào chết, loại bỏ các hắc sắc tố một cách nhanh chóng và hiệu quả. Đồng thời kích thích sản sinh collagen giúp da được trẻ hóa toàn diện từ sâu bên trong da.<br>Laser còn có khả năng phân hủy và loại bỏ những sắc tố gây ra sạm màu da, khống chế và điều tiết sự ổn định melanin giúp da trắng đều màu, loại bỏ nếp nhăn. Hơn nữa Laser còn có chức năng điều tiết nhờn, làm sạch nang lông giúp lỗ chân lông se khít, bề mặt da thông thoáng.<br>VÌ SAO NÊN LỰA CHỌN CARBON LASER LÀM TRẺ HÓA DA?<br>QUY TRÌNH Y KHOA ĐIỀU TRỊ TRẺ HÓA<br>Bước 1: Thăm khám Bác sĩ<br>Bác sĩ tiến hành soi da xác định chính xác tình trạng lão hóa da và lên liệu trình điều trị phù hợp<br>Bước 2: Làm sạch vùng trị liệu<br>Bước 3: Xông hơi và hút tạp<br>Bước 4: Đắp mặt nạ Carbon hoạt tính<br>Đắp mặt nạ Carbon trong khoảng 20 phút<br>Bước 5. Sử dụng Công nghệ Laser NanoTech<br>Điều trị bằng Laser nhất thiết phải được Bác sĩ trực tiếp điều trị mới mang lại hiệu quả cao và an toàn<br>Bước 6: Đắp mặt nạ<br>Bước 7: Thoa kem chống nắng<br>Bước 8: Lắng nghe lời khuyên từ Bác sĩ<br>LIỆU TRÌNH TRẺ HÓA DA CARBON LASER DIỄN RA TRONG BAO LÂU?<br>Liệu trình thông thường khoảng 4 – 6 buổi (Sau khi thăm khám tình trạng lão hóa da, Bác sĩ sẽ chỉ định số buổi điều trị phù hợp)<br>LÝ DO NÊN LỰA CHỌN PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, 1, NULL, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(48, 45, 'Làm trắng, căng bóng da với DNA Cá Hồi Nhật Bản có thể chữa bệnh tổ đỉa', 'Phương pháp cấy tinh chất DNA cá hồi ra đời đã và đang thu hút sự quan tâm của rất nhiều chị em yêu thích làm đẹp. Đây được xem là “bí quyết”  giúp trẻ hóa da, xóa tan mọi nỗi lo về dấu hiệu lão hóa, tuổi tác và dự kiến sẽ trở thành xu hướng làm đẹp trong năm 2018 này. <br>Cá hồi chứa nhiều Vitamin A, D, các axit béo omega-3, protein…cung cấp dưỡng chất cải thiện kết cấu da, giúp da luôn căng mịn và giữ ẩm cho da, hạn chế tình trạng thô sần, khô ráp.<br>Trong bào thai cá hồi có chứa tinh chất PDRN (polydeoxyribonucleotide) là nhân tố tăng trưởng tế bào da, rất tốt cho việc phục hồi làn da khỏe đẹp.<br>Các chuyên gia thẩm mỹ đã tận dụng những thành phần quý trong cá hồi để điều chế thành công tinh chất trị liệu da DNA. Phương pháp này tái sinh làn da và ngăn ngừa các dấu hiệu lão hóa như nếp nhăn, lỗ chân lông to… giúp da sáng, mịn  màng rõ rệt sau mỗi lần trị liệu.<br>Có rất nhiều phương pháp làm đẹp, trẻ hóa da khác nhau song phái đẹp các nước Nhật Bản, Hàn Quốc…luôn ưu tiên lựa chọn cấy tinh chất DNA cá hồi  vì những lợi ích tuyệt vời mà phương pháp này mang lại:<br>Duy trì hiệu quả làn da khoẻ mạnh lâu dài<br>Da duy trì độ trẻ đẹp, cân bằng, khoẻ mạnh lâu dài tuỳ thuộc vào quá trình hoạt động của nguyên bào sợi. Thay vì đắp mặt nạ, bôi kem dưỡng da chỉ cần 1 buổi cấy tinh chất DNA cá hồi da sẽ mịn màng, căng bóng.<br>Cải thiện đàn hồi cho da<br>Bên cạnh tác dụng trẻ hóa, tinh chất DNA cá hồi còn hỗ trợ quá trình tăng sinh Collagen và Elastin giúp da chắc khoẻ, phục hồi da mỏng, da chùng xệ, mất khả năng đàn hồi kéo dãn. Bên cạnh đó, cấy tinh chất DNA cá hồi còn giúp duy trì và cân bằng độ ẩm của da.<br>Cải thiện tone da<br>Thúc đẩy khả năng tuần hoàn máu và các cơ quan chức năng khác trong cơ thể, nâng tone da sáng trắng, loại bỏ da ửng đỏ, sạm đen, da lốm đốm, nhạt màu, da thâm mụn, da ngứa. Da sẽ bật tone rõ rệt sau liệu trình cấy DNA cá hồi.<br>Hiệu quả nhanh chóng và an toàn<br>Sau khi thực hiện cấy tinh chất DNA cá hồi bạn hoàn toàn không phải kiêng khem gì vì phương pháp này không gây tổn thưởng da. Cho đến nay chưa có bất cứ khách hàng nào bị dị ứng, viêm nhiễm sau khi sử dụng dịch vụ.<br>Quy trình cấy tinh chất DNA tại Phòng khám Da liễu Hà Nội tuân thủ đúng mọi quy định của Bộ Y tế và được các Bác sĩ chuyên khoa da liễu, chuyên gia thẩm mỹ đánh giá an toàn với mọi tình trạng da.<br>Bước 1. Bác sĩ chuyên khoa thăm khám và tư vấn<br>Bước 2. Làm sạch vùng trị liệu<br>Bước 3.  Xông hơi và hút tạp<br>Bước 4. Thoa và ủ tê để khách hàng không bị châm chích khi thực hiện<br>Bước 5. Sát khuẩn bề mặt da<br>Bước 6. Thực hiện cấy tinh chất DNA cá hồi<br>Tùy thuộc vào mục đích làm đẹp, tình trạng da Bác sĩ sẽ điều chỉnh lực tác động của máy để đưa tinh chất đến độ sâu mong muốn.<br>Đối với trường hợp da bị lão hóa, thâm mụn tinh chất DNA sẽ được đưa vào trung bì của da để thúc đẩy quá trình tăng sinh Collagen và Elastin giúp da căng bóng, mịn màng và trắng sáng đều màu. Trường hợp da bị sẹo rỗ, sẹo lõm do mụn tinh chất DNA sẽ được đưa sâu xuống hạ bì để hỗ trợ phá vỡ đáy sẹo và làm đầy sẹo, kể cả tình trạng sẹo lâu năm cũng được cải thiện đáng kể.<br>Bước 7. Sát khuẩn bề mặt da<br>Bước 8. Đắp mặt nạ để thanh lọc da<br>Bước 9. Làm sạch và bôi kem chống nắng<br>Bước 10. Bác sĩ hướng dẫn cách chăm sóc da tại nhà<br>Thời gian và liệu trình<br>Mỗi buổi cấy tinh chất DNA cá hồi dao động từ 45-60 phút, mỗi buổi cách nhau 3 – 4 tuần. Bác sĩ chuyên khoa da liễu khuyến cáo nên thực hiện liệu trình 2-5 buổi để đạt hiệu quả như mong muốn.<br>Nắm bắt được nhu cầu làm đẹp ngày càng tăng cao của chị em, nhiều thẩm mỹ viện, spa mọc lên nhưng không được cấp phép hoạt động. Do đó bác sĩ chuyên khoa da liễu, chuyên gia thẩm mỹ khuyến cáo lựa chọn những cơ sở y tế uy tín để làm đẹp và Phòng khám Da liễu Hà Nội là sự lựa chọn hoàn hảo của chị em.<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 094.947.0055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 15, NULL, NULL, 1),
(49, 16, 'Xóa sạch hình xăm không để lại sẹo', 'Xăm lên cơ thể là hình thức thay đổi sắc tố da bằng loại mực sinh học ghi dấu trên cơ thể với mong muốn làm đẹp, tạo ấn tượng. Từ lúc con người phát minh ra phương pháp xăm hình nghệ thuật cũng chính là lúc họ có nhu cầu xóa đi những hình xăm không còn phù hợp hoặc bị hỏng do nhiều nguyên nhân khác nhau.<br>Trào lưu xăm hình nghệ thuật<br>GIẢI PHÁP XÓA SẠCH HÌNH XĂM NHANH TỪ CÔNG NGHỆ LASER NANOTECH<br>Laser NanoTech là công nghệ xóa hình xăm hiện đại nhất hiện nay, thực hiện nguồn năng lượng Laser cảm ứng chiếu trực tiếp, chính xác lên mực xăm, tác động đó sẽ làm phá vỡ sắc tố trong da thành vô số hạt li ti sau đó cơ thể sẽ tự đào thải ra ngoài theo cơ chế tự nhiên.<br>Bác sĩ sẽ điều chỉnh năng lượng phù hợp giúp xóa bỏ mực xăm trên da an toàn và nhanh chóng<br>ƯU ĐIỂM VƯỢT TRỘI KHI SỬ DỤNG LASER NANOTECH TRONG VIỆC XÓA HÌNH XĂM<br>KẾT QUẢ XÓA HÌNH XĂM TẠI PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Kết quả xóa hình xăm bằng công nghệ NanoTech<br>LÝ DO NÊN XÓA XĂM TẠI PHÒNG KHÁM DA LIỄU HÀ NỘI<br>Đội ngũ Bác sĩ tại Phòng khám da liễu Hà Nội luôn sẵn sàng giúp bạn<br>Thông tin địa chỉ:\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\n Hotline: 0949.470.055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 11, NULL, NULL, 1),
(50, 4, 'Bị mụn có nên dùng sữa rửa mặt không?', '&nbsp;<br>Bạn Nguyễn Quỳnh, 21 tuổi hỏi:  Chào bác sĩ. Em nghe mọi người bảo da bị mụn đừng nên dùng sữa rửa mặt, nó có thể khiến mụn mọc nhiều hơn. Xin bác sĩ tư vấn giúp em, liệu da bị mụn có nên dùng sữa rửa mặt hay không ạ? Em cảm ơn bác sĩ ạ. <br>BÁC SĨ TRẢ LỜI:<br>Chào bạn. Cảm ơn bạn đã quan tâm đến dịch vụ trị mụn tại Phòng khám da liễu Hà Nội, với câu hỏi của bạn như sau:<br>Mụn hình thành chủ yếu là do da mặt không được vệ sinh sạch sẽ, khiến các lỗ chân lông nơi mồ hôi và tuyến bã nhờn tiết ra bị bít tắt, dẫn đến viêm nhiễm. Chính vì thế, nếu muốn da hết mụn và ngăn ngừa mụn quay trở lại thì bắt buộc bạn phải quan tâm đến vấn đề giữ vệ sinh da mặt. Hơn nữa, da mặt thông thoáng cũng góp phần phát huy tối đa hiệu quả mà phương pháp trị mụn mang lại.<br>Sở dĩ nhiều người cho rằng việc không nên dùng sữa rửa mặt khi đang bị mụn bởi sẽ làm mụn mọc ồ ạt thêm là vì họ dùng sản phẩm không phù hợp, kém chất lượng hoặc dùng sai cách. Do đó bạn nên lựa chọn loại sữa rửa mặt chuyên biệt dành cho da mụn, bởi vì:<br>Da mụn rất nhạy cảm, rất dễ bị kích ứng khi dùng sữa rửa mặt thường, nếu không chăm sóc cẩn thận sẽ khiến tình trạng mụn nặng nề hơn, thậm chí biến chứng và để lại thâm, sẹo. Việc làm sạch, loại bỏ nhờn, bụi bẩn giúp ngăn ngừa tắc lỗ chân lông thực sự cần thiết với người bị mụn. Điều này góp phần tiêu diệt vi khuẩn gây mụn và hỗ trợ giảm thâm, tiêu viêm.<br>Đối với da mụn bạn nên lựa chọn những dòng sản phẩm làm sạch vừa phải, dịu nhẹ và ít bọt. Nếu được bạn nên lựa chọn sản phẩm không chứa xà phòng vì  sử dụng chất tẩy rửa nhẹ nhàng bởi một số chất tẩy rửa có nồng độ cao của xà phòng có thể tạo ra làn da khô và kích ứng.<br>Nếu da bạn bị mụn và nhiều dầu hãy sử dụng các sản phẩm chứa chất Nicotinamide như sữa rửa mặt kiềm dầu Papulex, Repavar&#8230; Bởi vì chất Nicotinamide giúp làm dịu da đỏ và viêm. Hãy cố gắng sử dụng các sản phẩm chăm sóc da phù hợp với loại da của bạn. Ví dụ những người có da nhờn nên sử dụng các sản phẩm chăm sóc da không có khả năng gây độc và ngăn ngừa các lỗ chân lông tắc nghẽn là điều quan trọng.<br>Bạn có thể tham khảo một số dòng sữa rửa mặt như: Papulex, Repavar, Calahara tại Nhà thuốc của Phòng  khám  Da  liễu  Hà  Nội. Đây là những sản phẩm rửa mặt chuyên biệt dành cho da mụn, được chứng nhận an toàn và Nếu bạn có da bạn dễ bị mụn thì đây chắc chắn  là những sự lựa chọn hoàn hảo.<br>Bên cạnh sữa rửa mặt, bạn có thể tham khảo thêm một số cách chăm sóc da tại nhà để có được làn da khỏe, mịn màng nhé:<br>Bạn đã rửa mặt đúng cách cho làn da bị mụn của mình chưa ?<br>Cách lựa chọn sữa rửa mặt dành cho da mụn<br>Những sai lầm bạn thường mắc phải khi bị mụn<br>Hy vọng, những thông tin trên sẽ giúp bạn lựa chọn được loại sữa rửa mặt phù hợp với tình trạng mụn của mình. Nếu cần hỗ trợ thêm thông tin, vui lòng liên hệ với chúng tôi để được tư vấn. Hotline: 094.947.0055<br>&nbsp;<br>Thạc sĩ. Bác sĩ Huy Hoàng<br>&nbsp; <br>', 14, NULL, NULL, 1),
(51, 2, 'Cách chữa trị tàn nhang cho nam giới?', 'Chào bác sĩ. E năm nay 23 tuổi. Gần đây mặt em xuất hiện một số nốt màu nâu và đang có dấu hiệu lây lan ra khắp mặt. Nhìn qua mặt thường em thấy các nốt tách biệt nhau. Mong bác sĩ cho biết đó là bệnh gì và cách chữa trị dứt điểm. Em xin chân thành cảm ơn. ( Tuấn Anh &#8211; Hải Dương)<br>Nám ở nam giới<br> Trả lời:<br>Cảm ơn Tuấn Anh đã tin tưởng và gửi câu hỏi về chuyên mục hỏi đáp của Phòng khám da liễu Hà Nội. Tôi xin trả lời câu hỏi của bạn như sau:<br>Nhiều người nghĩ rằng nam giới không bị nám, tàn nhang nhưng thực tế không phải vậy. Mặc dù tỷ lệ nam giới bị tàn nhang ít hơn phụ nữ, song không phải không có. Dựa vào những dấu hiệu bạn chia sẻ, tôi nghi ngờ bạn đang bị tàn nhang. Để biết chính xác, bạn có thể qua trực tiếp phòng khám da liễu Hà Nội để được bác sĩ soi da miễn phí.<br>Về nguyên nhân gây ra tàn nhang ở nam giới không có quá nhiều khác biệt so với nữ giới. Có thể kể đến một số nguyên nhân như: Tác động của ánh nắng mặt trời, di truyền, thói quen sinh hoạt, chăm sóc da không đúng cách…. Cụ thể như sau:<br>&#8211; Đa phần số nam giới có tàn nhang là do di truyền từ bố mẹ, ngoài ra cũng có thể do màu da. Theo thống kê cho thấy, nam giới có làn da trắng thường dễ mắc tàn nhang hơn nam giới da đen. Tuy nhiên, tàn nhang ở nam giới thường tập trung thưa thớt và mờ nhạt hơn so với nữ giới.<br>&#8211; Ngoài ra, một yếu tố mà hầu như các đấng mày râu đều mắc phải đó là thói quen chăm sóc da không đúng hoặc không chăm sóc da. Bởi đàn ông thường không biết cách chăm sóc da như nữ giới.<br>&#8211; Tác động từ ánh nắng mặt trời do nam giới không bảo vệ da khi ra ngoài, không sử dụng kem trị tàn nhang, không dùng mỹ phẩm chống nắng, không dưỡng da thường xuyên.<br>Chữa trị tàn nhang ở nam giới tương đối khó vì đòi hỏi phải kiên trì trong thời gian dài, trong khi đó nam giới rất ít khi quan tâm đến chăm sóc da. Chính vì vậy, tôi khuyên bạn nên điều trị bằng công nghệ Laser NanoTech. Đây là công nghệ laser mới, với bước sóng phù hợp, các tia laser tác động sâu đến hạ bì đẩy các vết tàn nhang lên bề mặt da và biến mất sau đó. Công nghệ này giúp đem lại hiệu quả nhanh chóng, an toàn vì chỉ tác động đến các nốt tàn nhang mà không xâm lấn, gây tổn thương vùng da xung quanh. Laser NanoTech được xem là giải pháp hoàn hảo cho làn da bị nám, tàn nhang.<br>Dù là nam hay nữ, khi tàn nhang xuất hiện sẽ lây lan đến các vị trí khắp nhau và khiến cho da mặt trở nên khô ráp. Không những thế, tàn nhang càng để lâu càng khó chữa. Do vậy, bạn nên đến các cơ sở chữa trị nám, tàn nhang uy tín để khám và điều trị sớm để tránh biến chứng nguy hiểm.<br>Nếu cần tư vấn kĩ hơn, xin vui lòng liên hệ với chúng tôi:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI\n   \n   Hotline: 098.123.1234 <br>', 15, NULL, 9, 1),
(52, 4, 'Chữa mụn trứng cá ở vùng lưng bằng cách nào?', 'Chào bác sĩ. Mình bị rất nhiều mụn trứng cá ở lưng, da thì lúc nào cũng nhờn. Đã thữ đủ mọi cách (bôi và uống nhiều thuốc tây và bắc). Đi khám da liễu mấy chỗ lớn, tốn quá trời tiền, nhưng chỉ hết vài ngày lại lên lại. Mình đã rất buồn và tuyệt vọng. Mình vô tình đọc được bài viết của 1 bạn nói rằng đã chữa khỏi mụn lưng ở Phòng khám da liễu Hà Nội. Mong bác sĩ tư vấn giúp mình phương pháp và thời gian điều trị. Cảm ơn bác sĩ. ( Chị Đỗ Thúy – Đan Phượng, Hà Nội).<br>Trả lời:<br>Cảm ơn chị Thúy đã quan tâm đến dịch vụ và tin tưởng đội ngũ bác sĩ của Phòng khám da liễu Hà Nội.<br>Mụn trứng cá vùng lưng là một trong những loại trứng cá khó điều trị, tốn nhiều thời gian nhất. Mặc dù đều có nguyên nhân xuất phát từ việc rối loạn nội tiết tố, tuy nhiên mụn trứng cá ở lưng khó điều trị hơn mụn trứng cá ở các vùng da khác. Thông thường, lưng là vùng da ít được động tới, kể cả trong quá trình tắm rửa hàng ngày vì tay không thể với tới được, nên lỗ chân lông bị tắc, lớp chất sừng dày.<br>Mặt khác, trên cơ thể chúng ta, ngoài bộ phận mặt thì lưng là nơi phân giải chất nhờn và mồ hôi nhiều nhất, áo bẩn có dính mồ hôi lại thường xuyên cọ xát trên bề mặt da và nốt mụn trứng cá, khiến chúng bị viêm nhiễm nặng thêm và dễ dàng lan ra các vùng khác.<br>Nguyên nhân gây mụn ở lưng, ngực cũng giống như ở mặt, có thể do di truyền. Ngoài yếu tố di truyền, mụn ở lưng còn xảy ra do những nguyên nhân như: sự mất cân bằng nội tiết tố. Bên cạnh đó, trái ngược với mụn ở mặt, mụn trên người gây ra do đổ mồ hôi quá nhiều khi hoạt động hoặc tập thể dục.<br>Để điều trị dứt điểm và ngăn mụn quay trở lại, bạn nên đến trực tiếp Phòng khám để được bác sĩ khám và tìm ra căn nguyên gây bệnh. Vì trên thực tế, mỗi người sẽ có những nguyên nhân khác nhau.<br>Về phương pháp chữa trị mụn lưng, Phòng khám da liễu Hà Nội đã và đang áp dụng công nghệ ánh sáng E-light 3s. Đây là công nghệ hiện đại bậc nhất hiện nay, hỗ trợ điều trị mụn rất hiệu quả. Bên cạnh đó, nó còn giúp tiêu viêm nhanh chóng, không để lại thâm, sẹo sau điều trị mụn.<br>Kết quả sau 6 buổi điều trị mụn vùng lưng<br>Thời gian điều trị phụ thuộc vào tình trạng mụn, khả năng tái tạo da của từng bệnh nhân. Chính vì vậy bạn nên qua trực tiếp phòng khám để được bác sĩ xây dựng phác đồ điều trị phù hợp.<br>Nếu chưa thể qua thăm khám, bạn nên thực hiện một số nguyên tắc sau để mụn không bị biến chứng nặng nề. Trước hết, bạn nên tắm rửa vùng da bị mụn ít nhất hai lần một ngày với xà phòng và nước ấm và sử dụng salicylic acid, chất tẩy rửa này sẽ giúp bạn kháng khuẩn, đó là công thức đặc biệt để điều trị các vấn đề về mụn. Một trong những điều quan trọng trong công tác phòng và điều trị mụn trứng cá là bạn nên tắm càng sớm càng tốt sau khi hoạt động làm cơ thể toát mồ hôi. Không để mồ hôi ở lại trên da của bạn, vì nó có thể dẫn đến tắc nghẽn lỗ chân lông và gây ra nhiều mụn hơn. Tuy nhiên bạn cũng nên tránh việc tắm rửa làn da của bạn quá nhiều để ngăn chặn việc da bị khô. Điều cuối cùng là bạn không nên tự ý nặn mụn ở nhà vì sẽ khiến mụn bị viêm loét, thâm sẹo.<br>Nếu cần tư vấn kĩ hơn về dịch vụ trị mụn lưng, liên hệ với Phòng khám da liễu Hà Nội theo đường dây nóng: 098.135.9595<br> Cảm ơn và chúc bạn sớm khỏi bệnh !<br> Bác sĩ Huyền Trang <br>', 15, NULL, NULL, 1),
(53, 4, 'Mụn trứng cá sau dậy thì', 'Chào bác sĩ. Em trước đây dậy thì không hề bị mụn, nhưng thời gian gần đây mặt em nổi rất nhiều mụn trứng cá. Hiện tại em đang rất lo lắng và tự ti. Mong bác sĩ tư vấn giúp em cách điều trị. Em cảm ơn bác sĩ ! Ngọc Trúc (Đống Đa &#8211; Hà Nội)<br>Bị mụn ở độ tuổi dậy thì<br>Trả lời<br>Ngọc Trúc thân mến !\n  Cảm ơn bạn đã tin tưởng và gửi câu hỏi về hòm thư của Phòng khám da liễu Hà Nội.<br>Có rất nhiều nguyên nhân khác nhau gây ra mụn trứng cá, bao gồm cả nguyên nhân bên trong và bên ngoài. Có thể kể đến một số yếu tố sau:<br>&#8211; Sự thay đổi nội tiết tố: Giai đoạn dậy thì khiến cho cơ thể có nhiều thay đổi lớn, đây là thời kì cơ thể nhạy cảm nên rất dễ bị mụn. Tuy nhiên đây không phải là yếu tố duy nhất khiến cho bạn bị nổi mụn.<br>&#8211; Do sự tăng tiết tuyến bã, nhiễm khuẩn tuyến bã, chủ yếu do sự phát triển của vi khuẩn P.acnes.<br>&#8211; Mụn do dị ứng (viêm da dị ứng): Dị ứng mỹ phẩm do sử dụng sản phẩm kém chất lượng hoặc không phù hợp với da; dị ứng thực phẩm, dị ứng nguồn nước, dị ứng do thuốc…<br>&#8211; Mụn do sự tích tụ độc tố trong cơ thể: Khi gan, thận, ruột không thể lọc hết các độc tố từ thực phẩm thì chúng sẽ được bài tiết ra ngoài qua da.<br>&#8211; Các nguyên nhân bên ngoài như: Môi trường sống, nguồn nước bị ô nhiễm cũng khiến da dễ bị mụn.<br>Mặc dù có rất nhiều tiến bộ của y học và có rất nhiều phương pháp chữa mụn hiệu quả ra đời. Nhưng qua những gì bạn mô tả, tôi chưa thể kết luận tình trạng của bạn. Do vậy, để biết chính xác nguyên nhân gây mụn và phương pháp chữa trị bạn nên trực tiếp phòng khám để được bác sĩ thăm khám và soi da hoàn toàn miễn phí. Nếu không tiện qua, bạn có thể gọi điện đến đường dây nóng của phòng khám để trao đổi cụ thể và được tư vấn rõ hơn.<br>Bạn lưu ý không nên nặn mụn hoặc tự ý mua, bôi các loại thuốc  trị mụn khi chưa được bác sĩ khám và chỉ định để tránh làm cho bệnh nặng và khó chữa hơn.<br>Thông tin liên hệ:<br>PHÒNG KHÁM DA LIỄU HÀ NỘI – Nơi gửi trọn niềm tin\n    (Đối diện cổng chính ĐH Điện lực)\n   Hotline: 098.123.1234<br>Chúc bạn sớm khỏi bệnh ! <br>', 11, NULL, 6, 1),
(54, 2, 'Chữa nám bằng laser có ảnh hưởng sức khỏe không?', '&#8220;Chào bác sĩ. Em bị nám hỗn hợp đã lâu. Gần đây em được một người bạn giới thiệu công nghệ Laser NanoTech tại Phòng khám da liễu Hà Nội chữa nám rất hiệu quả. Em vẫn đang băn khoăn không biết có ảnh hưởng gì không? Mong bác sĩ tư vấn giúp em&#8221; &#8211; Quỳnh Châu 25 tuổi<br>Nám hỗn hợp<br>Trả lời:<br>Quỳnh Châu thân mến !<br>Cảm ơn bạn đã quan tâm đến các dịch vụ của Phòng khám da liễu Hà Nội. Tôi xin trả lời  như sau:<br>Nám hỗn hợp là loại nám được tích hợp nhiều loại nám da gây nên bao gồm có nám sâu và nám mảng. Loại nám này chịu tác động gây bệnh từ cả bên trong lẫn môi trường bên ngoài nên có đặc điểm ăn sâu trong lớp biểu bì da nên rất khó khăn trong việc điều trị. Những phương pháp tác động bên ngoài như dùng kem, mặt nạ trị nám khó có thể trị tận gốc nám hỗn hợp. Chính vì vậy, công nghệ Laser NanoTech là giải pháp phù hợp nhất với tình trạng nám của bạn.\n Laser NanoTech chỉ tác động trực tiếp vào các hắc sắc tố melanin gây nám để phá vỡ cấu trúc và đào thải hắc sắc tố ra ngoài cơ thể, hiệu quả không gây thương tổn nhiệt, không gây hại lớp thượng bì và các mô xung quanh nên tuyệt đối an toàn với sức khỏe. Trong quá trình điều trị,bạn sẽ không đau đớn mà chỉ có cảm giác châm chích trên bề mặt vùng da điều trị. Ngay sau điều trị, bạn hoàn toàn có thể trang điểm và không phải kiêng gì.\n Tôi xin khẳng định, điều trị bằng nám bằng công nghệ Laser NanoTech tại phòng khám da liễu Hà Nội không gây ảnh hưởng đến sức khỏe nên bạn có thể yên tâm nhé.<br>Nếu cần tư vấn về công nghệ Laser NanoTech, bạn có thể đến trực tiếp Phòng khám để được bác sĩ thăm khám và chỉ định phương pháp điều trị, liệu trình phù hợp nhất với mình.<br>Chúc bạn sớm khỏi bệnh ! <br>', 12, NULL, NULL, 1),
(55, 2, 'Da ít tiếp xúc với ánh nắng mặt trời tại sao vẫn bị nám?', '“Chào bác sĩ. Tôi năm nay 33 tuổi, hiện đang là nhân viên văn phòng. Da tôi rất ít tiếp xúc với ánh nắng mặt trời nhưng không hiểu sao vẫn bị nám. Xin bác sĩ tư vấn giúp tôi cách điều trị nám an toàn và hiệu quả nhanh. Tôi xin cảm ơn” ( Chị Phan Ngọc &#8211; Nghệ An )<br>Da dễ bị nám<br>Trả lời:<br>Cảm ơn chị Ngọc đã tin tưởng và gửi câu hỏi về chuyên mục hỏi đáp của Phòng khám da liễu Hà Nội.\n Chị Ngọc thân mến !<br>Có rất nhiều nguyên nhân gây ra nám, tàn nhang khác nhau, bao gồm yếu tố bên trong và bên ngoài. Có thể kể đến một số nguyên nhân sau:\n &#8211; Do sự thay đổi nội tiết tố\n Đa số phụ nữ ngoài 30 hoặc phụ nữ sau sinh dễ xuất hiện nám do sự thay đổi nội tiết tố estrogen. Độ tuổi này, da bắt đầu có những dấu hiệu lão hóa và biểu hiện là những vết nám, tàn nhang.\n &#8211; Chế độ dinh dưỡng, sinh hoạt\n Thức quá khuya, ăn uống không đủ chất khiến cơ thể mệt mỏi, tạo điều kiện thuận lợi để hắc sắc tố melanin lay lan nhanh.\n &#8211; Yếu tố di truyền\n Theo nhiều nghiên cứu, yếu tố di truyền là một trong số những nguyên nhân gây ra nám. Nếu trong gia đình chị có người bị nám, tàn nhang thì nguy cơ chị mắc bệnh sẽ cao hơn người bình thường.\n &#8211; Ánh nắng mặt trời, khói bụi là các tác nhân khiến cho tàn nhang lây lan nhanh, sậm màu hơn chứ không phải là nguyên nhân chính gây ra nám.<br>Chữa trị nám, tàn nhang có rất nhiều cách khác nhau, chị có thể dùng các biện pháp như sử dụng mỹ phẩm hay các biện pháp tự nhiên, tuy nhiên cách này chỉ tác động ở thượng bì, không thể can thiệp sâu tới chân nám, tàn nhang do đó chỉ có tác dụng làm mờ và hạn chế những vết tàn nhang mới xuất hiện, chứ không có tác dụng chữa trị hiệu quả.<br>Ngoài ra, chị cũng có thể dùng thuốc trị nám. Đối với tình trạng tăng sắc tố melanin, bản thân thuốc trị nám chỉ có tác dụng cân bằng nội tiết tố phụ nữ, làm giảm mức độ tăng sắc tố melanin, chỉ có tác dụng chữa nám, tàn nhang, đồi mồi tạm thời và phù hợp với người bị nám do nguyên nhân thay đổi hormone chứ không có tác dụng trị nám, tàn nhang, đồi mồi hiệu quả và hiệu quả. Ngoài ra chị em lưu ý nếu dùng thuốc trị nám trong thời gian dài có thể gây ra các biến chứng nguy hiểm như da lốm đốm, không đều màu, có thể gây ung<br>Thấu hiểu mong muốn làm đẹp của chị em phụ nữ, Phòng khám da liễu Hà Nội đã và đang áp dụng công nghệ Laser Nanotech. Đây là công nghệ trị nám độc quyền, chữa trị nám chuyên sâu. Với bước sóng phù hợp, các tia laser  tác động sâu đến thượng bì, giúp loại bỏ hiệu quả các hắc sắc tố melanin gây nám ở tầng trên, cô lập hệ thống hắc sắc tố melanin tại lớp đáy, cắt đứt và đánh bật chân nám ở sâu đồng thời ức chế sự hình thành melanin. Khác với công nghệ laser cũ, Laser NanoTech chỉ tác động đến vết nám, không gây thương tổn đến vùng da xung quanh. Hiệu quả trị nám bằng công nghệ Laser NanoTech có thể thấy rõ ngay sau buổi đầu tiên.<br>Hình ảnh bệnh nhân điều trị sạm da, nám đốm và tàn nhang<br>Để biết chính xác hơn, bạn có thể liên lạc với Phòng khám để được bác sĩ tư vấn cụ thể và hoàn toàn miễn phí.\n PHÒNG KHÁM DA LIỄU HÀ NỘI\n Số 447 Hoàng Quốc Việt\n  Hotline: 098.123.1234\n Chúc chị sớm khỏi bệnh !\n  Thạc sĩ. Bác sĩ Huy Hoàng <br>', 19, NULL, NULL, 1),
(56, 4, 'Chữa thâm sau mụn có để lại sẹo không?', 'Chào bác sĩ. Mặt tôi bị rất nhiều vết thâm do mụn để lại. Các cách chữa thủ công tôi đã thử nhưng dường như không có kết quả. Tôi nghe nói Phòng khám da liễu Hà Nội đang áp dụng phương pháp hiện đại chữa thâm do mụn rất hiệu quả. Không biết đó là phương pháp gì và có để lại sẹo không ? Mong bác sĩ tư vấn giúp tôi quy trình và chi phí. (Hồng Phương – Lạng Sơn)<br> Trả lời:<br>Hồng Phương thân mến !\n  Cảm ơn bạn đã quan tâm đến dịch vụ và tin tưởng các bác sĩ của chúng tôi. Tôi xin giải đáp  câu hỏi của bạn như sau:<br>Trước khi chữa trị vết thâm, bạn cần phải hiểu vết thâm là gì và nguyên nhân do đâu? Vết thâm là những vết đen, sậm xuất hiện trên da do tổn thương vùng mặt, thói quen nặn mụn hoặc sử dụng mỹ phẩm gây kích ứng cho da hoặc bị tai nạn… Hiện nay những tình trạng bị thâm trên mặt là do tình trạng nặn mụn gây ra, việc này làm tổn thương sâu đến vùng da tại nơi bị mụn, làm cho da bị thâm tím, kết cấu tế bào ở vùng này bị tổn thương nặng nề, việc nặn mụn quá đà còn gây ra hiện tượng sẹo lõm trên mặt.<br>Cách đây khoảng 2 – 3 năm, công nghệ trị vết thâm phổ biến sử dụng tia laser, ứng dụng nhiệt lượng để phá vỡ lớp sừng trên bề mặt da (vết thâm) khiến chúng bong ra. Tuy đem lại hiệu quả hơn so với các phương pháp truyền thống như đắp mặt nạ thiên nhiên nhưng chữa trị thâm bằng laser thường để lại sẹo trắng, do sự tác động mạnh của tia laser đến bề mặt da. Nhưng hiện nay, công nghệ này đã không còn được sử dụng nữa, thay vào đó là cách trị vết thâm bằng ánh sáng E-light 3s. Với công nghệ này, Phòng khám da liễu Hà Nội đã chữa trị thành công tình trạng thâm do mụn giúp làn da căng mịn, sáng đều màu. Cơ chế hoạt động của E-light 3s dựa vào năng lượng IPL chiếu trực tiếp vào vùng da cần điều trị, các tia sáng hấp thụ rồi phá vỡ thành nhiều mảnh nhỏ li ti để hệ thống thực bào đào thải ra khỏi cơ thể theo tuyến bài tiết dưới da giúp bảo vệ cấu trúc da, nên sẽ không để lại sẹo trên da.<br>Hình ảnh khách hàng đang được điều trị bằng công nghệ E-light 3s<br>Mặc dù hiệu quả, song E-light 3s không phải là công nghệ thần kỳ có thể xóa bỏ vết thâm do mụn trong 1 lần điều trị mà cần phải có liệu trình phù hợp với tình trạng, cơ địa của từng người. Vì bạn không nói rõ tình trạng vết thâm của bạn ở mức độ nào nên chi phí cũng như liệu trình tôi không thể tư vấn chính xác cho bạn được. Tuy nhiên, bạn có thể đến trực Phòng khám da liễu Hà Nội để được bác sĩ soi da, khám và tư vấn về chi phí, liệu trình cũng như phương pháp chữa trị phù hợp.<br>Hy vọng những chia sẻ trên đã phần nào giúp bạn dễ dàng hơn trong việc lựa chọn phương pháp điều trị. Cảm ơn bạn và chúc bạn sớm sở hữu làn da trắng hồng không vết thâm nhé!\n Thạc sĩ. Bác sĩ Huy Hoàng <br>', 15, NULL, NULL, 1),
(57, 4, 'Nguyên nhân và cách chữa trị mụn ở lưng?', 'Em bị mụn lưng đã hơn 3 năm rồi, em cũng chưa từng sử dụng biện pháp chữa trị gì, mà cứ để nó tự xuất hiện rồi tự lặn xuống. Ban đầu mụn chỉ có vài nốt nhỏ, nhưng càng ngày số lượng lại tăng dần lên, mụn sưng to gây đau đớn và để lại rất nhiều vết thâm. Điều này khiến em không dám mặc áo hai dây, áo hở lưng và luôn lo sợ người khác thấy khuyết điểm của mình. Em muốn nhờ chuyên mục Hỏi-Đáp giúp em nguyên nhân và cách trị mụn ở lưng tận gốc.. Em xin cảm ơn nhiều! (Thanhmai… @gmail.com)<br>Trả lời:<br>Cảm ơn bạn đã tin tưởng và gửi câu hỏi cho các bác sĩ của chúng tôi. Đối với tình trạng của bạn tôi xin trả lời như sau:\n Nhiều người khi bị mụn ở lưng mặc định là do nóng trong gây ra. Tuy nhiên, có rất nhiều nguyên nhân khiến mụn nổi ở lưng, có thể kể đến một số nguyên nhân như:\n &#8211; Do sự thay đổi nội tiết tố vào các giai đoạn nhất định: Trước và sau kỳ kinh nguyệt, trước – trong – sau khi sinh, dậy thì…\n &#8211; Cơ thể vận động quá nhiều làm tăng tiết mồ hôi, kết hợp với áo bó sát khiến lỗ chân lông bị tắc nghẽn gây ra mụn.\n &#8211; Cơ thể phản ứng lại với các chất hóa học có trong sữa tắm, các loại sữa dưỡng thể, phấn trang điểm…\n &#8211; Vệ sinh sạch sẽ vùng lưng không sạch sẽ\n &#8211; Chế độ ăn uống, nghỉ ngơi thiếu khoa học\n &#8211; Nóng gan, chức năng gan bị suy yếu\n Do đó, để điều trị dứt điểm bạn cần đến gặp bác sĩ để khám và tìm ra nguyên nhân gây bệnh từ đó có phương pháp điều trị hợp lý. Điều trị mụn ở lưng không có quá nhiều khác biệt so với mụn ở mặt. Bạn có thể tham khảo một số cách chữa trị dưới đây:\n &#8211; Sử dụng nguyên liệu thiên nhiên\n Đây là phương pháp thực hiện với chi phí thấp, nguyên liệu dễ kiếm và có thể thực hiện tại nhà. Tuy nhiên hiệu quả mang lại rất thấp, không triệt để và không thể áp dụng với tất cả loại da.\n &#8211; Dùng thuốc bôi và uống\n Sử dụng thuốc có thể đem lại hiệu quả nhanh hơn với điều kiện đơn thuốc được bác sĩ chỉ định. Bệnh nhân tuyệt đối không tự ý uống, bôi thuốc vì dễ gây dị ứng và khiến tình trạng mụn nặng hơn. Hơn nữa, phương pháp này còn tiềm ẩn nguy cơ bào mòn da. Vì vậy, bạn nên tham khảo ý kiến của bác sĩ trước khi quyết định.\n &#8211; Chữa trị bằng công nghệ ánh sáng E-light 3s\n E-light 3s là công nghệ hiện đại bậc nhất hiện nay, duy nhất tại Phòng khám da liễu Hà Nội. Cơ chế hoạt động dựa vào năng lượng IPL chiếu trực tiếp vào vùng da cần điều trị, phá tan màng bọc bên ngoài nhân mụn, làm teo biến nhân mụn một cách nhanh chóng mà không hề gây tổn thương cho da. Tăng sức đề kháng cho làn da và ngăn chặn tình trạng viêm nhiễm, lây lan. Mụn được loại bỏ một cách hiệu quả và an toàn. Đây được xem là sự đột phá trong điều trị mụn ở mặt, lưng.\n Những cách chữa trị kể trên chỉ mang tính chất tham khảo, để biết chính xác bạn nên đến các cơ sở y tế uy tín để thăm khám bác sĩ và có cách chữa trị kịp thời, hiệu quả và an toàn.\n Chúc bạn sớm khỏi bệnh !<br>Bác sĩ Huyền Trang <br>', 15, NULL, NULL, 1),
(58, 4, 'Chữa mụn viêm, mụn ẩn bằng cách nào?', '“E đang bị mụn viêm, mụn ẩn rất nhiều. E có dùng 1 vài loại mỹ phẩm trị mụn nhưng không hiệu quả ạ. E muốn hỏi về  phương pháp và liệu trình trị mụn tại phòng khám. Mong bác sĩ tư vấn giúp em”- Phương Thủy<br> Trả lời:<br>Phương Thủy thân mến. Cảm ơn bạn đã tin tưởng các bác sĩ tại phòng khám da liễu Hà Nội. Tôi xin trả lời câu hỏi của bạn như sau:\n Mụn ẩn là một loại mụn thường mọc thành từng cụm ẩn sâu dưới da gây ra tình trạng sần sùi trên bề mặt. Mụn chỉ hơi nhô lên khỏi bề mặt da gây sần sùi, thậm chí không thể nhận ra bằng mắt mà chỉ có thể thấy được khi dùng tay tiếp xúc với da mặt và cảm nhận bằng xúc giác. Rất có thể sản phẩm bạn đang dùng không phù hợp dẫn tới mụn viêm rất khó điều trị dứt điểm và khiến lỗ chân lông to hơn, khả năng để lại sẹo thâm, vết thâm trên da rất cao. Vì vậy, bạn nên ngưng sử dụng sản phẩm và tìm giải pháp hiệu quả hơn.\n Để chữa trị mụn ẩn trước hết cần phải lấy hết nhân mụn. Thế nhưng mụn ẩn dưới da không thể tự đào thải ra ngoài và biến mất nếu không có sự hỗ trợ của các sản phẩm và phương pháp lấy nhân mụn ẩn dưới da. Phòng khám da liễu Hà Nội áp dụng phương pháp lấy mụn chuẩn y khoa, do bác sĩ da liễu trực tiếp điều trị. Bên cạnh đó, các dụng cụ chuyên dụng được vô trùng bằng máy hấp trước khi đưa vào sử dụng để tránh nhiễm khuẩn trong quá trình điều trị.\n Tuy nhiên, đối với tình trạng vừa mụn ẩn vừa mụn viêm như của Thủy, chúng tôi sẽ kết hợp điều trị bằng công nghệ ánh sáng Elight 3s. Dựa vào năng lượng IPL, ánh sáng sẽ chiếu trực tiếp vào vùng da cần điều trị, phá tan màng bọc bên ngoài nhân mụn, làm teo biến hoàn toàn nhân mụn mà không hề gây tổn thương cho da, không để lại thâm sẹo sau mụn. Đồng thời, tiêu diệt hoàn toàn vi khuẩn còn trú ngụ trong các nang lông.\n Cho dù bỏ ra nhiều chi phí, nhiều thời gian và công sức để điều trị tại các spa lớn nhưng nếu không điều trị đúng cách thì mụn ẩn vẫn không thể giải quyết được triệt để, hoặc nếu có khỏi cũng chỉ là tạm thời và dễ quay trở lại. Để biết chính xác phương pháp và liệu trình điều trị , bạn nên đến trực tiếp phòng khám để được các bác sĩ thăm khám và tư vấn hoàn toàn miễn phí.\n Chúc bạn sớm khỏi bệnh !\n  Thạc sĩ. Bác sĩ Mai Dung <br>', 15, NULL, NULL, 1),
(59, 4, 'Quy trình và chi phí chữa trị mụn ẩn?', 'Bạn Đặng Thị Xuân (Hòa Bình) gửi câu hỏi vào hòm thư của phòng khám với nội dung “Chào bác sĩ. Em năm nay 20 tuổi. Gần đây da e nổi rất nhiều mụn ẩn mặc dù e không dùng bất cứ loại mỹ phẩm nào. Bác sĩ tư vấn giúp em 1 liệu trình trị mụn ẩn thì gồm những bước nào và chi phí bao nhiêu? Em cảm ơn ạ.”<br> Trả lời:<br>Bạn Xuân Thân mến !\n Theo những gì bạn chia sẻ, có thể loại bỏ nguyên nhân gây mụn do dùng mỹ phẩm. Rất có thể do da bạn phải tiếp xúc với môi trường nhiều bụi bẩn kết hợp với thói quen sinh hoạt không khoa học khiến da bạn bị mụn ẩn. Tuy nhiên, để biết chính xác tình trạng và liệu trình điều trị, bạn nên đến gặp bác sĩ để được soi da và tư vấn. Sau khi thăm khám bác sĩ sẽ xây dựng phác đồ điều trị phù hợp với từng bệnh nhân.\n QUY TRÌNH:\n Đối với tình trạng mụn ẩn, Phòng khám da liễu Hà Nội điều trị bằng phương pháp lấy mụn chuẩn y khoa bao gồm các bước sau:\n Bước 1: Bác sĩ thăm khám, lên phác đồ điều trị phù hợp\n  Bước 2: Chuẩn bị dụng cụ lấy nhân mụn đã được vô trùng\n  Bước 3: Làm sạch da bằng sữa rửa mặt Papulex\n Lí do Phòng Khám Da liễu Hà Nội lựa chọn sữa rửa mặt Papulex vì đây là loại sữa rửa mặt thích hợp với mọi loại da mụn, đồng thời nhẹ nhàng làm sạch da với công thức không chứa xà phòng; giúp làm giảm sự xuất hiện của mụn trứng cá, duy trì cảm giác tươi mát và dễ chịu cho da.\n Bước 4: Xông hơi, hút tạp chất\n  Bước 5: Sát khuẩn bề mặt da\n Sát khuẩn là bước vô cùng quan trọng, vì khi các lỗ chân lông giãn nở, giúp cho việc lấy mụn dễ dàng nhưng đồng thời là điều kiện để các vi khuẩn chui theo lỗ chân lông vào da. Do vậy, bước sát khuẩn giúp tiêu diệt các vi khuẩn, bảo vệ da.\n Bước 6: Lấy nhân mụn\n  Bước 7: Sát khuẩn bề mặt da\n  Bước 8: Thoa thuốc kháng sinh\n Đối với từng bệnh nhân sẽ có những nguyên nhân gây mụn khác nhau. Trên cơ sở đó, các bác sĩ sẽ chỉ định loại kháng sinh phù hợp với da.\n Bước 9: Lắng nghe lời khuyên từ Bác sĩ\n Trong quá trình trị liệu mụn, bệnh nhân phải đặc biệt tuân thủ đúng chỉ định của Bác sĩ để đảm bảo hiệu quả. Khuyến cáo bệnh nhân không nên tự nặn mụn vì sẽ gây ra nhiều hậu quả nghiêm trọng.\n BẢNG GIÁ DỊCH VỤ LẤY NHÂN MỤN Y KHOA\n • ƯU ĐÃI 75% dành cho HSSV (có thẻ HSSV) chỉ còn: 75,000 vnđ/lần\n • ƯU ĐÃI 50% dành cho đối tượng khác chỉ còn: 150,000 vnđ/lần<br>Để tránh những biến chứng của mụn, bạn nên đến gặp bác sĩ sớm để bác sĩ <br>', 15, NULL, NULL, 1),
(60, 2, 'Vì sao nên lựa chọn Laser NanoTech để chữa tàn nhang?', '“Chào bác sĩ. E năm nay 22 tuổi, mặt em bị tàn nhang khoảng 4 năm rồi. Em đang phân vân giữa phương pháp đốt laser truyền thống và phương pháp Laser NanoTech của Phòng khám. Mong bác sĩ tư vấn giúp em” .<br> Trả lời:<br> Chào bạn, cảm ơn bạn đã quan tâm đến dịch vụ và tin tưởng các bác sĩ của chúng tôi. Tôi sẽ trả lời câu hỏi của bạn như sau:\n Tàn nhang xuất hiện và có biểu hiện nhận biết khá dễ dàng. Do không ảnh hưởng đến sức khỏe nên nhiều chị em vẫn chần chừ trong việc trị liệu. Thế nhưng, tàn nhang để lâu ngày sẽ gây ra nhiều vấn đề đáng lo ngại cho da.\n Tàn nhang để lâu rất khó chữa, khiến da bị khô và không thể chữa khỏi bằng các phương pháp thủ công như đắp mặt nạ, bôi kem hay đốt laser truyền thống…Đối với phương pháp đốt laser truyền thống, nám, tàn nhang phần nào được xóa bỏ những năng lượng laser lớn làm tăng nguy cơ tổn thương da. Trên thực tế nhiều người do lựa chọn đốt laser trị tàn nhang mà gặp phải tình trạng xâm lấn, bỏng rát. Năng lượng laser lạc hậu quá mạnh có thể khiến da bị tổn thương, trở nên nhạy cảm, dễ bị phồng rộp khi tiếp xúc với ánh sáng mặt trời. Tàn nhang cũng dễ tái phát trở lại sau khi đốt laser khiến bạn phải thực hiện đốt tàn nhang trong nhiều lần. Vì vậy đối với việc điều trị những bệnh lý về da cứng đầu như tàn nhang đặc biệt là tàn nhang trên mặt thì việc lựa chọn phương pháp điều trị phù hợp luôn là bước quan trọng đầu tiên quyết định tới 80% tỉ lệ thành công của bạn.\n Laser NanoTech được xem là sự đột phá trong chữa trị tàn nhang. Đây là công nghệ công nghệ không xâm lấn, tức là chỉ tác động đến vùng da bị nám mà không gây ra bất cứ thương tổn nào cho vùng da xung quanh. Các tia laser sẽ tác động sâu đến hạ bì, chính xác từng vết nám và sẽ đẩy các vết nám lên bề da và bong tróc sau 3-5 ngày. Mặc dù chi phí đắt hơn công nghệ đốt laser nhưng hiệu quả lại cao hơn gấp nhiều lần. Do đó, tôi khuyên bạn nên chữa trị bằng công nghệ Laser NanoTech để đảm bảo an toàn và đem lại kết quả nhanh chóng.\n Tại Phòng khám da liễu Hà Nội, công nghệ Laser NanoTech đang được ứng dụng trong điều trị nám, tàn nhang và chữa trị thành công cho hàng trăm bệnh nhân. Để biết chính xác, bạn nên đến trực tiếp để bác sĩ khám và xây dựng phác đồ điều trị phù hợp.\n Chúc bạn nhanh khỏi bệnh !<br>Thạc sĩ. Bác sĩ Mai Dung <br>', 15, NULL, NULL, 1),
(61, 1, 'Cách phục hồi da nhiễm Corticoid', '“Chào bác sĩ. Tháng trước em có sử dụng kem dưỡng da mặt, 2 tuần gần đây e ngưng thì da mặt bị dị ứng, nổi rất nhiều mụn, có cách nào để chữa khỏi không ạ ?” &#8211; Bạn gái có nick name Hoa Đá.<br>Trả lời: <br>Xin chào bạn Hoa Đá. Cảm ơn bạn đã  gửi câu hỏi về hòm thư của Phòng khám da liễu Hà Nội. \n Theo những gì bạn mô tả, tôi nghi ngờ da bạn bị bị dị ứng do sử dụng chứa nhiều corticoid. Thời gian đầu dùng sản phẩm chứa corticoid bạn cảm thấy da mình giảm mụn hẳn, vết thâm mờ dần đi nên cho rằng sản phẩm này đem lại hiệu quả và lệ thuộc vào nó. Khi bạn ngưng thì sẽ phát mụn lại, gây thương tổn cho da. Dù bạn thử nhiều loại sản phẩm sau đó nhưng da sẽ nhạy cảm hơn đặc biệt cảm giác châm chích và mẩn ngứa thường xuyên xảy ra.\n Để khắc phục tình trạng trên, trước hết bạn cần phải đến gặp bác sĩ để khám và bác sĩ sẽ có những lời khuyên cụ thể, đúng với tình trạng thương tổn trên da của bạn. Bên cạnh đó bạn cũng nên có những cách chăm sóc da khoa học để tránh sự biến chứng nguy hiểm.\n &#8211; Luôn giữ cho da sạch sẽ, khô thoáng\n Khi mặt bị nổi quá nhiều mụn, bạn không nên rửa mặt quá nhiều lần trong ngày thay vào đó là nên bảo vệ da để hạn chế tiếp xúc với các tác nhân gây hại từ bên ngoài. Nếu da khỏe, bạn vẫn có thể sử dụng sữa rửa mặt, nhưng cần tránh các loại sữa rửa mặt có tác dụng làm sạch sâu.\n Bạn cũng có thể dùng nước trà xanh để rửa mặt, giúp cho da được thanh lọc từ bên trong, hơn nữa trà xanh có tác dụng điều trị mụn, kháng khuẩn và thải độc rất tốt.\n &#8211; Không sử dụng thuốc trị mụn\n Tôi khuyên bạn không nên tự ý ử dụng thuốc trị mụn, bởi vì làn da bị nhiễm corticoid đang rất yếu trong khi đó các loại thuốc trị mụn hay tinh dầu lại hoạt động khá mạnh nên có thể khiến làn da mỏng manh không chịu được, gây rát hoặc đỏ.\n &#8211; Đến gặp bác sĩ để khám và điều trị\n Nhiễm corticoid gây dị ứng da, không chỉ ảnh hưởng đến thẩm mỹ mà còn nguy hiểm đến sức khỏe. Với công nghệ hiện đại cùng đội ngũ bác sĩ chuyên khoa da liễu kinh nghiệm,  Phòng khám da liễu Hà Nội đã điều trị thành công cho hàng trăm bệnh nhân. Chính vì vậy, bạn nên đến gặp bác sĩ để được khám và tư vấn cách điều trị hiệu quả.\n Chúc bạn sớm khỏi bệnh !\n  Thạc sĩ. Bác sĩ Mai Dung <br>', 15, NULL, NULL, 1),
(62, 4, 'Điều trị  sẹo lõm do mụn ở nam giới bằng cách nào?', '“Chào bác sĩ. Trước đây mặt em bị mụn, mặc dù bây giờ đã khỏi nhưng lại có rất nhiều sẹo lõm. 2 năm qua em đã thử nhiều cách trong đó cả phương pháp vi kim nhưng vẫn không đỡ. Bác sĩ cho em hỏi, ngoài vi kim còn phương pháp nào điều trị sẹo lõm hiệu quả và thời gian ngắn không ạ ? Em cảm ơn ạ.”<br>Trả lời:<br>Cảm ơn bạn Thành Đạt đã quan tâm đến dịch vụ của Phòng khám và tin tưởng sự tư vấn của các bác sĩ. Tôi xin trả lời câu hỏi của bạn:\n Các phương pháp như laser, vi kim…không thể điều trị dứt điểm tình trạng sẹo lõm lâu năm. Hơn nữa, làn da mỏng, thiếu colagen hoặc vẫn đang bị viêm do mụn thì phương pháp này sẽ làm cho sẹo bị nặng hơn. Đây có thể là nguyên nhân bạn cảm thấy không hiệu quả khi chữa trị bằng phương pháp vi kim.\n Đối với những trường hợp sẹo lõm trên 2 năm như của Đạt, thành sẹo cao nên rất khó để phá vỡ. Tuy nhiên, với sự phát triển mạnh mẽ của khoa học kỹ thuật, đặc biệt là trong ngành y học Phòng khám da liễu Hà Nội đang áp dụng phương pháp trị sẹo lõm bằng công nghệ cấy sẹo sinh học Mesotherapy Mix và đem lại hiệu quả nhanh chóng, đảm bảo sự an toàn cho người bệnh.\n Mesotherapy Mix là phương pháp đưa hoạt chất sinh học vào đúng những điểm sẹo cần điều trị, hoạt chất sinh học sẽ được nuôi cấy tự động trên mô da, kích thích các tế bào da sản sinh tế bào mới để lấp đầy vết sẹo nhanh chóng.. Không chỉ làm đầy sẹo, các hoạt chất sinh học còn giúp thúc đẩy quá trình tăng sinh chuỗi liên kết collagen dưới da, giúp tái tạo lại các tế bào da. Hơn nữa Mesotherapy Mix chỉ tác động với điểm sẹo, không ảnh hưởng tới các mô da xung quanh nên không có hiện tượng thâm da như các phương pháp điều trị sẹo bằng Laser.\n Thông thường, đối với các biện pháp tự nhiên, bạn cần phải kiên trì thực hiện thường xuyên và tối thiểu phải từ 6 tháng đến 1 năm bạn mới cảm nhận được sự cải thiện của làn da. Chưa kể, bạn sẽ phải chuẩn bị và trải qua khá nhiều giai đoạn, chăm sóc rất cẩn thận… Với công nghệ cấy sẹo sinh học Mesotherapy Mix bạn sẽ được điều trị bằng công nghệ cao, được bác sĩ chuyên khoa da liễu trực tiếp khám và lên phác đồ điều trị phù hợp và bạn sẽ thấy hiệu quả ngay sau lần đầu tiên điều trị. Chỉ sau 3-5 buổi, sẹo được đẩy lùi nhanh chóng, không gây tác dụng phụ.\n Khác với phái nữ là nam giới ít dành thời gian để chăm sóc da mặt, vì vậy để đem lại hiệu quả nhanh chóng và tiết kiệm thời gian điều trị Đạt nên chữa trị bằng công nghệ Cấy sẹo sinh học Mesotherapy Mix.\n Chúc bạn sớm khỏi bệnh !<br>Thạc sĩ. Bác sĩ Mai Dung <br>', 15, NULL, 9, 1),
(63, 16, 'Làm thế nào để hết sẹo rỗ?', '&nbsp;<br>“Chào bác sĩ. Cháu là Trần Anh Thư, năm nay 17 tuổi. Trước đây mặt cháu bị rất nhiều mụn, bây giờ đã hết mụn nhưng để lại sẹo rỗ, cháu rất buồn và tự ti. Cho cháu hỏi là bây giờ cháu phải chữa trị như thế nào và thời gian bao lâu ạ? Cháu cảm ơn bác sĩ. ”\n Trả lời:\n  Anh Thư thân mến !\n Sẹo rỗ là tình trạng da bị tổn thương sau mụn, nguyên nhân có thể do bệnh nhân tự ý nặn mụn khiến da bị bị viêm loét, tạo thành sẹo hoặc do dùng mỹ phẩm kém chất lượng gây ra dị ứng.\n Để điều trị sẹo rỗ, có rất nhiều phương pháp. Hiện tại, phòng khám da liễu Hà Nội đang áp dụng 2 phương pháp là Phi kim và Cấy sẹo sinh học Mesotherapy Mix.\n &#8211; Đối với tình trạng sẹo rỗ do mụn để lại ở mức độ nhẹ, vừa mới xuất hiện cháu có thể chữa trị bằng phương pháp Phi kim để tiết kiệm chi phí.Với phương pháp này, các tế bào gốc sẽ được đưa vào trung bì thông qua các đầu vi kim để lấp đầy các vết lõm, tăng cường lượng collagen, thúc đẩy các nguyên bào sợi phục hồi là giải pháp hiệu quả làm đầy vết sẹo rỗ, tái tạo làn da mới trẻ khoẻ, căng mịn.\n -Trường hợp sẹo rỗ của cháu trên 1 năm, cháu nên chữa trị bằng công nghệ Mesotherapy Mix để đem lại hiệu quả nhanh chóng. Phương pháp này giúp đưa các hoạt chất sinh học vào đúng điểm sẹo cần điều trị, tạo khả năng thâm nhập tối ưu cho các thành phần dưỡng chất. Với Mesotherapy, cùng một lúc 2 tác động được thực hiện: Kích thích khả năng tự làm lành, tự phục hồi của vùng da được trị liệu đồng thời đưa các thành phần hoạt chất tái tạo vào da, giúp cho quá trình tăng sinh tự nhiên của các khối mô biểu bì, mô liên kết, kích thích lưu dẫn tĩnh mạch, tăng cường tuần hoàn trao đổi chất ngay trong da, giúp làm đầy và xóa bỏ hoàn toàn sẹo rỗ.<br>Nguyên tắc điều trị sẹo rỗ là phải lấy hết nhân mụn, do đó cháu cần chữa khỏi mụn hoàn toàn trước khi chữa sẹo.\n Để biết chính xác thời gian và liệu trình điều trị, cháu nên đến trực tiếp Phòng khám da liễu Hà Nội để được bác sĩ khám và xây dựng phác đồ trị liệu an toàn, hiệu quả. Không nên tự ý chữa trị, tránh làm cho bệnh nặng và khó chữa hơn.\n Chúc cháu sớm khỏi bệnh !<br>Thạc sĩ. Bác sĩ Huy Hoàng <br>', 11, NULL, NULL, 1),
(64, 2, 'Đâu là giải pháp cho phụ nữ bị nám sau sinh?', '&#8220;Chào bác sĩ. Tôi năm nay 32 tuổi. Sau khi sinh em bé thứ 2, mặt tôi xuất hiện nhiều đốm màu nâu, liên kết với nhau thành mảng. Xin bác sĩ cho biết nguyên nhân và cách chữa trị an toàn, hiệu quả ?&#8221; &#8211; Chị Liên (Tuyên Quang)<br>Trả lời: <br>Phòng khám da liễu Hà Nội đã nhận được câu hỏi và cảm ơn bạn Liên đã tin tưởng các bác sĩ của chúng tôi. Tôi xin trả lời câu hỏi của bạn như sau:\n Thông thường phụ nữ bước vào giai đoạn trung niên thường rất dễ bị nám tàn nhang. Đặc biệt là đối với phụ nữ sau sinh, hiện tượng nám da, tàn nhang càng được biểu hiện rõ rệt hơn.\n &#8211; Phụ nữ sau sinh dễ bị nám và tàn nhang, nguyên nhân do bị rối loạn nội tiết tố . Đồng thời, do sự lão hóa của cơ thể nên làm tăng sắc tố melanin trong da dẫn tới việc hình thành các vết nám trên da.\n &#8211; Ngoài ra, phụ nữ sau khi sinh cơ thể thường mệt mỏi, tâm lý có nhiều thay đổi thường rất dễ cáu gắt, căng thẳng dẫn đến stress. Sức khỏe giảm sút và dẫn đến có nguy cơ mắc bệnh trầm cảm và có thể gây xuất hiện nám, tàn nhang.\n &#8211; Quá trình sau lúc sinh con cơ thể người mẹ thường yếu hơn so với bình thường . Hệ thống miễn dịch của cơ thể cũng yếu đi, khả năng chống lại những tác nhân từ ngoài cũng giảm thiểu. Vì thế, để da tránh được sự tác động của tia UV khi đi nắng, cơ thể lập tức sản sinh melanin để tạo thành màng chắn bảo vệ cho da. Chính vì điều này đã tạo phải những vết nám da, tàn nhang đối với phụ nữ sau sinh.\n Phương pháp trị mụn bằng các loại mặt nạ thiên nhiên rất lâu sau mới đem lại kết quả (ít nhất 2 năm).  Các loại kem trị nám có thể đem lại hiệu quả nhanh hơn, nhưng lại tiềm ẩn nhiều nguy cơ khi thị trường mỹ phẩm ngày càng hỗn loạn.\n Để đem lại hiệu quả nhanh chóng, an toàn và ngăn chặn nám quay trở lại, bạn nên chữa trị bằng công nghệ Laser NanoTech. Đây là công nghệ trị nám, tàn nhang hiện đại bậc nhất hiện nay. Laser NanoTech là công nghệ không xâm lấn, tức là chỉ tác động đến vùng da bị nám mà không gây ra bất cứ thương tổn nào cho vùng da xung quanh. Các tia laser sẽ tác động sâu đến hạ bì, chính xác từng vết nám và sẽ đẩy các vết nám lên bề da và ra đi sau 3-5 ngày. Hơn nữa, công nghệ này giúp điều trị nám nhanh chóng, hiệu quả cao rất phù hợp với các “bà mẹ bỉm sữa” không có thời gian làm đẹp.Với phương pháp này sẽ giúp khách hàng loại bỏ hơn 90% tình trạng nám trên mặt, kể cả nám sâu hay nám hỗn hợp, hạn chế nguy cơ tái phát nám trở lại, đồng thời cải thiện sắc tố da, cho da dần đều màu, tươi trẻ tự nhiên.\n Trước khi chữa trị, bạn nên gặp bác sĩ để khám và lên phác đồ điều trị phù hợp. Phòng khám da liễu Hà Nội với đội ngũ bác sĩ chuyên khoa da liễu, thiết bị máy móc hiện đại sẽ là nơi lý tưởng giúp bạn xóa bỏ nỗi lo mang tên NÁM DA.<br>Chúc bạn sớm khỏi bệnh !<br>Thạc sĩ. Bác sĩ Mai Dung <br>', 15, NULL, 8, 1),
(65, 16, 'Cách xóa hình xăm không để lại sẹo?', 'Anh Nguyễn Bá Dũng, 35 tuổi gửi câu hỏi vào hòm thư của phòng khám với nội dung “Tôi có 1 xăm hình ở cổ, vì không còn phù hợp nên tôi muốn xóa bỏ. Bác sĩ tư vấn giúp tôi phương pháp xóa xăm an toàn, hiệu quả và không để lại sẹo? ”<br>Trả lời:<br>Chào bạn. Cảm ơn bạn đã tin tưởng và gửi câu hỏi cho các bác sĩ tại Phòng khám Da liễu Hà Nội. Tôi xin trả lời câu hỏi của bạn như sau: \n Xăm lên cơ thể là hình thức thay đổi sắc tố da bằng loại mực sinh học ghi dấu trên cơ thể với mong muốn làm đẹp, tạo ấn tượng. Do nhiều nguyên nhân khác nhau, hình xăm đó không còn phù hợp nên nhiều người mong muốn xóa bỏ nhưng lại không biết đâu là phương pháp an toàn, hiệu quả và đảm bảo tính thẩm mỹ.\n Trước đây có rất nhiều phương pháp xóa hình xăm như: xăm chồng lên hình xăm cũ, sử dụng axit hoặc dùng máy đốt điện. Các phương pháp này đem lại hiệu quả nhưng lại tiềm ẩn nhiều nguy hiểm vì có thể gây tổn thương vùng da xung quanh.\n Tại Phòng khám da liễu Hà Nội, chúng tôi đang áp dụng công nghệ Laser NanoTech &#8211; công nghệ xóa hình xăm hiện đại nhất hiện nay, thực hiện nguồn ánh sáng cảm ứng chiếu trực tiếp lên hình xăm làm phá vỡ sắc tố trong da, đào thải mực xăm ra ngoài. Các tia laser sẽ tác động vào thượng bì của da, tác động trực tiếp vào hình xăm, đẩy màu xăm lên trên bề mặt da và bong tróc sau 3-5 ngày mà không gây tổn thương xâm lấn. Hơn nữa, công nghệ Laser NanoTech không gây bỏng rát, không để lại sẹo. Chính vì vậy, bạn nên lựa chọn phương pháp này để xóa hình xăm.\n Như vậy, để xóa hình xăm, Công nghệ Laser NanoTech là sự lựa chọn tối ưu vì có thể xóa bỏ hình xăm mà không để lại sẹo, đảm bảo tính thẩm mỹ như mong muốn.\n Chúc bạn thành công !\n  Bác sĩ Huyền Trang <br>', 11, NULL, NULL, 1),
(66, 5, 'Da khô, bong tróc phải làm gì?', 'Bạn Hà An, 26 tuổi – “Chào bác sĩ, vào mùa Đông, da mặt em rất khô thậm chí bị bong tróc da, xin bác sĩ cho biết giải pháp để khắc phục tình trạng đó?”<br>Trả lời:<br>Chào Hà An, Phòng khám da liễu Hà Nội rất vui và cảm ơn sự tin tưởng của bạn. Đối với tình trạng da của Hà An, tôi sẽ chỉ ra một số giả thuyết về nguyên nhân và cách khắc phục như sau: \n Da khô là tình trạng chung của rất nhiều bạn nữ vào Đông. Nguyên nhân có thể do thời tiết hanh khô, cách chăm sóc da không khoa học hoặc do chế độ ăn uống không đủ chất dinh dưỡng…\n Để khắc phục tình trạng da khô, trước hết cần xác định nguyên nhân khiến cho da khô để có cách khắc phục kịp thời.\n &#8211; Nếu da khô do thời tiết, bạn nên sử dụng kem dưỡng ẩm để giúp cân bằng độ PH của da, giúp da mịn màng. Cần tìm hiểu kỹ trước khi sử dụng các loại mỹ phẩm.\n &#8211; Đối với tình trạng da khô do chăm sóc:\n + Khi rửa mặt bạn nên kết hợp dùng cả nước ấm và nước lạnh. Nước ấm có tác dụng làm lỗ chân lông giãn nở, giúp loại bỏ tạp chất dễ dàng hơn. Sau đó bạn rửa lại bằng nước lạnh để se khít lỗ chân lông.\n + Sửa rửa mặt cũng là yếu tố quan trọng, bạn nên dùng các loại sữa rửa mặt có công dụng làm mềm da, dưỡng ẩm để da không bị khô. Tránh dùng các sữa rửa mặt có chất làm sạch sâu.\n + Đắp các loại mặt nạ thiên nhiên để cung cấp vitamin, giúp da sáng mịn.\n + Khi đi ra ngoài, cần che chắn da cẩn thận bởi khói bụi, ánh nắng là tác nhân khiến do khô và xỉn màu. Bạn cũng nên sử dụng kem chống nắng để tránh sự tác động của tia UV.\n &#8211; Cung cấp đủ chất dinh dưỡng cho cơ thể bằng cách bổ sung protein và vitamin như rau xanh, hoa quả; uống ít nhất 2 lít nước mỗi ngày.\n Khoảng thời gian từ 21-23h là lúc cơ thể cần được nghỉ ngơi, bạn nên đi ngủ trong khoảng thời gian này. Nếu cần phải thức khuya vì lí do công việc, học tập bạn nên sử dụng kem dưỡng ẩm.\n &#8211; Ngoài ra, bạn không nên ăn các đồ ăn cay, nóng; không sử dụng chất kích thích như rượu, bia, thuốc lá…\n Để da hết khô, hãy áp dụng cách trên mỗi ngày. Chúc bạn thành công !<br>Thạc sĩ. Bác sĩ Huy Hoàng <br>', 15, NULL, NULL, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(67, 4, 'Phương pháp  nào trị thâm mụn an toàn, hiệu quả?', 'Bạn Nguyễn Lê Anh, 19 tuổi -“ Chào bác sĩ. Em bị mụn đầu đỏ và giờ có nhiều vết thâm lắm, em muốn mặt đỡ bị vết thâm sẹo đấy bác sĩ tư vấn giúp em cách chữa trị và thời gian điều trị bao lâu được không ạ ? ”<br>Trả lời:<br>Thân chào bạn Lê Anh, cảm ơn bạn đã tin tưởng và gửi câu hỏi về chuyên mục Hỏi – Đáp của Phòng khám da liễu Hà Nội. Tôi xin trả lời như sau:\n Có thể dễ dàng nhận ra ngày càng có nhiều phương pháp trị thâm từ thiên nhiên hay kem bôi trị thâm phải thực hiện trong khoảng thời gian dài nhưng hiệu quả lại không cao. Vì thế, nhiều người đã lựa chọn các công nghệ thẩm mỹ hiện đại, điều trị vết thâm một cách nhanh chóng và hiệu quả.\n Đối với tình trạng thâm của bạn Lê Anh, nguyên nhân chính là do mụn gây ra, vì vậy bạn nên điều trị bằng công nghệ ánh sáng IPL. Công nghệ này sẽ giúp hồi sinh và khôi phục chức năng của các sợi collagen, làm cho những vết thâm mờ đi một cách nhanh chóng và còn giúp các sẹo lõm cũng được cải thiện đáng kể.\n Với bước sóng phù hợp, công nghệ ánh sáng IPL có thể tác động đến tận lớp sâu của da, nơi các tế bào hắc tố gây ra vết thâm tập trung, loại bỏ hoàn toàn các vết thâm để giúp bạn có làn da sáng và mịn màng hơn. Tại Phòng khám Da liễu Hà Nội bằng việc ứng dụng công nghệ ánh sáng IPL vào trị vết thâm đã giúp cho việc điều trị được rút ngắn lại mà hiệu quả đem lại cao hơn bất kỳ phương pháp điều trị thông thường nào.\n Về liệu trình điều trị, bạn phải đến Phòng khám để khám sau đó bác sĩ sẽ lên phác đồ điều trị phù hợp với tình trạng thâm của bạn. Thông thường, trị thâm bằng Công nghệ ánh sáng IPL có liệu trình từ 3-5 buổi, mỗi buổi cách nhau khoảng 2-3 tuần.\n Tuy nhiên, nếu lựa chọn điều trị thâm mụn bằng Công nghệ IPL, bạn phải tuân thủ đúng các nguyên tắc trong và sau quá trình điều trị. Cụ thể:\n &#8211; Không được rửa mặt bằng nước nóng trong 5 ngày đầu tiên\n &#8211; Tránh sử dụng phấn trang điểm trong những ngày đầu sau điều trị vết thâm.\n &#8211; Kiêng đồ cay nóng, thực phẩm có tính kích thích cao như: hải sản, thịt dê… trong những ngày đầu vì khi đó, da đang trong giai đoạn bị tổn thương.\n &#8211; Có chế độ làm việc, ăn uống và nghỉ ngơi hợp lý nhằm tăng sức đề kháng cho cơ thể, chống vi khuẩn gây mụn và vết thâm\n Chúc bạn sớm khỏi bệnh !\n  Thạc sĩ. Bác sĩ Mai Dung <br>', 14, NULL, NULL, 1),
(68, 4, 'Chữa trị mụn bằng phương pháp vi kim đúng hay sai?', '“ Chào bác sĩ. E tên là Mai Hồng Nhung, năm nay 23 tuổi, thời gian gần đây mặt e nổi rất nhiều mụn mủ. Qua tìm hiểu e thấy ở một số Spa điều trị mụn bằng phương pháp vi kim khá hiệu quả, tại sao Phòng Khám lại không áp dụng phương pháp này ạ ? ”<br> Trả lời:<br>Phòng Khám Da liễu Hà Nội đã nhận được câu hỏi và rất cảm ơn bạn Hồng Nhung đã tin tưởng các bác sĩ của chúng tôi. Tôi xin trả lời như sau:\n Hiện nay, có rất nhiều phương pháp trị mụn hiện đại được các cơ sở y tế, thẩm mỹ viện áp dụng. Tuy nhiên, lựa chọn phương pháp  hiệu quả nhưng vẫn đảm bảo an toàn  là điều không hề dễ dàng. Bệnh nhân cần được bác sĩ khám để tìm ra nguyên nhân gây mụn, tình trạng mụn và có cách điều trị hợp lý.\n Phòng khám Da liễu Hà Nội đang áp dụng phương pháp vi kim (phi kim) kết hợp tế bào gốc để làm đầy sẹo lõm chứ không áp dụng trong điều trị mụn. Tôi không rõ Spa bạn đã tìm hiểu là cơ sở thẩm mỹ nào, nhưng nếu thật sự điều trị mụn đặc biệt là mụn mủ bằng phương pháp vi kim là HOÀN TOÀN SAI LẦM. Bởi vì, nguyên tắc điều trị mụn phải lấy hết nhân mụn và không được làm tổn thương da. Bên cạnh đó, bản chất của phương pháp vi kim dựa vào cơ chế tự làm lành vết thương của da, khi các vi kim tác động đến mặt da sẽ tạo ra thương tổn mới, kết hợp với tế bào gốc giúp tái tạo và làm đầy da. Chính vì vậy, nếu sử dụng phương pháp vi kim trong điều trị mụn, các đầu kim tác động đến mụn (có cả những mụn chưa chín nhân) sẽ làm mụn bị viêm loét và rất lâu khỏi.\n Phòng khám đã áp dụng và điều trị thành công cho hàng trăm bệnh nhân gặp phải các vấn đề da liễu bằng những phương pháp sau:<br>&#8211; Đối với những người bị mụn ẩn, vừa mới xuất hiện nên đi khám bác sĩ để điều trị sớm. Phương pháp đem lại hiệu quả, tiết kiệm thời gian và chi phí, được Phòng khám ưu tiên lựa chọn là phương pháp lấy nhân mụn chuẩn y khoa. \n &#8211; Với bệnh nhân mụn mủ, mụn viêm, mụn trứng cá tuyệt đối không được tự ý mua thuốc uống, thuốc bôi mà phải theo sự chỉ định của bác sĩ. Với công nghệ hiện đại và phác đồ trị liệu do chính bác sĩ chuyên khoa da liễu khám và xây dựng, Phòng khám da liễu Hà Nội chữa trị thành công cho rất nhiều bệnh nhân, giúp họ lấy lại diện mạo và sự tự tin.\n &#8211; Ngoài ra, bệnh nhân bị mụn nên kiêng ăn các đồ ăn cay, nóng; bổ sung vitamin và uống nước nhiều để thanh lọc cơ thể. Song song với đó là phải chăm sóc da mặt khoa học, không sử dụng mỹ phẩm kém chất lượng.<br>Các phương pháp chỉ có tính chất tham khảo, tôi khuyến cáo người bệnh nên đến gặp bác sĩ để được khám và chữa trị an toàn, hiệu quả. Chúc bạn lựa chọn đúng phương phương điều trị và sớm lấy lại làn da mịn màng.\n  Thạc sĩ. Bác sĩ Huy Hoàng <br>', 14, NULL, NULL, 1),
(69, 4, 'Kinh nghiệm và chia sẻ của một người bị mụn lâu năm', 'Năm nay tôi 25 tuổi, da dầu, đã bị mụn suốt thời cấp 3 và 1 thời gian khi lên đại học, rồi có thời kỳ mụn hết, da láng mịn, rồi đến khi sắp tốt nghiệp ra trường đi làm mụn lại lên, tàn phá trầm trọng. Có lẽ tôi đã phải chịu đủ khổ sở và những hậu quả về mặt tinh thần khi da bị mụn mang lại. Vậy nên tôi muốn chia sẻ kinh nghiệm của mình về việc đối phó và điều trị mụn, cũng như tâm sự 1 chút về những nỗi buồn của tôi với lan da mụn này.<br>Mụn trứng cá là vấn đề mà hầu như bất cứ ai cũng từng gặp trong cuộc đời, nhưng không phải ai cũng cần phải trị mụn. Và cũng rất nhiều người chủ quan khi mụn lên và nghĩ rằng ôi dào ôi nó sẽ hết ấy mà, để đến lúc nó trầm trọng và điều trị rất khó khăn.\n Vậy thì, hãy nghĩ đến việc điều trị mụn khi bạn cảm thấy nó bắt đầu làm bạn cảm thấy da mình xấu xí đi, và đơn giản là khi số lượng mụn cám mụn đầu đen chiếm khoáng 1/4 diện tích khuôn mặt bạn, hay mụn bọc mụn mủ xuất hiện từ 5 cái trở lên. Khi đó, bạn sẽ khó lòng mà kiểm soát được mụn đấy!<br>Tình trạng mụn<br>Có nhiều loại mụn nhưng phân vào 2 nhóm chính là mụn viêm và mụn không viêm:\n Mụn viêm gồm mụn bọc, mụn mủ. Là những loại mụn có quầng đỏ xung quanh, ấn đau, kích thước thường lớn, nếu nặn ra sẽ ra nhiều mủ trắng và máu. Mụn này thường để lại những hậu quả nghiêm trọng nếu không điều trị đúng cách, để lại những vết lỗ rỗ trênda, nhưng mụn này lại thường đáp ứng rất tốt với kháng sinh. Vì thế nếu dùng liệu trình điều trị bằng kháng sinh đúng cách, thường sẽ khỏi.\n Mụn không viêm gồm mụn đầu trắng, mụn đầu đen. Những mụn này thường chìm dưới da hoặc nhô li ti trên bề mặt da, có nhân cứng như hạt tấm. Mụn này không để lại những hậu quả nghiêm trọng trên da, nhưng lại dai dẳng và khó chữa hơn. Những phương pháp điều trị thì cuối cùng bạn vẫn phải lấy được nhân mụn lên, và ngăn không cho nó tiếp tục lan rộng.<br>Phân loại mụn trứng cá<br>&#8211; Có 1 lời khuyên chân thành là khi bạn đã cảm thấy mụn có nhiều ảnh hưởng đến làn da bạn, đừng nên chỉ tìm những phương pháp người nó truyền người kia, rồi bôi bôi trét trét đủ thứ lên mặt. Bác sỹ da liễu sẽ là người bạn nên đặt niềm tin. Các loại rau củ quả thảo mộc được mọi người truyền tai nhau, có lẽ cũng có tác dụng đấy, nhưng bạn nên nhớ 1 điều là, da mụn đã rất nhạy cảm và dễ lên mụn, những loại rau quả ấy có thành phần tốt nhưng cũng còn rất nhiều hoá chất độc hại cũng như những thành phần không tốt, đắp lên mặt sẽ chỉ làm tăng nguy cơ bị mụn thôi. Chúng chỉ hợp lý khi da bạn đã khoẻ và bạn muốn dưỡng da.\n &#8211; Ngừng dùng tất cả các loại mỹ phẩm và sữa rữa mặt là lời khuyên thứ 2. Không biết chúng có phải là tác nhân thực sự gây mụn cho bạn không, nhưng ít ra mình loại bỏ những loại hoá chất có &#8211; Khả &#8211; năng là nguyên nhân cũng khiến mình yên tâm hơn trong điều trị. Hãy trang bị 1 chiếc khăn bông mềm, rửa mặt bằng nước lã, lau thấm khô da bằng khăn, vừa đủ để láy đi lượng chất nhờn dư trên da ( khăn cần phải được tiệt trùng bằng nước sôi).\n &#8211; Bổ sung vitamin cho cơ thể . Rau củ quả tự nhiên là những điều không thể thiếu, và khi bị mụn bạn càng nên ăn nhiều hơn. Hãy ăn tất cả các loại rau củ quả mà bạn thích và có thể ăn được với lượng lớn nhất trong ngày. Ngoài ra, cần bổ sung các vitamin sau băng đường uống : vtamin A, B2, E, H, và viên kẽm.\n &#8211; Thanh lọc gan thận bằng 1 số loại rau quả đã có tác dụng kiểm chứng: Hãy cố gắng uống sinh tố rau diếp cá mỗi ngày. Rau diếp cá sẽ mang đến cho bạn 1 tác dụng thần kỳ đấy. Hoặc ít nhất nó cũng có tác dụng bổ trợ và ngăn ngừa sự hình thành tiếp tục của mụn.\n &#8211; Hãy tìm 1 môn thể thao để tập luyện. Chạy, cầu lông, aerobic&#8230; bất cứ 1 môn gì bạn có thể dành 1 ngày 1 tiếng để tập luyện và ra mồ hôi. Bạn sẽ không thể cảm nhận được ngay tác dụng của nó đâu. Nhưng chắc chắn là có đấy, làn da bạn sẽ được cải thiện dần dần, tươi sáng hơn.\n &#8211; Đừng quan tâm quá mức đến mụn, đừng nghĩ đến sự hiện diện của nó trên mặt bạn quá nhiều. Hạn chế dùng gương hết mức có thể trong thời gian đang bị mụn. Hãy cứ vui tươi nhưng khi bạn không có mụn đi, rồi bạn cũng sẽ thấy nó qua nhanh thôi\n Đây là những lời khuyên đúc rút từ kinh nghiệm của chính bản thân mình chứ không phải copy paste trên mạng đâu. Cách đây 2 năm mình đã từng bị mụn rất trầm trọng và dai dẳng, cũng lên mạng tìm hiểu và thử đủ phương pháp, rồi cách đây 6 tháng mụn cũng hết da mình láng mịn trở lại và mình muốn lên share với các bạn những gì mình thấy là đúng đắn và nên làm nhất. Bởi mình hiểu, khi bạn đang đọc những dòng này. Ban cũng đang rất buồn và hoang mang đúng không?<br>Trị mụn theo phác đồ của Bác sĩ<br>Mình muốn tâm sự 1 chút về câu chuyện của cuộc đời mình, không biết có được không?\n Khi học đại học, da mình đã hết mụn và rất tươi sáng mịn màng. Mình đã rất tự hào về làn da mình lúc đó. Và trong thời gian đó, mình cũng đã có 1 tình yêu rất đẹp. Rồi tự nhiên, mụn bùng phát. Chắc cũng không phải tự nhiên vì thời gian đó mình cũng gặp rất nhiều căng thẳng trong cuộc sống công việc tình yêu. Chỉ trong 1 thời gian ngắn da mình bị tàn phá nặng nề, trông nó đen đen, xin màu, và trông như kiểu bị lão hoá. Vấn đề của mình là mụn cám và mụn đầu đen, mụn mủ cũng có nhưng không nhiều. Nhưng mụn cám và mụn đầu đen thì quá nhiều, và quá nghiêm trọng. Đã có rất nhiều người nói với mình&#8221; trông nhan sắc trước 10 giờ chắc chỉ còn 1&#8243; . Và mình đã vô cùng tự ti. Người yêu mình vẫn ở bên mình thôi, vẫn yêu thương và dường như ko để ý đến vấn đề đấy. Nhưng bạn thử tưởng tượng xem. Chỉ trong 1 thời gian ngắn, mình đang có 1 làn da mịn màng không tì vết rồi bất chợt nó xuống cấp trầm trọng như bà già, bản thân mình còn không thể chấp nhận nổi. Những cãi vã nổ ra, mà phần lớn do mình, do mình nghĩ anh ấy sẽ không còn yêu thương mình như trước kia nữa, và dù ít dù nhiều thì con trai yêu bằng mắt mà, bạn có thể không xinh đẹp nhưng ít ra 1 làn da không mụn cũng có nhiều thiện cảm hơn da lỗ chỗ rổ rá. Rồi bọn mình chia tay, sau tất cả những xung đột cãi vã, mình nhận ra rằng nếu không có mụn, nếu mình ko trở nên xấu xí và kém tự tin đến thế, những cãi vã đấy có thể đã không xảy ra. Chia tay mối tình 3 năm, mình đã rất suy sụp, rồi khi đã hơi lấy lại tinh thần, mình quyết tâm điều trị tích cực bằng tất cả những kinh nghiệm mình đã chia sẻ ở trên. Sau 4 tháng, mụn hết. Da mình dần hồi phục, tuy chưa được như xưa nhưng cũng đc 8 9 phần. Lấy lại được niềm vui trong cuộc sống, mình lại bắt đầu 1 tình yêu mới, vui vẻ yêu đời, đi đâu cũng nhận được những lời khen về nhan sắc dạo này tươi tắn hơn, xinh hơn. Rất vui. Nhưng rồi&#8230; cách đây 2 tháng, mụn lại lên, và laị trầm trọng như trước, thậm chí còn hơn trước. Tự ti tự ti kinh khủng. Người yêu mình hiện tại là 1 chàng trai thành đạt, học thức và ngoại hình đều có, mình không xinh đẹp nhưng mình cũng biết ăn mặc và tóc tai make up chút nên cũng khá hợp với ng yêu. Nhưng khi mụn lại lên, nhan sắc mình lại 1 lần nữa bị huỷ hoại trầm trọng. Mình đã rất buồn, và khóc rất nhiều. Và tất nhiên thời gian đầu ng yêu mình cũng an ủi và nói rằng đừng quan tâm đến nó, anh yêu em đâu phải vì ngoại hình của em đâu. Nhưng đúng.. con trai yêu bằng mắt mà. Không có người phụ nữ xấu chỉ có người phụ nữ không biết làm đẹp là 1 câu nói chính xác, nhưng khi bị mụn nhiều đến như mình, thật khó để làm thế nào trông mình tươi tắn và xinh đẹp hơn được. Rồi những cãi vã lại nổ ra, và đến ngày hôm nay, mình lại kết thúc mối tình này.Mình đang rất buồn và suy sụp, nếu mặt mình chưa từng có mụn, tâm trạng mình chưa từng tự ti đến vậy, liệu mình có phải chịu nhiều nỗi đau đến như thế không?\n Ps: Hiện tại mụn trên mặt mình đã đỡ nhiều. Một lời khuyên bị mụn được chữa khỏi không còn nữa cho các bạn là những lời khuyên trên của mình các bạn cần thực hiện thường xuyên kể cả khi mụn đã hết, bởi dó nghĩa là sẽ không bao giờ bị mụn lại, hãy phòng bệnh bạn nhé!\n Chúc các bạn luôn xinh tươi, yêu đời, làn da luôn đẹp nhé. Em yêu cả nhà. Và cám ơn chân thành ai đã kiên trì đọc đến tận dòng này.<br>', 11, NULL, NULL, 1),
(70, 2, 'Nguy hiểm từ việc sử dụng những mỹ phẩm trị nám ko rõ nguồn gốc và chưa qua chỉ định của bác sĩ', 'Nám là tình trạng hắc sắc tố xuất hiện ở nhiều phụ nữ Việt, nguyên nhân do nhiều yếu tố gây lên, trong đó nguyên nhân thay đổi nội tiết sau sinh chiếm phần lớn.<br>Trị nám bằng các công thức tự nhiên <br>Bạn sẽ bắt gặp nhưng chi sẻ điều trị nám tại nhà bằng các công thức tự nhiên như mặt nạ rau củ, mặt nạ nghệ…vv các hình thưc dân gian. Nhưng biện pháp này chỉ giúp bạn chăm sóc làn da khỏe mạnh mà thôi, nó không thể loại bỏ các hắc sắc tố đã được hình thành trên da.<br>Trị nám bằng các công thức thiên nhiên<br>Trị nám bằng kem bôi <br>Hiện nay trên thị trường có rất nhiều kem trị nám, có nhưng sản phẩm nước ngoài, dược mỹ phẩm, kem đặc trị……rất nhiều sản phẩm kem trộn bán tràn lan trên thị trường mà chúng ta không lường trước được tác hại của nó.<br>Nhiều chị em đã phải trả giá cho việc sử dụng các sản phẩm kem trộn, nám xuất hiện càng nhiều, da yếu, da nhiễm corticoi khiến mụn hình thành……Những sản phẩm này chỉ đánh vào tâm lý người dùng muốn nhanh hết nám, da trắng nhanh. Có thể thấy trân thị trường các sản phẩm trị nám không rõ nguồn gốc rất nhiều, ản chứa các nguy cơ tiềm ẩn cho chị em.<br>Hậu quả của trị nám bằng kem trộn<br>Theo các bác sĩ da liễu, do có nhiều sản phẩm trên thị trường nên khi dùng bất cứ sản phầm nào bạn cũng cần xem xét kỹ nguồn gốc, thành phần…..tránh tình trạng kích ứng không đáng có. Tốt nhất khi muốn điều trị nám bạn nên đến khám và hỏi ý kiến bác sĩ trước khi sử dụng các sản phẩm điều trị.<br>Dùng kem trị nám<br>Công nghệ Laser <br>Hiện nay phần lớn các cơ sở làm đẹp sử dụng công nghệ laser trong quá trình điều trị Nám, với phương pháp này việc loại bỏ các sắc tố dễ dàng hơn tuy nhiên cần tay nghề bác sĩ cũng như sự nâng cấp của dòng máy.<br>Theo Bác sĩ Huy Hoàng tại P.K da liễu Hà Nội, khi điều trị nám các chị nên đến gặp bác sĩ khám để tìm ra nguyên nhân gây nám ở mình, dựa trên kết quả khám và tình trạng da Bác sĩ sẽ lên phác đồ điều trị phù hợp.<br>Bác sĩ khám và tư vấn điều trị nám<br>Đối với những trường hợp bị nám do nội tiết tố cần kết hợp cả viên uống để cân bằng từ bên trong….Các loại kem bôi chỉ hỗ trợ chứ không thể loại bỏ được tình trạng nám hoàn toàn. Vì vậy hãy hỏi ý kiến bác sĩ trước khi sử dụng các sản phẩm điều trị nám, tránh các sản phẩm kem trộn có chứa Corticoid gây tổn thương cho da.<br>Liên hệ 0949470055 để được tư vấn điều trị.<br>', 12, NULL, NULL, 1),
(71, 1, 'Baby Care', 'Nhà sản xuất: ISIS PHARMA<br>Hộp 01 tuýp kem 40ml<br>BabyCare<br>Thành phần chính:\n &#8211; Lanolin (8%)\n &#8211; Sesame oil (13%)\n &#8211; Sweet almond oii (10%)\n &#8211; Zinc oxyde (4%)<br>Tác dụng: Điều trị\n &#8211; Viêm ngứa , dị ứng da ở trẻ em\n &#8211; Các chứng hăm da (hăm tã, hăm nách, hăm hẹn)\n &#8211; Phòng ngừa loét da (do nằm lâu một chỗ ở người lớn)<br>Hướng dẫn sử dụng:\n &#8211; Trẻ em :\n   + Điều trị viêm ngứa da và các chứng hăm : Thoa lên vùng da cần điều trị 2 lần/ngày,Thoa lên vùng da bị viêm ngứa, hăm da ngày 2 lần.\n   + Phòng ngừa hăm tã : Thoa 1 lớp dày lên vùng da quấn tã sau mỗi lần thay tã.\n &#8211; Phòng ngừa loét da do nằm lâu ở người lớn : \n + Thoa và xoa bóp lên vùng da bị đè ép do nằm lâu ở người lớn (bệnh nhân hôn mê, chấn thương, nhằm bất động 1 chỗ&#8230;)<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(72, 1, 'Tacrolimus', 'Hộp 01 tuýp kem 30g<br>Xuất sứ Nhật Bản. Thuốc được sử dụng cho người lớn, sử dụng tốt hơn cho các bệnh nhân không đáp ứng hoặc không dung nạp các điều trị khác.<br>Tacrolimus<br>Thành phần thuốc<br>Thuốc có chứa thành phần Tacrolimus monohydrate 1mg cho 1g thuốc mỡ Protopic 0,1%.<br>Cách dùng và liều dùng:<br>Bôi lớp mỏng lên vùng da bị tổn thương. Người ≥ 16 tuổi Protopic 0,1% 2 lần/ngày cho đến khi sạch tổn thương, nếu tái phát dùng lại Protopic 0,1% 2 lần/ngày.<br>Trường hợp không nên sử dụng thuốc Protopic:<br>Thuốc chống chỉ định sử dụng với các trường hợp quá mẫn với macrolide, tacrolimus hoặc với thành phần thuốc.<br>Lưu ý khi sử dụng:<br>Các trường hợp dưới đây cần thận trọng khi sử dụng thuốc để tránh gặp phải các tác dụng phụ không mong muốn ảnh hưởng đến tình trạng bệnh và sức khỏe, cũng như cần chứ ý các điểm sau:<br>– Trẻ &lt; 2 tuổi.<br>– Người bệnh suy gan, chứng đỏ da toàn thân.<br>– Phụ nữ có thai và cho con bú.<br>– Tránh để da tiếp xúc với ánh sáng mặt trời và tia cực tím.<br>– Không bôi cùng với chế phẩm ngoài da khác.<br>– Tránh để thuốc tiếp xúc với mắt &amp; niêm mạc.<br>– Không băng kín chỗ bôi thuốc.<br>Tác dụng phụ:<br>– Cảm giác đau, rát bỏng, ngứa, dị cảm, phát ban, ban đỏ.<br>– Tăng nguy cơ viêm nang lông, trứng cá, nhiễm virus Herpes.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(73, 1, 'Lamisil', 'Hộp 01 tuýp kem bôi ngoài da 15g<br>Lamisil<br>Thành phần <br>Mỗi 100g:Terbinafine hydrochloride 1g. Chất bảo quản: Benzyl alcohol<br>Chỉ định<br>Nhiễm nấm da gây ra bởi các loại nấm như Trichophyton (T. rubum, T. mentagrophytes, T. vurrucosum, T. violaceum), Microsporum canis và Epidermophyton foccosum. Nhiễm nấm men ở da, đặc biệt gây ra bởi nấm Candida (như Candida albicans). Lang ben do Pityrosporum orbiculare (được biết đến như Malassezia furfur).<br>Chống chỉ định<br>Mẫn cảm với terbinafine hay với bất cứ thành phần tá dược nào của thuốc.<br>Thận trọng<br>Lamisil dạng kem chỉ để dùng ngoài. Tránh để thuốc tiếp xúc với mắt.<br>Tác dụng phụ<br>Mẩn đỏ, ngứa, cảm giác kiến đốt đôi khi xảy ra tại chỗ dùng thuốc nhưng rất ít khi phải ngừng điều trị vì lý do này. Các phản ứng vô hại này cần phải được phân biệt với các phản ứng dị ứng đòi hỏi phải ngừng điều trị.<br>Liều lượng<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(74, 1, 'Acid Fusidic', 'Kháng sinh cấu trúc steroid, hoạt phổ hẹp<br>Fusidic acid<br>Dạng thuốc và Hàm lượng:<br>Chỉ định:\n <br>Chống chỉ định:<br>Liều lượng và cách dùng:\n <br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(75, 1, 'Hydrocortisone', 'Tên quốc tế: Hydrocortisone.<br>Loại thuốc: Glucocorticosteroid, corticosteroid.<br>Hydrocortisone<br>Dạng thuốc và hàm lượng<br>Tác dụng<br>Hydrocortison là corticoid tiết từ vỏ thượng thận, thuộc nhóm glucocorticoid có tác dụng chống viêm, chống dị ứng, chống ngứa và ức chế miễn dịch.<br>Chỉ định<br>Bôi tại chỗ (thuốc mỡ và kem): Chữa eczema cấp và mạn do nhiều nguyên nhân khác nhau, ngứa hậu môn &#8211; sinh dục.<br>Uống và tiêm: Liệu pháp thay thế: Hydrocortison (hoặc cortison) thường là corticosteroid được lựa chọn để điều trị thay thế cho người bị suy vỏ thượng thận (suy vỏ thượng thận tiên phát và thứ phát, tăng sản thượng thận bẩm sinh hoặc hội chứng thượng thận sinh dục). Liệu pháp tiêm bắp hoặc tĩnh mạch thường dành cho người bệnh không uống được thuốc hoặc trong tình huống cấp cứu, khi cần phải có tác dụng nhanh, như ở người bị suy thượng thận cấp (do cơn Addison hoặc sau cắt bỏ tuyến thượng thận, do ngừng thuốc đột ngột liệu pháp corticosteroid hoặc do tuyến thượng thận không đáp ứng được với stress gia tăng ở các người bệnh đó) và ở một số trường hợp cấp cứu do dị ứng: trạng thái hen và sốc, đặc biệt sốc phản vệ.<br>Ðể sử dụng tác dụng chống viêm hoặc ức chế miễn dịch (thí dụ trong điều trị lupus ban đỏ toàn thân, bệnh bạch cầu, u lympho bào ác tính&#8230;), các glucocorticoid tổng hợp có tác dụng mineralocorticoid tối thiểu được ưa chọn hơn.<br>Chống chỉ định<br>Người bệnh nhiễm khuẩn (đặc biệt trong lao tiến triển), nhiễm virus (thủy đậu, zona, herpes giác mạc), nhiễm nấm bệnh hay kí sinh trùng chỉ được dùng glucocorticoid sau khi đã được điều trị bằng các thuốc chống nhiễm các bệnh kể trên.<br>Liều lượng và cách dùng<br>Cách dùng<br>Liều lượng<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(76, 1, 'Fucicort', 'Hộp 01 tuýp kem bôi 15g<br>Fucicort<br>Thành phần <br>Acid fusidic 20mg. Betamethasone valerate 1mg.<br>Chỉ định<br>Fucicort được chỉ định điều trị những bệnh lý viêm da do nhiễm trùng hoặc có thể do nhiễm trùng. Bệnh lý viêm da gồm viêm da dị ứng, viêm da tiết bã nhờn, vảy nến, da cháy nắng, chàm dạng đĩa, viêm da tiếp xúc, lupus ban đỏ dạng đĩa, chàm do ứ đọng, liken đơn mạn tính.<br>Chống chỉ định<br>Chống chỉ định dùng corticosteroids tại chỗ trong trường hợp nhiễm trùng da do virus, lao và nấm, viêm da quanh miệng, trứng cá đỏ và những tình trạng lo t da, và trong trường hợp có tăng mẫn cảm với bất kỳ thành phần nào của thuốc.<br>Thận trọng<br>Chỉ dùng ngoài da, không bôi lên mắt. Không nên dùng liên tục lâu dài, đặc biệt ở mặt, các nếp gấp, vùng bị hăm, ở trẻ nhũ nhi và trẻ lớn. Steroid tại chỗ không nên dùng nhiều ở phụ nữ có thai. Những bất thường về phôi thai đã được ghi nhận ở súc vật thực nghiệm do hấp thu thuốc vào toàn thân<br>Liều lượng<br>Sang thương hở: Thoa 2-3 lần/ngày.<br>Sang thương kín: Thoa ít lần hơn.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(77, 1, 'Azelaic Acid', 'Tên biệt dược Azelin, Nacner, ZA<br>Azelaic acid cream<br>Thành phần Azelaic Acid<br>Chỉ định<br>Ðiều trị tại chỗ mụn trứng cá thông thường (mụn đầu đen, đầu trắng), sạm da, tàn nhang, nốt ruồi và vết nám, bệnh rosacea (chứng đỏ mặt), các biểu hiện xấu khác do chứng tăng sắc tố da.<br>Chống chỉ định<br>Quá mẫn cảm với thành phần của thuốc<br>Hướng dẫn sử dụng <br>Sau khi rửa sạch da, thoa thuốc lên vùng da bị ảnh hưởng 2 lần/ ngày; xoa kỹ, dùng hàng ngày. Nếu có kích ứng, giảm xuống 1 lần/ ngày hoặc tạm ngừng thuốc. Bệnh thường cải thiện sau 4 tuần. Lưu ý chỉ dùng ngoài da không dây vào mắt.<br>Tác dụng phụ\n Ngứa ran, nóng rát da, đau nhói da, phát ban. A-xít azelaic có thể gây ra các tác dụng phụ khác. Gọi cho bác sĩ nếu bạn có bất kỳ vấn đề bất thường nào khi dùng thuốc.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(78, 1, 'Hydroquinone', 'Tên biệt dược: Dominakrem, Hiqueen<br>Hydroquinone<br>Thành phần: Hydroquinone<br>Chỉ định <br>Tẩy trắng da trong chứng tăng sắc tố da như nám da, sạm da, tàn nhang, nốt ruồi son và các biểu hiện xấu khác do chứng tăng sắc tố gây ra.<br>Chống chỉ định <br>Quá mẫn với thành phần của thuốc, phụ nữ có thai, trẻ em dưới 12 tuổi<br>Thận trọng khi dùng <br>Tuyệt đối tránh ánh nắng, khi ra đường phải dùng kem chống nắng để ngăn nhiễm sắc tố trở lại trên vùng da được tẩy trắng. Tránh dây thuốc vào mắt, nên thử tính nhạy cảm khi dùng kem.<br>Lưu ý :  Không pha trộn với cá kem bôi ngoài da khác<br>Liều lượng: Thoa thuốc lên vùng da bệnh mỗi ngày 1 lần vào ban đêm<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(79, 1, 'Anzela Cream', 'Kem có màu trắng đến vàng nhạt<br>Hộp 01 tuýp 10g<br>Nhà sản xuất: CHUNG GEI PHARMA. CO., LTD.<br>Anzela<br>Thành phần  Acid Azelaic …200mg<br>Tá dược: Cetostearyl alcohol, Propylene glycol, Parafin lỏng, Polysorbate 80, Sorbitan monostearate, Butylated hydroxytoluene, Butyl parahydroxybenzoate, Methyl parahydroxy banzoate, nước tinh chế vừa đủ.<br>Chỉ định &#8211; Tác dụng bất lợi:\n &#8211; Điều trị tại chỗ chứng viêm da do trứng cá sẹo mức độ nhẹ và vừa.\n &#8211; Tác dụng có thể xảy ra: giảm sắc tố trắng da gây bạch biến, các đốm mờ sắc tố da nhỏ.\n &#8211; Các phản ứng kích ứng da nhất thời (ngứa, nóng rát hoặc như kim châm) đôi khi xảy ra, thường khi bắt đầu dùng thuốc. Tuy nhiên, các kích ứng này thường giảm đi sau khi dùng một thời gian.\n &#8211; Có thể gặp ban đỏ, khô da, nổi ban, bong ban, viêm da và viêm da tiếp xúc.\n &#8211; Phản ứng dị ứng có thể xảy ra khi dùng kem Anzela.\n &#8211; Các tác dụng bất lợi sau có thể xảy ra nhưng hiếm: làm nặng thêm bệnh hen, rậm lông, đỏ da và làm nặng thêm bệnh herpes môi tái phát.\n Liều lượng và cách dùng:\n Rửa sạch vùng da cần điều trị và để khô, bôi một lớp thuốc mỏng và thoa kỹ hai lần mỗi ngày (sáng và tối). Rửa tay sau khi bôi thuốc. Thời gian sử dụng thuốc thay đổi tùy theo từng người và theo mức độ nặng của bệnh. Thông thường, các tổn thương do viêm được cải thiện rõ sau 4 tuần điều trị.\n Chống chỉ định:\n Bệnh nhân quá mẫn cảm với bất kỳ thành phần nào của thuốc.\n Tương tác thuốc:\n Chưa được biết.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(80, 1, 'Retinoid', 'Loại thuốc: Retinoid dùng bôi trị trứng cá.<br>Retin acream (Retinoid)<br>Dạng thuốc và hàm lượng <br>Chỉ định<br>Da liễu (bôi): Trứng cá các loại; lão hóa da do ánh sáng; các rối loạn sừng da.<br>Chống chỉ định<br>Quá mẫn với tretinoin.<br>Tác dụng phụ<br>Thường gặp: Khô da, ban đỏ, vảy da, tăng hoặc giảm sắc tố, nhạy cảm ánh sáng, bột phát trứng cá ban đầu.Nhói đau, cảm giác châm chích, bỏng rát, mụn nước hoặc phù.<br>Liều lượng và cách dùng<br>Trứng cá: Nên bôi tretinoin trước khi đi ngủ; chỗ da cần bôi phải khô, sạch. Lúc đầu, bôi ngày một lần trong 2 &#8211; 3 ngày và nên dùng kem hoặc gel có nồng độ thuốc thấp. Nếu dung nạp thuốc tốt thì sau đó có thể dùng chế phẩm nồng độ cao hơn. Ðáp ứng điển hình là trong vòng 7 &#8211; 10 ngày da đỏ lên và bong vảy. Tác dụng điều trị thường xuất hiện sau 2 &#8211; 3 tuần. Có thể phải hơn 6 tuần mới thấy hiệu quả điều trị tối ưu của thuốc. Ở những người bệnh mà trước đó đã phải tạm ngừng hoặc giảm số lần bôi thuốc, trị liệu có thể được lặp lại khi người bệnh có thể dung nạp thuốc. Cần theo dõi cẩn thận sự đáp ứng và dung nạp thuốc ở người bệnh mỗi khi thay đổi dạng thuốc, nồng độ thuốc hay số lần bôi. Khi đã đạt kết quả mong muốn, có thể điều trị duy trì bằng cách giảm số lần bôi thuốc hoặc dùng dạng thuốc có nồng độ thấp hơn.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(81, 1, 'Differin', 'Hộp 01 tuýp dạng kem 30g<br>Nhà sản xuất Laboratoires Galderma<br>Diferin<br>Thành phần <br>Adapalene<br>Chỉ định <br>Điều trị mụn trứng các có nhiều nhân chứng cá, sần và mụn mủ, mụn trứng cá ở mặt – ngực và lưng.<br>Chống chỉ định quá mẫn với thành phần của thuốc <br>Liều lượng <br>Rửa sạch và làm khô vùng da bị mụn, bôi thuốc 1 lần/ngày vào buổi tối trước khi đi ngủ cho toàn bộ vùng da bị mụn. Khoảng 1 hạt đậu xanh cho mỗi lần bôi.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(82, 1, 'Acid Salicylic', 'Tên chung quốc tế Salicylic acid.<br>Hộp 01 tuýp kem bôi 15g<br>Loại thuốc: Thuốc tróc lớp sừng da, chống tiết bã nhờn, trị vảy nến; chất ăn da.<br>Acid Salicylic<br>Dạng thuốc và hàm lượng<br>Dược lý và cơ chế tác dụng<br>Dược động học<br>Chỉ định<br>Chống chỉ định<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(83, 1, 'Corticoid', 'Retin acream<br>Cách lựa chọn thuốc bôi <br>Khi chọn lựa thuốc bôi Corticoids cần chú ý đến chẩn đoán là bệnh gì, vị trí mắc bệnh, lứa tuổi, vùng da…để lựa chọn Corticoids có độ mạnh phù hợp.<br>Một số bệnh như vẩy nến thể mảng và Eczema ở bàn tay cần dùng loại Corticoids bôi tại chỗ loại mạnh (nhóm 1), vẩy nến thể đồng tiền và Lupus đỏ bôi Corticoids nhóm mạnh (nhóm 1, 2).<br>Ngược lại viêm da da dầu dùng nhóm 5 &#8211; 7 (loại nhẹ) viêm da quanh mi mắt cũng dùng loại nhẹ 5 &#8211; 7.<br>Vị trí bàn chân bàn tay dùng nhóm loại mạnh (nhóm 1 &#8211; 3) vì da dày, trái lại vùng mặt, quanh mi mắt dùng nhóm loại nhẹ (5 &#8211; 7), vùng nếp kẽ nách bẹn, đáy chậu, nếp dưới vú vì ẩm ướt và hấp thu các viêm chỉ bôi thuốc nhẹ (5 &#8211; 7).<br>Cách sử dụng thuốc <br>Nhưng nếu bệnh da cần dùng loại Corticoid bôi tại chỗ loại mạnh, và vị trí vùng da dày (như bàn tay bàn chân) mà lại chỉ định loại Corticoid bôi tại chỗ loại nhẹ thì không có kết quả, đây là lỗi thường gặp.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(84, 1, 'Acneal', 'Hãng sản xuất Công ty Kunming Dihon Pharm Co.<br>Hộp 01 hỗn dịch 20ml<br>Acneal<br>Thành phần thuốc <br>1ml dung dịch chứa &#8211; Clindamycin 10mg và Metronidazole 8mg<br>Chỉ định <br>Điều trị bệnh : Viêm tai giữa , viêm da do vi khuẩn , viêm nang lông , chốc đầu , viêm lợi , viêm loét miệng và các nhiễm khuẩn da khác như bệnh trứng cá.<br>Cách dùng <br>Dùng ngoài da, cắt đầu ống thuốc khi dùng, lắc kĩ dung dịch trước mỗi lần dùng<br>Liều dùng<br>Điều trị trứng cá: nhỏ giọt dung dịch lên đầu que bông sau đó bôi nhẹ vào vùng da bị bệnh sau khi đã được làm sạch và khô<br>Điều trị viêm tai giữa, mũi, nha chu: nhỏ trực tiếp vào nơi bị bệnh<br>Dùng 3 lần một ngày, một đợt điều trị khoảng 2 tuần, nếu cần thiết có thể lặp lại một đợt điều trị nữa<br>Hạn dùng <br>36 tháng kể từ ngày sản xuất<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(85, 1, 'Hiteen Gel [Acne Treatment Preparations]', 'Hộp 01 tuýp dạng Gel 10g<br>Hãng sản xuất Asia Pharm<br>HiteenGel<br>Thành phần thuốc <br>Mỗi g gel: Erythromycin 40 mg, tretinoin 0.25 mg.<br>Chỉ định <br>Thuốc trị mụn [Acne Treatment Preparations]<br>Chống chỉ định\n &#8211; Bệnh nhân mẫn cảm với thuốc hoặc với kháng sinh thuộc họ macrolide.\n &#8211; Bệnh nhân gia đình có tiền sử hoặc bị ung thư da.\n &#8211; Bệnh nhân bị chàm, gãy xương, lột da.\n &#8211; Bệnh nhân bị phỏng nắng không nên dùng cho đến khi da được phục hồi hoàn toàn.\n &#8211; Không nên dùng cho phụ nữ có thai, cho con bú hoặc nghi ngờ có thai.<br>Liều lượng và cách dùng\n &#8211; Liều tấn công : thoa một lần mỗi ngày trong vòng ba tháng.\n &#8211; Liều duy trì : hai đến ba ngày thoa một lần.\n &#8211; Thuốc phải được dùng vào buổi tối, 15 phút sau khi làm vệ sinh, thoa thuốc trên vùng da đã được lau khô hoàn toàn. Sáng hôm sau phải rửa mặt kỹ.\n &#8211; Sự tăng liều cho thấy có những nguy cơ gây phản ứng tại chỗ nguy hiểm, mà không có sự cải thiện trong kết quả điều trị.\n &#8211; Thuốc phải được dùng bằng cách dùng đầu ngón tay thoa nhẹ lên da cho đến khi thuốc được thấm hoàn toàn, để tránh trường hợp thuốc tạo thành một lớp mỏng trên da.<br>Tác dụng phụ\n &#8211; Khô da, ngứa, có vảy, cảm giác đau buốt, ban đỏ, đỏ da, phù, mụn rộp hoặc có vảy cứng có thể xảy ra tùy từng người. Trong những trường hợp này cần ngưng dùng thuốc hoặc theo dõi cẩn thận khi dùng.\n &#8211; Thay đổi sắc tố da (tăng hoặc giảm) đã được ghi nhận khi dùng thuốc kéo dài.\n &#8211; Viêm da dị ứng do tiếp xúc được báo cáo là hiếm khi xảy ra.\n &#8211; Có thể làm tăng nhạy cảm với ánh sáng.\n &#8211; Khí hậu khắc nghiệt như gió, lạnh có thể gây kích ứng cho bệnh nhân.\n &#8211; Nổi ban hiếm khi xảy ra, nên ngưng dùng thuốc trong trường hợp này.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(86, 1, 'Dalacin T 1%', 'Hộp 01 tuýp thuốc mỡ 30ml<br>Nhà sản xuất Pfizer<br>Dalacin<br>Thành phần thuốc <br>Chỉ định <br>Chống chỉ định <br>Tiền sử quá mẫn với clindamycin hoặc lincomycin. Tiền sử viêm đại tràng liên quan dùng kháng sinh.<br>Liều dùng và cách dùng <br>Bôi một lớp mỏng dung dịch lên vùng da bệnh 2 lần mỗi ngày.<br>Phản ứng phụ <br>Kích ứng da, da khô, mày đay. Rối loạn tiêu hóa, tăng tiết bã nhờn, viêm nang lông, đau mắt, viêm đại tràng giả mạc, đau bụng, viêm da tiếp xúc.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(87, 1, 'Eumovate', 'Hộp 01 tuýp thuốc mỡ (kem bôi ngoài da) 5g<br>Nhà sản xuất GlaxoSmithKline<br>Eumovate_cream<br>Thành phần thuốc <br>Chỉ định<br>Ðiều trị ngắn hạn các tình trạng ngứa, mẫn đỏ, da đóng vảy, tróc vảy, vẩy nến, chàm, viêm da hay các tình trạng khó chịu khác của da. Ðiều trị ngắn hạn eczema &amp; viêm da như eczema dị ứng, viêm da ánh sáng, viêm tai ngoài, viêm da dị ứng, các nốt sẩn ngứa, viêm da bã nhờn, vết côn trùng cắn.<br>Chống chỉ định<br>Quá mẫn với thành phần thuốc. Trẻ &lt; 12 tuổi Trứng cá đỏ, mụn trứng cá, viêm da quanh miệng, nhiễm virus da nguyên phát, nhiễm nấm, vi khuẩn.<br>Liều dùng và cách dùng<br>Phản ứng thuốc <br>Rất hiếm: Nhiễm trùng cơ hội, viêm da tiếp xúc dị ứng, mày đay, teo da, thay đổi sắc tố da, trầm trọng thêm các triệu chứng tiềm ẩn, bỏng rát tại vùng da tiếp xúc, rậm lông, phát ban, ngứa, ban đỏ.<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng. <br>', 11, NULL, NULL, 1),
(88, 1, 'Một số loại thuốc bôi ngoài da được dùng phổ biến', 'Xanhmetilen<br>Lưu ý: Bài viết mang tính chất tham khảo, Bệnh nhân cần hỏi ý kiến Bác sĩ trước khi sử dụng.<br>\n\nRivanaol <br>Rivanaol<br>', 11, NULL, NULL, 1),
(89, 1, 'Thuốc bôi ngoài da', ' <br> <br>', 11, NULL, NULL, 1),
(90, 2, 'Dầu oliu, cách dưỡng da hiều quả trong quá trình trị Nám', 'Dầu oliu luôn là nguyên liệu làm tuyệt vời được các chị em lựa chọn, bạn có thể dùng trực tiếp hoặc dùng dầu oliu cùng các nguyên liệu khác để tạo ra các hỗn hợp mặt nạ chăm sóc da thân thiện. <br>Đồi với những chị em nào đang bị nám cần chăm dóc da trong quá trình điều trị thì dầu oliu là sản phẩm số 1 với các tiêu chí, rẻ, thân thiện, hiệu quả.<br>Trong dầu oliu có chứa rất nhiều chất dinh dưỡng, chất oxy hóa và các loại vitamin A và E, tốt cho da, giúp da mềm mịn và tươi sáng tự nhiên. Mỗi dưỡng chất và vitamin có trong dầu oliu rất tốt cho da, da bạn sẽ mềm mại và tươi sáng hơn khi chăm chỉ sử dụng hàng ngày. Mỗi dưỡng chất có trong dầu oliu có những tác dụng khác nhau, cùng khám phá loại nguyên liệu này nhé.<br>Dầu oliu làm đẹp da<br>Dùng dầu oliu để tẩy trang <br>Bạn dùng ½ thìa đường thêm dầu oliu vào đánh tan, sau đó thoa hỗn hợp lên mặt dùng bông tẩy trang lau sạch. Vậy là bạn đã có bước tẩy trang hoàn hảo, khi dùng oliu tẩy trang bạn hoàn toàn yên tâm da sẽ không bị khô như bạn dùng các dung dịch khác.<br>Sau đó bạn chỉ cần rửa sạch bằng nước là được, nếu hết 1 ngày bạn thêm bước rửa mặt bằng sữa rửa mặt để có thể thực hiện các bước chăm sóc da khác nếu muốn.<br>Dùng oliu giúp da bạn sáng hơn <br>Hàng ngày bạn có thể dùng dầu oliu để dưỡng da sẽ giúp bạn có làn da khỏe mạnh và mịn màng.<br>Công thức: ½ chén oliu + ¼ dấm táo + ¼ chén nước<br>Trộn đều ta được một hỗn hợp bôi chăm sóc da rất tốt, nếu bạn dùng ko hết có thể cho vào hộp kín dùng những ngày tiếp theo.<br>Công thức dùng oliu để se khít lỗ chân lông <br>3 thìa café đường + 1 thìa dầu oliu + 1 thìa nước cốt chanh<br>Trộn đều sau đó đắp lên mặt sau 30p rửa lại bằng nước sạch, với cách này lỗ chân lông sẽ được thu nhỏ lại, dầu oliu giúp da chân mềm hơn, bóng khỏe hơn.<br>Dưỡng trắng da toàn thân bằng dầu oliu <br>Sử dụng mặt nạ dầu oliu cho body là cách dưỡng trắng da cực kỳ hiệu quả, cùng xem công thức nhé !<br>Trắng da body =  5 thìa oliu + 2 lòng trắng trứng gà + 3 thìa nước cốt chanh<br>Sau đó bôi hỗn hợp này lên toàn thân để 15p sau đó tắm lại bằng sữa tắm, cách này sẽ giúp bạn có làn da khỏe mạnh và mịn màng.<br>Chăm sóc da bằng dầu oliu<br>Tác dụng của các dưỡng chất có trong oliu khiến bạn chăm sóc da trắng sáng hiệu quả <br>– Vitamin E: Cải thiện độ đàn của hồi da, loại bỏ các nếp nhăn, thúc đẩy sự tăng trưởng của mái tóc.<br>– Vitamin A: Chữa lành nhanh chóng vết thương, làm giảm nếp nhăn và giữ gìn da khỏi các tia cực tím.<br>– Vitamin K: Thúc đẩy quá trình đông máu, làm chắc xương, giảm vết bầm tím, mụn trứng cá và tăng khả năng chữa lành vết thương, vết rạn da, bỏng.<br>– Vitamin D: Giúp hấp thu canxi trong cơ thể<br>Ngoài các tác dụng dưỡng trắng, tẩy da chết….dầu oliu còn được sử dụng phương pháp trị nám da bằng dầu oliu.<br>Những công thức sử dụng dầu oliu giúp quá trình trị nám hiệu quả <br>Oliu và mật ong tỉ lệ 1:1 = giúp da mềm và sáng.<br>Dùng đắp mặt từ 20p đến 30p sau đó rửa sạch bằng nước bạn sẽ thấy da mướt mềm.<br>Oliu và chanh 1 thìa oliu + 1 quả chanh<br>Khi dầu oliu kết hợp cùng chanh giúp làm mờ các vết thâm, nám và tàn nhang vô cùng hiệu quả, bạn chỉ cần bôi hỗn hợp lên và để khoảng 30p, kiên trì thực hiện 2 tuần/ lần. Da bạn không những mịn màng hơn mà còn trắng sáng nhờ các hoạt chất trong chanh mà vẫn giữ được an toàn cho da. Các vết nám cũng nhờ đó mà giảm đi trong quá trình bạn chăm sóc.<br>Trị nám da bằng dầu oliu<br>Tuy nhiên các công thức trị nám da từ dầu oliu chỉ giúp phần nào giảm tình trạng tăng sắc tố, mờ đi các sắc tố đổi màu trên bề mặt da. Để điều trị Nám hiệu quả bạn cần sự tác động từ công nghệ để đi sâu được vào chân nám nằm dưới bề mặt da mà mắt thường không nhìn thấy được.<br>Điều trị nám hiệu quả bằng Công nghệ Laser NanoTech <br>Hiện nay Phòng khám Da liễu Hà Nội đang điều trị thành công cho nhiều bệnh nhân bị nám khác nhau bằng công nghệ mới này. Việc điều trị bằng công nghệ khá thuận lợi, không mất thời gian, khoảng 2 tuần bạn mới phải đến điều trị 1 lần. Hơn nữa trong quá trình điều trị bạn hoàn toàn yên tâm về tình thẩm mỹ.<br>Liên hệ 0949470055 để được đăg ký lịch khám Bác sĩ hoàn toàn miến phí.<br>Điều trị nám chuyên sâu theo phác đồ của Bác sĩ <br>Nguy hiểm từ việc sử dụng những mỹ phẩm trị nám ko rõ nguồn gốc và chưa qua chỉ định của bác sĩ<br>&nbsp;<br>', 11, NULL, NULL, 1),
(91, 4, 'Cách chăm sóc da trong quá trình điều trị mụn bạn nên biết', 'Việc chăm sóc da trong quá trình điều trị mụn vô cùng quan trọng, nó ảnh hưởng đến hiệu quả điều trị cũng như tình trạng mụn. <br>Để việc điều trị mụn đạt hiệu quả cao, nhanh hết mụn bạn cần có kiến thức cơ bản về chăm sóc da, lựa chọn mỹ phẩm và chăm chỉ vệ giữ vệ sinh. Sau đây là những việc bạn nên biết và cần thực hiện trong quá trình điều trị mụn.<br>Giữ vệ sinh vùng da bị mụn <br>Điều này vô cùng quan trọng, trong quá trình trị mụn, nhất là sau khi lấy nhân mụn, vùng da bị mụn bị tổn thương rất dễ nhiễm khuẩn. Vì vậy bạn cần có những biện pháp bảo vệ da và giữ sạch cho vùng da vừa điều trị.<br>Luôn đeo khẩu trang khi ra ngoài, tránh bụi bẩn, bôi kem chống nắng trước khi ra ngoài 20 phút, hạn chế tiếp xúc với bụi bẩn và ánh nắng mặt trời.<br>Rửa mặt đúng cách, nghe có vẻ đơn giản nhưng chưa chắc bạn đang rửa mặt đúng cách nhé !<br>&#8211; Rửa mặt có vai trò vô cùng quan trọng trong việc chăm sóc da, rửa mặt đúng cách giúp loại bỏ bụi bẩn và chất nhờn giúp thông thoáng lỗ chân lông và không gây bít tắc mụn.<br>&#8211; Với những bạn da dầu khả năng bị bụi bẩn tấn công cao hơn, chính vì thế cần lựa chọn sản phẩm sữa rửa mặt phù hợp.<br>&#8211; Nên rửa mặt 2 lần/ngày, khi ngủ dậy vào buổi sáng và trước khi đi ngủ để loại bỏ dầu nhờn trên mặt.<br>&#8211; Có thể rửa mặt bằng nước ấm để lỗ chân lông giãn nở giúp loại bỏ triệt để bụi bẩn bám trên da, nhưng sau đó cần rửa lại bằng nước lạnh để đóng lỗ chân lông ngăn bụi tấn công.<br>Giữ vệ sinh cho vùng da điều trị mụn<br>Nên tẩy tế bào chết cho da <br>Nhiều bạn nghĩ rằng rửa mặt là đủ nhưng bạn đang nhầm rồi, rửa mặt không loại bỏa được các tế bào chết trên da mà chỉ loại bỏ được bụi bẩn. Do đó mỗi tuần bạn nên tẩy tế bào chết 2 lần  để da thông thoáng, ngăn ngừa mụn hình thành. Việc này cũng làm da bạn mịn màng hơn và dễ hấp thu các dưỡng chất trong quá trình chăm sóc da tốt hơn.<br>Tẩy tế bào chết cho da<br>Dùng kem chống nắng hàng ngày <br>Dù bạn ở trong nhà hay ra ngoài đường việc sử dụng kem chống nắng vẫn luôn là điều kiện cần, tia UPV – UVA từ ánh nắng mặt trời là một trong những tác nhân gây mụn trứng cá và hủy hoại da. Bôi kem chống nắng hàng ngày không chỉ giúp ta đối phó với ánh nắng mặt trời mà còn giúp ngăn ngừa mụn và quá trình lão hóa một cách hiệu quả.<br>Dùng kem chống nắng hàng ngày<br>Dưỡng ẩm cho da <br>Nhiều bạn cho rằng trong quá trình điều trị mụn không cần dưỡng ẩm, nhưng suy nghĩ này hoàn toàn sai. Dưỡng ẩm là vô cùng cần thiết do trong quá trình điều trị bạn sẽ được chỉ định thuốc bôi lên điểm mụn, thuốc này khả năng kiềm dầu cao nên bạn sẽ thấy khô da và có khi còn bong tróc.<br>Vì thế dùng kem chống ẩm sẽ giúp bạn cân bằng độ PH, bảo vệ da trước các tác động bên ngoài, không những vậy chúng còn cấp nước giúp da không đổ nhiều dầu, nhờ đó giảm được tình trạng mụn, ngừa mụn và làm chậm quá trình lão hóa.<br>Dưỡng ẩm cho da<br>Uống và bôi thuốc theo chỉ định của bác sĩ <br>Để đảm bảo hiệu quả điều trị bác sĩ sẽ dựa trên tình trạng mụn của bạn để kê đơn bao gồm thuốc bôi, thuốc uống, sữa rửa mặt và kem chống nắng phù hợp để bạn chủ động chăm sóc da tại nhà.<br>Vì thế bạn cần tuân thủ mọi hướng dẫn của bác sĩ trong quá trình điều trị để quá trình phụ hồi và hết mụn đạt mục tiêu cũng như thời gian đề ra.<br>Uống và bôi thuốc theo chỉ định của bác sĩ<br>Nói không với trị mụn cấp tốc<br>Điều trị mụn chuẩn y khoa, hết mụn sau 1 liệu trình điều trị<br>Hành trình trị mụn của bạn Phạm Đình Ảnh (Nhà hát Chèo Việt Nam) <br>', 15, NULL, NULL, 1),
(92, 17, 'Ăn gì để chống lão hóa ?', 'Tuổi tác là vấn đề ai cũng quan tâm, tuổi càng nhiều chúng ta càng quan tâm nhiều hơn tới vẻ bề ngoài. Vì vậy ngoài việc quan tâm tới làm đẹp, chăm sóc da thì chúng ta cũng không thể không quan tâm đến chế độ ăn uống. <br>Nếu dùng mỹ phẩm, đi spa để níu giữ tuổi trẻ thì không đủ, mỹ phẩm chị giúp bạn dưỡng da hay che đi những khuyết điểm, spa giúp bạn thư thái về tinh thần. Ngoài những việc làm đẹp bạn cần chăm chỉ tập luyện thể dục để cải thiện tình trạng sức khỏe, tăng cường sức đề kháng giúp bạn luôn khỏe mạnh.<br>Quan trọng nhất vẫn là chế độ ăn uống, lượng thực phẩm và dinh dưỡng ảnh hưởng lớn tới thể trạng cũng như quá trình lão hóa của con người. Vì vậy để giữ được sức khỏe, vẻ đẹp và nét thanh xuân bạn nên có chế độ sinh hoạt lành mạnh kết hợp với dinh dưỡng đầy đủ. Sau đây là những thực phẩm bạn nên đưa vào thực đơn hàng ngày để “níu giữ tuổi xuân” duy trì dung mạo.<br>Đây là những nhóm thực phẩm cung cấp các vitamin cần thiết dành cho da, chất xơ, chống được oxy hóa, đào thải độc tố giúp các tế bào tái tạo nhanh hơn. Nhờ đó giảm được các nếp nhăn, dấu hiệu rõ nét của thuổi già. Ngoài ra rau và trái cây còn giúp bạn thanh lọc cơ thể giảm các nguy cơ bệnh tim, ung thư…..<br>Một số loại rau củ bạn nên ăn: trái có nhiều màu sắc, cà rố, cà tím, súp lơ, xà lách, đu đủ, bí đỏ…..cam, oliu, chanh leo, nhất là những trái mọng.<br>Rau củ và trái cây tươi<br>Protein là dưỡng chất cần thiết để làn da của chúng ra có độ đàn hồi cao, mềm mại. Nếu bạn thường xuyên ăn những loại thịt giàu protein sẽ giúp bạn tăng cường lượng collagen trong cở thể. Nhờ protein bạn có thể cai thiện được các nếp nhăn, độ đàn hồi của da và khuôn mặt đầy đặn.<br>Ngoài thịt ra protein còn có trong lòng trắng trứng gà, thịt gia cầm bỏ da, cá. Với thịt bò và thịt heo bạn nên mua phần ít béo như nạc mông hay thăn. Đậu hũ cũng chứa protein giúp bạn cải thiện độ đàn hồi của da nhé, thực phầm này có thể dùng nhiều lần trong ngày, rất tiên lợi.<br>Các thực phẩm giàu Protein<br>Omega 3 có nhiều nhất trong cá, giúp ngăn ngừa lão hóa, các axit béo omega 3 trong dầu cá còn giúp bảo vệ tim, giảm thấp nguy cơ đột quỵ. Ngoài ra chất béo trong dầu cá omega 3 còn giúp bảo vệ da bạn dưới ánh nắng mặt trời.<br>Mỗi tuần bạn nên ăn ít nhất 2 bữa cá, trong cá ngừ, cá hồi ….ngoài ra trong cá mòi hay cá trích cũng có một lượng omega 3, tốt nhất nên ăn cá khi còn tươi để đảm bảo lượng dinh dưỡng còn trong cá.<br>Với những người không thích ăn cá có thể ăn dầu hạt cải, hạt bí, đậu hũ, …..trong những loại dầu từ các hạt này có chứa hợp chất gọi là ALA sẽ được cơ thể chúng ta chuyển hóa thành một dngj axit béo tương tự, có hiệu quả tương tự.<br>Các loại cá giàu Omega 3<br>Trong sữa hay các chế phẩm từ sữa có chứa canxi và vitamin D rất cần thiết cho cơ thể giúp xương chắc khỏe, tránh được các bệnh như chứng loãng xương và bệnh xương thủy tinh. Ngoài việc chống các bệnh về xương thì việc cung cấp canxi giúp bạn cao lớn và trẻ trung hơn.<br>Hãy ăn các chế phẩm từ sữa hay uống sữa mỗi ngày như phomai, sữa chua, bơ…..nếu sợ béo bạn có thể sử dụng những loại ít béo hơn.<br>Các chế phẩm từ xữa chứa nhiều canxi<br>Ngũ cốc là nguồn dinh dưỡng ít béo, thanh tịnh, ít đạm, với những người giảm cân họ thường ăn bột ngũ cốc để hạn chế tinh bột hay đạm dung nạp vào cơ thể. Ngũ cốc nguyên hạt có chứa nhiều chất xơ hỗ trợ tốt cho hệ tiêu hóa, giảm được các nguy cơ về bệnh tim mạch, tiểu đường.<br>Ngũ cốc nguyên hạt<br>Trà xanh rất tốt cho cơ thể, giúp chống lại quá trình oxy hóa, đào thải các độc tố giúp bạn thanh lọc cơ thể. Trong trà xanh có chứa nhiều EGC phòng chống ung thư, uống trà xanh hàng ngày sẽ tăng cường quá trình trao đổi chất, cải thiện khả năng đốt mỡ của cơ thể. Ngoài ra uống trà xanh sẽ giúp đầu óc minh mẫn, sảng khoái, tâm trí thanh tịnh.<br>Bạn có thể uống một cốc trà sau bữa sáng để có tinh thần sảng khoái, tuy nhiên hạn chế chọn những loại trà có đường hay bổ sung hương liệu đi kèm.<br>Trà xanh chốn lão hóa da<br>Nước là thứ không thể thiều hàng ngày, chỉ cần đảm bảo mỗi ngày bạn uống 8 cốc nước, không chỉ có cơ thể bạn được đảm bảo lượng nước mà còn giúp bạn thanh lọc cơ thể, giúp da khỏe mạnh, căng mịn.<br>Hãy ăn uống lạnh mạnh, kết hợp chế độ thể dục thể thao hàng ngày, bạn sẽ có một cơ thể khỏe mạnh và căng tràn sức sống. Tuổi trẻ và tinh thần của bạn sẽ giữ được “phong độ” như ngày nào.<br>Vitamin C – Thần dược cho làn da được hàng triệu chị em mơ ước<br>“Bí kíp” nâng cấp làn da giúp U30 trẻ đẹp không kém gì 20 tuổi<br>Những lưu ý khi điều trị nám bạn không nên bỏ qua<br>&nbsp; <br>', 11, NULL, NULL, 1);
INSERT INTO `tuvan` (`id`, `category_id`, `title`, `TuVan`, `topic`, `climate`, `patience`, `status`) VALUES
(93, 4, 'Nói không với trị mụn cấp tốc tuổi dậy thì', 'Trị mụn cấp tốc là câu nói câu view của các doanh nghiệp hay cá nhân sản xuất kem trị mụn, với tốc độ khỏi mụn tính bằng giờ và những biến chứng để lại không ngờ. <br>Chỉ cần lên mạng tra phương pháp trị mụn cấp tốc, chúng ta sẽ thấy hàng loạt kết quả, tới 90% sẽ là sản phẩm kem bôi từ hand made, gia truyền đến đông y …..Giá cả cũng vô cùng, có loại chỉ vài chục ngàn nhưng có những loại đến cả triệu.<br>Liệu các sản phẩm này có hiệu quả như những gì họ nói không mà rất nhiều người chia sẻ khiến những ai đang bị mụn đều sao động. Để biết được nó có tốt như những lời đồn hay không hãy cùng phân tích.<br>Mụn và những điều bạn nên biết <br>Mụn có nhiều loại như mụn bọc, mụn mủ, mụn màu đen, mụn cám….xuất hiện chủ yếu ở mặt, số ít ở lưng và ngực gây những khó chịu nhất định cho chủ thể, nhất là tâm lý mặc cảm. Mụn hình thành do nhiều nguyên nhân khác nhau, từ bên trong cơ thể như nội tiết tố khiến da tiết nhiều bã nhờn….kết hợp với các yếu tố bên ngoài bụi bẩn, thói quen xấu, chế độ ăn uống không hợp lý ăn đồ cay nóng chiên xào..vv<br>Vấn đề nội tiết <br>Phần lớn mụn trứng cá là do vấn đề nội tiết bên trong cơ thể vì thế cần can thiệp từ bên trong mới có thể loại bỏ được vấn đề gốc rễ của mụn. Theo các bác sĩ tại phòng khám Da liễu Hà Nội cần dựa vào tình trạng mụn cụ thể từ đó áp dụng phương pháp điều trị phù hợp để đạt được hiệu quả như mong muốn. Đối với mụn trứng cá cần phải kiên trì và kết hợp nhiều phương pháp mới điều trị triệt để được tình trạng mụn.<br>Các tình trạng mụn<br>Xu hướng trị mụn cấp tốc <br>Trước nhu cầu làm đẹp, trị mụn sự ra đời của các loại sản phẩm thuốc, kem bôi trị mụn xuất hiện ngày càng nhiều với những quảng cáo mê hoặc lòng người. 10 sản phẩm trị mụn cấp tốc thì cả 10 đều không rõ thành phần, nguồn gốc. Với lời giới thiệu hết mụn sau 24h, quả đúng như những gì họ quảng cáo, mụn đi rất nhanh, dân tình lại càng chia sẻ mạnh.<br>Nhưng chỉ sau 1 thời gian ngắn mụn xuất hiện trở lại và còn nặng hơn trước, lúc này không có loại kem nào có thể cứu vãn được tình hình.<br>Nguyên nhân của sự biến mất đó là do sản phẩm bạn bôi có chứa thành phần corticoid loại kháng sinh nặng chỉ sử dụng cho những trường hợp cấp cứu đặc biệt. Khi sử dụng ban đầu sẽ diệt khuẩn kháng lại các tình trạng mụn, nhưng càng dùng sẽ càng bào mòn da, khiến da yếu đi và bị nhiễm corticoid.<br>Corticoid dùng để trị mụn, làm trắng da là sai lầm, nó là con dao hai lưỡi, đặc tính của nó là chống viêm, kháng khuẩn loại bỏ mụn hay làm trắng da nhưng mặt khác lại không trị dứt điểm được mụn. Dùng một thời gian sẽ gây mỏng da, làm giảm khả năng sản sinh các tế bào da, khả năng mụn sẽ mọc nhiều hơn là 90%, nếu sử dụng lâu bạn sẽ bị lệ thuộc vào thuốc. Ngoài ra các sản phầm chứ corticoid dùng một thời gian sẽ khiến da bị sừng hóa, giãn tĩnh mạch, gây đỏ da, nóng rát và khả năng tăng sắc tố sau viêm cao.<br>Xu hướng trị mụn cấp tốc<br>Lời khuyên của Bác sĩ “nói không với trị mụn cấp tốc” <br>Với những tác hại và hậu quả để lại không nên sử dụng các sản phẩm trị mụn cấp tốc, để điều trị mụn tân gốc nên đến bác sĩ để xác định được nguyên nhân gây mụn, tình trạng….để có biện pháp điều trị phù hợp.<br>Đặc biết trị mụn cần phải kiên trì, không thể một sớm một chiều có thể khỏi ngay được, cần kết hợp chăm sóc da tại nhà để đạt hiệu quả cao trong điều trị.<br>Bị mụn và tổn thương da do sử dụng mỹ phẩm chứa Corticoid<br>Xu hướng sở hữu làn da căng bóng bằng phương pháp cấy tinh chất DNA Cá Hồi của Sao Hàn<br>Giải pháp trị mụn hoàn hảo <br>', 11, NULL, 6, 1),
(94, 2, 'Ăn gì để trị nám, tàn nhang hiệu quả ?', 'Tàn nhang, nám hình thành do cả vấn đề từ bên trong cơ thể lẫn tác động bên ngoài từ môi trường cũng như cách chăm sóc da. Để điều trị hiệu quả bạn cần điều trị kết hợp và cân bằng các hoạt động sinh hoạt cũng như tâm lý. <br>Trong thời gian điều trị nám, tàn nhang, để hỗ trợ bạn nên chú ý đến việc chăm sóc sức khỏe bàn thân, thời gian nghỉ ngơi, chăm sóc da và nhất là chế độ ăn uống hợp lý, cải thiện yếu tố từ bên trong cơ thể.<br>Khi bị tàn nhang bạn nên bổ sung các thực phẩm chứa nhiều vitamin giúp da khỏe mạnh như trái cây, rau củ quả, ngoài ra những thực phẩm giàu omega 3 cũng rất cần thiết.<br>Sữa chua nha đam  <br>Sữa chua được chúng ta sử dụng hàng ngày giúp tiêu hóa tốt, bổ sung vitamin, lợi khuẩn, giúp da khỏe đẹp, kết hợp cùng nha đam lại càng tăng khả năng ngăn ngừa sự xuất hiện của mảng hội tụ không đều màu melanin. Ăn sữa chua nha đam hàng ngày sẽ giúp da của bạn luôn khỏe mạnh và trắng mịn.<br>Các trái cây quả mọng <br>Những quả trái mọng như cherry, việt quất, mâm xôi, cà chua…..vv có hàm lượng chất chống oxy hóa cao, ngăn cản được sự hình thành của các gốc tự do gây nám da, sạm da, tàn nhang . Lượng vitamin dồi dào trong các loại quả này có thể nói là thực phẩm hỗ trợ trị nám – tàn nhang cao cấp đến từ thiên nhiên.<br>Ăn quả mọng tốt cho da, hỗ trợ trong quá trình điều trị nám<br>Các loại ngũ cốc như đậu tương <br>Sự xuất hiện của nám, tàn nhan một phần do độ tuổi, thay đổi nội tiết tố khiến da lão hóa dễ bị tác động gây biến đổi sắc tố da. Trong đậu tương có chứa chất giúp cải thiện tình trạng nội tiết tố, cải thiện tình trạng bị giảm sút các yếu tố hình thành collagen và elastin duy trì độ đàn hồi căng bóng và khóe mạnh của da.<br>Cà rốt <br>Cà rốt là loại củ có nhiều vitamin, ăn carot giúp bổ máu, sáng mắt, chứa các vitamin A; C; D; E và vitamin nhóm B có lợi cho hệ miễn dịch của cơ thể. Đặc biết trong cà rốt có thành phần chống oxy hóa, thúc đẩy quá trình sản sinh collagen làm sáng da, loại bỏ các sắc tố da tối màu. Mỗi ngày bạn uống 2-3 ly nước ép cà rốt sẽ giúp quá trình điều trị nám, tàn nhang hiệu quả hơn nhiều.<br>Uống nước ép cà rốt hàng ngày giúp da đẹp hơn<br>Tinh bột nghệ <br>Nghệ được coi là loại thuốc quý, được ông cha ta sử dụng bao đời nay, nghệ giúp bổ máu, mờ sẹo , chống thâm, phá tan sự hội tụ của các sắc tố melanin trên da mạnh mẽ. Bạn có thể dùng tinh bột nghệ pha cùng sữa tươi, hay nước ấm để uống hàng ngày sẽ giúp da trắng mịn và hỗ trợ giảm tình trạng thâm nám.<br>Tinh bột nghệ pha sữa tươi<br>Bí đao <br>Bí đao là loại quả được dùng như một loại rau củ thanh nhiệt mùa hè ngoài ra còn được nhiều công ty nước giải khát dùng làm nguyên liệu tạo ra dòng nước bí đao thanh nhiệt. Bạn cũng có thể mua bí đao về ép lấy nước uống hàng ngày, nên uống trước bữa ăn. Uống nước bí đao hàng ngày sẽ giúp bạn có làn da trắng, kích thích tăng sinh collagen hạn chế sự tăng sinh của các melanin gây nám – xạm da.<br>Nước bí đao giúp thanh nhiệt<br>Trong quá trình điều trị nám, tàn nhang nên theo chỉ định của bác sĩ, tham khảo ý kiến của bác sĩ trong quá trình điều trị để có cách chăm sóc bằng sản phầm hay thực phẩm phù hợp.<br>Kem trộn, nguyên nhân gây nám da và những hậu quả khôn lường<br>Điều trị nám hiệu quả, trực tiếp bác sĩ thực hiện  <br>', 11, NULL, NULL, 1),
(95, 2, 'Tình trạng tăng sắc tố sau viêm khi điều trị Nám – Tàn nhang', 'Tăng sắc tố sau viêm là tình trạng dễ gặp phải khi chị em điều trị nám, tàn nhang bằng nhiều phương pháp khác nhau.  <br>Tình trạng tăng sắc tố sau viêm là hiện tượng tạm thời khi da bạn bị tổn thương hay viêm da, dễ nhận thấy với dấu hiệu da sậm màu hơn so với vùng da bình thường của bạn. Triệu chứng sưng hoặc những mảng đen hay nâu sậm màu …..Các tổn thương sẽ nghiêm trọng hơn nếu không điều trị sớm sẽ dẫn đến tổn thương vĩnh viễn.<br>Hậu quả của tăng sắc tố sau viêm<br>Nguyên nhân gây tăng sắc tố sau viêm\n &#8211; Da bị tổn thương do nhiều nguyên nhân khác nhau như ánh nắng mặt trời, dùng mỹ phẩm không rõ nguồn gốc, điều trị nám, tàn nhang<br>&#8211; Tăng sắc tố sau viêm không gây ra sẹo và thậm chí không qua điều trị, nhưng nó sẽ cải thiện theo thời gian. Tỷ lệ phục hồi khác nhau tùy theo từng cá thể, trung bình nó có thể mất 3 đến 24 tháng để vùng sẫm màu được cải thiện hơn. Tuy nhiên, một số trường hợp có thể kéo dài hơn thế nữa. Thời gian này tùy thuộc vào màu da và mức độ tăng sắc tố – quan trọng hơn là sự khác biệt, thời gian dài hơn sẽ giúp cho màu sắc được cân bằng lại. Các phương pháp điều trị có sẵn có thể cải thiện và tăng tốc thời gian phục hồi.<br>Phương pháp điều trị tăng sắc tố da sau viêm <br>Loại bỏ sắc tố da <br>Để loại bỏ sắc tố da sau viêm cần sử dụng công nghệ laser, với ánh sáng từ tia laser sẽ loại bỏ được các tế bào bị tăng sắc tố để tái tạo làn da mới. Bác sĩ sẽ điều chỉnh bước sóng phù hợp với tình trạng da của bạn, tác động lên lớp biểu bì hay xâm nhập sâu dưới lớp da loại bỏ sắc tố sau viêm.<br>Tùy vào tình trạng của mỗi người bác sĩ sẽ đưa ra phác đồ điều trị phù hợp, ngoài ra còn có nhiều loại thuốc và sản phẩm chăm sóc da có thể điều trị chứng tăng sắc tố sau viêm và thường chứa một hay nhiều thành phần sau đây:<br>Lưu ý khi sử dụng bất cứ loại thuốc nào để điều trị cần có sự chỉ định của bác sĩ, không nên tùy tiện sử dụng, mỗi trường hợp có mức độ khác nhau vì vậy cần có sản phẩm phù hớp với tình trạng của bạn.<br>Điều trị tăng sắc tố sau viêm bằng Laser Nanotech<br>&nbsp;<br>Chú ý chăm sóc da khi bị tăng sắc tố sau viêm <br>Kem trộn, nguyên nhân gây nám da và những hậu quả khôn lường<br>Vitamin C – Thần dược cho làn da được hàng triệu chị em mơ ước <br>', 13, NULL, NULL, 1),
(96, 6, 'Theo bác sĩ da liễu, chúng ta đang mắc một số sai lầm khi dùng kem chống nắng', 'Dùng kem chống nắng nhưng da vẫn sần sùi xấu xí, có thể là do các nàng đang mắc phải sai lầm tai hại này.<br>Những ngày đầu hè oi ả đã bắt đầu xuất hiện, bạn lục trong tủ kem chống nắng từ năm ngoái để thoa lên da. Nhưng mà khoan đã, kem chống nắng đó đã hết hạn chưa nhỉ? Các nàng có chắc rằng SPF trong kem chống nắng còn tác dụng hay không?<br>Trước tiên, chị em ta cần phải xác định rằng bạn cần dùng kem chống nắng quanh năm. Thứ 2, kem chống nắng cần thoa hoặc xịt lên toàn bộ cơ thể, kể cả phần mắt nhạy cảm. Thứ 3, chúng ta cần sử dụng kem chống nắng có độ SPF vừa phải để bảo vệ da tối ưu. Vậy nên trước khi sử dụng, hãy luôn chắc chắn rằng kem chống nắng của bạn vẫn còn hạn sử dụng.<br>Theo bác sĩ da liễu Shari Marchbein, chủ phòng khám DR.Shari Marchbein, giải thích:&#8220;Giống như thực phẩm, kem chống nắng cũng bị giảm chất lượng vì thành phần bị thay đổi dẫn đến tách nước/chảy nước. Chúng hoạt động kém hiệu quả, nghĩa là tăng khả năng làm bạn bị cháy nắng, tổn thương da do ánh nắng mặt trời, tạo đốm nâu và có nguy cơ phát triển tế bào ung thư da&#8221;.<br>Kem chống nắng hết hạn sẽ gây ra hậu quả khôn lường<br>Theo chuyên gia hóa học nổi tiếng Perry Romanowski, đồng thời là người sáng lập trang web làm đẹp The Beauty Brains, chia sẻ: &#8220;Những loại kem chống nắng khi hết hạn sẽ không còn đủ khả năng bảo vệ làn da mỏng manh của bạn. Lúc này kem chống nắng, dù là vật lý hay hóa học, đã không còn đạt được mức SPF như bao bì quảng cáo. Trong khi, các loại kem chống nắng hóa học với thành phần có chứa oxybenzone, avobenzone và homosalate có thể bị oxy hóa và trở nên kém hiệu quả. Còn kem chống nắng vật lý có thành phần kẽm oxit hoặc titanium dioxit dù không xảy ra tình trạng bị oxy hóa nhưng chúng vẫn bị giảm chất lượng và hiệu quả chống nắng. Hiểu đơn giản là: trong khi các thành phần kem chống nắng vẫn hoạt động thì chúng có thể không phát huy đúng chức năng bảo vệ da&#8221;.<br>Ngoài ra, bác sĩ Erin Gilbert, hiện đang là chủ phòng khám eringilbertmd và từng hợp tác với nhiều trang báo làm đẹp lớn, cho biết: &#8220;Khi bạn mở lọ kem chống nắng với đôi bàn tay bẩn, hoặc mở quá nhiều lần có thể khiến vi khuẩn xâm nhập vào sản phẩm&#8221;. Điều này sẽ làm giảm chất lượng kem và khiến da bị nổi mụn khi sử dụng.<br>Khi nào nên thay kem chống nắng?<br>Cũng theo bác sĩ Gilbert chia sẻ, hầu hết các loại kem chống nắng có hạn sử dụng kéo dài trong 3 năm. Vì vậy, điều đầu tiên khi đi mua kem chống nắng là bạn phải kiểm tra hạn sử dụng trên bao bì.<br>Ngoài ra, bác sĩ Gilbert cũng nhấn mạnh: &#8220;Nếu bạn để kem chống nắng ở nơi có nhiệt độ cao như trong xe hơi, hồ bơi hoặc trong túi xách thì nó sẽ hết hạn nhanh hơn do môi trường nhiệt độ cao. Trong trường hợp này, bạn nên thay thế vài tháng 1 lần để đảm bảo rằng kem chống nắng sẽ luôn hoạt động hiệu quả&#8221;.<br>Còn khi sử dụng kem chống nắng, bác sĩ Gilbert khuyên bạn luôn luôn phải kiểm tra chất lượng sản phẩm trước khi sử dụng. &#8220;Quan sát màu, độ lỏng/đặc, mùi trước và sau khi dùng sẽ cho bạn biết sản phẩm có bị nhiễm vi khuẩn hay chưa&#8221;. Và hãy nhớ dù còn thừa nhiều hay lười bôi đến đâu, thì bạn cần luôn thay mới kem chống nắng sau 1 năm kể từ khi mở nắp.<br>Dưới đây là 4 lưu ý cuối cùng về kem chống nắng của các chuyên gia da liễu mà tín đồ làm đẹp nào cũng nên thuộc nằm lòng:<br>1. Sử dụng đủ lượng<br>Theo bác sĩ Marchbein: &#8220;Một ounce – tương đương khoảng 1 chén nhỏ &#8211; là lượng kem cần thiết cho mặt và cơ thể&#8221;. Một ounce khoảng bằng 28 gram, đây là lượng kem chống nắng tiêu chuẩn giúp bảo vệ tối ưu làn da của bạn trước ánh nắng mặt trời. Còn nếu bạn chỉ dùng cho mặt thì cần khoảng 1 thìa café cho mỗi lần.<br>2. Thường xuyên bôi lại<br>Còn bác sĩ Gilbert lại nhắn nhủ: &#8220;Hãy bôi lại kem chống nắng của bạn sau mỗi 2 giờ. Nếu bạn trang điểm, có thể tìm đến những loại kem chống nắng dạng phấn bột. Nó vừa tạo thành &#8220;lớp chắn&#8221; bảo vệ da trước nắng mà không làm ảnh hưởng đến lớp trang điểm của bạn&#8220;.<br>3. Sử dụng thêm sản phẩm có chất chống oxy hóa<br>Các sản phẩm có chứa chất chống oxy hóa sẽ giúp bạn có làn da khỏe mạnh hơn. Cũng theo bác sĩ Gilbert: &#8220;Vitamin C là 1 chất chống oxy hóa mạnh, không chỉ giúp làn da của bạn khỏe hơn mà còn hạn chế việc gia tăng sắc tố gây nên đốm nâu, tàn nhang do tia UV gây ra&#8221;. Chính vì vậy ngoài kem chống nắng, bạn cũng đừng quên bổ sung thêm sản phẩm có chứa vitamin C hoặc có chứa các chất chống oxy hóa khác để tăng khả năng bảo vệ làn da.<br>4. Che chắn cơ thể cẩn thận<br>&#8220;Đừng quên rằng chỉ dùng kem chống nắng thì không đủ khả năng bảo vệ bạn hoàn toàn trước ánh nắng mặt trời. Trên thị trường hiện nay có rất nhiều loại mũ có thêm khả năng chống nắng&#8221; – bác sĩ Gilbert nói. Chưa kể mùa hè năm nay mũ chống nắng đang là mốt được nhiều tín đồ làm đẹp tìm mua, vì vậy bạn cũng đừng quên sắm thêm 1 chiếc mũ để bảo vệ da khỏi tác động của ánh nắng mặt trời.<br>Nguồn: Allure<br> Những lưu ý khi điều trị nám bạn không nên bỏ qua<br>Nguyên lý điều trị mụn bằng công nghệ E-light 3S<br> <br>', 11, NULL, NULL, 1),
(97, 2, 'Kem trộn, nguyên nhân gây nám da và những hậu quả khôn lường', 'Xu hướng làm đẹp ngày càng tăng cao, với nhu cầu muốn đẹp nhanh và rẻ khiến thị trường kem trộn ngày càng phát triển. Nhưng ít ai biết đến hậu quả của việc sử dụng các loại kem trộn này …<br>Chị em phụ nữ ai cũng mong muốn có một làn da trắng và thời gian cũng phải nhanh, vi vậy ngày càng có nhiều người làm kem trộn bán. Đối với người dùng chúng ta chỉ thấy tốc độ làm trắng nhanh trông thấy, điều này thảo mãn nhu cầu của chị em phụ nữ với các tiêu chí đưa ra “trắng – nhanh”<br>Nhưng ít ai quan tâm tới việc xuất sứ các sản phẩm đang dùng, thành phần là gì, có tác dụng phụ hay không ……vvv tất cả nhưng gì chúng ta thấy từ sản phẩm chỉ là cái tên và tác dụng thần kỳ của chúng.<br>Corticoid <br>&#8211; Là thành phần dùng trong y khoa để điều trị nhiễm trùng, đau…..những bệnh hiểm nghèo.<br>&#8211; Nhưng nếu lạm dụng sẽ gây hậu quả lớn đối với da của bạn<br>Nó sẽ giúp bạn làm trắng da hiệu quả, da căng mịn, mụn biến mất nhanh chóng chỉ sau 1 đêm nhưng thực tế da bạn đang bị ngậm nước, mất đường vân da do bị bào mòn. Tuy nhiên sau một thời gian bạn sẽ bắt đầu các triệu chứng sau: Nổi mẩn ngứa, đỏ tấy, rát da, nặng sẽ phồng roopk, mưng mủ sưng …..vv<br>Da bị tổn thương sau một thời gian ngắn sử dụng kem trộn, nếu nặng thì khả năng phục hồi da là không thể. Có rất nhiều bạn đã phải làm bạn với khẩu trang và tự ti về khuôn mặt “tấy đỏ” của mình.<br>Kem trộn và những tác hại để lại<br>Hydroquinone hay còn gọi là thủy ngân <br>&#8211; Công dụng làm sáng da, ức chế sự hoạt động của Melanin, các vấn đề về da như tàn nhang, đồi mồi, thâm, nám…..giúp việc điều trị hiệu quả hơn.<br>&#8211; Các loại kem trị nám đang nạm dụng hoạt chất này đưa nồng độ vượt quá mức cho phép gây ra tác dụng phụ trong quá trình sử dụng.<br>&#8211; Hoạt chất này ở các nước Châu Âu đã bị cấm sử dụng nhưng tại Việt Nam các hãng sản xuất kem vẫn đưa vào sử dụng và rao bán nhiều trên mạng xã hội.<br>&#8211; Các sản phẩm kem trộn có chứa nồng độ Hydroquinon cao sẽ làm da trắng nhanh, nhưng hàm lượng cao sẽ gây tăng hắc sắc tố da vĩnh viễn, nguy cơ phá hủy tế bào tạo hắc tố, khả năng bị tai biến cao gây các bệnh về da.<br>Acid citric (kem trộn)<br>&#8211; Là hoạt chất giúp trẻ hóa da, xóa tan nếp nhăn và những vết tích trên da dưới tác động của ánh nắng và kem trộn.<br>&#8211; Khi dùng một lượng lớn trong sản phẩm làm trắng sẽ khiến da bạn bị ngứa đỏ, sưng tấy, phồng rộp và bỏng da. Đó là tác dụng phụ khi dùng quá liều lượng khiến da biến đổi sắc tố tạo thành nám và tàn nhang.<br>Hậu quả sau khi dùng kem trộn<br>Ngoài ra trong kem trộn còn chứa các thành phần sau: Acide salicylique , Hydrogen peroxide, Aspirin là những hóa chất mạnh, dễ gây tổn thương da khiến da bị bào mòn, tổn thương sâu, mất đi khả năng đàn hồi của da, thậm chí là teo da….vv<br>Vì vậy khi muốn làm đẹp, chăm sóc da bạn cần kiểm tra thành phần và chứng nhận của bộ y tế trước khi sử dụng bất kỳ loại mỹ phẩm chăm sóc da nào. Nên mua sản phẩm tại các showroom, cửa hàng có uy tín. Tốt nhất nên khám và hỏi bác sĩ trước khi sử dụng các sản phẩm làm đẹp, hỗ trợ điều trị cho da, tránh những hậu quả không đáng có.<br>[Đúng hay Sai] những tác dụng của vitamin C dành cho da<br>Hậu quả kinh hoàng khi sử dụng kem trộn phụ nữ nhất định phải biết<br>CẢNH BÁO dấu hiệu nhận biết các sản phẩm, mỹ phẩm chứa Corticoid <br>', 12, NULL, NULL, 1),
(98, 1, '[Đúng hay Sai] những tác dụng của vitamin C dành cho da', 'Mọi thông tin chúng ta đọc được đều nói rằng vitamin C rất tốt cho da, muốn da đẹp cần bổ sung trái cây giàu vitamin C…vv Hãy cùng Phòng khám Da liễu Hà Nội khám phá về vi chất “thần thánh’ này nhé.<br>Da chúng ta căng bóng và khỏe mạnh là nhờ các sợi elastin và colagen tạo thành một cấu trúc bền vững. Nếu bạn bố sung vitamin C sẽ kích thích collagen hình thành giảm được quá trình lão hóa da, giúp làm mờ vết thâm nám. Vì vậy có thể nói vitamin C là một phần không thể thiếu cho làn da khỏe mạnh.<br>Tác dụng của Vitamin C với da<br>Chúng ta vẫn luôn nghĩ vitamin C chỉ là 1 nhưng thực chất dẫn xuất vitamin C khác nhau mang đến những tác dụng khác hoàn toàn.<br>&#8211; Với vitamin Cg có tác dụng giúp tăng cường tái tạo collagen, cung cấp màn bảo vệ chống oxy hóa và làm chậm quá trình lão hóa da.<br>&#8211; L-Ascorbic Acid hay Pure Vitamin C là một thành phần dưỡng da quyền lực, dễ dàng hấp thụ vào da và hoạt động hiệu quả tức thì. Là thành phần được khuyến nghị bởi các bác sĩ da liễu, tác dụng của Vitamin C L-Ascorbic Acid thường được dùng trong các sản phẩm chống lão hóa để cải thiện kết cấu da, cho da thêm rạng rỡ đồng thời giảm sự xuất hiện của đường nhăn, nếp nhăn cùng vết chân chim.  Tuy nhiên khi sử dụng chúng ta cần để ý đến nồng độ vitamin C<br>&#8211; Activated C là một dẫn xuất của Vitamin C thế hệ tiếp theo với hiệu quả đột phá. Loại Vitamin C mạnh mẽ này dễ dàng thấm vào da với khả năng làm mờ vết thâm mụn, giảm dấu hiệu lão hóa và làm đều màu da.<br>Nồng độ vitamin C càng cao không có nghĩa tác dụng của nó càng lớn, các sản phẩm có nồng độ L-Ascorbic Acid quá cao, da sẽ dễ bị kích ứng.<br>Vitamin C có nhiều loại và tác dụng khác nhau<br>Làn da cần được bổ sung vitamin C mỗi ngày ?<br>Vitamin C không những là một phần thiết yếu trong chế độ dinh dưỡng, mà còn là một yếu tố quan trọng trong chu trình chăm sóc da hàng ngày. Cơ thể con người không thể tự sản sinh ra Vitamin C mà phải hấp thu thông qua quá trình chuyển hóa dinh dưỡng hoặc bổ sung trực tiếp. Vitamin C giúp tái tạo collagen và ngăn chặn các gốc tự do. Việc sử dụng đều đặn và ổn định các loại tinh chất Vitamin C, sẽ đảm bảo làn da được hấp thụ các công dụng hữu ích từ thành phần mạnh mẽ này.<br>Những tác dụng của vitamin C ít ai biết<br>Vitamin C khiến da bạn yếu đi dưới ánh nắng mặt trời ?<br>Thực tế, Vitamin C đã được chứng minh là một hàng phòng vệ thứ hai chống lại tia UVA có hại đồng thời giúp tăng cường bảo vệ da chống lại quá trình oxy hóa. Bạn nên sử dụng tinh chất Vitamin C vào buổi sáng trước khi thoa kem chống nắng có chứa SPF để tăng thêm hiệu quả chống lại tia UV.<br>Vitamin C phù hợp với mọi loại da ?<br>Tác dụng tuyệt vời của Vitamin C áp dụng cho tất cả các loại da, bao gồm cả da khô và nhạy cảm. Ngoài ra, công thức của Vitamin C còn đạt hiệu quả trong việc điều chỉnh các đốm nâu và vết thâm mụn.<br>Vitamin C rất tốt cho làn da của bạn nếu dùng thường xuyên nó sẽ giúp bạn có làn da khỏe mạnh và đều màu. Sau khi rửa mặt hãy thoa một lớp mỏng tinh chất vitamin C lên da, sử dụng vào buổi sáng và tối trước bước dưỡng ẩm để tối đa hóa tác dụng của vitamin C <br>Vitamin C – Thần dược cho làn da được hàng triệu chị em mơ ước<br>Những cách trị mụn từ thiên nhiên các bạn gái đang “rỉ” tai nhau<br>Xu hướng sở hữu làn da căng bóng bằng phương pháp cấy tinh chất DNA Cá Hồi của Sao Hàn<br>&nbsp;<br>&nbsp; <br>', 11, NULL, NULL, 1),
(99, 1, 'Hậu quả kinh hoàng khi sử dụng kem trộn phụ nữ nhất định phải biết', '&nbsp;<br>Liệu kem trộn có thực sự thần thánh như lời quảng cáo, chỉ cần ba hôm bảy bữa là có thể hóa thiên nga? Chắc chắn nhiều người sẽ phải rùng mình khi biết được sự thật rùng rợn đằng sau nó.<br>Thời buổi bây giờ có thể ví von là ‘’nhà nhà bán kem trộn, người người dùng kem trộn’’ quả không sai. Khắp các tiệm mỹ phẩm online cho đến các thương hiệu handmade đình đám đều không thể thiếu sản phẩm làm trắng da thần thánh mang tên kem trộn. Mặc dù đã được cảnh báo rất nhiều lần về nguồn gốc cũng như chất lượng của kem trộn kém an toàn nhưng nhiều chị em vẫn bất chấp nguy hiểm để ‘’liều mình’’ sử dụng<br>Kem trộn là tên gọi chung của các loại thuốc hay kem để làm trắng da được trộn từ nhiều thành phần khác nhau như vitamin E, aspirin, corticoid,… Trong đó, corticoid được coi là lọai hóa chất nguy hiểm nhất nếu hàm lượng quá mức cho phép, có khả năng khiến da ngậm nước mạnh, da trắng mịn và căng mọng chỉ trong 24h<br>Đừng vội vui mừng nếu bạn vừa sử dụng 1 lần mà da đã trắng mịn, bớt mụn, giảm nếp nhăn rõ rệt. Kết quả này không phải là tác dụng của kem trộn mà do làn da của bạn phản ứng với các thành phần độc hại có trong kem, tốt nhất hãy ngừng sử dụng ngay lập tức và gặp bác sĩ nếu có những dấu hiệu nặng hơn.<br>Da bật tone rõ rệt chỉ sau 1 lần sử dụng<br>Nếu vẫn tiếp tục sử dụng, bạn sẽ nhận thấy vùng da mặt trắng bạch, khác biệt rõ rệt với vùng cổ không bôi kem, hai má ửng hồng khiến nhiều người lầm tưởng là tác dụng của kem làm da hồng hào, nhưng thực tế đó là vì các mao mạch dãn nở, khiến da bị đỏ. Nếu bạn đang bị nám, tình trạng này sẽ càng nặng hơn và thường xuyên có cảm giác nóng rát, thậm chí như kiến cắn trên da rất khó chịu.<br>&nbsp;<br>Da bị bào mòn, mỏng và nổi mao mạch do sử dụng sản phẩm kém chất lượng thời gian dài sử dụng<br>Phát hiện càng sớm thì sẽ càng giảm thiểu tác hại của kem lên da. Về lâu dài, vừa khó dứt vừa tốn thời gian, tiền bạc điều trị để phục hồi lại da sau này. Đừng vì cả tin mà rơi vào tình trạng tiền mất, tật mang đáng tiếc.<br>Là loại kem handmade gắn mác an toàn cho da, kem trộn ngày nay cũng được biến tướng rất nhiều loại. Dưới đây là những mẹo đơn giản giúp bạn nhận biết được kem trộn ‘’rởm’’:<br>&#8211; Bao bì sơ sài, mẫu hộp đại trà, cẩu thả, không có nhãn mác cụ thể.<br>&#8211; Thành phần không rõ ràng, không ghi hạn sử dụng của kem trên bao bì.<br>&#8211; Chất kem đục hoặc hơi vàng, mùi thơm nhẹ thậm chí ngửi kĩ còn có mùi chua<br>Hình ảnh kem trộn không có nhãn mác được bán ở rất nhiều cửa hàng mỹ phẩm<br>Tuy nhiên, hiện nay mánh khóe của dân buôn kem trộn trở nên tinh vi hơn nhiều, đầu tư bao bì, mẫu mã sang chảnh như hàng xách tay, thậm chí còn mua các loại giấy chứng nhận cam kết chất lượng để tạo niềm tin cho người tiêu dùng. Một số thương hiệu còn thuê người nổi tiếng PR sản phẩm. Vì thế, chị em hãy thật sự cẩn thận, đừng để bị đánh lừa nhé!<br>NGUỒN: BÁO MỚI <br>', 12, NULL, 8, 1),
(100, 4, 'Bị mụn và tổn thương da do sử dụng mỹ phẩm chứa Corticoid', 'Bản chất mụn hình thành do những vấn đề về da gây lên như da dầu, da nhờn, da bị các tác nhân môi trường gây ra. Nhưng đôi khi nó lại được hình thành do sự vô tình của chủ thể khi lựa chọn các sản phẩm chăm sóc da. <br>Corticoid là gì ? <br>Corticoid hay Glucocorticoid là một trong những hormon được tổng hợp tại tuyến vỏ thượng thận, trong đó quan trọng nhất đó là cortisol. Liệu pháp corticoid dựa trên tác dụng sinh học của các thành phần glucocorticoid tổng hợp để áp dụng trong lĩnh vực điều trị nhằm mục đích kháng viêm, chống dị ứng và ức chế miễn dịch.<br>Đây là loại độc dược nằm ở bảng B của Bộ Y tế, được ứng dụng để điều trị rất nhiều bệnh khác nhau: Chống viêm ở giai đoạn sớm, giai đoạn muộn bất kể nguyên nhân gì như cơ học, hóa học, nhiễm khuẩn và các bệnh dị ứng, hen phế quản, bệnh phổi tắc nghẽn mạn tính, khớp, viêm đại tràng, viêm gan mạn tự miễn và một số bệnh ác tính về máu. Ngoài ra còn trị xuất huyết giảm tiểu cầu do cơ chế miễn dịch, tán huyết miễn dịch…<br>Corticoid<br>Corticoid lợi hay hại?<br>Do corticoid chống viêm mạnh nên rất hiệu quả trong việc trị các mụn viêm, mụn mủ. Bên cạnh đó, nó còn có khả năng ức chế nhanh, tạm thời các tuyến bã nhờn giúp hạn chế tiết nhờn trên da dầu, từ đó ngăn chặn sự phát triển và hình thành của mụn. Nhờ công dụng giữ nước và bào mòn da nên Corticoid làm cho những làn da sần sùi, sẫm màu trở nên trắng sáng, mịn màng hơn, cho dù trước đó da bạn bị nám, bị mụn, bị nhăn nheo, già nua thì dưới tác động của Corticoid, cũng sẽ được thay đổi hoàn toàn. Do vậy, nhiều người lầm tưởng loại thuốc này là “thần dược”.<br>Song corticoid chỉ điều trị triệu chứng mà không điều trị nguyên nhân gây viêm, dẫn đến dễ gây lệ thuộc thuốc và hiệu ứng phản hồi. Vì vậy, nếu sử dụng liên tục thì chỉ 2-3 tuần sau đó, làn da sẽ gặp những biến chứng nặng nề như viêm da, nhiễm trùng thứ phát, nám lan rộng và sâu khó chữa, teo da, lão hóa da, giãn mạch gây đỏ da…<br>Các sản phẩm mỹ phẩm chứa Corticoid trên thị trường <br>Trên thị trường hiện nay có rất nhiều sản phẩm kem trị mụn, làm trắng da ….chỉ cần dùng trong 1 đến 2 tuần bạn sẽ hết mụn hay trắng da. Da bạn sẽ đẹp hơn nhanh chóng nhưng chỉ sau một thời gian ngắn da bị bào mòn, dễ kích ứng nổi mụn trở lại và bị nhiễm Corticoid nặng.<br>Nếu bạn bị nhiễm corticoid từ các sản phẩm mỹ phẩm bạn sẽ thấy dấu hiệu sau: Da mỏng yếu, đỏ, khô, nhạy cảm, sinh mụn, giãn mao mạch …có thể bị gia tăng các hắc sắc tố hay bị mất sắc tố, xuất hiện nhứng vết thâm sạm, da tối màu, thay đổi các kết cấu da.<br>Do những hiệu quả nhất thời mà các bạn trẻ đang vô tình làm tổn thương làn da của chính mình, vì vậy các bạn đừng quá tham rẻ, muốn nhanh hết mụn, trắng nhanh để mua bệnh vào người nhé !<br>Tình trạng bị nhiễm Corticoid <br>1.Thể viêm da kích thích<br>Đây là thể rất nặng và cấp tính do dùng các sản phẩm có chứa corticoid mạnh và độc tính cao, gây khó chịu cho người bệnh: đỏ da, nóng rát, ngứa, bong tróc da, mẩn đỏ hay lên mụn nước có dịch bên trong,.,,\n 2.Thể viêm da tăng tiết nhờn và nổi mụn<br>Da nhờn nhiều, da nổi mụn đầu trắng, đầu đen nhiều khi ngừng bôi kem, da có mụn viê mụn mủ, nhiều sẹo lõm và thâm..\n 3.Thể đỏ da dãn mạch kéo dài<br>Đây là thể nặng dai dẳng có dấu hiệu nóng rát, da đỏ hơn khi vận động, tình trạng dãn mạch đỏ da lập lại nhiều cơn trong ngày. Sưn phù mặt, châm chích, ngứa ngáy ….<br>4.Thể viêm da dạng phồng dộp<br>Thường sảy ra ở những người có cơ địa dị ứng bẩm sinh. Da nổi mụn nước phồng như bỏng, gây đau nhức, nóng rát, có thể nhiễm trùng thành các túi mủ. Để lại tình trạng thâm sau viêm…<br>5.Thể khô da bong tróc<br>Đây là thể nhẹ, nhiễm corticoid độc tính thấp, dùng thời gian ngắn và có pha trộn với các thành phần khác.<br>Da khô sần có kèm cảm giác ngứa, thời gian phục hồi nhanh hơn các thể khác.<br>Nhiễm Corticoid khiến da bị bào mòn vỡ cấu trúc<br>Cách để bạn tránh xa các sản phẩm làm đẹp chứa Corticoid <br>Để không mua phải những sản phẩm chứa corticoid bạn nên tránh xa các mỹ phẩm bán online từ thuốc đông y, sản phẩm hand made, tác dụng hết mụn nhanh và làm trắng cấp tốc. Lựa chọn các sản phẩm làm đẹp an toàn, có xuất xứ rõ ràng.<br>Nếu bạn đang dùng những sản phẩm nghi ngờ có chứa corticoi hãy ngừng sử dụng và đến ngay bác sĩ để được khám và điều trị kịp thời.<br> Thanh xuân cùng mụn của cô sinh viên năm 3 Vũ Phương Thảo<br>Tâm thư của “em gái mụn” gửi Phòng khám da liễu Hà Nội <br>', 11, NULL, NULL, 1),
(101, 1, 'Vitamin C – Thần dược cho làn da được hàng triệu chị em mơ ước', 'Theo khoa học vitamin C tốt cho sức đề kháng của cơ thể, giúp bạn có làn da khỏe mạnh và tươi sáng. Cùng xem vitamin C giúp được gì cho làn da của bạn nhé! <br>Vitamin C có nhiều trong rau củ quả, trái cây, để bổ sung dễ nhất bạn nên ăn nhiều cam, quýt, nước chanh, bưởi……nhưng hấp thụ từ thực phẩm vẫn chưa đủ để bạn có làn da đẹp. Bạn có thể mua vitamin C bôi trực tiếp lên da, đây là liệu pháp skincare được các chị em ưa chuộng nhất.<br>Chức năng chính của vitamin C là sản sinh collagen, chất đạm chính của cơ thể, collagen có vai trò vô cùng quan trọng trong việc liên kết các cấu trúc cơ thể với nhau như mô liên kết với sụn khớp ….Collagen cần thiết cho sự làm lành vết thương, “sức khỏe’ của nướu.<br>Vitamin C là nguồn dinh dưỡng giúp bạn chống lại oxy hóa, chống lão hóa, giúp da đẹp va hạn chế các tác nhân gây lão hóa, các bệnh thoái hóa hay ngăn ngừa ung thư.<br>Khi da bị thiếu vitamin C sẽ trở nên khô, sần sùi, tăng khả năng bị sừng hóa ở nang lông.<br>Vitamin C giúp da luôn khỏe mạnh<br>Chính vì thế mỗi ngày cơ thể của chúng ta cần 70 đến 100mg vitamin C tương ứng với việc ta cần ăn 100gr đến 200gr trái cây các loại hoặc rau. <br>Giúp da trắng mịn<br>&#8211; Vitamin C có tác dụng oxy hóa nên có thể chống lại được các gốc tự do, ngăn ngừa sự lão hóa, giúp duy trì làn da khỏe mạnh.<br>&#8211; Vitamin C cần thiết cho sự tạo thành collagen, sửa chữa các mô trong cơ thể và tham gia vào một số quá trình chuyển hóa chất.<br>Trị thâm nám<br>&#8211; Vitamin C có khả năng cải thiện nhiều vấn đề về da, lấy đi lớp sừng già, ức chế việc tạo thành melanin, làm mờ sắc tố, thúc đẩy quá trình hình thành sợi collagen tái tạo da.<br>Chống lão hóa<br>&#8211; Vì vitamin C giảm được các tác nhân gây gốc tự do và là chất bảo vệ da hữu hiệu nên chống lại được sự ảnh hưởng hưởng của các tia tử ngoại từ ánh nắng mặt trời.<br>&#8211; Hiệu quả của vitamin C kéo dài vì vậy nó giúp làm chậm quá trình lão hóa da do ánh nắng.<br>Công dụng trẻ hóa của vitamin C<br>Quả bơ ít ai biết nó chứa vitamin  C và rất nhiều vitamin khác nữa<br>&#8211; Xay nhuyễn bơ hoặc dằm nát, sau đó bôi 1 lớp mỏng lên mặt để trong vòng 20 phút, sau đó rửa mặt sạch.<br>Mặt nạ cà chua có chứa nhiều vitamin giúp da sáng<br>&#8211; Bạn chỉ cần lột vỏ cà chua, bỏ hạt, nghiền nhuyễn, thêm vài giọt chanh tươi và sau đó đắp lên mặt. Để trong vòng 15p đến 20p sau đó rửa mặt.<br>&#8211; Thực hiện 2-3 lần, mặt nạ cà chua sẽ giúp da bạn sáng hồng trông thấy.<br>Mặt nạ từ trái cây có chứa vitamin C<br>“Bí kíp” nâng cấp làn da giúp U30 trẻ đẹp không kém gì 20 tuổi<br>Bí quyết để có làn da trẻ trung đầy sức sống<br>&nbsp; <br>', 11, NULL, 8, 1),
(102, 4, 'Những cách trị mụn từ thiên nhiên các bạn gái đang “rỉ” tai nhau', 'Bị mụn thường gặp ở tuổi dậy thì, thay đổi nội tiết, môi trường sống hay do kích ứng với sản phẩm mỹ phẩm. Khi bị mụn bạn cần chăm sóc da tốt hơn để tránh tình trạng mụn bị viêm nhiễm.<br>Trên các trang mạng xã hội các bạn gái đang truyền tai nhau những phương pháp trị mụn bằng thiên nhiên hãy cùng chúng tôi phân tích để thấy liệu có thực sự hết được mụn không nhé !<br>Nước chanh giàu vitamin C, uống nước chanh giúp thanh lọc cơ thể, bạn có thể dùng nước cốt chanh để lau lên vùng da bị mụn làm se và khô vết mụn, vì chanh có tính axit tẩy tế bào chết trên da. Để làm sạch mụn, đặc biệt là các nốt mụn mủ, bạn có thể ngâm một ít bông gòn vào 2 muỗng nước cốt chanh tươi. Thoa dung dịch lên vết mụn, để khoảng 20 phút rồi rửa sạch bằng nước ấm. Thực hiện 2 – 3 lần mỗi ngày.<br>Ngoài ra, hỗn hợp nước cốt chanh và nước hoa hồng cũng sẽ cho kết quả tương tự. Thực hiện bằng cách bôi hỗn hợp này lên mụn trong vòng 30 phút. Rửa sạch với nước ấm và nên áp dụng 2 lần/ngày trong 1 tuần để có kết quả như mong đợi.<br>Trị mụn bằng chanh<br>&gt;&gt; Chanh có tính axit cao nên khi bôi lên mụn, nhất là mụn viêm sẽ rất xót, bạn cũng không thể chắc chắn được nước chanh có đảm bảo diệt khuẩn không. Dùng nước cốt chanh nhiều cũng không tốt cho da vì tính bào mòn và dễ bị bắt nắng.<br>Các thành phần như baking soda, hydrogen peroxide và tinh dầu bạc hà trong kem đánh răng có thể giúp làm khô nốt mụn nhanh chóng. Tuy nhiên, chỉ nên sử dụng kem đánh răng màu trắng. Bôi kem đánh răng lên mụn, để trên 2 giờ hoặc qua đêm và rửa sạch bằng 1 chiếc khăn ấm. Dùng thêm kem dưỡng ẩm nhẹ ngay sau đó để tránh khô da. Lặp lại mỗi ngày một lần trong 1 hoặc 2 tuần.<br>Trị mụn bằng kem đánh răng<br>&gt;&gt; Kem đánh răng có tính diệt khuẩn, nó chỉ có tác dụng kháng khuẩn và làm xe đầu mụn, kem đánh răng hoàn toàn không có khả năng trị mụn.<br>Dù có mùi khó chịu, nhưng tính kháng khuẩn và hàm lượng lưu huỳnh cao trong tỏi lại là phương thuốc điều trị các loại mụn nhanh, hiệu quả. Bên cạnh đó, tỏi còn có khả năng kháng sinh, kháng nấm, sát khuẩn và làm lành da.\n Để khôi phục làn da láng mịn, bạn có thể cắt đôi tép tỏi và dùng mặt trong chà xát lên nốt mụn. Thực hiện trong vòng từ 5 – 10 phút trước khi rửa sạch. Lặp lại phương pháp này vài lần trong ngày. Bạn cũng có thể ăn 1 – 2 tép tỏi sống mỗi ngày khi đói bụng giúp cơ thể thải loại độc tố trong máu, nhờ đó các vết mụn nhọt mau lành và không để lại sẹo thâm.<br>Trị mụn bằng tỏi<br>&gt;&gt; Thực tế dùng tỏi trị mụn rất dễ gây thâm da do trong tỏi có thành phần dễ gây bỏng da gây thâm, tỏi vừa nóng lại có mùi gây khó chịu cho người dùng cũng như người xung quanh ta.<br>Xông mặt bằng hơi nước sẽ làm lỗ chân lông được mở to, giúp vi khuẩn, bụi bẩn và chất dầu tích tụ sâu trong da theo mồ hôi thoát ra khỏi cơ thể. Trị mụn bằng hơi nước khá đơn giản và dễ thực hiện. Đổ nước sôi vào bát lớn, canh nhiệt độ vừa đủ nóng để không làm bỏng da.<br>Đưa mặt lên phía trên chậu, trùm thêm 1 chiếc khăn lông dày qua đầu ngăn hơi nước thoát ra ngoài. Xông khoảng 10 – 15 phút, tháo khăn, lau khô mặt rồi thoa 1 lớp kem dưỡng ẩm không chứa dầu. Nên thực hiện 2 lần mỗi ngày, liên tục trong 4 – 5 ngày để giảm vết mụn sưng tấy và làm làn da bạn thêm tươi sáng, mịn màng.<br>Xông mặt bằng hơi nước trị mụn<br>&gt;&gt; Xông mặt bằng nước ấm để giãn nở lỗ chân lông giúp việc loại bỏ bụi bẩn tốt nhưng sau đó phải rửa lại bằng nước lạnh để lỗ chân lông khít lại, tránh bụi bẩn bám vào. Xông hơi nước chỉ giúp bạn vệ sinh da tốt tránh vi khuẩn gây hại làm mụn nặng, không có tác dụng trị mụn.<br>Nước đá cải thiện tình trạng lưu thông máu dưới vùng da bị mụn, làm đóng băng khu vực có lỗ chân lông đang tắc nghẽn, từ đó loại bỏ các hạt dầu và bụi bẩn tích tụ trên da. Đồng thời, nước đá cũng giúp giảm viêm và đau do mụn nhọt.<br>Chỉ cần bọc đá viên hoặc đá vụn trong vải cotton sạch, nhẹ nhàng nhấn lên các khu vực bị viêm nhiễm do mụn trong vài giây. Chờ khoảng 1 phút trước khi lặp lại thao tác tương tự từ 3 &#8211; 4 lần. Áp dụng phương pháp này mỗi ngày khoảng 2 lần sẽ giúp bạn nhanh chóng “giã biệt” các nốt mụn đáng ghét chỉ trong vòng 1 hoặc 2 ngày.<br>Trị mụn bằng đá viên<br>&gt; &gt;Nước đá chỉ có tác dụng làm dịu mụn, giảm tình trạng sưng tấy.<br>Nhờ đặc tính khử trùng, tiêu diệt các vi khuẩn gây mụn lẫn ngăn ngừa hình thành nốt mụn mới mà nghệ luôn có mặt trong cẩm nang làm đẹp từ hàng nghìn năm trước. Nghệ còn chứa nhiều chất chống oxy hóa tốt, từ đó giúp da mau liền sẹo và giảm thâm hiệu quả.<br>Trộn 1 muỗng bột nghệ với nước tạo thành hỗn hợp sệch. Bôi lên vùng da bị mụn, để khô rồi rửa sạch với nước lạnh mỗi ngày 2 lần trong 1 tuần. Hoặc có thể trộn bột nghệ với 1 thìa nước ép lá rau mùi thành mặt nạ đắp mụn. Để khô tự nhiên trước khi rửa sạch. Thực hiện đều đặn phương pháp này 1 – 2 lần mỗi ngày để các nốt mụn nhanh biến mất.<br>Nếu muốn tăng công dụng ngừa và trị mụn nhọt , bạn có thể pha 1 muỗng cà phê bột nghệ vào sữa ấm. Uống mỗi ngày trước khi ngủ, bạn sẽ thấy bất ngờ vì hiệu quả trị mụn của loại thảo dược rất rẻ tiền và dễ tìm này mang lại cho làn da của mình.<br>Trị mụn bằng củ nghệ tươi<br>&gt;&gt; Nghệ chỉ dùng đắp mặt khi đã hết mụn, vì khi bạn vẫn còn mụn mà đắp mặt nạ bột nghệ sẽ gây bít tắc mụn, nghệ có tác dụng phụ nhiễm màu gây vàng da. Các bạn nên nghĩ kỹ trước khi dùng nghệ nhé.<br>Nhìn chung các cách trên chỉ giúp bạn chăm sóc và bảo vệ tốt hơn trong thời gian bị mụn, không có tác dụng trị được mụn hoàn toàn. Để trị mụn cần lấy hết được nhân mụn, cân bằng được lượng dầu và kháng khuẩn tốt cho da. <br>Giải pháp trị mụn hoàn hảo <br>Bạn đã rửa mặt đúng cách khi bị mụn chưa  <br>', 15, NULL, 8, 1),
(103, 1, 'Cạo lông mặt đúng cách sẽ giúp da bạn sáng lên trông thấy', 'Không phải cô gái nào cũng cạo lông mặt vì lý do sợ lông mọc lại sẽ dày cà đen hơn, nhưng nếu bạn biết cạo đúng cách thì đây là giải pháp khiến da sáng hơn trông thấy nhanh nhất đấy.<br>Cạo lông mặt giúp da sáng hơn<br>Cạo lông mặt thực chất là một hình thức tẩy tế bào chết, giúp kích thích các tế bào da mới phát triển, nhờ vậy các dưỡng chất từ các sản phẩm chăm sóc da sẽ thấm sâu hơn. Ngay sau khi cạo lông mặt bạn sẽ thấy da sáng hẳn lên, đây là tác dụng của việc loại bỏ các tế bào chết trên da. Bạn nên cạo lông mặt vào buổi tối trước hôm cần trang điểm để kem nền, kem trang điểm bắt sáng và được phủ đều màu.<br>Theo tiến sỹ Michael Prager (bác sỹ thẩm mỹ tại London), việc cạo lông mặt thường xuyên có tác dụng tích cực góp phần chống lão hoá da, hơn nữa, việc làm này còn khuyến khích sản xuất ra collagen, từ đó làm giảm nếp nhăn và kích thích tái tạo tế bào da.<br>Tuy nhiên, để cạo lông mặt sao cho da mềm và đẹp, lại hạn chế lông mặt mọc lại dày hơn, chúng ta cũng cần biết thực hiện như thế nào cho đúng cách.<br>Trên thực tế, các bước cạo lông mặt cũng rất đơn giản, tuy nhiên, không phải ai cũng biết và thực hiện đúng quy trình này. Hãy cùng tham khảo ngay các bước cạo lông mặt nhé!<br>Hướng dẫn cạo lông mặt đúng cách <br>Để cạo lông mặt hiệu quả và đúng cách nên làm theo các bước sau:<br>Bước 1. Làm sạch da<br>Dùng dầu tẩy trang làm sạch da mặt, sau đó rửa mặt lại với sữa rửa mặt<br>Bước 2. Thoa kem cạo<br>Thoa một lớp phấn hoặc kem cạo vừa đủ lên vùng da muốn cạo<br>Bước 3. Cạo lông mặt<br>Dùng dao cạo phần lông mặt theo chiều xuôi của lông, hướng từ ngoài vào trong<br>Bước 4. Làm sạch da mặt<br>Rửa mặt lại với nước lạnh, sau đó dùng toner để se khít lôc chân lông<br>Bước 5. Dưỡng da<br>Để cho da nghỉ khoảng 20 đến 25 phút sau đó bôi kem dưỡng ẩm để làm mềm và cấp ẩm cho da<br>Cạo lông mặt đúng cách<br>Lưu ý <br>&nbsp;<br>&nbsp; <br>', 11, NULL, NULL, 1),
(105, 45, 'Bệnh tổ đỉa ở người lớn chữa như thế nào ?', 'Phương pháp cấy tinh chất DNA cá hồi ra đời đã và đang thu hút sự quan tâm của rất nhiều chị em yêu thích làm đẹp. Đây được xem là “bí quyết”  giúp trẻ hóa da, xóa tan mọi nỗi lo về dấu hiệu lão hóa, tuổi tác và dự kiến sẽ trở thành xu hướng làm đẹp trong năm 2018 này. <br>Cá hồi chứa nhiều Vitamin A, D, các axit béo omega-3, protein…cung cấp dưỡng chất cải thiện kết cấu da, giúp da luôn căng mịn và giữ ẩm cho da, hạn chế tình trạng thô sần, khô ráp.<br>Trong bào thai cá hồi có chứa tinh chất PDRN (polydeoxyribonucleotide) là nhân tố tăng trưởng tế bào da, rất tốt cho việc phục hồi làn da khỏe đẹp.<br>Các chuyên gia thẩm mỹ đã tận dụng những thành phần quý trong cá hồi để điều chế thành công tinh chất trị liệu da DNA. Phương pháp này tái sinh làn da và ngăn ngừa các dấu hiệu lão hóa như nếp nhăn, lỗ chân lông to… giúp da sáng, mịn  màng rõ rệt sau mỗi lần trị liệu.<br>Có rất nhiều phương pháp làm đẹp, trẻ hóa da khác nhau song phái đẹp các nước Nhật Bản, Hàn Quốc…luôn ưu tiên lựa chọn cấy tinh chất DNA cá hồi  vì những lợi ích tuyệt vời mà phương pháp này mang lại:<br>Duy trì hiệu quả làn da khoẻ mạnh lâu dài<br>Da duy trì độ trẻ đẹp, cân bằng, khoẻ mạnh lâu dài tuỳ thuộc vào quá trình hoạt động của nguyên bào sợi. Thay vì đắp mặt nạ, bôi kem dưỡng da chỉ cần 1 buổi cấy tinh chất DNA cá hồi da sẽ mịn màng, căng bóng.<br>Cải thiện đàn hồi cho da<br>Bên cạnh tác dụng trẻ hóa, tinh chất DNA cá hồi còn hỗ trợ quá trình tăng sinh Collagen và Elastin giúp da chắc khoẻ, phục hồi da mỏng, da chùng xệ, mất khả năng đàn hồi kéo dãn. Bên cạnh đó, cấy tinh chất DNA cá hồi còn giúp duy trì và cân bằng độ ẩm của da.<br>Cải thiện tone da<br>Thúc đẩy khả năng tuần hoàn máu và các cơ quan chức năng khác trong cơ thể, nâng tone da sáng trắng, loại bỏ da ửng đỏ, sạm đen, da lốm đốm, nhạt màu, da thâm mụn, da ngứa. Da sẽ bật tone rõ rệt sau liệu trình cấy DNA cá hồi.<br>Hiệu quả nhanh chóng và an toàn<br>Sau khi thực hiện cấy tinh chất DNA cá hồi bạn hoàn toàn không phải kiêng khem gì vì phương pháp này không gây tổn thưởng da. Cho đến nay chưa có bất cứ khách hàng nào bị dị ứng, viêm nhiễm sau khi sử dụng dịch vụ.<br>Quy trình cấy tinh chất DNA tại Phòng khám Da liễu Hà Nội tuân thủ đúng mọi quy định của Bộ Y tế và được các Bác sĩ chuyên khoa da liễu, chuyên gia thẩm mỹ đánh giá an toàn với mọi tình trạng da.<br>Bước 1. Bác sĩ chuyên khoa thăm khám và tư vấn<br>Bước 2. Làm sạch vùng trị liệu<br>Bước 3.  Xông hơi và hút tạp<br>Bước 4. Thoa và ủ tê để khách hàng không bị châm chích khi thực hiện<br>Bước 5. Sát khuẩn bề mặt da<br>Bước 6. Thực hiện cấy tinh chất DNA cá hồi<br>Tùy thuộc vào mục đích làm đẹp, tình trạng da Bác sĩ sẽ điều chỉnh lực tác động của máy để đưa tinh chất đến độ sâu mong muốn.<br>Đối với trường hợp da bị lão hóa, thâm mụn tinh chất DNA sẽ được đưa vào trung bì của da để thúc đẩy quá trình tăng sinh Collagen và Elastin giúp da căng bóng, mịn màng và trắng sáng đều màu. Trường hợp da bị sẹo rỗ, sẹo lõm do mụn tinh chất DNA sẽ được đưa sâu xuống hạ bì để hỗ trợ phá vỡ đáy sẹo và làm đầy sẹo, kể cả tình trạng sẹo lâu năm cũng được cải thiện đáng kể.<br>Bước 7. Sát khuẩn bề mặt da<br>Bước 8. Đắp mặt nạ để thanh lọc da<br>Bước 9. Làm sạch và bôi kem chống nắng<br>Bước 10. Bác sĩ hướng dẫn cách chăm sóc da tại nhà<br>Thời gian và liệu trình<br>Mỗi buổi cấy tinh chất DNA cá hồi dao động từ 45-60 phút, mỗi buổi cách nhau 3 – 4 tuần. Bác sĩ chuyên khoa da liễu khuyến cáo nên thực hiện liệu trình 2-5 buổi để đạt hiệu quả như mong muốn.<br>Nắm bắt được nhu cầu làm đẹp ngày càng tăng cao của chị em, nhiều thẩm mỹ viện, spa mọc lên nhưng không được cấp phép hoạt động. Do đó bác sĩ chuyên khoa da liễu, chuyên gia thẩm mỹ khuyến cáo lựa chọn những cơ sở y tế uy tín để làm đẹp và Phòng khám Da liễu Hà Nội là sự lựa chọn hoàn hảo của chị em.<br>Thông tin địa chỉ:\r\n PHÒNG KHÁM DA LIỄU HÀ NỘI\r\n Cơ sở: 447 Hoàng Quốc Việt, Hà Nội (Đối diện trường Đại học điện lực)\r\n Cơ sở: 54 Nguyễn Xiển, Thanh Xuân, Hà Nội\r\n Cơ sở: 114 Lương Văn Thăng, TP Ninh Bình\r\n Hotline: 094.947.0055<br>YÊU CẦU GỌI LẠI<br> <br> <br>Bạn cần tư vấn: Mời chọn dịch vụTrị mụnLấy nhân mụnTrị sẹo rỗ, sẹo lõmTrị thâm mụnTrị nám, tàn nhangTrẻ hóa daXóa hình xămKhám da liễuDịch vụ khác <br> <br><br>', 15, NULL, 7, 1),
(106, 15, 'Trẻ em hay bị côn trùng tấn công vào mùa hè vì sao ?', 'Theo nghiên cứu của hiệp hội bảo vệ trẻ em toàn cầu, trẻ em thường xuyên bị tấn công hơn người trưởng thành bởi côn trùng là do 1 trong các lí do sau đây:\r\n1. Chúng thường mặc quần áo sặc sỡ, bắt mắt, điều này thu hút cảm quan của côn trùng\r\n2. Da trẻ em thường mỏng, ít lông. Đây là lợi thế của côn trùng khi chúng chọn mục tiêu là những đứa trẻ thay vì người lớn\r\n3. Do tính hiếu động, hay đi vào các nơi có thể là nơi chú ngụ của côn trùng, dẫ tới chúng bám theo và tấn công trẻ', 19, 3, 1, 1),
(107, 15, 'Côn trùng nào tấn công gây tử vong ?', '', 11, NULL, NULL, 1),
(108, 2, 'Dấu hiệu của nám da tuổi tiền mãn kinh ở phụ nữ', 'Khi sắc tố da giảm sút cộng với rối loạn tâm sinh lí tuổi tiền mãn kinh sẽ khiến cho chất lượng cuốc sông giảm sút, 1 trong những biểu hiện chính là sự xuất hiện của nám da.\r\nVề biểu hiện, nám da sẽ để lại trên mặt nạn nhân những đốm nhỏ khoảng 0.2 cm2, chúng có màu xám, xám vàng hăọc xám nhạt, đậm dần theo thời gian.', 13, NULL, 8, 1),
(109, 19, 'Tại sao  dễ mắc bệnh viêm da ở trẻ dị tật', 'Theo thống kê, có tới 40% trẻ bị mắc các tật bẩm sinh sẽ mắc bệnh viêm da cơ địa khi lớn lên, và lí do được đưa ra là:\r\n đối với các trẻ bị dị tật, khuýt, suy dinh dưỡng, đề kháng yếu thì da là con đường dễ lây bệnh nhất, khi chúng tiếp xúc vào các bề mặt nhiễm khuẩn thì có thể sẽz bị tấn công.', 19, NULL, 1, 1);
